-- 사도행전 주석 Part 1 (1-14장)
-- 교회의 탄생, 초기 전도, 바울의 첫 번째 선교여행

-- 사도행전 1장: 승천과 맛디아 선택
UPDATE bible_verses SET
    chapter_title = '승천 명령과 성령 강림 약속, 맛디아 선택',
    chapter_summary = '데오빌로여 내가 먼저 쓴 글에는 예수의 행하시며 가르치시기를 시작하심부터 그의 택하신 사도들에게 성령으로 명하시고 승천하신 날까지의 일을 기록하였노라 하며, 해받으신 후에 또한 저희에게 확실한 많은 증거로 친히 나타나사 사십 일 동안 저희에게 보이시며 하나님 나라의 일을 말씀하시니라 하시고, 사도와 같이 모이사 저희에게 분부하여 가라사대 예루살렘을 떠나지 말고 내게 들은 바 아버지의 약속하신 것을 기다리라 요한은 물로 세례를 베풀었으나 너희는 몇 날이 못되어 성령으로 세례를 받으리라 하시니라 하시며, 저희가 모였을 때에 예수께 묻자와 가로되 주께서 이스라엘 나라를 회복하심이 이 때니이까 하니 가라사대 때와 기한은 아버지께서 자기의 권한에 두셨으니 너희의 알 바 아니요 오직 성령이 너희에게 임하시면 너희가 권능을 받고 예루살렘과 온 유대와 사마리아와 땅 끝까지 이르러 내 증인이 되리라 하시고 이 말씀을 마치시고 저희 보는데서 올리워 가시니 구름이 저를 가리워 보이지 않게 하더라 하시며, 이 후에 저희가 다락방으로 올라가니 베드로, 요한, 야고보, 안드레, 빌립, 도마, 바돌로매, 마태, 알패오의 아들 야고보, 셀롯인 시몬, 야고보의 아들 유다가 다 마음을 같이하여 전혀 기도에 힘쓰니라 하시고 유다를 대신하여 맛디아를 사도로 선택합니다.',
    chapter_themes = '["부활 후 40일", "성령으로 세례", "땅 끝까지 증인", "승천", "다락방 기도", "맛디아 선택"]',
    chapter_context = '예수님의 승천과 성령강림을 위한 기도, 그리고 열두 사도의 완성으로 초대교회의 기초가 마련되는 과정입니다.',
    chapter_application = '성령의 임하심을 간절히 기다리고, 온 세상에 복음을 전할 준비를 하며, 함께 기도하는 공동체를 이루어야 합니다.'
WHERE book_id = 'act' AND chapter = 1 AND verse = 1;

-- 사도행전 2장: 오순절 성령강림과 초대교회
UPDATE bible_verses SET
    chapter_title = '오순절 성령강림과 베드로의 설교, 초대교회',
    chapter_summary = '오순절 날이 이미 이르매 저희가 다 같이 한곳에 모였더니 홀연히 하늘로부터 급하고 강한 바람 같은 소리가 있어 저희 앉은 온 집에 가득하며 불의 혀같이 갈라지는 것이 저희에게 보여 각 사람 위에 임하여 있더니 저희가 다 성령의 충만함을 받고 성령이 말하게 하심을 따라 다른 방언으로 말하기를 시작하니라 하시며, 그 때에 경건한 유대인이 천하 각국으로부터 와서 예루살렘에 우거하더니 이 소리가 나매 큰 무리가 모여 각각 자기의 방언으로 제자들의 말하는 것을 듣고 소동하여 다 놀라 기이히 여겨 가로되 보라 이 말하는 사람이 다 갈릴리 사람이 아니냐 우리가 우리 각 사람의 난 곳 방언으로 듣게 되는 것이 어찜이뇨 하며, 베드로가 열한 사도와 함께 서서 소리를 높여 가로되 유대인들과 예루살렘에 사는 모든 사람들아 이 일을 너희로 알게 할 것이니 내 말에 귀를 기울이라 때가 제삼시니 너희 생각과 같이 이 사람들이 취한 것이 아니라 이는 곧 선지자 요엘로 말씀하신 것이니 하나님이 가라사대 말세에 내가 내 영으로 모든 육체에게 부어 주리니 너희의 자녀들은 예언할 것이요 너희의 젊은이들은 환상을 보고 너희의 늙은이들은 꿈을 꾸리라 하시고, 나사렛 예수를 너희가 법 없는 자들의 손을 빌어 못 박아 죽였으나 하나님이 사망의 고통을 풀어 살리셨으니 이는 그가 사망에게 매여 있을 수 없었음이라 하시며 저희가 이 말을 듣고 마음에 찔려 베드로와 다른 사도들에게 물어 가로되 형제들아 우리가 어찌할꼬 하거늘 베드로가 가로되 너희가 회개하여 각각 예수 그리스도의 이름으로 세례를 받고 죄 사함을 얻으라 그리하면 성령을 선물로 받으리라 하니 그 날에 제자의 수가 삼천이나 더하더라.',
    chapter_themes = '["오순절 성령강림", "방언의 역사", "베드로의 설교", "요엘의 예언 성취", "회개하여 세례를", "삼천 명 회심"]',
    chapter_context = '약속하신 성령이 강림하여 교회가 탄생하고 복음이 전파되기 시작하는 교회사의 출발점입니다.',
    chapter_application = '성령의 충만함을 받아 담대히 복음을 전하고, 회개와 세례를 통해 새로운 삶을 시작하며, 사도들의 가르침에 전념해야 합니다.'
WHERE book_id = 'act' AND chapter = 2 AND verse = 1;

-- 사도행전 3장: 앉은뱅이 치유와 베드로의 두 번째 설교
UPDATE bible_verses SET
    chapter_title = '앉은뱅이 치유와 솔로몬 행각에서의 설교',
    chapter_summary = '제구시 기도 시간에 베드로와 요한이 성전에 올라갈새 나면서 못 걷게 된 자를 사람들이 메고 오니 이는 성전에 들어가는 사람들에게 구걸하기 위하여 날마다 미문이라는 성전 문에 둔 자라 그가 베드로와 요한이 성전에 들어가려 함을 보고 구걸하거늘 베드로가 요한으로 더불어 주목하여 가로되 우리를 보라 하니 그가 저희에게 무엇을 얻을까 하여 바라거늘 베드로가 가로되 은과 금은 내게 없거니와 내게 있는 것으로 네게 주노니 곧 나사렛 예수 그리스도의 이름으로 걸으라 하고 오른손을 잡아 일으키니 발과 발목이 곧 힘을 얻고 뛰어 서서 걸으며 그들과 함께 성전으로 들어가면서 걷기도 하고 뛰기도 하며 하나님을 찬송하니 모든 백성이 그 걷는 것과 및 하나님을 찬송함을 보고 그 본래 성전 미문에 앉아 구걸하던 사람인 줄 알고 그의 당한 일을 인하여 심히 기이히 여기며 놀라니라 하시고, 나은 사람이 베드로와 요한을 붙잡으니 모든 백성이 크게 놀라며 솔로몬의 행각이라 칭하는 행각에 달려와 모이거늘 베드로가 이것을 보고 백성에게 말하되 이스라엘 사람들아 이 일을 왜 기이히 여기느냐 우리 개인의 권능과 경건으로 이 사람을 걷게 한 것처럼 왜 우리를 주목하느냐 아브라함과 이삭과 야곱의 하나님 곧 우리 조상의 하나님이 그 종 예수를 영화롭게 하셨느니라 너희가 저를 넘겨주고 빌라도가 놓아주기로 결의한 앞에서 저를 부인하였으니 너희가 거룩하고 의로운 자를 부인하고 도리어 살인한 자를 놓아주기를 구하여 생명의 주를 죽였도다 그러나 하나님이 죽은 자 가운데서 살리셨으니 우리가 이 일에 증인이로라 그 이름을 믿으므로 그 이름이 너희 보고 아는 이 사람을 성하게 하였느니라.',
    chapter_themes = '["앉은뱅이 치유", "은과 금은 내게 없거니와", "나사렛 예수 이름으로", "솔로몬 행각", "생명의 주", "그 이름을 믿으므로"]',
    chapter_context = '기적을 통해 예수 그리스도의 권능이 나타나고 이를 통해 복음이 더욱 효과적으로 전파되는 모습입니다.',
    chapter_application = '예수 이름의 권능을 믿고 의지하며, 기적의 영광을 하나님께 돌리고, 생명의 주이신 예수님을 증거해야 합니다.'
WHERE book_id = 'act' AND chapter = 3 AND verse = 1;

-- 사도행전 4장: 첫 번째 박해와 공동체의 나눔
UPDATE bible_verses SET
    chapter_title = '첫 번째 박해와 담대한 증언, 모든 것을 공유',
    chapter_summary = '사도들이 백성에게 말할 때에 제사장들과 성전 맡은 자와 사두개인들이 이르러 백성을 가르침과 예수를 들어 죽은 자 가운데서 부활이 있다 전함을 싫어하여 저희를 잡으매 날이 이미 저물었으므로 이튿날까지 가두었더니 말씀을 들은 사람 중에 믿는 자가 많으니 남자의 수가 약 오천이나 되었더라 하시고, 이튿날에 관원과 장로와 서기관들이 예루살렘에서 모이고 대제사장 안나스와 가야바와 요한과 알렉산더와 및 대제사장의 문중이 다 참여하여 사도들을 가운데 세우고 묻되 너희가 무슨 권세와 뉘 이름으로 이 일을 행하였느냐 하거늘 그 때에 베드로가 성령이 충만하여 가로되 백성의 관원과 장로들아 만일 병인에게 행한 착한 일에 대하여 이 사람이 어떻게 성하게 되었느냐 하는 것을 오늘 우리에게 질문하면 너희와 모든 이스라엘 백성들은 알라 너희가 십자가에 못 박고 하나님이 죽은 자 가운데서 살리신 나사렛 예수 그리스도의 이름으로 이 사람이 건강하게 되어 너희 앞에 섰느니라 이 예수는 너희 건축자들의 버린 돌로서 집 모퉁이의 머릿돌이 되었느니라 다른 이로서는 구원을 얻을 수 없나니 천하 인간에 구원을 얻을 만한 다른 이름을 우리에게 주신 일이 없음이니라 하더라 하시고, 저희가 베드로와 요한의 담대함을 보고 그들을 본래 학문 없는 범인으로 알았다가 이상히 여기며 또 그 전에 예수와 함께 있던 줄도 알고 또 병 나은 사람이 그들과 함께 섰는 것을 보고 힐난할 말이 없어 명하여 공회에서 나가라 하고 서로 의논하여 가로되 이 사람들을 어떻게 할꼬 저희로 인하여 유명한 표적 나타난 것이 예루살렘에 사는 모든 사람에게 알려졌으니 우리도 부인할 수 없고 이것이 민간에 더 퍼지지 못하게 하기 위하여 저희를 위협하여 다시는 아무 사람에게도 이 이름으로 말하지도 가르치지도 말게 하자 하고 그들을 불러 경계하여 도무지 예수의 이름으로 말하지도 말고 가르치지도 말라 하니 베드로와 요한이 대답하여 가로되 하나님 앞에서 너희 말 듣는 것이 하나님 말씀 듣는 것보다 옳은가 판단하라 우리는 보고 들은 것을 말하지 아니할 수 없다 하니.',
    chapter_themes = '["첫 번째 박해", "베드로의 담대한 증언", "집 모퉁이 머릿돌", "다른 이름으로는 구원 없음", "보고 들은 것을 말하지 않을 수 없다", "모든 것을 공유"]',
    chapter_context = '복음 전파로 인한 첫 번째 공식적 박해와 사도들의 담대한 응답, 그리고 초대교회의 공동체적 삶이 나타납니다.',
    chapter_application = '박해 중에도 담대히 복음을 증거하고, 예수 이름의 유일성을 선포하며, 형제들과 물질을 나누는 공동체를 이루어야 합니다.'
WHERE book_id = 'act' AND chapter = 4 AND verse = 1;

-- 사도행전 5장: 아나니아와 삽비라, 두 번째 박해
UPDATE bible_verses SET
    chapter_title = '아나니아와 삽비라의 죽음, 두 번째 박해',
    chapter_summary = '아나니아라 하는 사람이 그 아내 삽비라로 더불어 소유를 팔아 그 값에서 얼마를 감추고 얼마만 가져다가 사도들의 발 앞에 두니 베드로가 가로되 아나니아야 어찌하여 사단이 네 마음에 가득하여 네가 성령을 속이고 땅값 얼마를 감추었느냐 땅이 그대로 있을 때에는 네 땅이 아니며 판 후에도 네 임의로 할 수가 없더냐 어찌하여 이 일을 네 마음에 두었느냐 사람에게 거짓말 한 것이 아니요 하나님께로다 하니 아나니아가 이 말을 듣고 엎드러져 혼이 떠나니 이 일을 듣는 모든 사람에게 큰 두려움이 임하니라 하시고, 젊은 사람들이 일어나 시체를 싸서 메고 나가 장사하니라 세 시간쯤 후에 그 아내가 그 생긴 일을 알지 못하고 들어오니 베드로가 가로되 그 땅 판 값이 이것뿐이냐 내게 말하라 하니 가로되 예 이뿐이로라 베드로가 가로되 너희가 어찌 함께 꾀하여 주의 영을 시험하려 하느냐 보라 네 남편을 장사하고 온 사람들의 발이 문 앞에 이르렀으니 또 너를 메어 내가리라 하니 곧 베드로의 발 앞에 엎드러져 혼이 떠나는지라 젊은 사람들이 들어와 죽은 것을 보고 메어다가 그 남편 곁에 장사하니 온 교회와 이 일을 듣는 모든 사람에게 큰 두려움이 임하니라 하시고, 사도들의 손으로 민간에 표적과 기사가 많이 되매 믿는 사람이 더 많아져 남녀의 큰 무리가 주께 더하여지고 심지어 베드로의 그림자라도 뉘게 덮일까 바라고 병든 사람을 메고 와서 침대와 요 위에 뉘우며 예루살렘 근방 성들로부터도 많은 사람이 모여 병든 사람과 더러운 귀신에게 괴로움 받는 사람을 데리고 와서 다 나음을 얻으니라 하시며, 대제사장과 그와 함께 있는 사람 즉 사두개인의 당파가 다 마음에 시기가 가득하여 일어나서 사도들을 잡아다가 옥에 가두었더니 주의 사자가 밤에 옥문을 열고 끌어내어 가로되 가서 성전에 서서 이 생명의 말씀을 다 백성에게 말하라 하매.',
    chapter_themes = '["아나니아와 삽비라", "성령을 속임", "큰 두려움", "베드로의 그림자", "두 번째 박해", "생명의 말씀을 말하라"]',
    chapter_context = '교회 내부의 죄와 외부의 박해를 통해 교회가 정화되고 성장하며, 하나님의 능력이 더욱 크게 나타나는 과정입니다.',
    chapter_application = '하나님 앞에서 정직하게 살고, 성령을 속이지 말며, 하나님을 경외하는 마음을 가지고, 박해 중에도 담대히 복음을 전해야 합니다.'
WHERE book_id = 'act' AND chapter = 5 AND verse = 1;

-- 사도행전 6장: 일곱 집사 선택과 스데반의 사역
UPDATE bible_verses SET
    chapter_title = '일곱 집사 선택과 스데반의 능력 있는 사역',
    chapter_summary = '그 때에 제자가 더 많아졌는데 헬라파 유대인들이 자기의 과부들이 그 매일 구제에 빠지므로 히브리파 사람을 원망한지라 열두 사도가 모든 제자를 불러 이르되 우리가 하나님의 말씀을 제쳐 놓고 공궤를 일삼는 것이 마땅치 아니하니 형제들아 너희 가운데서 성령과 지혜가 충만하여 칭찬 듣는 사람 일곱을 택하라 우리가 이 일을 저희에게 맡기고 우리는 오로지 기도하는 것과 말씀 전하는 것을 전무하리라 하니 온 무리가 이 말을 기뻐하여 믿음과 성령이 충만한 사람 스데반과 또 빌립과 브로고로와 니가노르와 디몬과 바메나와 유대교에 입교한 안디옥 사람 니골라를 택하여 사도들 앞에 세우니 사도들이 기도하고 그들에게 안수하니라 하나님의 말씀이 점점 왕성하여 예루살렘에 있는 제자의 수가 더 심히 많아지고 허다한 제사장의 무리도 이 도에 복종하니라 하시고, 스데반이 은혜와 권능이 충만하여 큰 기사와 표적을 민간에 행하니 리버디노, 구레네인, 알렉산드리아인, 길리기아와 아시아에서 온 사람들의 회당이라 하는 각 회당에서 어떤 자들이 일어나 스데반으로 더불어 변론할새 스데반이 지혜와 성령으로 말함을 저희가 능히 당치 못하여 사람들을 매수하여 말하게 하되 이 사람이 모세와 하나님을 모독하는 말 하는 것을 우리가 들었노라 하게 하고 백성과 장로와 서기관들을 충동시켜 와서 잡아 가지고 공회에 이르러 거짓 증인들을 세우니 가로되 이 사람이 이 거룩한 곳과 율법을 거슬러 말하기를 마지 아니하는도다 그의 말에 이 나사렛 예수가 이곳을 헐고 또 모세가 우리에게 전하여 준 규례를 고치겠다 함을 우리가 들었노라 하거늘 공회 중에 앉은 사람들이 다 스데반을 주목하여 보니 그 얼굴이 천사의 얼굴과 같더라.',
    chapter_themes = '["일곱 집사 선택", "스데반과 빌립", "기도와 말씀 전함에 전무", "허다한 제사장도 복종", "은혜와 권능이 충만", "천사의 얼굴"]',
    chapter_context = '교회 성장에 따른 조직 정비와 사역 분담, 그리고 스데반을 통한 복음 전파의 확장과 새로운 차원의 갈등이 시작됩니다.',
    chapter_application = '교회 내 섬김의 역할을 나누어 맡고, 기도와 말씀에 전념하며, 성령과 지혜가 충만한 사람을 세우고, 은혜와 권능으로 섬겨야 합니다.'
WHERE book_id = 'act' AND chapter = 6 AND verse = 1;

-- 사도행전 7장: 스데반의 설교와 순교
UPDATE bible_verses SET
    chapter_title = '스데반의 긴 설교와 첫 순교',
    chapter_summary = '대제사장이 가로되 이것이 사실이냐 하니 스데반이 가로되 여러분 부형들이여 들으소서 영광의 하나님이 우리 조상 아브라함이 하란에 있기 전 메소보다미아에 있을 때에 그에게 보여 가라사대 네 고향과 친척을 떠나 내가 네게 보일 땅으로 가라 하시니 아브라함이 갈대아 땅을 떠나 하란에 거하다가 그 아버지가 죽으매 하나님이 그를 거기서 너희 시방 거하는 이 땅으로 옮기셨느니라 하시며 아브라함부터 시작하여 이삭, 야곱, 요셉, 모세, 다윗에 이르기까지 이스라엘 역사를 통해 하나님의 구속사를 설명하며, 그러나 지극히 높으신 이는 손으로 지은 곳에 계시지 아니하시나니 선지자의 말한 바 주께서 가라사대 하늘은 나의 보좌요 땅은 나의 발등상이니 너희가 나를 위하여 무슨 집을 짓겠으며 나의 안식할 곳이 어디뇨 이 모든 것이 다 내 손으로 지은 것이 아니냐 하셨느니라 하시고, 목이 곧고 마음과 귀에 할례를 받지 못한 사람들아 너희가 항상 성령을 거스려 너희 조상과 같이 너희도 하는도다 너희 조상들이 선지자 중에 누구를 핍박지 아니하였느냐 의인이 오시리라 예고한 자들을 저희가 죽였고 이제 너희는 그 의인을 잡아준 자요 살인한 자가 되나니 너희가 천사의 전한 율법을 받고도 지키지 아니하였도다 하니라 하시며, 저희가 이 말을 듣고 마음에 찔려 저를 향하여 이를 갈거늘 스데반이 성령이 충만하여 하늘을 우러러 주목하여 가로되 보라 하늘이 열리고 인자가 하나님 우편에 서신 것을 보노라 하니 저희가 큰 소리를 지르며 귀를 막고 일심으로 그에게 달려들어 성 밖으로 내치고 돌로 치니 증인들이 옷을 벗어 사울이라 하는 청년의 발 앞에 두니라 저희가 돌로 스데반을 치니 스데반이 부르짖어 가로되 주 예수여 내 영혼을 받으시옵소서 하고 무릎을 꿇고 크게 불러 가로되 주여 이 죄를 저들에게 돌리지 마옵소서 이 말을 하고 자니라.',
    chapter_themes = '["이스라엘 역사 요약", "성령을 거스림", "하늘이 열리고", "인자가 우편에 서심", "돌로 스데반을 치니", "이 죄를 돌리지 마옵소서"]',
    chapter_context = '스데반의 순교를 통해 복음이 예루살렘을 넘어 확산되는 계기가 마련되고, 사울의 등장으로 새로운 전환점이 시작됩니다.',
    chapter_application = '하나님의 구속사를 깊이 이해하고, 성령을 거스리지 말며, 순교적 신앙으로 원수까지 용서하고, 복음 확산을 위해 헌신해야 합니다.'
WHERE book_id = 'act' AND chapter = 7 AND verse = 1;

-- 사도행전 8장: 박해와 복음의 확산, 빌립의 사역
UPDATE bible_verses SET
    chapter_title = '박해로 인한 복음 확산과 빌립의 사마리아 전도',
    chapter_summary = '사울이 그의 죽임 당함을 마땅히 여기더라 그 날에 예루살렘에 있는 교회에 큰 핍박이 나서 사도 외에는 다 유대와 사마리아 모든 땅으로 흩어지니라 경건한 사람들이 스데반을 장사하며 위하여 크게 울더라 사울이 교회를 잔멸할새 각 집에 들어가서 남녀를 끌어다가 옥에 넘기니라 하시고, 그 흩어진 사람들이 두루 다니며 복음의 말씀을 전할새 빌립이 사마리아 성에 내려가 그리스도를 백성에게 전파하니 무리가 빌립의 말도 듣고 행하는 표적도 보고 일심으로 그의 말하는 것을 좇더라 더러운 귀신들이 많은 사람에게서 큰 소리를 지르며 나가고 또 많은 중풍병자와 앉은뱅이가 나으니 그 성에 큰 기쁨이 있더라 하시고, 그 성에 시몬이라 하는 사람이 전부터 있어 마술을 행하여 사마리아 백성을 놀라게 하며 자칭 큰 자라 하니 낮은 사람부터 높은 사람까지 다 청종하여 가로되 이 사람은 크다 일컫는 하나님의 능력이라 하더라 오랫동안 그 마술에 놀랐으므로 저희가 청종하더니 빌립이 하나님 나라와 및 예수 그리스도의 이름에 관하여 전도하매 남녀가 다 믿고 세례를 받으니 시몬도 믿고 세례를 받은 후 전심으로 빌립을 따라다니며 그 나타나는 표적과 큰 능력을 보고 놀라니라 하시고, 예루살렘에 있는 사도들이 사마리아도 하나님의 말씀을 받았다 함을 듣고 베드로와 요한을 보내매 그들이 내려가서 그들을 위하여 성령 받기를 기도하니 이는 아직 한 사람에게도 성령 내리신 일이 없고 오직 주 예수의 이름으로 세례만 받을 뿐이러라 이에 두 사도가 저희에게 안수하매 성령을 받는지라.',
    chapter_themes = '["큰 핍박이 나서", "흩어진 사람들이 복음 전파", "빌립의 사마리아 전도", "시몬의 마술", "베드로와 요한의 안수", "에디오피아 내시"]',
    chapter_context = '박해가 오히려 복음 확산의 기회가 되고, 사마리아와 이방인에게까지 복음이 전해지기 시작하는 중요한 전환점입니다.',
    chapter_application = '어려움을 복음 전파의 기회로 삼고, 모든 족속에게 복음을 전하며, 성령의 역사와 능력을 구하고, 말씀을 깊이 연구해야 합니다.'
WHERE book_id = 'act' AND chapter = 8 AND verse = 1;

-- 사도행전 9장: 사울의 회심과 초기 사역
UPDATE bible_verses SET
    chapter_title = '사울의 다메섹 도상 회심과 첫 번째 사역',
    chapter_summary = '사울이 주의 제자들에 대하여 여전히 위협과 살기가 등등하여 대제사장에게 가서 다메섹 여러 회당에 가져 갈 공문을 청하니 이는 만일 그 도를 좇는 사람을 만나면 무론 남녀하고 결박하여 예루살렘으로 잡아오려 함이라 사울이 행하여 다메섹에 가까이 가더니 홀연히 하늘로서 빛이 저를 둘러 비추는지라 땅에 엎드러져 들으매 소리 있어 가라사대 사울아 사울아 네가 어찌하여 나를 핍박하느냐 하시거늘 대답하되 주여 뉘시오니이까 가라사대 나는 네가 핍박하는 예수라 네가 가시채를 뒷발질하기가 어려우니라 하시니 떨며 놀라 가로되 주여 무엇을 원하시나이까 가라사대 일어나 성으로 들어가라 행할 것을 네게 이를 자가 있느니라 하시니 같이 가던 사람들은 소리만 듣고 아무도 보지 못하여 말을 못하고 서 있더라 사울이 땅에서 일어나 눈은 떴으나 아무 것도 보지 못하고 사람의 손에 끌려 다메섹으로 들어가서 사흘 동안을 보지 못하고 식음을 전폐하니라 하시고, 그 때에 다메섹에 아나니아라 하는 제자가 있더니 주께서 환상 중에 불러 가라사대 아나니아야 하시거늘 대답하되 주여 내가 여기 있나이다 하시니 주께서 가라사대 일어나 직가라 하는 거리로 가서 유다의 집에서 다소 사람 사울이라 하는 자를 찾으라 저가 기도하는 중이다 하시고 사울이 아나니아라 하는 사람이 들어와서 자기에게 안수하여 다시 보게 하는 것을 환상으로 보았느니라 하시거늘 아나니아가 대답하되 주여 이 사람에 대하여 내가 여러 사람에게 듣사온즉 그가 예루살렘에서 주의 성도에게 적지 않은 해를 끼쳤다 하더니 여기서도 주의 이름을 부르는 모든 자를 결박할 권세를 대제사장들에게서 받았나이다 하거늘 주께서 가라사대 가라 이 사람은 내 이름을 이방인과 임금들과 이스라엘 자손들에게 전하기 위하여 택한 나의 그릇이라 그가 내 이름을 위하여 어떤 큰 고난을 받아야 할 것을 내가 그에게 보이리라 하시니.',
    chapter_themes = '["사울의 핍박", "다메섹 도상의 빛", "네가 핍박하는 예수", "아나니아의 환상", "택한 나의 그릇", "이방인과 임금들에게"]',
    chapter_context = '가장 극렬한 박해자였던 사울이 극적으로 회심하여 위대한 사도가 되는 기적적 사건으로 복음 역사의 전환점입니다.',
    chapter_application = '하나님의 은혜는 누구에게나 미치며, 과거를 불문하고 새로운 사명을 주시며, 순종하여 하나님의 뜻을 이루어야 합니다.'
WHERE book_id = 'act' AND chapter = 9 AND verse = 1;

-- 사도행전 10장: 고넬료와 베드로, 이방인 구원
UPDATE bible_verses SET
    chapter_title = '고넬료의 환상과 베드로의 이방인 전도',
    chapter_summary = '가이사랴에 고넬료라 하는 사람이 있으니 이달리야대라 하는 군대의 백부장이라 그가 경건하여 온 집으로 더불어 하나님을 경외하며 백성을 많이 구제하고 하나님께 항상 기도하더니 하루는 제구시쯤 되어 환상 중에 밝히 보매 하나님의 사자가 들어와 가로되 고넬료야 하니 고넬료가 주목하여 보고 두려워하여 가로되 주여 무엇이니이까 천사가 가로되 네 기도와 구제가 하나님 앞에 상달하여 기념하신바가 되었으니 네가 지금 사람들을 욥바에 보내어 베드로라 하는 시몬을 청하라 저는 피장 시몬의 집에 우거하니 그 집은 해변에 있느니라 하더라 하시고, 이튿날 저희가 행하여 성에 가까이 갔을 그 때에 베드로가 기도하려고 지붕에 올라가니 시간은 제육시더라 저가 시장하여 먹고자 하매 사람이 준비할 때에 비몽사몽간에 하늘이 열리며 한 그릇이 내려오는 것을 보니 큰 보자기 같고 네 귀를 매어 땅에 드리웠더라 그 안에는 땅에 있는 각색 네발 가진 짐승과 기는 것과 공중에 나는 것들이 있는데 또 소리가 있으되 베드로야 일어나 잡아 먹으라 하거늘 베드로가 가로되 주여 그럴 수 없나이다 속되고 깨끗지 아니한 물건을 내가 언제든지 먹지 아니하였삽나이다 하니 또 두 번째 소리 있으되 하나님께서 깨끗하게 하신 것을 네가 속되다 하지 말라 하더라 이런 일이 세 번 있은 후 그 그릇이 곧 하늘로 올리워 가니라 하시고, 베드로가 가서 저희에게 말하여 가로되 하나님께서 사람을 외모로 취하지 아니하시고 각 나라 중 하나님을 경외하며 의를 행하는 사람은 하나님이 받으시는 줄 깨달았도다 하시며 복음을 전하니 베드로가 이 말 할 때에 성령이 말씀 듣는 모든 사람에게 내려오시니 베드로와 함께 온 할례 받은 신자들이 이방인들에게도 성령 부어 주심을 인하여 놀라니 이는 방언을 말하며 하나님 높임을 들음이러라 이에 베드로가 가로되 이 사람들이 우리와 같이 성령을 받았으니 누가 능히 물로 세례 주기를 금하리요 하고 명하여 예수 그리스도의 이름으로 세례를 주라 하니라.',
    chapter_themes = '["고넬료의 경건", "기도와 구제가 상달", "베드로의 환상", "깨끗하게 하신 것을 속되다 하지 말라", "사람을 외모로 취하지 않음", "이방인에게도 성령 부어주심"]',
    chapter_context = '하나님의 구원이 유대인뿐만 아니라 이방인에게도 미친다는 것이 환상과 기적을 통해 확실히 드러나는 중요한 전환점입니다.',
    chapter_application = '하나님은 차별하지 않으시며, 모든 민족이 구원의 대상임을 인정하고, 편견을 버리며, 모든 사람에게 복음을 전해야 합니다.'
WHERE book_id = 'act' AND chapter = 10 AND verse = 1;

-- 사도행전 11장: 예루살렘 교회의 인정과 안디옥 교회
UPDATE bible_verses SET
    chapter_title = '이방인 구원의 인정과 안디옥 교회의 시작',
    chapter_summary = '사도들과 유대에 있는 형제들이 이방인들도 하나님의 말씀을 받았다 함을 들었더니 베드로가 예루살렘에 올라갔을 때에 할례자들이 힐난하여 가로되 네가 무할례자에게 가서 함께 먹었다 하거늘 베드로가 저희에게 이 일을 차례로 설명하여 가로되 내가 욥바 성에서 기도할 때에 비몽사몽간에 환상을 보니 하늘에서 한 그릇이 내려오는데 큰 보자기 같고 네 귀를 매어 내 앞에까지 드리워지거늘 자세히 보니 땅에 있는 네발 가진 짐승과 들짐승과 기는 것과 공중에 나는 것들이 보이더라 또 들으니 소리 있어 내게 이르되 베드로야 일어나 잡아 먹으라 하거늘 내가 가로되 주여 그럴 수 없나이다 속되거나 깨끗지 아니한 것이 내 입에 들어간 일이 없나이다 하니 또 하늘로부터 소리 있어 두 번째 내게 이르되 하나님께서 깨끗하게 하신 것을 네가 속되다 말라 하더라 하시며 고넬료 사건을 자세히 설명하니 저희가 이 말을 듣고 잠잠하여 하나님께 영광을 돌려 가로되 그러면 하나님께서 이방인에게도 생명 얻는 회개를 주셨도다 하니라 하시고, 그 때에 스데반의 일로 일어난 환난을 인하여 흩어진 자들이 베니게와 구브로와 안디옥까지 이르러 도를 유대인에게만 전하는데 그 중에 구브로와 구레네 몇 사람이 안디옥에 이르러 헬라인에게도 말하여 주 예수를 전파하니 주의 손이 그들과 함께 하시매 수다한 사람이 믿고 주께 돌아오더라 예루살렘 교회가 이 사람들의 소문을 듣고 바나바를 안디옥까지 보내니 저가 이르러 하나님의 은혜를 보고 기뻐하여 모든 사람에게 굳은 마음으로 주께 붙어 있으라 권하니 바나바는 착한 사람이요 성령과 믿음이 충만한 자라 이에 큰 무리가 주께 더하여지더라 바나바가 사울을 찾으러 다소에 가서 만나매 안디옥에 데리고 와서 둘이 교회에 일 년간 모여 있어 큰 무리를 가르쳤고 제자들이 안디옥에서 비로소 그리스도인이라 일컬음을 받게 되었더라.',
    chapter_themes = '["이방인에게도 생명 얻는 회개", "안디옥 교회", "헬라인에게도 전파", "바나바의 파송", "사울을 데려옴", "그리스도인이라 일컬음"]',
    chapter_context = '이방인 구원이 공식적으로 인정되고 안디옥 교회가 설립되어 이방인 선교의 새로운 거점이 되는 중요한 발전입니다.',
    chapter_application = '하나님의 은혜가 모든 민족에게 미침을 기뻐하고, 새로운 사역지를 개척하며, 동역자들과 협력하고, 그리스도인다운 삶을 살아야 합니다.'
WHERE book_id = 'act' AND chapter = 11 AND verse = 1;

-- 사도행전 12장: 헤롯의 박해와 베드로의 기적적 탈출
UPDATE bible_verses SET
    chapter_title = '헤롯의 박해와 베드로의 감옥 탈출',
    chapter_summary = '그 때에 헤롯 왕이 손을 들어 교회 중 몇 사람을 해하려 하여 요한의 형제 야고보를 칼로 죽이니 유대인들이 이 일을 기뻐하는 것을 보고 베드로도 잡으려 할새 때는 무교절일이라 잡으매 옥에 가두어 군사 넷씩 네 패에게 맡겨 지키고 유월절 후에 백성 앞에 끌어 내고자 하더니 이에 베드로는 옥에 갇혔고 교회는 그를 위하여 간절히 하나님께 빌더라 하시고, 헤롯이 잡아 내려고 하는 그 전날 밤에 베드로가 두 군사 틈에서 두 쇠사슬에 매여 누워 자는데 파수꾼들이 문 밖에서 옥을 지키더니 홀연히 주의 사자가 곁에 서매 옥중에 광명이 조요하며 또 베드로의 옆구리를 쳐 깨워 가로되 급히 일어나라 하니 쇠사슬이 그 손에서 벗어지더라 천사가 가로되 띠를 띠고 신을 신으라 하거늘 베드로가 그대로 하니 또 가로되 겉옷을 입고 따라 오라 하니 베드로가 나와서 따라갈새 천사의 하는 것이 참인 줄 알지 못하고 환상을 보는가 하니라 첫째와 둘째 파수를 지나 성으로 통한 쇠문에 이르니 문이 절로 열리는지라 나와 한 거리를 지나매 천사가 곧 떠나더라 이에 베드로가 정신이 나서 가로되 내가 이제야 참으로 주께서 그의 천사를 보내어 나를 헤롯의 손과 유대 백성의 모든 기대에서 벗어나게 하신 줄 알겠노라 하여 깨닫고 마가라 하는 요한의 어머니 마리아의 집으로 가니 여러 사람이 모여 기도하더라 하시고, 베드로가 대문을 두드린대 로데라 하는 계집아이가 영접하러 나왔다가 베드로의 음성인 줄 알고 기뻐하여 문을 열지도 못하고 달려 들어가 베드로가 대문 밖에 섰다 하니 저희가 가로되 네가 미쳤다 하나 계집아이는 힘써 말하되 참말이라 하니 저희가 가로되 그러면 그의 천사라 하더라 베드로가 문 두드리기를 그치지 아니하니 저희가 문을 열어 보고 베드로를 보고 놀라거늘.',
    chapter_themes = '["헤롯의 박해", "야고보의 순교", "교회의 간절한 기도", "천사의 구출", "쇠사슬이 벗어지더라", "로데의 기쁨"]',
    chapter_context = '정치적 박해가 극심해지는 가운데 하나님의 기적적 개입으로 교회가 보호받고 계속 성장할 수 있게 되는 사건입니다.',
    chapter_application = '어려운 상황에서 교회가 함께 간절히 기도하고, 하나님의 기적적 도우심을 믿으며, 구출받은 은혜에 감사하고 증거해야 합니다.'
WHERE book_id = 'act' AND chapter = 12 AND verse = 1;

-- 사도행전 13장: 첫 번째 선교여행 시작
UPDATE bible_verses SET
    chapter_title = '바나바와 사울의 첫 번째 선교여행 시작',
    chapter_summary = '안디옥 교회에 선지자들과 교사들이 있으니 곧 바나바와 니게르라 하는 시므온과 구레네 사람 루기오와 분봉왕 헤롯의 젖동생 마나엔과 사울이라 주를 섬겨 금식할 때에 성령이 가라사대 내가 불러 시키는 일을 위하여 바나바와 사울을 따로 세우라 하시니 이에 금식하며 기도하고 두 사람에게 안수하여 보내니라 하시고, 두 사람이 성령의 보내심을 받아 실루기아에 내려가 거기서 배 타고 구브로에 가서 살라미에 이르러 하나님의 말씀을 유대인의 여러 회당에서 전할새 요한을 수종자로 두었더라 온 섬 가운데로 지나서 바보에 이르러 바예수라 하는 유대인 거짓 선지자인 마술사를 만나니 그가 총독 서기오 바울과 함께 있으니 서기오 바울은 지혜 있는 사람이라 바나바와 사울을 불러 하나님의 말씀을 듣고자 하더라 그 마술사 엘루마는 저희를 대적하여 총독으로 믿지 못하게 힘쓰니 바울이라고도 하는 사울이 성령이 충만하여 그를 주목하고 가로되 모든 궤계와 악행이 가득한 자요 마귀의 자식이요 모든 의의 원수여 주의 바른 길을 굽게 하기를 그치지 아니하겠느냐 보라 이제 주의 손이 네 위에 있으니 네가 소경이 되어 얼마 동안 해를 보지 못하리라 하니 즉시 안개와 어두움이 그를 덮어 인도할 사람을 두루 구하는지라 이에 총독이 그렇게 된 것을 보고 주의 가르치심을 기이히 여겨 믿더라 하시고, 바울과 그 동행들이 바보에서 배 타고 밤빌리아에 있는 버가에 이르니 요한은 저희에게서 떠나 예루살렘으로 돌아가고 저희는 버가에서 더 나아가 비시디아 안디옥에 이르러 안식일에 회당에 들어가서 앉으니라.',
    chapter_themes = '["성령이 따로 세우라", "첫 번째 선교여행", "엘루마 마술사", "총독 서기오 바울", "주의 가르치심을 기이히 여겨", "비시디아 안디옥"]',
    chapter_context = '성령의 직접적 부르심으로 체계적인 이방인 선교가 시작되고, 바울이 선교 사역의 중심 인물로 부상하는 중요한 시작점입니다.',
    chapter_application = '성령의 부르심에 순종하여 선교에 헌신하고, 복음을 방해하는 세력과 담대히 맞서며, 하나님의 말씀의 능력을 전해야 합니다.'
WHERE book_id = 'act' AND chapter = 13 AND verse = 1;

-- 사도행전 14장: 이고니온과 루스드라에서의 사역
UPDATE bible_verses SET
    chapter_title = '이고니온과 루스드라에서의 복음 전파와 시련',
    chapter_summary = '이고니온에서도 두 사도가 함께 유대인의 회당에 들어가서 말하니 유대인과 헬라인 중에 허다한 무리가 믿더라 그러나 순종치 아니하는 유대인들이 이방인들의 마음을 선동하여 형제들에게 악감을 품게 하거늘 두 사도가 오래 있어 주를 힘입어 담대히 말하니 주께서 저희 손으로 표적과 기사를 행하게 하여 주사 자기 은혜의 말씀을 증거하시니 그 성내 무리가 나뉘어 유대인을 좇는 자도 있고 두 사도를 좇는 자도 있는지라 이방인과 유대인과 그 관원들이 두 사도를 능욕하며 돌로 치려고 달려드니 저희가 알고 루가오니아의 두 성 루스드라와 더베와 및 그 근방으로 피하여 거기서 복음을 전하니라 하시고, 루스드라에 발을 쓰지 못하는 한 사람이 앉아 있는데 나면서 앉은뱅이 되어 걸어 본 적이 없는 자라 바울의 말하는 것을 듣거늘 바울이 주목하여 보고 구원받을 만한 믿음이 그에게 있는 것을 보고 큰 소리로 가로되 네 발로 바로 일어서라 하니 그 사람이 뛰어 걸으니라 무리가 바울의 행한 일을 보고 루가오니아 방언으로 소리질러 가로되 신들이 사람의 형상으로 우리에게 내려왔다 하여 바나바는 쓰스라 하고 바울은 그 중에 말하는 자이므로 허메라 하더라 성 밖 쓰스 신당의 제사장이 소와 화관들을 가지고 대문 앞에 와서 무리와 함께 제사하려 하니 두 사도 바나바와 바울이 듣고 옷을 찢고 무리 가운데 뛰어 들어가서 소리질러 가로되 여러분이여 어찌하여 이러한 일을 하느냐 우리도 너희와 같은 성정을 가진 사람이라 너희에게 복음을 전하는 것은 이 헛된 일을 버리고 천지와 바다와 그 가운데 만유를 지으신 살아계신 하나님께로 돌아오라 함이라 하시며, 그러나 안디옥과 이고니온에서 온 어떤 유대인들이 무리를 충동하니 저희가 돌로 바울을 쳐서 죽은 줄로 알고 성 밖으로 끌어내치니라 제자들이 둘러섰을 때에 바울이 일어나 성에 들어갔다가 이튿날 바나바와 함께 더베로 가서 그 성에서 복음을 전하여 많은 사람을 제자로 삼고 루스드라와 이고니온과 안디옥으로 돌아가서 제자들의 마음을 굳게 하여 이 믿음에 거하라 권하고 또 우리가 하나님 나라에 들어가려면 많은 환난을 겪어야 할 것이라 하고 각 교회에서 장로들을 택하여 금식 기도 하며 저희를 그 믿는 바 주께 부탁하고 비시디아 가운데로 지나서 밤빌리아에 이르러 버가에서 도를 전하고 앗달리아로 내려가서 거기서 배 타고 안디옥으로 돌아가니 거기는 저희가 이룬 일을 위하여 하나님의 은혜에 부탁하여 보내심을 받은 곳이라.',
    chapter_themes = '["이고니온에서 담대히", "루스드라의 앉은뱅이", "신들이 내려왔다", "우리도 같은 성정", "돌로 바울을 쳐서", "많은 환난을 겪어야"]',
    chapter_context = '첫 번째 선교여행이 진행되면서 복음 전파의 열매와 함께 극심한 박해와 시련을 겪으며 교회가 설립되는 과정입니다.',
    chapter_application = '복음 전파 중에 오는 시련을 각오하고, 하나님만을 경배하며, 우상숭배를 거부하고, 환난 중에도 믿음을 지키며, 교회를 굳게 세워야 합니다.'
WHERE book_id = 'act' AND chapter = 14 AND verse = 1;