-- 에스겔서(Ezekiel) 19-36장 주석
-- 총 48장의 대예언서 2부 (애가와 심판, 회복의 약속)

-- 에스겔 19장: 이스라엘 방백들을 위한 애가
UPDATE bible_verses SET
    chapter_title = '이스라엘 방백들을 위한 애가 - 암사자와 포도나무',
    chapter_summary = '너는 이스라엘 방백들을 위하여 애가를 불러 이르라 네 어미는 무엇이었느뇨 암사자라 사자들 가운데 엎드리어 젊은 사자 중에서 그 새끼를 기르는데 그가 그 새끼 하나를 키우매 젊은 사자가 되어 움킬 것을 배워 사람을 삼키매 이방이 듣고 함정으로 그를 잡아 갈고리로 끌고 애굽 땅으로 간지라 암사자가 기다리다가 소망이 끊어진 줄을 알고 또 그 새끼 하나를 취하여 젊은 사자가 되게 하니 젊은 사자가 되매 사자 중에 왕래하며 움킬 것을 배워 사람을 삼키며 그들의 궁전들을 헐고 성읍들을 훼파하니 그 우는 소리로 인하여 온 땅과 그 가운데 가득한 것이 황폐하여진지라 이방이 그를 치려하여 사면 지방에서 와서 그의 위에 그물을 치고 함정에 잡아 갈고리로 끌어 철롱에 넣어 바벨론 왕에게 이르렀나니 그를 옥에 가두어서 그 소리로 다시 이스라엘 산에서 들리지 않게 하였느니라고 애가를 부르며, 또한 네 어미는 물 가에 심긴 포도나무 같아서 물이 많으므로 실과가 많고 가지가 무성하며 그 가지들이 견고하여 권세 잡은 자의 홀이 될 만하고 그 하나의 키가 높아져서 무성한 가지 가운데서 뛰어나며 그 높은 것과 많은 가지로 인하여 보이더니 분노 중에 뽑혀서 땅에 던짐을 당하매 동풍이 그 실과를 말렸고 그 견고한 가지들이 꺾이고 말라 불에 탔더라 이제는 광야 마르고 메마른 땅에 심긴바 되고 불이 그 가지 중 하나에서 나와서 그 실과를 삼키니 권세 잡은 자의 홀 될 만한 견고한 가지가 없도다 이것이 애가라 후에도 애가가 되리라고 마무리합니다.',
    chapter_themes = '["이스라엘 방백들을 위한 애가", "암사자와 새끼들", "움킬 것을 배워 사람을 삼킴", "바벨론 왕에게 끌려감", "물 가에 심긴 포도나무", "견고한 가지가 없도다"]',
    chapter_context = '여호아하스와 여호야긴, 시드기야 등 유다의 마지막 왕들의 비참한 최후를 암사자의 새끼들과 포도나무의 비유로 애도한 슬픈 노래입니다.',
    chapter_application = '지도자들의 타락이 백성들에게 미치는 영향을 깨닫고, 권력을 바르게 사용해야 하며, 하나님을 떠난 권력의 허무함을 인식하고, 참된 권위이신 하나님께 순종해야 합니다.'
WHERE book_id = 'ez' AND chapter = 19 AND verse = 1;

-- 에스겔 20장: 이스라엘 역사를 통한 심판
UPDATE bible_verses SET
    chapter_title = '애굽에서부터 시작된 이스라엘의 패역한 역사',
    chapter_summary = '제칠년 오월 십일에 이스라엘 장로들이 여호와께 묻고자 하여 와서 내 앞에 앉으니 여호와의 말씀이 내게 임하여 가라사대 인자야 이스라엘 장로들에게 고하여 이르라 주 여호와의 말씀에 너희가 내게 묻고자 하여 왔느냐 나의 삶을 두고 맹세하노니 나는 너희 묻는 것을 받지 아니하리라 하시며, 그들을 국문할찌니라 인자야 그들을 국문할찌니라 그들의 열조의 가증한 일을 그들로 알게 하여 이르라 주 여호와의 말씀에 내가 이스라엘을 택하던 날에 야곱 족속의 후예를 향하여 맹세하며 애굽 땅에서 그들에게 나타나 이르기를 나는 여호와 너희 하나님이라 하였고 그 날에 내가 그들에게 맹세하기를 그들을 애굽 땅에서 인도하여 내가 그들을 위하여 찾아 두었던 땅 곧 젖과 꿀이 흐르는 땅이요 모든 땅 중의 아름다운 곳에 이르게 하리라 하고 또 그들에게 이르기를 너희는 각기 그 눈으로 보고 좋아하는 가증한 것을 버리고 애굽의 우상들로 자기를 더럽히지 말라 나는 여호와 너희 하나님이니라 하였으나 그들이 내게 패역하여 듣고자 아니하며 그 눈으로 보고 좋아하는 가증한 것을 각기 버리지 아니하며 애굽의 우상을 떠나지 아니하였다고 역사를 회고하시며, 광야에서도 광야 후 세대에서도 계속 패역했으나 내 이름을 위하여 내가 달리 행하였나니 그들을 인도하여 낸 이방인의 목전에서 내 이름이 더럽혀지지 않게 하려 하였음이니라고 말씀하십니다.',
    chapter_themes = '["애굽에서부터 패역", "젖과 꿀이 흐르는 땅", "눈으로 보고 좋아하는 가증한 것", "광야에서도 패역", "내 이름을 위하여", "이방인의 목전에서"]',
    chapter_context = '이스라엘의 전 역사를 통해 그들이 처음부터 끝까지 하나님께 패역했음을 보여주며 현재의 심판이 정당함을 입증하는 역사적 고발입니다.',
    chapter_application = '하나님의 오래 참으심에 감사하고, 과거의 실패를 반복하지 말며, 하나님의 이름이 더럽혀지지 않도록 거룩한 삶을 살고, 하나님의 구원의 목적을 이해해야 합니다.'
WHERE book_id = 'ez' AND chapter = 20 AND verse = 1;

-- 에스겔 21장: 칼의 노래
UPDATE bible_verses SET
    chapter_title = '여호와의 칼이 날카롭게 갈리고 번쩍번쩍함',
    chapter_summary = '여호와의 말씀이 내게 임하여 가라사대 인자야 너는 얼굴을 예루살렘으로 향하며 성소를 향하여 소리내어 이스라엘 땅을 쳐서 예언하라 이스라엘 땅에게 이르기를 여호와의 말씀에 내가 너를 대적하여 내 칼을 집에서 빼어 의인과 악인을 네게서 끊을찌라 내가 의인과 악인을 네게서 끊을터이므로 내 칼이 집에서 나와서 남에서 북까지 모든 혈육을 칠찌니 모든 혈육이 나 여호와가 내 칼을 집에서 빼낸 줄을 알리라 다시 집에 꽂지 아니하리라 하셨다 하고 인자야 너는 탄식하되 허리가 끊어지는 듯하고 통분히 그들의 목전에서 탄식할찌어다 하시며, 칼아 날카롭게 갈리고 또 번쩍번쩍하도다 날카롭게 갈림은 살륙을 위함이요 번쩍번쩍함은 번개 같이 되기 위함이니 우리가 즐거워하겠느냐 내 아들의 홀이 모든 나무를 업신여기는도다 그 칼이 번쩍번쩍하도록 닦이되 손에 잡아 쓸만하도록 날카롭게 갈리고 번쩍번쩍하게 되었나니 이것이 살륙하는 자의 손에 붙이운 것이로다 하시며, 바벨론 왕이 갈래 길 곧 두 길 머리에 서서 점을 치되 살들을 흔들어 우상에게 묻고 희생의 간을 살펴서 그 오른손에 예루살렘 점괘가 나와서 공성퇴를 베풀며 입을 벌려 살륙하며 소리를 높여 외치며 성문을 향하여 공성퇴를 베풀고 토성을 쌓고 운제를 만들게 되었다고 예언하십니다.',
    chapter_themes = '["여호와의 칼", "의인과 악인을 끊음", "날카롭게 갈리고 번쩍번쩍함", "내 아들의 홀", "바벨론 왕의 점치기", "예루살렘 점괘"]',
    chapter_context = '바벨론을 하나님의 심판의 도구인 칼로 비유하여 예루살렘에 대한 무차별적인 심판이 임할 것을 예언한 강렬한 예언입니다.',
    chapter_application = '하나님의 심판은 피할 수 없음을 인정하고, 의인도 고난을 당할 수 있음을 각오하며, 하나님의 공의로운 심판을 두려워하고, 진정한 회개로 돌아가야 합니다.'
WHERE book_id = 'ez' AND chapter = 21 AND verse = 1;

-- 에스겔 22장: 예루살렘의 죄목들
UPDATE bible_verses SET
    chapter_title = '피 흘리는 성읍 예루살렘의 죄목 고발',
    chapter_summary = '여호와의 말씀이 또 내게 임하여 가라사대 인자야 네가 국문하려느냐 이 피 흘리는 성읍을 국문하려느냐 그리하려거든 자기의 모든 가증한 일을 그들로 알게 하라 너는 이르기를 주 여호와의 말씀에 자기 가운데 피를 흘려서 벌 받을 때로 이르게 하며 우상을 만들어서 스스로 더럽히는 성읍아 네가 흘린 피로 인하여 죄가 있고 네가 만든 우상으로 인하여 스스로 더럽혔으니 네 날이 가까왔고 네 연한이 찼도다 그러므로 내가 너로 이방의 능욕을 받으며 만국의 조롱거리가 되게 하였나니 너 이름이 더럽고 어지러움이 많은 자여 가까운 자나 먼 자나 다 너를 조롱하리라 하셨다 하라 하시며, 이스라엘 족속이 내게 찌끼가 되었나니 그들은 다 풀무 가운데 있는 놋이나 상납이나 철이나 납이며 은의 찌끼로다 그러므로 나 주 여호와가 말하노라 너희가 다 찌끼가 되었은즉 내가 너희를 예루살렘 가운데로 모으고 사람이 은이나 놋이나 철이나 납이나 상납이나 모아서 풀무 가운데 넣고 불을 불어 녹이는 것 같이 내가 노와 분으로 너희를 모아 거기 두고 녹일찌라고 선언하시며, 그 땅 선지자들이 우는 사자가 식물을 움키는 것 같이 사람의 영혼을 삼켰으며 전재와 보물을 탈취하며 과부로 그 가운데 많게 하였으며 그 제사장들은 내 율법을 범하며 내 성물을 더럽혔으며 거룩함과 속된 것을 분변치 아니하며 부정함과 정한 것을 사람으로 분별하게 하지 아니하며 그 눈을 가리워 내 안식일을 보지 아니하므로 내가 그 가운데서 더럽힘을 받았느니라고 책망하십니다.',
    chapter_themes = '["피 흘리는 성읍", "우상으로 스스로 더럽힘", "찌끼가 됨", "풀무에서 녹임", "우는 사자 같은 선지자들", "율법을 범한 제사장들"]',
    chapter_context = '예루살렘의 모든 계층의 죄악을 구체적으로 열거하며 그들이 하나님께 완전히 쓸모없는 찌끼가 되었음을 선언한 종합적 고발장입니다.',
    chapter_application = '각자의 직분에 맞는 책임을 다해야 하며, 지도자들은 더욱 엄격한 기준으로 살아야 하고, 사회 각 계층이 부패하지 않도록 경계하며, 하나님 보시기에 가치 있는 삶을 살아야 합니다.'
WHERE book_id = 'ez' AND chapter = 22 AND verse = 1;

-- 에스겔 23장: 오홀라와 오홀리바 - 두 음녀 자매
UPDATE bible_verses SET
    chapter_title = '오홀라와 오홀리바 - 사마리아와 예루살렘의 음행',
    chapter_summary = '여호와의 말씀이 내게 임하여 가라사대 인자야 두 여인이 있었나니 한 어미의 딸이라 그들이 애굽에서 행음하되 어렸을 때에 행음하여 그들의 유방이 눌리며 그 처녀의 가슴이 어루만진바 되었었나니 그 이름이 형은 오홀라요 아우는 오홀리바라 그들이 내게 속하여 자녀를 낳았나니 그 이름으로 말하면 오홀라는 사마리아요 오홀리바는 예루살렘이니라 하시며, 오홀라가 내게 속하여 있을 때에 행음하여 그 연인 곧 그 이웃 앗수르 사람을 사모하니 그들은 다 자색 옷을 입은 방백과 감독이며 준수한 소년으로 다 말 타는 자라 그가 앗수르의 아름다운 자들과 행음하고 무릇 그 사모하는 자의 우상들로 스스로 더럽혔으며 그가 젊었을 때에 애굽 사람과 동침하매 그들이 그 처녀의 가슴을 어루만지며 그들의 음란을 그에게 쏟았더라 하시고, 그러므로 내가 그를 그 연인의 손 곧 그 사모하던 앗수르 사람의 손에 붙였더니 그들이 그 하체를 드러내고 그 자녀를 빼앗으며 칼로 그를 죽여서 부녀 중에 이름이 나게 하였나니 이는 그들이 그에게 심판을 행하였음이니라 하시며, 그 아우 오홀리바가 이것을 보고도 그 형보다 음욕을 더하며 그 형의 간음함보다 그 간음이 더 심하니라고 두 자매의 죄악을 상세히 묘사하십니다.',
    chapter_themes = '["두 여인 오홀라와 오홀리바", "애굽에서 어렸을 때부터 행음", "앗수르 사람을 사모함", "자색 옷을 입은 방백들", "그 형보다 음욕을 더함", "간음이 더 심함"]',
    chapter_context = '북 이스라엘(사마리아)과 남 유다(예루살렘)의 정치적, 종교적 배신을 두 음녀 자매의 비유로 적나라하게 묘사한 강렬한 비유입니다.',
    chapter_application = '정치적 동맹보다 하나님을 의지해야 하며, 세상 권력의 화려함에 현혹되지 말고, 과거의 실패를 교훈으로 삼으며, 하나님께만 신실한 관계를 유지해야 합니다.'
WHERE book_id = 'ez' AND chapter = 23 AND verse = 1;

-- 에스겔 24장: 끓는 가마의 비유와 에스겔 아내의 죽음
UPDATE bible_verses SET
    chapter_title = '끓는 가마의 비유와 에스겔 눈동자의 죽음',
    chapter_summary = '제구년 시월 십일에 여호와의 말씀이 내게 임하여 가라사대 인자야 너는 날짜를 기록하라 곧 오늘날이라 바벨론 왕이 오늘날 예루살렘에 핍근하였느니라 너는 이 패역한 족속에게 비유를 베풀어 이르라 주 여호와의 말씀에 한 가마를 걸고 물을 그 가운데 부어 넣고 양떼에서 고른 것 곧 제일 좋은 것으로 그 각을 모아 넣으며 그 뼈를 가마 밑에 쌓아 넣고 잘 끓이되 그 뼈까지 무르도록 끓일찌어다 하셨다 하라 하시며, 그러므로 나 주 여호와가 말하노라 피 흘리는 성읍, 녹슨 가마 곧 그 속의 녹이 벗겨지지 아니한 가마여 화 있을진저 제비뽑을 것도 없이 그 덩이를 일일이 꺼낼찌어다 그 가운데 피가 있음이여 그가 그 피를 맨 반석 위에 두고 흙으로 덮지도 아니하였도다 내가 분노를 발하여 보응하려고 그 피를 맨 반석 위에 두어 덮이지 않게 하였느니라고 선언하시며, 또 아침에 여호와의 말씀이 내게 임하여 가라사대 인자야 내가 네 눈에 기뻐하는 것을 한 번 쳐서 빼앗으리니 너는 슬퍼하거나 울거나 눈물을 흘리거나 하지 말며 죽은 자들을 위하여 슬퍼하지 말고 종용히 탄식하며 수건으로 머리를 동이고 발에 신을 신고 입술을 가리우지 말며 사람의 부의하는 식물을 먹지 말라 하시기로 내가 아침에 백성에게 고하였더니 저녁에 내 아내가 죽기로 아침에 내가 받은 명령대로 행하매 백성이 내게 이르되 네가 행하는 이 일이 우리에게 무슨 상관이 있는지 너는 우리에게 고하지 아니하겠느냐 하므로 내가 그들에게 이르되 여호와의 말씀이 내게 임하여 가라사대 너는 이스라엘 족속에게 이르라 나 주 여호와가 말하노라 내가 내 성소를 더럽히리니 이는 너희 세력의 영광이요 너희 눈의 기뻐하는 것이요 너희 마음의 아끼는 것이라 너희의 버린 자녀들이 칼에 엎드러질 것이나 너희가 나의 행한 대로 행하여 입술을 가리우지 아니하며 사람의 식물을 먹지 아니하며 수건으로 머리를 동이고 발에 신을 신고 슬퍼하지도 아니하며 울지도 아니하되 너희 죄악 중에서 쇠패하여 피차 바라보고 탄식하리라고 전하십니다.',
    chapter_themes = '["끓는 가마의 비유", "녹슨 가마", "피를 맨 반석 위에", "네 눈에 기뻐하는 것", "종용히 탄식", "내 성소를 더럽히리니"]',
    chapter_context = '예루살렘 포위의 시작일과 함께 에스겔의 아내가 죽는 개인적 비극을 통해 성전 파괴의 충격을 미리 보여주는 상징적 사건입니다.',
    chapter_application = '하나님의 사명을 위해서는 개인적 슬픔도 감내해야 하며, 하나님의 성전을 소중히 여겨야 하고, 진정한 회개 없이는 형식적 애도도 무의미함을 깨달아야 합니다.'
WHERE book_id = 'ez' AND chapter = 24 AND verse = 1;

-- 에스겔 25장: 암몬, 모압, 에돔, 블레셋에 대한 심판
UPDATE bible_verses SET
    chapter_title = '암몬, 모압, 에돔, 블레셋 - 이웃 민족들에 대한 심판',
    chapter_summary = '여호와의 말씀이 내게 임하여 가라사대 인자야 암몬 족속을 향하여 그들을 쳐서 예언하라 암몬 족속에게 이르기를 너희는 주 여호와의 말씀을 들을찌어다 주 여호와의 말씀에 내 성소를 더럽힐 때에 네가 아하 하였고 이스라엘 땅이 황무할 때에 네가 그것을 멸시하였고 유다 족속이 사로잡힐 때에 네가 그들을 비웃었도다 그러므로 내가 너를 동방 사람에게 기업으로 붙이리니 그들이 네 가운데 진을 치며 네 가운데 거처를 베풀며 네 실과를 먹으며 네 젖을 마실찌라 내가 랍바로 약대의 우리를 만들며 암몬 족속의 땅으로 양무리의 눕는 곳을 삼은즉 너희가 나를 여호와인줄 알리라 하시며, 모압과 세일에 대하여는 말하기를 보라 유다 족속도 모든 이방인과 일반이라 하도다 그러므로 내가 모압의 지경을 열어서 모압의 영광인 그 변경의 아름다운 성읍들 곧 벧여시못과 바알므온과 기랴다임을 평지가 되게 하고 암몬 족속에게 붙여서 동방 사람에게 기업으로 주어서 암몬 족속으로 다시는 이방 가운데서 기억되지 아니하게 하리라 내가 모압에게 벌을 내린즉 그들이 나를 여호와인줄 알리라 하시며, 에돔에 대하여는 주 여호와의 말씀에 에돔이 유다 족속을 쳐서 원수를 갚았고 원수를 갚음으로 심히 범죄하였도다 그러므로 나 주 여호와가 말하노라 내가 에돔 위에 손을 펴서 사람과 짐승을 그 가운데서 끊어 데만에서부터 황무케 하리니 드단까지 칼에 엎드러뜨리리라 내가 내 백성 이스라엘의 손을 빙자하여 에돔에게 원수를 갚으리니 그들이 내 노와 분을 따라 에돔에 행한즉 에돔이 내 원수 갚음인줄 알리라 나 주 여호와의 말이니라 하시며, 블레셋 사람에 대하여는 주 여호와의 말씀에 블레셋 사람이 보수하는 마음으로 원한을 품어 옛날부터 미워하여 진멸하려 하였도다 그러므로 나 주 여호와가 말하노라 내가 블레셋 사람 위에 손을 펴서 그렛 사람을 끊으며 해변에 남은 자를 진멸하되 분노의 책벌로 내가 그들에게 크게 원수를 갚은즉 내가 그들에게 원수 갚음을 행할 때에 그들이 나를 여호와인줄 알리라고 선언하십니다.',
    chapter_themes = '["암몬의 아하 하는 소리", "동방 사람에게 기업으로", "모압도 이방인과 일반", "에돔의 원수 갚음", "블레셋의 옛날부터 미워함", "그들이 나를 여호와인줄 알리라"]',
    chapter_context = '이스라엘의 고난을 비웃고 원수를 갚으려 했던 주변 이방 민족들에 대한 하나님의 공의로운 심판을 선언한 내용입니다.',
    chapter_application = '다른 사람의 고난을 비웃지 말아야 하며, 원수 갚는 것은 하나님께 맡겨야 하고, 하나님의 백성을 괴롭히는 자들은 반드시 심판받을 것을 믿으며, 하나님의 공의를 신뢰해야 합니다.'
WHERE book_id = 'ez' AND chapter = 25 AND verse = 1;

-- 에스겔 26장: 두로에 대한 심판 예언
UPDATE bible_verses SET
    chapter_title = '바다의 여왕 두로의 멸망 - 느부갓네살의 침입',
    chapter_summary = '제십일년 어느 달 초일일에 여호와의 말씀이 내게 임하여 가라사대 인자야 두로가 예루살렘을 쳐서 이르기를 아하 좋다 만민의 문이 깨어져서 내게로 돌아왔도다 그가 황무하였으니 내가 충만하리라 하였도다 그러므로 나 주 여호와가 말하노라 두로야 내가 너를 대적하여 바다가 그 파도로 흉용케 함 같이 열국으로 와서 너를 치게 하리니 그들이 두로의 성벽을 훼파하며 그 망대를 헐 것이요 나도 티끌을 그 위에서 쓸어 버려서 말간 반석이 되게 하며 바다 가운데 그물 치는 곳이 되게 하리니 이는 내가 말하였음이니라 나 주 여호와의 말이니라 그가 이방의 노략거리가 될 것이요 들에 있는 그의 딸들은 칼에 죽으리니 그들이 나를 여호와인줄 알리라 하시며, 나 주 여호와가 말하노라 내가 열왕의 왕 곧 바벨론 왕 느부갓네살로 하여금 북방에서 말과 병거와 기병과 군대와 백성의 큰 무리를 거느리고 와서 두로를 치게 할지라 그가 들에 있는 너의 딸들을 칼로 죽이고 너를 치려고 운제를 세우며 토성을 쌓으며 방패를 갖출 것이며 공성퇴를 베풀어 네 성을 치며 도끼로 네 망대를 찍을 것이며 말이 많으므로 그 티끌이 너를 가릴 것이며 사람이 훼파된 성구멍으로 들어가는 것 같이 그가 네 성문으로 들어갈 때에 너의 성벽이 기병과 수레와 병거의 소리로 인하여 진동할 것이며 그가 그 말굽으로 네 모든 거리를 밟을 것이며 칼로 네 백성을 죽일 것이며 네 견고한 석상들을 땅에 엎드러뜨릴 것이며 네 재물을 빼앗으며 네 무역한 것을 노략하며 네 성을 헐며 네 기뻐하는 집을 무너뜨리고 또 네 돌들과 네 재목과 네 흙을 다 물 가운데 던질 것이라고 상세히 예언하십니다.',
    chapter_themes = '["만민의 문이 깨어짐", "바다가 파도로 흉용케 함", "말간 반석이 되게 함", "그물 치는 곳", "열왕의 왕 느부갓네살", "돌들과 재목을 물 가운데 던짐"]',
    chapter_context = '예루살렘의 멸망을 기뻐하며 상업적 이익을 도모한 두로에 대해 바벨론을 통한 하나님의 심판을 상세히 예언한 내용입니다.',
    chapter_application = '다른 사람의 불행을 기뻐하지 말아야 하며, 물질적 번영에만 치중하지 말고, 하나님의 공의로운 심판을 두려워하며, 겸손한 마음으로 살아야 합니다.'
WHERE book_id = 'ez' AND chapter = 26 AND verse = 1;

-- 에스겔 27장: 두로의 번영과 몰락에 대한 애가
UPDATE bible_verses SET
    chapter_title = '아름다운 배 두로의 무역과 파선에 대한 애가',
    chapter_summary = '여호와의 말씀이 또 내게 임하여 가라사대 인자야 너는 두로를 위하여 애가를 지으라 너는 두로를 향하여 이르기를 바다 어귀에 거하여 여러 섬 백성과 통상하는 자여 주 여호와의 말씀에 두로야 네가 말하기를 나는 온전히 아름답다 하였도다 네 지경이 바다 가운데 있음이여 너를 지은 자가 네 아름다움을 온전케 하였도다 스닐의 잣나무로 네 판자를 만들었음이여 레바논 백향목을 가져다가 돛대를 만들었도다 바산 상수리나무로 네 노를 만들었음이여 깃딤 섬 황양목에 상아로 꾸며 갑판을 만들었도다 애굽의 수놓은 가는 베로 돛을 만들어 기로 삼았음이여 엘리사 섬의 청색 자색 베로 차일을 만들었도다 하며, 시돈과 아르왓 거민들이 네 사공이 되었음이여 두로야 네 가운데 있는 박사들이 네 선장이 되었도다 그발의 노인과 박사들이 네 가운데서 배의 틈을 막는 자가 되었음이여 바다의 모든 배와 그 사공들은 네 가운데서 무역하였도다 하시며 온 세계의 상품들이 두로로 모여들었던 번영을 자세히 묘사하고, 동풍이 바다 가운데서 너를 파하는 날에 네 재물과 상품과 무역한 물건과 네 사공과 선장과 네 배의 틈을 막는 자와 네 장사와 네 가운데 있는 모든 용사와 네 가운데 있는 온 무리가 다 바다 가운데 빠질 것임이여 네 선장의 부르짖는 소리에 물결이 흔들리리로다 무릇 노를 잡은 자와 사공과 바다의 선장들이 다 배에서 내려 언덕에 서서 너를 위하여 크게 소리질러 통곡하고 티끌을 머리에 무릎쓰며 재 가운데 굶이며 너를 위하여 머리털을 밀고 굵은 베를 띠고 마음이 아프게 슬피 통곡하리로다 그들이 통곡할 때에 너를 위하여 애가를 불러 조상하는 말이 두로 같이 바다 가운데서 적막한 자 누구인고 네 물품을 바다로 실어 낼 때에 네가 여러 백성을 풍족하게 하였음이여 네 재물과 무역품이 많으므로 세상 열왕을 풍부케 하였었도다 이제는 네가 바다 깊은데서 파선하였음이여 네 무역품과 네 승객이 다 빠졌도다고 애가를 부릅니다.',
    chapter_themes = '["온전히 아름다운 배", "스닐의 잣나무 판자", "온 세계 상품들", "동풍이 바다 가운데서 파함", "바다 깊은데서 파선", "승객이 다 빠짐"]',
    chapter_context = '두로를 아름다운 무역선에 비유하여 그 번영과 몰락을 노래한 장대한 애가로, 세상 권세의 허무함을 보여줍니다.',
    chapter_application = '세상의 번영이 영원하지 않음을 깨닫고, 물질적 성공에만 의존하지 말며, 진정한 안전은 하나님께만 있음을 믿고, 겸손한 마음으로 살아야 합니다.'
WHERE book_id = 'ez' AND chapter = 27 AND verse = 1;

-- 에스겔 28장: 두로 왕과 루시퍼의 교만
UPDATE bible_verses SET
    chapter_title = '자신을 하나님이라 한 두로 왕과 에덴동산의 그룹',
    chapter_summary = '여호와의 말씀이 또 내게 임하여 가라사대 인자야 두로 왕에게 이르기를 주 여호와의 말씀에 네 마음이 교만하여 이르기를 나는 신이라 내가 하나님의 자리 곧 바다 가운데 앉았다 하도다마는 너는 사람이요 신이 아니어늘 네 마음을 하나님의 마음 같다 하도다 네가 다니엘보다 지혜로워서 은밀한 것을 깨닫지 못할 것이 없다 하고 네 지혜와 총명으로 재물을 얻었으며 금 은을 곳간에 저축하였으며 네 큰 지혜와 장사함으로 재물을 더하고 그 재물로 인하여 네 마음이 교만하였도다 하시며, 그러므로 나 주 여호와가 말하노라 네 마음이 하나님의 마음 같다 하였으니 그런즉 내가 외인 곧 열국의 강포한 자를 거느리고 와서 너를 치리니 그들이 칼을 빼어 네 지혜의 아름다운 것을 치며 네 영화를 더럽히며 또 너를 구덩이에 빠뜨려서 너로 바다 가운데서 죽임을 당한 자의 죽음 같이 죽게 할찌라 너를 죽이는 자 앞에서 네가 그래도 말하기를 내가 하나님이라 하겠느냐 너를 죽이는 자의 수중에서 너는 사람뿐이요 신이 아니라고 책망하시고, 또 여호와의 말씀이 내게 임하여 가라사대 인자야 두로 왕을 위하여 애가를 지어 그에게 이르라 주 여호와의 말씀에 너는 완전한 인이었고 지혜가 충족하며 온전히 아름다웠도다 네가 옛적에 하나님의 동산 에덴에 있어서 각종 보석 곧 홍보석과 황보석과 금강석과 황옥과 홍마노와 창옥과 청보석과 남보석과 홍옥과 황금으로 단장하였었음이여 네가 지음을 받던 날에 너를 위하여 소고와 비파가 예비되었었도다 너는 기름 부음을 받은 덮는 그룹임이여 내가 너를 세웠으므로 네가 하나님의 성산에 있어서 화광석 사이에 왕래하였었도다 네가 지음을 받던 날로부터 네 모든 길에 완전하더니 마침내 불의가 드러났도다 네 무역이 풍성하므로 네 가운데 강포가 가득하여 네가 범죄하였도다 그러므로 내가 너를 더럽게 여겨 하나님의 산에서 쫓아 내었고 덮는 그룹아 내가 화광석 사이에서 너를 멸하였도다 네가 아름다우므로 마음이 교만하였으며 네가 영화로우므로 네 지혜를 더럽혔음이여 내가 너를 땅에 던져 열왕 앞에 두어 그들로 너를 구경하게 하였느니라고 루시퍼의 타락을 묘사하십니다.',
    chapter_themes = '["나는 신이라", "네 마음을 하나님의 마음 같다", "완전한 인", "에덴 동산의 그룹", "기름 부음을 받은 덮는 그룹", "마침내 불의가 드러남"]',
    chapter_context = '두로 왕의 교만을 책망하면서 동시에 사탄의 타락을 상징적으로 묘사한 이중적 예언으로, 교만의 결과를 보여주는 중요한 내용입니다.',
    chapter_application = '교만을 경계하고 겸손해야 하며, 자신을 하나님과 같다고 생각하지 말고, 받은 은사와 능력을 하나님께 영광 돌리는 데 사용하며, 사탄의 유혹에 넘어가지 말아야 합니다.'
WHERE book_id = 'ez' AND chapter = 28 AND verse = 1;

-- 에스겔 29장: 애굽과 바로에 대한 심판
UPDATE bible_verses SET
    chapter_title = '큰 악어 바로와 애굽에 대한 심판',
    chapter_summary = '제십년 시월 십이일에 여호와의 말씀이 내게 임하여 가라사대 인자야 네 얼굴을 애굽 왕 바로에게로 향하고 그와 애굽 온 땅을 쳐서 예언하라 너는 말하여 이르라 주 여호와의 말씀에 애굽 왕 바로야 내가 너를 대적하노라 너는 자기의 강들 가운데 누운 큰 악어라 스스로 이르기를 내 강은 내 것이라 내가 나를 위하여 만들었다 하는도다 내가 갈고리로 네 아가미를 꿰고 네 강의 고기로 네 비늘에 붙게 하고 네 강 가운데서 너를 끌어내되 네 강의 모든 고기가 네 비늘에 붙은 채 나오게 하고 너와 네 강의 모든 고기를 광야에 던지리니 네가 지면에 떨어져서 집어 가거나 거두어갈 자가 없을 것은 내가 너를 들짐승과 공중의 새의 식물로 주었음이니라 하시며, 애굽 온 거민이 나를 여호와인줄 알리라 애굽이 이스라엘 족속에게 갈대 지팡이가 되었나니 그들이 손으로 너를 잡은즉 네가 부러져서 그들의 온 어깨를 찢었고 그들이 너를 의지한즉 네가 부러져서 그들의 온 허리로 흔들리게 하였느니라 그러므로 나 주 여호와가 말하노라 내가 칼로 네게 임하게 하여 네게서 사람과 짐승을 끊은즉 애굽 땅이 황무하고 적막하리니 그들이 나를 여호와인줄 알리라 네가 이르기를 강은 내 것이라 내가 만들었다 하도다 그러므로 내가 너와 네 강들을 대적하여 애굽 땅 믹돌에서부터 수에네까지 구스 지경까지 황무하고 적막한 땅이 되게 하리니 사십년 동안 사람의 발도 지나가지 아니하며 짐승의 발도 지나가지 아니하고 거기 거하는 것이 없을 것이라 내가 애굽 땅으로 황폐한 나라들 가운데 황폐하게 하며 애굽 성읍들로 사막이 된 성읍들 가운데 사십년 동안 사막이 되게 하고 애굽인들을 각국 가운데로 흩으며 열방 가운데로 헤치리라고 선언하십니다.',
    chapter_themes = '["큰 악어 바로", "내 강은 내 것", "갈고리로 아가미를 꿰음", "갈대 지팡이", "사십년 동안 황폐", "각국 가운데로 흩음"]',
    chapter_context = '이스라엘이 의뢰했던 애굽이 도움이 되지 못하고 오히려 해가 되었음을 책망하며 애굽에 대한 장기간의 심판을 예언한 내용입니다.',
    chapter_application = '하나님보다 세상 권력을 의지하지 말아야 하며, 자신의 능력을 과신하지 말고, 하나님의 주권을 인정하며, 신뢰할 수 없는 것에 의존하지 말아야 합니다.'
WHERE book_id = 'ez' AND chapter = 29 AND verse = 1;

-- 에스겔 30장: 애굽의 날이 가까웠도다
UPDATE bible_verses SET
    chapter_title = '애굽의 날이 가까웠도다 - 바로의 팔을 꺾음',
    chapter_summary = '여호와의 말씀이 또 내게 임하여 가라사대 인자야 너는 예언하여 이르라 주 여호와의 말씀에 너희는 통곡하며 이르기를 슬프다 그 날이여 하라 그 날이 가까웠도다 여호와의 날이 가까웠도다 구름의 날일 것이요 열국의 때이리로다 칼이 애굽에 임한즉 애굽에서 살륙 당한 자들이 엎드러질 때에 구스에 심한 근심이 있을 것이며 애굽의 무리가 옮기우며 그 기초가 헐리리로다 구스와 붓과 룻과 모든 섞인 백성과 굽과 및 동맹한 땅의 백성들이 그들과 함께 칼에 엎드러지리라 하시며, 애굽을 붙드는 자들이 엎드러질 것이요 애굽의 교만한 권세가 낮아질 것이라 믹돌에서부터 수에네까지 무리가 그 가운데서 칼에 엎드러지리라 나 주 여호와의 말이니라 황폐한 열방 같이 그들도 황폐할 것이며 사막이 된 성읍들 같이 그 성읍들도 사막이 될 것이라 내가 애굽에 불을 지르며 그 모든 돕는 자를 멸할 때에 그들이 나를 여호와인줄 알리라 하시며, 또한 제십일년 정월 칠일에 여호와의 말씀이 내게 임하여 가라사대 인자야 내가 애굽 왕 바로의 팔을 꺾었더니 칼을 잡을 힘이 있도록 그것을 싸매어 낫게 하거나 붕대를 놓지 못하였느니라 그러므로 나 주 여호와가 말하노라 내가 애굽 왕 바로를 대적하여 그 두 팔 곧 성한 팔과 이미 꺾인 팔을 꺾어서 칼이 그 손에서 떨어뜨리고 애굽인을 열국 가운데로 흩으며 열방 가운데로 헤칠찌라 내가 바벨론 왕의 팔을 견고하게 하고 내 칼을 그 손에 붙이려니와 바로의 팔은 꺾으리니 바로가 그 앞에서 상한 자의 신음하듯 신음하리라고 선언하십니다.',
    chapter_themes = '["애굽의 날이 가까웠도다", "구름의 날", "열국의 때", "바로의 팔을 꺾음", "바벨론 왕의 팔을 견고하게", "상한 자의 신음"]',
    chapter_context = '애굽의 멸망의 날이 임박했음을 선언하고 바로의 군사력을 팔에 비유하여 그것이 완전히 꺾일 것을 예언한 내용입니다.',
    chapter_application = '하나님의 심판의 날을 두려워하고, 세상의 군사력이나 무력을 의지하지 말며, 하나님이 역사의 주관자이심을 인정하고, 참된 힘은 하나님께만 있음을 믿어야 합니다.'
WHERE book_id = 'ez' AND chapter = 30 AND verse = 1;

-- 에스겔 31장: 레바논 백향목에 비유된 앗수르
UPDATE bible_verses SET
    chapter_title = '레바논 백향목 같던 앗수르의 교만과 몰락',
    chapter_summary = '제십일년 삼월 일일에 여호와의 말씀이 내게 임하여 가라사대 인자야 애굽 왕 바로와 그 무리에게 이르라 네 큰 위엄을 누구에게 비하겠느냐 볼찌어다 앗수르 사람은 가지가 아름답고 그늘이 많은 레바논 백향목이라 키가 높아서 그 꼭대기가 구름에 닿았나니 물들이 그것을 기르매 깊은 물이 그것으로 자라게 하며 강들이 그 심긴 곳을 둘러 흐르며 둑의 물이 들의 모든 나무에까지 미치매 그 나무가 물이 많으므로 들의 모든 나무보다 키가 크며 가지가 번성하며 가지가 길었고 그 그늘에 큰 나라들이 거하며 그 가지에 공중의 모든 새가 깃들이며 그 큰 가지 아래 들의 모든 짐승이 새끼를 낳으며 그 그늘 아래 큰 민족들이 거하였었느니라 그 뿌리가 큰 물가에 있으므로 그 나무가 크고 가지가 길어서 모양이 아름다웠으며 하나님의 동산의 모든 나무가 그를 투기하였느니라 하시며, 그러므로 나 주 여호와가 말하노라 그것이 키가 높아지며 꼭대기가 구름에 닿으며 높이 솟은 것을 마음에 자랑하였으므로 내가 열국의 능한 자의 손에 붙일찌라 그가 임의로 그것을 대접할 것은 내가 그 악으로 인하여 쫓아냈음이라 외인 곧 열국의 강포한 자들이 그것을 찍어 버렸으므로 그 가지가 산과 모든 골짜기에 떨어졌고 그 큰 가지가 그 땅 모든 시내가에서 꺾어졌으며 땅의 모든 백성이 그 그늘을 떠나서 버렸느니라 공중의 모든 새가 그 넘어진 나무에 거하며 들의 모든 짐승이 그 가지에 있으리니 이는 물가의 모든 나무로 키가 높다고 교만하지 못하게 하며 그 꼭대기로 구름에 닿지 못하게 하며 그 중에 물 받는 자로 스스로 높아 서지 못하게 함이니 그들을 다 죽음에 붙여서 지하로 내려가게 하여 인생 중에 구덩이로 내려가는 자와 함께 있게 하였음이니라고 교훈하십니다.',
    chapter_themes = '["레바논 백향목 비유", "키가 높아 구름에 닿음", "하나님의 동산의 나무들이 투기", "높이 솟은 것을 마음에 자랑", "열국의 강포한 자들이 찍어 버림", "지하로 내려가게 함"]',
    chapter_context = '한때 강대했던 앗수르를 레바논의 백향목에 비유하여 그 교만과 몰락을 보여주고, 애굽도 같은 운명을 맞을 것이라고 경고한 비유입니다.',
    chapter_application = '교만을 경계하고, 높아진 지위를 자랑하지 말며, 모든 권세가 하나님께로부터 온다는 것을 인정하고, 겸손한 마음으로 하나님을 섬겨야 합니다.'
WHERE book_id = 'ez' AND chapter = 31 AND verse = 1;

-- 에스겔 32장: 바로를 위한 애가
UPDATE bible_verses SET
    chapter_title = '바다 괴물 바로를 위한 애가와 음부로 내려감',
    chapter_summary = '제십이년 십이월 일일에 여호와의 말씀이 내게 임하여 가라사대 인자야 애굽 왕 바로에 대하여 애가를 불러 그에게 이르라 너를 열국의 젊은 사자에 비하였더니 실상은 너는 바다 가운데 큰 악어라 강에서 뛰놀며 발로 물을 어지럽히며 그 강을 더럽혔도다 나 주 여호와가 말하노라 내가 많은 백성의 무리를 거느리고 내 그물을 네 위에 치고 그 그물로 너를 끌어 올리되 내가 너를 뭍에 던지며 들에 던져 공중의 새들로 네 위에 앉게 할 것임이여 온 세계의 짐승으로 너를 먹여 배부르게 하리로다 내가 네 고기를 여러 산에 두며 네 시체로 골짜기에 채울 것임이여 네 피로 땅에 물 대듯 하여 산에 미치게 하며 그 시내도 네 피로 가득하게 하고 내가 너를 불 끄듯 할 때에 하늘을 가리워 별들을 어둡게 하며 해를 구름으로 가리우며 달로 빛을 발하지 못하게 할찌라 하늘의 모든 밝은 빛을 내가 네 위에서 어둡게 하고 어둠을 네 땅에 베풀리로다 나 주 여호와의 말이니라 하시며, 또 제십이년 정월 십오일에 여호와의 말씀이 내게 임하여 가라사대 인자야 애굽의 무리를 위하여 애곡하고 그와 유명한 나라의 딸들을 음부 곧 구덩이에 내려가는 자와 함께 지하에 던지라 너의 아름다움이 어떤 사람보다 승하느냐 너는 할례받지 않은 자와 함께 내려가서 칼에 죽인 자 중에 누울찌어다 칼이 이미 빠졌은즉 그와 그 모든 무리를 끌찌어다 용사 중에 강한 자가 그를 돕는 자와 함께 음부 가운데서 그에게 말함이여 할례받지 않은 자 곧 칼에 죽인 자들이 내려와서 가만히 누웠다 하리로다 하시며 앗수르와 그 온 무리가 거기 있음이여 다 칼에 죽임을 당하여 무덤이 그 사면에 있도다 그 무덤이 구덩이 깊은 곳에 베풀렸고 그 무리가 그 무덤 사면에 있음이여 이들은 다 생존 세상에서 두려움을 놓았던 자로되 칼에 죽임을 당하였도다고 음부의 모습을 그립니다.',
    chapter_themes = '["바다 가운데 큰 악어", "그물로 끌어 올림", "하늘을 가리워 별들을 어둡게", "음부로 내려감", "할례받지 않은 자와 함께", "구덩이 깊은 곳의 무덤"]',
    chapter_context = '애굽 바로의 최종적 멸망을 애가의 형태로 노래하면서 음부에서 다른 멸망한 나라들과 함께 있게 될 것을 묘사한 장엄한 심판 예언입니다.',
    chapter_application = '세상의 권세는 일시적임을 깨닫고, 죽음 후의 심판을 두려워하며, 교만한 자들의 최후를 교훈으로 삼고, 겸손하게 하나님을 경외하며 살아야 합니다.'
WHERE book_id = 'ez' AND chapter = 32 AND verse = 1;

-- 에스겔 33장: 파수꾼의 사명과 개인 책임
UPDATE bible_verses SET
    chapter_title = '파수꾼의 사명과 각자의 길에 따른 심판',
    chapter_summary = '여호와의 말씀이 내게 임하여 가라사대 인자야 너는 네 민족에게 고하여 이르라 가령 내가 칼을 한 땅에 임하게 한다 하자 그 땅 백성이 자기들 중에서 하나를 택하여 파수꾼을 삼은즉 그가 칼이 그 땅에 임함을 보고 나팔을 불어 백성에게 경고하되 아무든지 나팔 소리를 듣고도 경비를 하지 아니하므로 칼이 와서 그를 제하면 그 피가 자기의 머리로 돌아갈 것이라 그가 경고를 받았던들 자기 생명을 보전하였을 것이나 나팔 소리를 듣고도 경비를 하지 아니하였으니 그 피가 자기에게로 돌아가리라 그러나 파수꾼이 칼이 임함을 보고도 나팔을 불지 아니하여 백성에게 경고치 아니하므로 칼이 와서 그들 중에 한 사람을 제하면 그는 자기 죄악 중에서 제함을 당하려니와 내가 그 피를 파수꾼의 손에서 찾으리라 하시며, 인자야 내가 너로 이스라엘 족속의 파수꾼을 삼았으니 너는 내 입의 말을 들어야 하겠고 나를 대신하여 그들에게 경고할찌니라 가령 내가 악인에게 이르기를 악인아 너는 정녕 죽으리라 하였다 하자 네가 그 악인에게 말로 경고하여 그 길에서 떠나게 하지 아니하면 그 악인은 자기 죄악 중에서 죽으려니와 내가 그 피를 네 손에서 찾을 것이고 네가 악인에게 경고하여 그 길에서 떠나라 한대 그가 돌이키지 아니하면 그는 자기 죄악 중에서 죽으려니와 너는 네 생명을 보전하리라 하시며, 또 예루살렘이 함락되었다는 소식이 전해지자 하나님께서 너희는 이르기를 우리의 허물과 죄가 이미 우리에게 있어 우리로 그 중에서 쇠패하게 하니 어찌 능히 살리요 하거니와 너는 그들에게 이르라 나 주 여호와가 말하노라 나의 삶을 두고 맹세하노니 나는 악인의 죽는 것을 기뻐하지 아니하고 악인이 그 길에서 돌이켜 떠나서 사는 것을 기뻐하노라 이스라엘 족속아 돌이키고 돌이키라 너희 악한 길에서 떠나라 어찌 죽고자 하느냐 하시며 개인의 책임을 다시 강조하십니다.',
    chapter_themes = '["파수꾼의 사명", "나팔을 불어 경고", "그 피가 자기 머리로", "나를 대신하여 경고", "악인의 죽는 것을 기뻐하지 않음", "돌이키고 돌이키라"]',
    chapter_context = '예루살렘 함락 후 에스겔의 파수꾼 사명을 재확인하고 개인의 회개 가능성과 책임을 강조한 중요한 전환점의 메시지입니다.',
    chapter_application = '영적 파수꾼으로서 경고할 책임을 다해야 하며, 각자 자신의 죄에 대해 책임져야 하고, 하나님은 회개를 원하신다는 것을 믿으며, 즉시 돌이켜 회개해야 합니다.'
WHERE book_id = 'ez' AND chapter = 33 AND verse = 1;

-- 에스겔 34장: 악한 목자들과 참된 목자
UPDATE bible_verses SET
    chapter_title = '자기만 먹이는 목자들과 친히 양을 찾으시는 여호와',
    chapter_summary = '여호와의 말씀이 내게 임하여 가라사대 인자야 이스라엘의 목자들을 쳐서 예언하라 그들 곧 목자들에게 예언하여 이르라 주 여호와의 말씀에 자기만 먹이는 이스라엘의 목자들은 화 있을진저 목자들이 양떼를 먹이는 것이 마땅치 아니하냐 너희가 살진 양을 잡아 그 기름을 먹으며 그 털을 입되 양떼는 먹이지 아니하는도다 너희가 병든 자를 강건케 아니하며 상한 자를 고치지 아니하며 상한 자를 싸매지 아니하며 쫓긴 자를 돌아오게 아니하며 잃어버린 자를 찾지 아니하고 다만 포학으로 그것들을 다스렸도다 목자가 없으므로 그것들이 흩어지며 흩어져서 모든 들짐승의 밥이 되었도다 내 양떼가 모든 산과 높은 멧부리에 유리하며 내 양떼가 온 지면에 흩어졌으되 찾고 찾는 자가 없었도다 하시며, 그러므로 나 주 여호와가 말하노라 내가 나의 삶을 두고 맹세하노니 내 양떼가 노략거리가 되고 모든 들짐승의 밥이 된 것은 목자가 없음이라 내 목자들이 내 양을 찾지 아니하고 자기만 먹이고 내 양떤는 먹이지 아니하였도다 그러므로 너희 목자들아 여호와의 말씀을 들을찌어다 나 주 여호와가 말하노라 내가 목자들을 대적하여 내 양떼를 그들의 손에서 찾으리니 목자들로 다시는 양을 먹이지 못하게 하며 목자들로 다시는 자기를 먹이지 못하게 하리라 내가 내 양을 그들의 입에서 건져내어서 다시는 그 식물이 되지 않게 하리라 하시며, 나 주 여호와가 말하노라 보라 내가 친히 내 양을 찾고 찾되 목자가 양떼 가운데 있는 날에 양이 흩어졌으면 그 떼를 찾는 것 같이 내가 내 양을 찾아서 흐리고 캄캄한 날에 그 흩어진 모든 곳에서 그것들을 건져낼찌라 내가 그것들을 만민 중에서 끌어내며 열방 중에서 모아 그 고토로 데리고 와서 이스라엘 산에서, 시냇가에서, 거민이 거하는 모든 곳에서 먹이되 좋은 꼴로 먹이고 그 우리를 이스라엘 높은 산에 두리니 그것들이 거기서 좋은 우리에 누워 있으며 이스라엘 산에서 살진 꼴을 먹으리라 나는 친히 내 양떼를 먹이며 그것들로 눕게 하리라 나 주 여호와의 말이니라 그 잃어버린 자를 내가 찾으며 쫓긴 자를 내가 돌아오게 하며 상한 자를 내가 싸매며 병든 자를 내가 강건케 하려니와 살진 자와 강한 자는 내가 멸하고 공의로 그것들을 먹이리라 하시며, 내가 한 목자를 그들의 위에 세워 먹이게 하리니 그는 내 종 다윗이라 그가 그들을 먹이고 그들의 목자가 될찌라 나 여호와는 그들의 하나님이 되고 내 종 다윗은 그들 중에 왕이 되리라 나 여호와의 말이니라고 약속하십니다.',
    chapter_themes = '["자기만 먹이는 목자들", "병든 자를 강건케 하지 않음", "목자가 없으므로 흩어짐", "내가 친히 내 양을 찾고", "한 목자 내 종 다윗", "공의로 먹이리라"]',
    chapter_context = '이스라엘의 부패한 지도자들을 책망하고 하나님께서 친히 목자가 되시며 다윗 같은 참된 목자를 세우실 것을 약속한 목회적 예언입니다.',
    chapter_application = '참된 목자의 마음으로 양떼를 돌봐야 하며, 자기 유익보다 양떼의 유익을 구해야 하고, 예수님을 참된 목자로 믿고 따르며, 잃어버린 영혼들을 찾아야 합니다.'
WHERE book_id = 'ez' AND chapter = 34 AND verse = 1;

-- 에스겔 35장: 에돔에 대한 심판
UPDATE bible_verses SET
    chapter_title = '세일산 에돔에 대한 영영한 황무의 심판',
    chapter_summary = '여호와의 말씀이 내게 임하여 가라사대 인자야 네 얼굴을 세일산으로 향하고 그것을 쳐서 예언하라 이르기를 주 여호와의 말씀에 세일산아 내가 너를 대적하여 내 손을 네 위에 펴서 너로 황무지와 놀라움이 되게 하리라 내가 네 성읍들을 무너뜨리리니 네가 황무하리라 그리한즉 네가 나를 여호와인줄 알리라 하시며, 네가 옛날부터 한을 품고 이스라엘 자손의 환난 때 곧 죄악의 마지막 때에 칼의 위력에 그들을 붙였도다 그러므로 나 주 여호와가 말하노라 내가 나의 삶을 두고 맹세하노니 내가 너로 피를 당하게 하리니 피가 너를 따르리라 네가 피를 미워하지 아니하였은즉 피가 너를 따르리라 내가 세일산으로 황무지와 놀라움이 되게 하여 그 위에 왕래하는 자를 다 끊을찌라 내가 그 살륙 당한 자로 그 여러 산에 채우되 그 작은 산들과 골짜기들과 모든 시내에 칼에 죽인 자가 엎드러지게 하리라 내가 너로 영영한 황무가 되게 하여 네 성읍들에 거민이 없게 하리니 너희가 나를 여호와인줄 알리라 하시며, 네가 말하기를 이 두 민족과 이 두 땅이 다 내 것이 되리니 내가 그것을 얻으리라 하였도다 그러나 여호와께서 거기 계셨느니라 그러므로 나 주 여호와가 말하노라 내가 나의 삶을 두고 맹세하노니 네가 그들을 미워하여 노한대로, 질투한대로 내가 네게 행하리라 내가 너를 국문할 때에 그들이 나를 알리라 네가 이스라엘 산들을 가리켜 말하기를 산들이 황무하니 우리에게 주어서 삼키게 되었다 하여 입으로 나를 대적하여 자랑한 말을 나 여호와가 들었노라 나 주 여호와가 말하노라 온 땅이 즐거워할 때에 내가 너를 황무케 하되 이스라엘 족속의 기업이 황무함을 인하여 네가 즐거워한 것 같이 내가 너로 황무케 하리라 세일산아 너와 에돔 온 땅이 황무하리니 무리가 나를 여호와인줄 알리라고 선언하십니다.',
    chapter_themes = '["세일산을 향하여", "옛날부터 한을 품음", "피를 미워하지 않았은즉", "영영한 황무", "이 두 민족이 내 것", "입으로 나를 대적하여 자랑"]',
    chapter_context = '이스라엘의 고난을 기뻐하고 그들의 땅을 차지하려 한 에돔에 대한 하나님의 공의로운 심판과 영원한 황폐를 선언한 내용입니다.',
    chapter_application = '형제의 고난을 기뻐하지 말아야 하며, 하나님의 백성을 대적하지 말고, 다른 사람의 불행으로 이익을 취하려 하지 말며, 하나님의 공의로운 보응을 두려워해야 합니다.'
WHERE book_id = 'ez' AND chapter = 35 AND verse = 1;

-- 에스겔 36장: 이스라엘 산들의 회복
UPDATE bible_verses SET
    chapter_title = '이스라엘 산들아 가지를 내고 열매를 맺으라',
    chapter_summary = '인자야 너는 이스라엘 산들에게 예언하여 이르라 이스라엘 산들아 여호와의 말씀을 들으라 주 여호와의 말씀에 대적이 네게 대하여 말하기를 하하 옛적 높은 곳이 우리의 기업이 되었도다 하였느니라 그러므로 너는 예언하여 이르라 주 여호와의 말씀에 그들이 너희를 황무케 하고 너희 사방에서 삼켰으므로 너희가 다른 이방인의 기업이 되고 또 말하는 자의 입술에 오르며 백성의 비방거리가 되었도다 그러므로 이스라엘 산들아 주 여호와의 말씀을 들을찌어다 주 여호와께서 산들과 작은 산들과 시내들과 골짜기들과 황무한 사막들과 사람에게 버린바 되고 둘러 있는 이방인의 노략거리와 조롱거리가 된 성읍들에게 말씀하셨느니라 하시며, 그러므로 나 주 여호와가 말하노라 내가 진실로 질투의 불로 남은 이방인과 에돔 온 땅을 쳐서 말하였노니 그들이 심히 즐거워하며 멸시하는 마음으로 내 땅을 빼앗아 노략하여 자기 소유를 삼았음이니라 그러므로 너는 이스라엘 땅을 대하여 예언하되 산들과 작은 산들과 시내들과 골짜기들을 향하여 이르라 주 여호와의 말씀에 내가 질투와 분노로 말하였나니 이는 너희가 이방의 수치를 당하였음이라 그러므로 나 주 여호와가 맹세하여 이르노니 너희 사면에 있는 이방인이 자기들의 수치를 정녕 당하리라 하시며, 그러나 이스라엘 산들아 너희는 가지를 내고 내 백성 이스라엘을 위하여 과실을 맺으리니 그들의 올 때가 가까이 이르렀음이니라 내가 돌이켜 너희와 함께하리니 사람이 너희를 갈고 심을 것이며 내가 사람을 너희 위에 많게 하리니 이는 이스라엘 온 족속이라 그 성읍들에 거민이 있게 하며 빈 땅에 건물이 있게 하고 내가 너희 위에 사람과 짐승으로 많게 하되 생육이 중다하고 번성하게 할 것이라 너희 전 지위대로 사람이 거하게 하여 너희를 처음보다 낫게 대접하리니 너희가 나를 여호와인줄 알리라 하시며, 내가 또 새 마음을 너희에게 주되 새 영을 너희 속에 두고 너희 육신에서 굳은 마음을 제하고 부드러운 마음을 줄것이며 또 내 신을 너희 속에 두어 너희로 내 율례를 행하며 내 규례를 지켜 행하게 하리라 내가 너희 열조에게 준 땅에 너희가 거하여 내 백성이 되고 나는 너희 하나님이 되리라고 새 언약을 약속하십니다.',
    chapter_themes = '["이스라엘 산들아", "가지를 내고 과실을 맺음", "그들의 올 때가 가까이", "처음보다 낫게 대접", "새 마음과 새 영", "내 신을 너희 속에"]',
    chapter_context = '이스라엘 땅의 황폐함에서 회복으로의 전환을 선언하고 새 언약을 통한 영적 회복을 약속한 희망의 메시지입니다.',
    chapter_application = '하나님의 회복하시는 능력을 믿고, 절망적 상황에서도 소망을 잃지 말며, 새 마음과 새 영을 받아 하나님의 백성으로 살고, 하나님의 때를 기다려야 합니다.'
WHERE book_id = 'ez' AND chapter = 36 AND verse = 1;