-- 예레미야서(Jeremiah) 37-52장 주석
-- 총 52장의 대예언서 3부 (예루살렘 함락과 회복의 소망)

-- 예레미야 37장: 예레미야의 투옥
UPDATE bible_verses SET
    chapter_title = '바벨론군이 물러간 틈에 벤야민으로 가려던 예레미야',
    chapter_summary = '요시야의 아들 시드기야가 여고냐를 대신하여 왕이 되었으나 그와 그 신하들과 그 땅 백성이 여호와께서 선지자 예레미야로 하신 말씀을 듣지 아니하니라 하며, 시드기야 왕이 셀레먀의 아들 여후갈과 마아세야의 아들 제사장 스바냐를 선지자 예레미야에게 보내어 청컨대 우리를 위하여 우리 하나님 여호와께 기도하라 하였는데, 그 때에 예레미야가 갇히지 아니하였으므로 백성 가운데 출입하는 중이었더라고 기록합니다. 바로의 군대가 애굽에서 나오매 예루살렘을 에워쌌던 갈대아인이 그 소문을 듣고 예루살렘에서 떠났을 때, 예레미야가 베냐민 땅으로 가서 거기서 백성 중에 분깃을 받으려고 성문에서 나가려 하니 하나냐의 손자요 셀레먀의 아들인 문지기 이리야가 선지자 예레미야를 잡아 가로되 네가 갈대아인에게 항복하려 한다고 거짓 고발을 당해 구덩이에 갇혔다고 기록합니다.',
    chapter_themes = '["시드기야의 기도 요청", "바로 군대의 출현", "갈대아인의 철수", "베냐민으로 가려함", "항복하려 한다는 거짓 고발", "구덩이에 갇힘"]',
    chapter_context = '이집트 군대의 개입으로 바벨론군이 일시 철수했을 때 예레미야가 거짓 고발로 투옥된 사건을 기록한 내용입니다.',
    chapter_application = '진실을 말하는 자가 오해받고 고난당할 수 있음을 각오해야 하며, 거짓 고발에 굴복하지 말고, 하나님의 뜻을 끝까지 전해야 합니다.'
WHERE book_id = 'jr' AND chapter = 37 AND verse = 1;

-- 예레미야 38장: 구덩이에 던져진 예레미야
UPDATE bible_verses SET
    chapter_title = '구덩이에 던져진 예레미야와 에벳멜렉의 구원',
    chapter_summary = '맛단의 아들 스바댜와 바스훌의 아들 그다랴와 셀레먀의 아들 유갈과 말기야의 아들 바스훌이 예레미야의 모든 백성에게 하는 말을 들으니라 이 성에 거하는 자는 칼과 기근과 염병에 죽으리라마는 갈대아인에게 나아가는 자는 살리니 그는 노략한 것 같이 그 생명을 얻으리라고 하매, 이 사람이 이 백성의 평안을 구치 아니하고 해를 구하나니 그를 죽이소서 하니 시드기야 왕이 가로되 그가 너희 손에 있나니 왕은 너희를 거스려 아무것도 할 수 없노라 하매 그들이 예레미야를 취하여 왕궁에 있는 말기야의 구덩이에 던지고 줄로 예레미야를 달아 내렸는데 그 구덩이에는 물이 없고 진흙뿐이므로 예레미야가 진흙에 빠졌더라고 기록합니다. 그때 왕궁의 환관 구스인 에벳멜렉이 왕에게 고하여 예레미야를 구덩이에서 끌어올렸다고 합니다.',
    chapter_themes = '["이 백성의 해를 구함", "구덩이에 던짐", "진흙에 빠짐", "에벳멜렉의 구원", "헌 넝마와 헌 옷", "시드기야의 은밀한 만남"]',
    chapter_context = '예레미야가 항복을 권유했다고 해서 죽을 뻔했지만 이방인 에벳멜렉의 도움으로 구원받은 사건을 기록한 내용입니다.',
    chapter_application = '진리를 말하는 자를 핍박하지 말고, 위험에 처한 의인을 도와야 하며, 때로는 이방인이 더 의로울 수 있음을 인정하고, 하나님께서 의인을 보호하심을 신뢰해야 합니다.'
WHERE book_id = 'jr' AND chapter = 38 AND verse = 1;

-- 예레미야 39장: 예루살렘의 함락
UPDATE bible_verses SET
    chapter_title = '예루살렘 함락과 시드기야의 비참한 최후',
    chapter_summary = '유다 왕 시드기야의 구년 시월에 바벨론 왕 느부갓네살과 그 모든 군대가 와서 예루살렘을 에워쌌고 시드기야의 십일년 사월 구일에 성이 함락되니라 예루살렘이 함락되매 바벨론 왕의 모든 방백이 와서 중문에 앉으니 곧 네르갈사레셀과 삼갈느보와 환관장 살스김과 박사장 네르갈사레셀 등이라고 기록합니다. 유다 왕 시드기야와 모든 군사가 그들을 보고 도망하여 밤에 왕의 동산 길로 두 담 사이 문으로 나가서 아라바 길로 가더니 갈대아인의 군대가 그들을 따라 여리고 평지에서 시드기야를 따라 잡아서 하맛 땅 립나에 있는 바벨론 왕 느부갓네살에게로 끌고 가매 느부갓네살이 그를 심문하고 시드기야의 아들들을 그 눈 앞에서 죽였고 시드기야의 눈을 빼고 놋사슬로 결박하여 바벨론으로 끌고 갔다고 기록합니다.',
    chapter_themes = '["예루살렘 함락", "바벨론 방백들이 중문에 앉음", "시드기야의 도망", "여리고에서 사로잡힘", "아들들이 눈 앞에서 죽임", "눈을 빼고 결박"]',
    chapter_context = '오랫동안 예고되었던 예루살렘의 최종 함락과 시드기야 왕의 비참한 최후를 기록한 역사적 기록입니다.',
    chapter_application = '하나님의 경고를 무시한 결과의 심각성을 깨닫고, 하나님의 말씀에 즉시 순종해야 하며, 교만한 자의 비참한 최후를 교훈으로 삼아야 합니다.'
WHERE book_id = 'jr' AND chapter = 39 AND verse = 1;

-- 예레미야 40장: 그다랴 총독의 임명
UPDATE bible_verses SET
    chapter_title = '시위대장 느부사라단이 예레미야를 놓아준 후 그다랴 총독',
    chapter_summary = '시위대장 느부사라단이 라마에서 예레미야를 놓아 준 후에 여호와의 말씀이 예레미야에게 임하니라 시위대장이 예레미야를 불러다가 그에게 이르되 네 하나님 여호와께서 이곳에 이 재앙을 선고하시더니 여호와께서 말씀하신 대로 이루셨나니 너희가 여호와께 범죄하고 그 목소리를 청종치 아니하였으므로 이 일이 너희에게 임하였느니라고 말합니다. 또한 아히감의 아들 그다랴를 유다 총독으로 세워 백성을 다스리게 했는데, 들에 있던 군대장관들과 그들의 사람들이 바벨론 왕이 그다랴를 이 땅 총독으로 세운 것을 듣고 그다랴에게로 나아왔다고 기록합니다. 그다랴가 그들과 그들의 사람들에게 맹세하여 가로되 갈대아인을 섬기기를 두려워 말고 이 땅에 거하여 바벨론 왕을 섬기라 그리하면 너희가 평안하리라고 권면했다고 합니다.',
    chapter_themes = '["느부사라단이 예레미야를 놓아줌", "네 하나님 여호와께서", "그다랴 총독 임명", "갈대아인을 섬기라", "이 땅에 거하라", "평안하리라"]',
    chapter_context = '예루살렘 함락 후 바벨론이 유다 총독으로 그다랴를 세우고 잔류민들을 다스리게 한 상황을 기록한 내용입니다.',
    chapter_application = '하나님의 심판도 결국 하나님의 뜻임을 인정하고, 주어진 현실에서 최선을 다해 살아야 하며, 평화를 위해 노력해야 합니다.'
WHERE book_id = 'jr' AND chapter = 40 AND verse = 1;

-- 예레미야 41장: 그다랴의 암살
UPDATE bible_verses SET
    chapter_title = '이스마엘이 그다랴와 함께 있던 자들을 죽임',
    chapter_summary = '칠월에 왕족 엘리사마의 손자요 느다냐의 아들 이스마엘이 왕의 장관 열 사람과 함께 미스바에 있는 아히감의 아들 그다랴에게로 와서 미스바에서 함께 떡을 먹다가 느다냐의 아들 이스마엘과 그와 함께 한 열 사람이 일어나서 바벨론 왕이 이 땅 총독으로 세운 사반의 손자요 아히감의 아들 그다랴를 칼로 쳐 죽이고 또 미스바에서 그다랴와 함께 있던 모든 유다인과 거기 있던 갈대아 군사들을 죽였다고 기록합니다. 또한 실로와 사마리아와 북방에서 온 팔십 명이 수염을 깎고 옷을 찢고 몸을 상하게 하고 손에 소제물과 유향을 가지고 여호와의 전으로 가려 하매 이스마엘이 그들을 속여 칠십 명을 죽이고 열 명만 살려두었다고 합니다. 그리고 백성들을 사로잡아 가다가 요하난과 그와 함께 한 모든 군대장관들이 추격하여 구원했다고 기록합니다.',
    chapter_themes = '["이스마엘의 그다랴 암살", "함께 떡을 먹다가", "왕족의 반역", "실로 사마리아에서 온 자들", "칠십 명을 죽임", "요하난의 추격"]',
    chapter_context = '바벨론이 세운 유다 총독 그다랴가 왕족 이스마엘에 의해 암살당하면서 유다의 잔류민들이 더욱 어려워진 상황을 기록한 내용입니다.',
    chapter_application = '정치적 야심이 공동체를 파괴할 수 있음을 경계하고, 신뢰를 배신하는 일을 하지 말며, 무고한 사람들을 해치지 말아야 합니다.'
WHERE book_id = 'jr' AND chapter = 41 AND verse = 1;

-- 예레미야 42장: 애굽으로 가려는 백성들
UPDATE bible_verses SET
    chapter_title = '애굽으로 가지 말고 이 땅에 거하라',
    chapter_summary = '모든 군대장관과 가레아의 아들 요하난과 호사야의 아들 야사냐와 모든 백성의 작은 자로부터 큰 자까지가 다 가까이 나아와 선지자 예레미야에게 이르되 우리의 간구함이 네 앞에 받아들여지기를 원하노라 우리를 위하여 네 하나님 여호와께 기도하되 이 남은 자 전부를 위하여 하라 우리가 많았으나 이제 적게 남은 것을 네 눈이 보는 바와 같으니라 네 하나님 여호와께서 우리의 행할 길과 할 일을 우리에게 알게 하시기를 구하노라고 요청합니다. 예레미야가 십 일 후에 여호와의 말씀을 전하되 만일 너희가 이 땅에 머물러 거하면 내가 너희를 세우고 헐지 아니하며 심고 뽑지 아니하리니 이는 내가 너희에게 내린 재앙에 대하여 뜻을 돌이켰음이니라 너희가 두려워하는 바벨론 왕을 두려워하지 말라 내가 너희와 함께 하여 너희를 구원하며 그의 손에서 건지리라 하시며, 만일 너희가 애굽으로 가서 거기 거하면 너희의 두려워하는 칼이 애굽 땅에서 너희에게 미칠 것이요 너희의 걱정하는 기근이 애굽에서 너희를 따를 것이며 거기서 너희가 죽으리라고 경고하십니다.',
    chapter_themes = '["우리를 위하여 기도하라", "행할 길과 할 일", "이 땅에 머물러 거하면", "뜻을 돌이키심", "바벨론 왕을 두려워 말라", "애굽으로 가면 죽으리라"]',
    chapter_context = '그다랴 암살 후 바벨론의 보복이 두려워 애굽으로 피하려는 백성들에게 이 땅에 머물라는 하나님의 뜻을 전한 내용입니다.',
    chapter_application = '하나님의 뜻을 구하고 기다려야 하며, 두려움 때문에 하나님의 뜻을 거역하지 말고, 하나님이 정하신 곳에서 순종하며 살아야 합니다.'
WHERE book_id = 'jr' AND chapter = 42 AND verse = 1;

-- 예레미야 43장: 애굽으로 강제로 끌려감
UPDATE bible_verses SET
    chapter_title = '호사야의 아들 아사랴와 가레아의 아들 요하난의 불순종',
    chapter_summary = '예레미야가 그들의 하나님 여호와의 말씀 곧 여호와께서 자기를 보내사 그들에게 이르게 하신 이 모든 말씀을 백성에게 다 고하매 호사야의 아들 아사랴와 가레아의 아들 요하난과 모든 교만한 자가 예레미야에게 말하여 가로되 네가 거짓을 말하는도다 우리 하나님 여호와께서 너를 보내어 말씀하시기를 너희는 애굽으로 가서 거기 우거하지 말라 하지 아니하셨느니라 이는 네리야의 아들 바룩이 너를 부추겨서 우리를 갈대아인의 손에 붙여 죽이며 바벨론으로 사로잡혀 가게 하려 함이니라 하고 가레아의 아들 요하난과 모든 군대장관과 모든 백성이 유다 땅에 거하라 하신 여호와의 목소리를 청종치 아니하고 가레아의 아들 요하난과 모든 군대장관이 유다의 남은 자 곧 쫓겨났다가 유다 땅에 거하려고 열방 중에서 돌아온 자들을 다 모으되 곧 남자와 여자와 유아와 왕의 딸들과 시위대장 느부사라단이 사반의 손자 아히감의 아들 그다랴에게 위탁한 모든 사람과 선지자 예레미야와 네리야의 아들 바룩을 끌어가지고 애굽 땅에 들어가서 다바네스에 이르렀다고 기록합니다.',
    chapter_themes = '["네가 거짓을 말한다", "바룩이 부추긴다", "여호와의 목소리를 청종치 않음", "모든 백성을 끌어감", "애굽 땅 다바네스", "강제로 끌려간 예레미야"]',
    chapter_context = '하나님의 명령을 거역하고 애굽으로 피한 유다 잔류민들이 예레미야와 바룩을 강제로 데려간 사건을 기록한 내용입니다.',
    chapter_application = '하나님의 말씀이 마음에 들지 않는다고 거역하지 말고, 선지자를 핍박하지 말며, 두려움보다 하나님을 더 경외해야 합니다.'
WHERE book_id = 'jr' AND chapter = 43 AND verse = 1;

-- 예레미야 44장: 애굽에서의 마지막 경고
UPDATE bible_verses SET
    chapter_title = '애굽에 거한 유다인들에게 주는 마지막 경고',
    chapter_summary = '애굽 땅에 거하는 모든 유다인 곧 믹돌과 다바네스와 놉과 바드로스 땅에 거하는 자들에게 임한 말씀이라 만군의 여호와 이스라엘의 하나님이 가라사대 내가 예루살렘과 유다 모든 성읍에 내린 모든 재앙을 너희가 보았나니 보라 오늘날 그것들이 황무하고 거민이 없나니 이는 그들이 자기와 너희와 너희 열조의 알지 못하던 다른 신들에게 나아가 분향하여 섬김으로 나의 노를 격동하여 행한 악을 인함이니라고 하시며, 그러므로 나 만군의 여호와 이스라엘의 하나님이 말하노라 너희가 어찌하여 큰 악을 행하여 자기 영혼을 해하며 너희 중에서 유다 가운데로부터 남자와 여자와 유아와 젖먹는 자를 끊어서 하나도 남지 않게 하려느냐고 책망하십니다. 그러나 백성들이 우리가 우리 입으로 낸 모든 말을 반드시 행하여 하늘의 여왕에게 분향하고 전제를 드리되 우리와 우리 열조와 우리 왕들과 우리 방백들이 유다 성읍들과 예루살렘 거리에서 행하던 것과 같이 하리라 그 때에 우리가 식물이 풍족하며 복을 받고 재앙을 보지 아니하였더니 우리가 하늘의 여왕에게 분향하며 전제 드리기를 그친 후부터는 모든 것이 핍절하고 칼과 기근에 망하였느니라고 완강히 거부합니다.',
    chapter_themes = '["애굽의 유다인들", "다른 신들에게 분향", "큰 악을 행함", "하늘의 여왕", "우리 입으로 낸 말을 행하리라", "완강한 거부"]',
    chapter_context = '애굽으로 피한 유다인들이 그곳에서도 우상숭배를 계속하며 하나님의 경고를 완전히 거부한 최종적 배신을 기록한 내용입니다.',
    chapter_application = '우상숭배의 유혹에서 완전히 벗어나야 하며, 하나님의 경고를 무시하고 고집을 부리지 말고, 과거의 죄를 반복하지 말아야 합니다.'
WHERE book_id = 'jr' AND chapter = 44 AND verse = 1;

-- 예레미야 45장: 바룩에게 주신 위로
UPDATE bible_verses SET
    chapter_title = '서기관 바룩에게 주신 하나님의 위로',
    chapter_summary = '유다 왕 요시야의 아들 여호야김 제사년에 네리야의 아들 바룩이 예레미야의 구전을 받아 이 모든 말을 책에 기록하니라 선지자 예레미야가 네리야의 아들 바룩에게 말하되 바룩이 이르기를 슬프다 여호와께서 나의 고통에 슬픔을 더하셨나니 나는 탄식함으로 피곤하여 평안치 못하다 하도다 하였더니 너는 그에게 이같이 말하라 여호와의 말씀에 보라 내가 건설한 것을 내가 헐며 내가 심은 것을 내가 뽑나니 온 땅에 이러하니라 네가 너를 위하여 큰 일을 도모하느냐 도모하지 말라 보라 내가 모든 육체에게 재앙을 내리리라 그러나 네가 어느 곳으로 가든지 내가 너로 네 생명을 얻게 하여 노략물을 얻는 것같이 하리라 여호와의 말이니라 하니라고 기록합니다.',
    chapter_themes = '["바룩의 탄식", "고통에 슬픔을 더하심", "탄식함으로 피곤", "큰 일을 도모하지 말라", "네 생명을 얻게 함", "노략물을 얻는 것같이"]',
    chapter_context = '예레미야의 충실한 서기관 바룩이 고난 중에 탄식할 때 하나님께서 주신 개인적인 위로와 약속의 말씀입니다.',
    chapter_application = '하나님의 일을 하다가 고난을 당해도 낙심하지 말고, 개인적 야심보다 하나님의 뜻을 우선하며, 하나님께서 생명을 보호해 주실 것을 믿어야 합니다.'
WHERE book_id = 'jr' AND chapter = 45 AND verse = 1;

-- 예레미야 46장: 애굽에 대한 예언
UPDATE bible_verses SET
    chapter_title = '열방에 대한 말씀 - 애굽과 바로의 군대',
    chapter_summary = '열방에 대한 여호와의 말씀이 선지자 예레미야에게 임하니라 애굽을 논한 것이라 유다 왕 요시야의 아들 여호야김의 제사년에 바벨론 왕 느부갓네살이 유브라데강 가 갈그미스에서 친 애굽 왕 바로느고의 군대를 가리켜 말씀하시니라 너희는 방패와 손 방패를 예비하고 나아가서 싸우라 너희는 말을 메우고 기병은 올라타며 투구를 쓰고 나서며 창을 갈며 갑옷을 입으라 하시며, 내가 어찌하여 그들의 놀라 물러가는 것을 보는고 그들의 용사는 패하여 급히 도망하며 뒤를 돌아보지 아니함은 어찜인고 두려움이 사방에 있도다 여호와의 말이니라고 선언하십니다. 그러나 야곱아 내 종아 두려워 말라 이스라엘아 놀라지 말라 보라 내가 너를 원방에서 구원하며 네 후손을 포로된 땅에서 구원하리니 야곱이 돌아와서 평안히 안전히 거할 것이라 그를 두렵게 할 자가 없으리라고 위로하십니다.',
    chapter_themes = '["열방에 대한 말씀", "갈그미스 전투", "애굽의 패배", "두려움이 사방에", "야곱아 두려워 말라", "원방에서 구원"]',
    chapter_context = '이방 민족들에 대한 심판 예언의 시작으로, 바벨론에 의한 애굽의 패배와 이스라엘의 최종적 구원을 예언한 내용입니다.',
    chapter_application = '세상 강대국들도 하나님의 심판을 받을 것을 믿고, 하나님의 백성은 두려워하지 말며, 하나님의 최종적 구원을 소망해야 합니다.'
WHERE book_id = 'jr' AND chapter = 46 AND verse = 1;

-- 예레미야 47장: 블레셋에 대한 예언
UPDATE bible_verses SET
    chapter_title = '블레셋에 대한 심판과 가사 아스글론의 멸망',
    chapter_summary = '바로가 가사를 치기 전에 블레셋에 대한 여호와의 말씀이 선지자 예레미야에게 임하니라 여호와께서 이같이 말씀하시되 보라 물이 북방에서 일어나 넘치는 시내를 이루어 그 땅과 그 중에 있는 모든 것과 그 성읍과 거기 거하는 자들을 삼킬 것이라 사람들이 부르짖으며 그 땅 모든 거민이 애곡하리로다 아비들이 그 손의 약함을 인하여 자녀를 돌아보지 못하리니 이는 블레셋 사람을 진멸하시며 두로와 시돈을 도울 남은 자를 끊으시는 날이 이름이라 여호와께서 갑돌 해변에서 남은 블레셋 사람을 진멸하시리로다 가사가 삭발되었고 아스글론과 그들의 골짜기에 남은 자가 멸절되었나니 네가 언제까지 네 몸을 상하게 하겠느냐고 하시며, 여호와의 칼아 언제까지 쉬지 않겠느냐 네 집에 들어가서 가만히 쉴찌어다 하나 여호와께서 이를 명하셨은즉 어찌 쉬겠느냐 아스글론과 해변을 치려 하여 명정하셨느니라고 선언하십니다.',
    chapter_themes = '["북방에서 일어나는 물", "넘치는 시내", "블레셋 진멸", "가사가 삭발됨", "아스글론 멸절", "여호와의 칼"]',
    chapter_context = '이스라엘을 오랫동안 괴롭힌 블레셋족에 대한 바벨론을 통한 하나님의 심판을 예언한 내용입니다.',
    chapter_application = '하나님의 백성을 괴롭히는 자들은 반드시 심판받을 것을 믿고, 하나님의 공의로운 보응을 신뢰하며, 원수 갚는 것은 하나님께 맡겨야 합니다.'
WHERE book_id = 'jr' AND chapter = 47 AND verse = 1;

-- 예레미야 48장: 모압에 대한 심판
UPDATE bible_verses SET
    chapter_title = '모압에 대한 심판 - 그모스의 부끄러움',
    chapter_summary = '모압에 대한 말씀이라 만군의 여호와 이스라엘의 하나님이 가라사대 슬프다 느보여 그것이 황폐되었도다 기랴다임이 수치를 당하여 점령되었고 미스갑이 수치를 당하여 파괴되었으니 모압의 영예가 없어졌도다 헤스본에서 그들이 모압을 모해하여 이르기를 와서 그를 끊어서 나라를 이루지 못하게 하자 하는도다 맏멘아 너도 적막하게 되리니 칼이 너를 따르리로다 하시며, 모압이 여호와께 대하여 자만하였으므로 너희는 저로 취하게 하라 저가 구토할 것이라 모압이 이스라엘을 조롱거리로 삼았나니 저가 도적 중에서 발견되었느냐 네가 저를 말할 때마다 네 머리를 흔드는도다 하시고, 모압 거민들아 성읍을 떠나 바위 사이에 거할찌어다 깊은 골짜기 어귀에 깃들이는 비둘기 같이 할찌어다 모압의 교만을 우리가 들었나니 심한 교만 곧 저의 자고와 거만과 교만과 마음의 높음이로다라고 책망하십니다.',
    chapter_themes = '["모압의 영예가 없어짐", "헤스본에서 모해함", "여호와께 대한 자만", "이스라엘을 조롱거리로", "깊은 골짜기 어귀의 비둘기", "심한 교만"]',
    chapter_context = '이스라엘과 인접한 모압족이 교만과 조롱으로 인해 받을 하나님의 심판을 상세히 예언한 내용입니다.',
    chapter_application = '교만을 버리고 겸손해야 하며, 하나님의 백성을 조롱하지 말고, 자만에 빠지지 말며, 하나님의 공의로운 심판을 두려워해야 합니다.'
WHERE book_id = 'jr' AND chapter = 48 AND verse = 1;

-- 예레미야 49장: 암몬, 에돔, 다메섹, 게달, 엘람에 대한 예언
UPDATE bible_verses SET
    chapter_title = '암몬, 에돔, 다메섹, 게달, 엘람에 대한 심판',
    chapter_summary = '암몬 자손에 대한 말씀이라 여호와께서 가라사대 이스라엘이 무자하냐 상속자가 없느냐 말감이 어찌하여 갓을 점령하며 그 백성이 그 성읍들에 거하느냐 하시고, 에돔에 대한 말씀이라 만군의 여호와께서 가라사대 데만에 다시는 지혜가 없게 되었느냐 명철한 자에게 모략이 끊어졌느냐 그들의 지혜가 없어졌느냐 하시며, 다메섹에 대한 말씀이라 하맛과 아르밧이 수치를 당하리니 저희가 흉한 소문을 들었음이로다 바닷가에서 염려하여 평안치 못하리로다 다메섹이 피곤하여 몸을 돌이켜 달아나려 하니 떨림이 그를 움켰고 해산하는 여인 같이 고통과 슬픔이 그를 잡았도다 하시며, 아라비아와 하솔의 나라들 곧 바벨론 왕 느부갓네살에게 공격을 당한 게달에 대한 말씀이라 여호와께서 가라사대 너희는 일어나 게달로 올라가서 동방 자손들을 멸하라 하시고, 유다 왕 시드기야의 즉위한지 오래지 아니하여서 엘람에 대한 여호와의 말씀이 선지자 예레미야에게 임하니라 만군의 여호와께서 가라사대 보라 내가 엘람의 힘의 으뜸인 활을 꺾을 것이라고 선언하십니다.',
    chapter_themes = '["암몬의 갓 점령", "에돔의 지혜 없어짐", "다메섹의 피곤함", "게달을 멸하라", "엘람의 활을 꺾음", "사방으로 흩어짐"]',
    chapter_context = '이스라엘 주변의 여러 민족들이 바벨론에 의해 심판받을 것을 예언한 포괄적인 이방 민족 심판 예언입니다.',
    chapter_application = '모든 민족이 하나님의 주권 아래 있음을 인정하고, 하나님을 대적하는 자들은 반드시 심판받을 것을 믿으며, 하나님의 공의를 신뢰해야 합니다.'
WHERE book_id = 'jr' AND chapter = 49 AND verse = 1;

-- 예레미야 50장: 바벨론 심판 예언 1부
UPDATE bible_verses SET
    chapter_title = '바벨론과 갈대아인의 땅에 대한 심판 예언',
    chapter_summary = '여호와께서 선지자 예레미야로 바벨론과 갈대아인의 땅에 대하여 말씀하신 것이라 너희는 열방 중에 선포하며 공포하고 기호를 세우고 숨기지 말고 이르기를 바벨론이 함락되고 벨이 수치를 당하며 므로닥이 파괴되고 그 신상들이 수치를 당하며 우상들이 파괴된다 하라 북방에서 한 나라가 올라와서 그 땅을 황폐케 하여 그 중에 거하는 자가 없게 하리니 사람이나 짐승이 다 도망하여 가리라고 선언하십니다. 그 날 그 때에 이스라엘 자손이 돌아오며 유다 자손도 함께 돌아오되 그들이 울며 그 길을 행하며 그 하나님 여호와를 구할 것이며 그들이 그 얼굴을 시온으로 향하여 그 길을 물으며 말하기를 너희는 와서 잊어버리지 아니할 영영한 언약으로 여호와와 연합하자 하리라고 약속하십니다.',
    chapter_themes = '["바벨론이 함락됨", "벨과 므로닥의 수치", "북방에서 한 나라", "이스라엘 자손이 돌아옴", "울며 그 길을 행함", "영영한 언약으로 연합"]',
    chapter_context = '이스라엘을 괴롭힌 바벨론에 대한 하나님의 최종적 심판과 이스라엘의 완전한 회복을 예언한 중요한 내용입니다.',
    chapter_application = '하나님의 원수들은 반드시 심판받을 것을 믿고, 하나님의 백성의 최종적 회복을 소망하며, 하나님과의 영원한 언약을 신뢰해야 합니다.'
WHERE book_id = 'jr' AND chapter = 50 AND verse = 1;

-- 예레미야 51장: 바벨론 심판 예언 2부
UPDATE bible_verses SET
    chapter_title = '바벨론의 완전한 멸망과 여호와의 보복',
    chapter_summary = '여호와께서 가라사대 보라 내가 멸망시키는 자의 마음을 일으켜 바벨론과 갈대아에 거하는 자를 치리라 내가 타국인을 바벨론에 보내리니 그들이 그를 키질하여 그 땅을 공허하게 할 것이라 재앙의 날에 그들이 사방에서 그를 치리로다 하시며, 바벨론은 여호와의 손에 있는 금잔이라 온 세계로 취케 하되 열방이 그 포도주를 마시고 인하여 미쳤도다 바벨론이 갑자기 넘어져 파괴되니 그로 인하여 애곡하라 그 상처를 인하여 유향을 가져다가 혹시 나을까 하라 하시고, 여호와께서 우리 의를 드러내셨으니 오라 시온에서 우리 하나님 여호와의 일을 선포하자 하시며, 바벨론아 네가 많은 물 가에 거하여 재물이 많으나 네 탐람의 한계와 생명의 끝이 이르렀도다 만군의 여호와께서 자기를 가리켜 맹세하시되 내가 진실로 사람을 황충 같이 네게 채우리니 그들이 너를 향하여 승전가를 부르리라 하시니라고 선언하십니다.',
    chapter_themes = '["멸망시키는 자의 마음", "타국인을 보내어 키질", "여호와의 손에 있는 금잔", "갑자기 넘어져 파괴", "우리 의를 드러내심", "탐람의 한계"]',
    chapter_context = '바벨론이 하나님의 도구로 사용되었지만 결국 심판받을 것과 이스라엘의 의가 드러날 것을 예언한 완결편입니다.',
    chapter_application = '하나님의 공의로운 보복을 믿고, 악한 세력의 최종적 멸망을 확신하며, 하나님의 백성의 의가 드러날 것을 소망해야 합니다.'
WHERE book_id = 'jr' AND chapter = 51 AND verse = 1;

-- 예레미야 52장: 시드기야의 최후와 여호야긴의 대우 개선
UPDATE bible_verses SET
    chapter_title = '시드기야의 최후와 성전 파괴, 여호야긴의 대우 개선',
    chapter_summary = '시드기야가 위에 나아갈 때에 나이 이십일 세라 예루살렘에서 십일년을 치리하니라 그 모친의 이름은 하무달이라 립나 예레미야의 딸이더라 시드기야가 여호야김의 모든 행위를 본받아 여호와 보시기에 악을 행한지라 여호와께서 예루살렘과 유다를 진노하사 그들을 그 앞에서 쫓아내시니라 하며, 바벨론 왕의 시위대장 느부사라단이 여호와의 전과 왕궁을 사르고 예루살렘의 모든 집을 불살랐으며 예루살렘 사면 성벽을 헐었고 성중에 남아 있는 백성과 바벨론 왕에게 항복한 자와 무리의 남은 자를 시위대장 느부사라단이 사로잡아 갔으나 빈민을 그 땅에 남겨두어 포도원을 다스리는 자와 농부가 되게 하였다고 기록합니다. 또한 바벨론 왕 에윌므로닥이 즉위한 원년 십이월 이십오일에 유다 왕 여호야긴을 옥에서 내어놓아 그에게 선히 말하고 그 위를 바벨론에 저와 함께 있는 왕들의 위보다 높게 하고 그 죄수의 의복을 바꾸게 하고 그 일평생에 항상 왕의 앞에서 먹게 하였으며 그 쓸 것은 날마다 바벨론 왕에게서 받는 정수를 따라 종신토록 끊이지 아니하였다고 기록합니다.',
    chapter_themes = '["시드기야의 악행", "성전과 왕궁을 사름", "예루살렘 성벽을 헐음", "빈민을 남겨둠", "여호야긴을 옥에서 내어놓음", "위를 높게 하고 종신토록 대우"]',
    chapter_context = '예레미야서의 마지막 장으로 예루살렘의 완전한 파괴와 바벨론 포로, 그리고 미래 회복의 희망을 암시하는 여호야긴의 대우 개선을 기록한 역사적 결말입니다.',
    chapter_application = '하나님의 심판은 정확히 이루어짐을 확신하고, 절망적 상황에서도 하나님의 긍휼하심을 기대하며, 작은 회복의 징조라도 소망을 잃지 말아야 합니다.'
WHERE book_id = 'jr' AND chapter = 52 AND verse = 1;