-- 욥기 주석 Part 1 (1-21장)
-- 고난의 의미와 하나님의 주권

-- 욥 1장: 의인 욥의 시험과 사탄의 참소
UPDATE bible_verses SET
    chapter_title = '의인 욥에 대한 사탄의 참소와 첫 번째 시험',
    chapter_summary = '우스 땅에 욥이라는 온전하고 정직하여 하나님을 경외하며 악에서 떠난 사람이 있었고, 그에게 아들 일곱과 딸 셋이 있었으며 양 칠천 마리, 낙타 삼천 마리, 소 오백 겨리, 암나귀 오백 마리와 많은 종이 있어 동방 사람 중에 가장 큰 자였습니다. 하나님의 아들들이 여호와 앞에 서는 날에 사탄도 그들 가운데 와서 욥을 참소하며 욥이 까닭 없이 하나님을 경외하겠느냐 하고 시험을 요구합니다. 여호와께서 사탄에게 욥의 모든 소유물을 손에 붙이시되 욥의 몸에는 손을 대지 말라 하시니, 스바 사람들이 소와 나귀를 빼앗고, 하늘에서 불이 내려 양과 종들을 태우고, 갈대아 사람들이 낙타를 빼앗으며, 큰 바람이 집을 무너뜨려 자녀들이 다 죽었으나 욥이 일어나 겉옷을 찢고 머리털을 밀고 땅에 엎드려 경배하며 여호와께서 주셨고 여호와께서 가져가셨으니 여호와의 이름이 찬송을 받으실지니이다 하고 범죄하지 아니하였습니다.',
    chapter_themes = '["온전하고 정직한 욥", "동방의 가장 큰 자", "사탄의 참소", "까닭 없이 경외하겠느냐", "모든 소유의 상실", "여호와께서 주셨고 가져가셨다"]',
    chapter_context = '의인의 고난 문제를 다루는 욥기의 서론으로, 순전한 신앙을 가진 욥이 사탄의 시험을 받게 되는 과정과 첫 번째 시험에서 보인 욥의 신앙을 보여줍니다.',
    chapter_application = '고난이 반드시 죄의 결과가 아님을 깨닫고, 모든 것이 하나님께로부터 왔음을 인정하며, 어떤 상황에서도 하나님을 찬송하는 신앙을 가져야 합니다.'
WHERE book_id = 'job' AND chapter = 1 AND verse = 1;

-- 욥 2장: 두 번째 시험과 욥의 아내, 세 친구의 등장
UPDATE bible_verses SET
    chapter_title = '욥의 몸에 대한 시험과 세 친구의 위로',
    chapter_summary = '다시 하나님의 아들들이 여호와 앞에 서는 날에 사탄이 와서 욥을 참소하되 가죽을 위하여 가죽을 내어주나니 사람이 그의 모든 소유물을 바꾸어서라도 생명을 보전하려 하므로 욥의 뼈와 살을 치소서 그리하면 틀림없이 면전에서 주를 욕할 것이니이다 하니 여호와께서 사탄에게 욥을 네 손에 맡기되 다만 그의 생명은 해하지 말라 하십니다. 사탄이 여호와 앞에서 물러가서 욥을 쳐서 발바닥에서 정수리까지 악한 종기가 나게 하니 욥이 재 가운데 앉아서 기와 조각으로 몸을 긁습니다. 그의 아내가 욥에게 이르되 당신이 아직도 순전함을 지키느뇨 하나님을 욕하고 죽으라 하되 욥이 이르되 그대의 말이 우매한 여자 중 하나의 말 같도다 우리가 하나님께 복을 받았은즉 재앙도 받지 아니하겠느뇨 하고 범죄하지 아니하였습니다. 욥의 친구 세 사람 데만 사람 엘리바스와 수아 사람 빌닷과 나아마 사람 소발이 들어와 칠 일 밤낮을 욥과 함께 땅에 앉아 있으되 욥의 고통이 심함을 보고 한 마디도 하지 아니하였습니다.',
    chapter_themes = '["가죽을 위하여 가죽을", "발바닥에서 정수리까지 종기", "하나님을 욕하고 죽으라", "복도 받고 재앙도 받는다", "세 친구의 침묵", "칠 일 밤낮"]',
    chapter_context = '사탄의 두 번째 시험으로 욥의 몸에 악한 종기가 나게 되고, 아내마저 욥을 떠나라 하지만 욥이 여전히 신앙을 지키며, 세 친구가 위로하러 오는 과정을 그립니다.',
    chapter_application = '육체적 고통 중에도 하나님을 원망하지 말고, 가장 가까운 사람의 유혹에도 굴복하지 말며, 고난당하는 자와 함께 슬퍼하는 진정한 위로를 보여야 합니다.'
WHERE book_id = 'job' AND chapter = 2 AND verse = 1;

-- 욥 3장: 욥의 첫 번째 탄식
UPDATE bible_verses SET
    chapter_title = '욥이 자신의 생일을 저주하며 드리는 탄식',
    chapter_summary = '그 후에 욥이 입을 열어 자기의 생일을 저주하여 이르되 내가 난 날이 멸망하였었더라면, 남자아이를 배었다 하던 그 밤도 그러하였었더라면 하며 자신의 출생을 한탄합니다. 어찌하여 내가 태에서 죽어 나오지 아니하였던가 어찌하여 내 어머니가 나를 낳을 때에 내가 즉시 기절하지 아니하였던가 하며, 지금쯤 내가 평안히 누워서 자고 있었을 것이요 그러면 나는 쉬었을 것이라 하며 죽음을 그리워합니다. 거기서는 왕들과 땅의 모사들이 자기를 위하여 황폐한 곳에 건축한 자들과 함께 쉬며, 거기서는 악한 자가 소요를 그치며 곤비한 자가 쉼을 얻는다고 말합니다.',
    chapter_themes = '["생일을 저주함", "태에서 죽지 못한 한탄", "평안히 누워 쉼", "왕들과 모사들과 함께", "악한 자도 소요를 그침", "곤비한 자의 쉼"]',
    chapter_context = '극심한 고난 속에서 욥이 처음으로 입을 열어 자신의 출생을 저주하며 죽음을 그리워하는 깊은 절망과 탄식을 드러내는 장면입니다.',
    chapter_application = '극한 고난 중에 절망하는 마음을 이해하되, 생명을 주신 하나님의 주권을 인정하고, 고난 중에도 소망을 잃지 말며 하나님께 기도해야 합니다.'
WHERE book_id = 'job' AND chapter = 3 AND verse = 1;

-- 욥 4장: 엘리바스의 첫 번째 변론
UPDATE bible_verses SET
    chapter_title = '엘리바스의 첫 번째 위로와 응보 사상',
    chapter_summary = '데만 사람 엘리바스가 대답하여 이르되 누가 네게 말하면 네가 답답하겠느냐 그러나 누가 참고 말하지 아니하겠느냐 하며 욥에게 말하기 시작합니다. 전에 네가 많은 사람을 가르쳤고 손이 늘어진 자들을 강하게 하였으며 넘어지는 자를 말로 붙들어 주었고 무릎이 약한 자를 굳게 하였거늘 이제 이런 일이 네게 닥치매 네가 답답하여 하고 이것이 네게 당하매 네가 놀라는구나 하며 욥의 과거 선행을 상기시킵니다. 그러나 네 경외함이 네 의뢰가 아니냐 네 행위의 온전함이 네 소망이 아니냐 하며, 죄 없이 망한 자가 누구인가 정직한 자의 끊어짐이 어디 있는가 하며 응보 사상에 따라 고난의 원인을 죄에서 찾습니다. 또한 한 영이 내 앞으로 지나가매 내 몸의 털이 주뼛하였었느니라 하며 자신이 받은 계시를 통해 인생이 하나님보다 의로울 수 있겠느냐 하나님이 그 종들을 믿지 아니하시며 그 사자들에게 허물이 있다 하시거든 하물며 흙 집에 거하는 자들이랴 하며 인간의 유한성을 강조합니다.',
    chapter_themes = '["많은 사람을 가르쳤던 욥", "죄 없이 망한 자가 누구인가", "정직한 자의 끊어짐", "한 영이 지나감", "하나님보다 의로울 수 있겠느냐", "흙 집에 거하는 자들"]',
    chapter_context = '욥의 첫 번째 친구 엘리바스가 전통적인 응보 사상에 기초하여 욥의 고난이 죄의 결과일 것이라고 추론하며 위로하려는 첫 번째 발언입니다.',
    chapter_application = '성급한 판단으로 고난당하는 자를 정죄하지 말고, 인간의 한계를 인정하되, 하나님의 깊은 뜻을 겸손히 받아들이며 진정한 위로를 베풀어야 합니다.'
WHERE book_id = 'job' AND chapter = 4 AND verse = 1;

-- 욥 5장: 엘리바스의 계속된 권면
UPDATE bible_verses SET
    chapter_title = '엘리바스의 하나님께 구하라는 권면',
    chapter_summary = '부르짖을지라도 네게 응답할 자가 있겠느냐 거룩한 자들 중 네가 누구에게로 향하겠느냐 하며 욥이 다른 곳에서 도움을 구하는 것의 헛됨을 지적합니다. 분노가 미련한 자를 죽이고 시기가 어리석은 자를 멸하나니 하며 욥의 감정을 경계시키고, 나는 미련한 자가 뿌리를 박는 것을 보고 그 집을 당장에 저주하였노라 하며 악인의 일시적 번영을 말합니다. 사람은 고생을 위하여 났나니 불꽃이 위로 치솟는 것 같으니라 하며 고난이 인생의 본래적 조건임을 주장합니다. 그러나 나라면 하나님께 구하겠고 내 일을 하나님께 의탁하겠노라 하며 욥에게 하나님께 돌아갈 것을 권면하고, 하나님은 큰 일을 행하사 측량할 수 없으며 기이한 일을 행하사 셀 수 없으시니라 하며 하나님의 전능하심을 찬양합니다. 하나님이 징계하시는 자는 복이 있나니 그런즉 전능자의 경책을 경히 여기지 말지니라 하며 고난을 하나님의 징계로 해석하고 그것을 받아들일 것을 권합니다.',
    chapter_themes = '["응답할 자가 있겠느냐", "미련한 자의 뿌리", "고생을 위하여 났다", "하나님께 구하겠다", "큰 일을 행하시는 하나님", "징계하시는 자는 복이 있다"]',
    chapter_context = '엘리바스가 욥에게 하나님께 돌아가서 회개하고 겸손히 하나님의 징계를 받아들이면 회복될 것이라고 권면하는 내용입니다.',
    chapter_application = '고난을 하나님의 교육적 징계로 받아들이고, 다른 곳이 아닌 하나님께 도움을 구하며, 하나님의 크신 능력과 지혜를 신뢰해야 합니다.'
WHERE book_id = 'job' AND chapter = 5 AND verse = 1;

-- 욥 6장: 욥의 두 번째 변론 - 친구들의 몰이해
UPDATE bible_verses SET
    chapter_title = '욥의 고통과 친구들의 몰이해에 대한 탄식',
    chapter_summary = '욥이 대답하여 이르되 나의 분한 것을 달아 보며 나의 모든 재앙을 저울에 둘 수 있으면 바다 모래보다도 무거울 것이라 그러므로 내 말이 조급하였구나 하며 자신의 극심한 고통을 호소합니다. 전능자의 화살이 내게 박히매 나의 영이 그 독을 마셨나니 하나님의 두려움이 나를 엄습하느니라 하며 고난이 하나님으로부터 온 것임을 인정하면서도 그 이유를 알지 못하는 괴로움을 표현합니다. 들나귀가 풀이 있으면 어찌 울겠으며 소가 꼴이 있으면 어찌 울겠느냐 싱거운 것을 소금 없이 먹을 수 있으랴 닭의 알 흰자위에 무슨 맛이 있으랴 하며 자신의 불평이 당연함을 변호합니다. 나의 형제들은 시내같이 변하기 쉽고 개울의 잔류 같으니 얼음이 녹으면 물이 사라지는 것 같이 친구들이 자신을 저버렸다고 탄식하며, 이제 너희도 아무것도 아니구나 너희가 재앙을 보고는 두려워하는도다 하며 친구들의 무력함을 지적합니다.',
    chapter_themes = '["분한 것이 바다 모래보다 무겁다", "전능자의 화살", "들나귀와 소의 비유", "형제들은 시내같이 변함", "얼음이 녹으면 사라지는 물", "재앙을 보고 두려워함"]',
    chapter_context = '욥이 친구들의 피상적인 위로에 실망하며 자신의 극심한 고통과 친구들의 몰이해에 대해 탄식하는 내용입니다.',
    chapter_application = '고난당하는 자의 마음을 이해하려 노력하고, 쉽게 변하는 인간관계보다 변함없으신 하나님을 의지하며, 진정한 친구가 되어 끝까지 함께해야 합니다.'
WHERE book_id = 'job' AND chapter = 6 AND verse = 1;

-- 욥 7장: 인생의 허무와 하나님께 대한 간구
UPDATE bible_verses SET
    chapter_title = '인생의 허무함과 하나님께 드리는 간절한 기도',
    chapter_summary = '세상에 있는 인생에게 전쟁 같은 때가 있지 아니하냐 그의 날이 품꾼의 날과 같지 아니하냐 하며 인생의 고달픔을 호소합니다. 종은 저녁 그늘을 간절히 기다리고 품꾼은 그의 삯을 바라나니 이와 같이 내가 여러 달째 헛된 달을 세게 되며 수고로운 밤이 내게 주어졌구나 하며 자신의 고통스러운 나날들을 묘사합니다. 내가 누우면 언제나 일어날까 언제나 밤이 갈까 하며 새벽까지 뒤척이는구나 하며 잠 못 이루는 밤의 고통을 토로하고, 내 살에는 구더기와 흙덩이가 가득하고 내 가죽은 터져서 고름이 흐르는구나 하며 육체적 고통을 생생히 표현합니다. 나의 날은 베틀의 북보다 빠르니 소망 없이 보내는구나 하며 인생의 덧없음을 탄식하고, 내가 다시는 집으로 돌아가지 못할 것이라 내 곳이 다시는 나를 보지 못하리라 하며 죽음의 확실성을 말합니다. 그런즉 내가 내 입을 금하지 아니하고 내 영혼의 고민 중에서 말하며 내 마음의 괴로움 중에서 원망하리이다 하며 하나님께 자신의 심정을 토로하겠다고 다짐합니다.',
    chapter_themes = '["인생에게 전쟁 같은 때", "품꾼의 삯을 바람", "구더기와 흙덩이", "베틀의 북보다 빠른 날", "집으로 돌아가지 못함", "영혼의 고민 중에서 말함"]',
    chapter_context = '욥이 인생의 허무함과 자신의 극한 고통을 토로하며 하나님께 솔직한 마음을 고백하는 깊은 신앙적 탄식입니다.',
    chapter_application = '인생의 허무함을 인정하되 절망하지 말고, 하나님께 솔직하게 마음을 털어놓으며, 고난 중에도 기도하는 관계를 유지해야 합니다.'
WHERE book_id = 'job' AND chapter = 7 AND verse = 1;

-- 욥 8장: 빌닷의 첫 번째 변론
UPDATE bible_verses SET
    chapter_title = '빌닷의 조상의 지혜와 응보 사상',
    chapter_summary = '수아 사람 빌닷이 대답하여 이르되 네가 어느 때까지 이런 말을 하겠으며 네 입의 말이 언제까지 광풍과 같겠느냐 하며 욥의 말을 비판합니다. 하나님이 어찌 심판을 굽게 하시겠으며 전능자가 어찌 공의를 굽게 하시겠느냐 네 자녀들이 그에게 범죄하였으므로 그들을 그들의 과실에 버려두셨구나 하며 욥의 자녀들의 죽음을 그들의 죄 때문이라고 단정합니다. 만일 네가 하나님을 부지런히 구하며 전능자에게 간구하고 또 청결하고 정직하면 반드시 너를 돌보사 네 의로운 처소를 평안하게 하실 것이라 하며 회개하면 회복될 것이라고 권면합니다. 이전 세대에게 물어보라 그들의 조상들이 터득한 일을 준비하라 하며 조상들의 지혜에 의지할 것을 권하고, 갈대가 진펄이 아닌 데서 크겠으며 물 없는 곳에서 자라겠느냐 하며 하나님을 잊는 자는 갈대와 같이 말라버릴 것이라고 경고합니다.',
    chapter_themes = '["심판과 공의를 굽게 하지 않음", "자녀들의 과실", "청결하고 정직하면 돌보심", "이전 세대에게 물어보라", "갈대가 진펄 없이 크겠느냐", "하나님을 잊는 자"]',
    chapter_context = '빌닷이 전통적인 지혜와 조상들의 경험에 기초하여 욥에게 회개할 것을 권면하며 하나님의 공의로운 응보를 강조하는 발언입니다.',
    chapter_application = '전통적 지혜를 존중하되 맹신하지 말고, 하나님의 공의를 믿되 그분의 자비도 함께 기억하며, 성급한 판단보다 깊은 이해를 추구해야 합니다.'
WHERE book_id = 'job' AND chapter = 8 AND verse = 1;

-- 욥 9장: 욥의 세 번째 변론 - 하나님과 논쟁할 수 없음
UPDATE bible_verses SET
    chapter_title = '하나님과 논쟁할 수 없는 인간의 한계',
    chapter_summary = '욥이 대답하여 이르되 진실로 내가 이 일이 그런 줄을 알거니와 인생이 어찌 하나님 앞에 의롭다 하겠느냐 하며 하나님의 절대적 의로우심 앞에서 인간의 한계를 인정합니다. 사람이 하나님과 쟁변하려 할지라도 천 마디 중에서 한 마디도 대답하지 못하리라 하며 하나님과의 논쟁이 불가능함을 고백하고, 그는 마음이 지혜로우시고 힘이 강하시니 누가 그를 거슬러 완강하게 굴고서 평안하였으랴 하며 하나님의 전능하심을 인정합니다. 그가 산을 무너뜨리시되 진노하심으로 뒤엎으시며 땅을 그 자리에서 움직이시니 그 기둥들이 흔들리며 해에게 명령하여 뜨지 못하게 하시며 별들을 봉하시며 하늘을 홀로 펴시며 바다 물결을 밟으시며 북두성과 오리온과 묘성과 남방의 밀실을 만드시며 큰 일을 행하사 측량할 수 없으며 기이한 일을 행하사 셀 수 없으시니라 하며 하나님의 창조 사역을 찬양합니다. 그런데 하나님이 내 앞으로 지나가시나 내가 보지 못하며 그가 내 앞에서 나아가시나 내가 깨닫지 못하겠고 하나님이 빼앗으시면 누가 그를 막으며 네가 무엇을 하느냐 누가 그에게 말하겠느냐 하며 하나님의 절대 주권을 고백합니다.',
    chapter_themes = '["인생이 하나님 앞에 의롭다 하겠느냐", "천 마디 중 한 마디도 대답 못함", "산을 무너뜨리시는 분", "북두성과 오리온과 묘성", "지나가시나 보지 못함", "빼앗으시면 누가 막으랴"]',
    chapter_context = '욥이 하나님의 절대적 주권과 전능하심을 인정하면서도 자신과 하나님 사이의 거리감을 느끼며 고민하는 깊은 신학적 고백입니다.',
    chapter_application = '하나님의 절대 주권을 겸손히 인정하고, 인간의 한계를 받아들이되, 하나님과의 관계에서 소외감을 느낄 때에도 신뢰를 잃지 말아야 합니다.'
WHERE book_id = 'job' AND chapter = 9 AND verse = 1;

-- 욥 10장: 생명을 주신 하나님께 대한 간구
UPDATE bible_verses SET
    chapter_title = '생명을 주신 하나님께 드리는 간절한 호소',
    chapter_summary = '내 영혼이 살기를 싫어하니 내가 내 원통함을 토로하고 내 마음의 괴로움으로 말하리라 하며 내가 하나님께 아뢰기를 나를 죄인으로 여기지 마시옵고 무슨 까닭으로 나와 더불어 쟁변하시는지 내게 알게 하옵소서 하며 하나님께 직접 호소합니다. 주께서 나를 억압하시며 주의 손으로 지으신 것을 멸시하시고 악인의 꾀에 빛을 비추시는 것이 선하시니이까 하며 하나님의 행하심에 대해 의문을 표합니다. 주의 눈이 육신의 눈이니이까 주께서 사람이 보는 것처럼 보시니이까 주의 날이 인생의 날과 같으니이까 주의 해가 사람의 해와 같으니이까 하며 하나님의 초월성을 확인하면서도 자신을 이해해 주시기를 간구합니다. 주의 손으로 나를 빚으사 만드셨는데 이제 나를 멸하시나이다 하며 생명을 주신 하나님께 호소하고, 주께서 나를 젖과 같이 쏟으셨으며 엄긱 같이 엉기게 하셨으며 가죽과 살로 내게 입히시며 뼈와 힘줄로 나를 엮으셨으며 생명과 은혜를 내게 주시고 권고하심으로 내 영을 지키셨나이다 하며 하나님의 창조 은혜를 고백합니다.',
    chapter_themes = '["영혼이 살기를 싫어함", "무슨 까닭으로 쟁변하시는지", "손으로 지으신 것을 멸시하심", "젖과 같이 쏟으심", "엄긱 같이 엉기게 하심", "생명과 은혜를 주심"]',
    chapter_context = '욥이 하나님께 자신의 억울함을 토로하면서도 하나님의 창조 은혜를 기억하며 간절히 호소하는 기도적 고백입니다.',
    chapter_application = '하나님께 솔직하게 마음을 털어놓되 원망보다는 간구로, 하나님의 창조와 양육의 은혜를 기억하며, 어려운 중에도 하나님과의 관계를 유지해야 합니다.'
WHERE book_id = 'job' AND chapter = 10 AND verse = 1;

-- 욥 11장: 소발의 첫 번째 변론
UPDATE bible_verses SET
    chapter_title = '소발의 욥에 대한 강한 비판과 회개 촉구',
    chapter_summary = '나아마 사람 소발이 대답하여 이르되 말이 많으니 어찌 대답이 없으랴 입이 부푼 사람이 어찌 의롭다 함을 얻겠느냐 하며 욥의 많은 말을 비판합니다. 네 말이 사람들을 잠잠하게 하겠으며 네가 비웃으되 너를 부끄럽게 할 자가 없겠느냐 하며 욥이 교만하다고 지적하고, 네 말에 의하면 내 도가 깨끗하고 내가 주의 목전에 깨끗하다 하는구나 하며 욥이 자신을 의롭다고 주장한다고 비판합니다. 하나님은 마음의 간사함을 아시나니 하나님이 말씀하시기를 원하며 네게 입을 여시기를 원하노라 하며 하나님께서 직접 욥에게 말씀하시기를 바란다고 하고, 지혜의 오묘함을 네게 보이시기를 원하노니 이는 그의 지혜가 광대하심이라 하나님께서 네 죄악보다 경하게 벌하심을 알라 하며 욥이 받는 고난이 죄에 비해 가벼운 형벌이라고 주장합니다. 네가 하나님의 오묘함을 어찌 능히 측량하며 전능자의 한계를 어찌 능히 알겠느냐 하늘보다 높으시니 네가 무엇을 하겠으며 스올보다 깊으시니 네가 어찌 알겠느냐 하며 하나님의 무한하심을 강조합니다.',
    chapter_themes = '["말이 많으니 대답이 없으랴", "내 도가 깨끗하다", "마음의 간사함을 아심", "죄악보다 경하게 벌하심", "하나님의 오묘함을 측량할 수 없음", "하늘보다 높고 스올보다 깊음"]',
    chapter_context = '소발이 세 친구 중 가장 강하게 욥을 비판하며 욥의 교만을 지적하고 하나님의 무한한 지혜 앞에서 겸손할 것을 촉구하는 발언입니다.',
    chapter_application = '다른 사람을 성급히 판단하지 말고, 하나님의 무한하신 지혜 앞에서 겸손하며, 고난당하는 자에게 정죄보다는 위로를 베풀어야 합니다.'
WHERE book_id = 'job' AND chapter = 11 AND verse = 1;

-- 욥 12장: 욥의 네 번째 변론 - 친구들에 대한 반박
UPDATE bible_verses SET
    chapter_title = '욥의 친구들에 대한 반박과 하나님의 주권 고백',
    chapter_summary = '욥이 대답하여 이르되 너희만 사람이로구나 너희가 죽으면 지혜도 죽겠구나 하며 친구들의 교만을 비꼽니다. 나도 너희 같이 총명이 있어 너희만 못하지 아니하니 이 같은 일을 누가 알지 못하겠느냐 하며 자신도 지혜가 있음을 주장하고, 내가 하나님께 부르짖어서 들으심을 입은 자라도 이웃에게는 웃음거리가 되었으니 의롭고 온전한 자가 웃음거리가 되었구나 하며 자신의 처지를 한탄합니다. 평안한 자의 마음은 재앙을 멸시하나니 실족하는 자에게 닥치리라 하며 고난을 당하지 않은 자들의 무관심을 지적하고, 강도의 장막은 평안하고 하나님을 진노하게 하는 자가 평안하니 하나님이 그의 손에 후히 주심이니라 하며 현실에서 악인이 잘되는 것의 모순을 지적합니다. 이제 뭍의 짐승들에게 물어 보라 그들이 네게 가르치리라 하며 자연만물도 하나님의 주권을 안다고 말하고, 이 모든 것을 여호와의 손이 행하셨나니 그의 손에 모든 생물의 생명과 모든 인류의 영혼이 있느니라 하며 하나님의 절대 주권을 고백합니다.',
    chapter_themes = '["너희만 사람이로구나", "웃음거리가 된 의인", "평안한 자는 재앙을 멸시", "강도의 장막이 평안", "뭍의 짐승들에게 물어보라", "모든 생명이 그 손에"]',
    chapter_context = '욥이 친구들의 일방적인 비판에 반박하면서 현실의 모순을 지적하고 하나님의 절대 주권을 고백하는 변론입니다.',
    chapter_application = '교만하지 말고 겸손하며, 고난당하는 자를 함부로 판단하지 말고, 현실의 모순 속에서도 하나님의 주권을 신뢰해야 합니다.'
WHERE book_id = 'job' AND chapter = 12 AND verse = 1;

-- 욥 13장: 하나님과 직접 변론하고자 하는 욥의 소원
UPDATE bible_verses SET
    chapter_title = '친구들을 떠나 하나님과 직접 변론하려는 욥의 결심',
    chapter_summary = '보라 이것을 내 눈이 다 보았고 내 귀가 듣고 깨달았나니 너희가 아는 것을 나도 아노라 내가 너희만 못하지 아니하니라 하며 친구들과 자신의 지혜가 비슷함을 주장합니다. 그러나 나는 전능자에게 말하려 하며 하나님과 변론하기를 원하노라 하며 친구들보다는 하나님과 직접 대화하고 싶어하고, 너희는 거짓말을 지어내는 자요 다 쓸데없는 의사니라 하며 친구들의 무능함을 지적합니다. 너희가 잠잠하기를 원하노라 그것이 너희의 지혜일 것이니라 하며 침묵할 것을 권하고, 내 변론을 들으며 내 입술의 간구를 귀담아 들으라 하며 자신의 말을 들어달라고 요청합니다. 보라 하나님이 나를 죽이시리니 내가 소망이 없노라 그러나 내가 내 길을 그 앞에서 변백하리라 하며 죽음을 각오하고라도 하나님 앞에서 자신의 무죄를 주장하겠다는 의지를 표명하고, 이것이 오히려 나의 구원이 되리니 경건하지 않은 자는 그 앞에 오지 못함이니라 하며 자신의 결백함에 대한 확신을 드러냅니다.',
    chapter_themes = '["전능자에게 말하려 함", "하나님과 변론하기를 원함", "거짓말을 지어내는 자들", "잠잠하기를 원하노라", "하나님이 나를 죽이시리니", "내 길을 변백하리라"]',
    chapter_context = '욥이 친구들의 잘못된 위로에 실망하여 그들을 떠나 하나님과 직접 변론하겠다는 강한 의지를 표명하는 중요한 전환점입니다.',
    chapter_application = '인간의 위로보다 하나님과의 직접적 만남을 추구하고, 죽음을 각오하고라도 진실을 추구하며, 하나님 앞에서 정직한 삶을 살아야 합니다.'
WHERE book_id = 'job' AND chapter = 13 AND verse = 1;

-- 욥 14장: 인생의 허무와 죽음에 대한 탄식
UPDATE bible_verses SET
    chapter_title = '여인에게서 난 사람의 허무함과 죽음 이후의 소망',
    chapter_summary = '여인에게서 난 사람은 생애가 짧고 괴로움이 가득하며 꽃과 같이 나고 쇠하며 그림자 같이 지나가며 머물지 아니하나니 하며 인생의 덧없음을 탄식합니다. 이러한 자에게 주께서 눈을 뜨시나이까 나를 주와 함께 심판에 들어가게 하시나이까 하며 하나님께서 연약한 인간을 심판대에 세우시는 것에 대해 의문을 표하고, 누가 깨끗한 것을 더러운 것 가운데서 낼 수 있으리이까 하나도 없나이다 하며 인간의 근본적 죄성을 인정합니다. 그의 날을 정하셨고 그의 달 수를 계수하셨고 그의 한계를 정하여 넘어가지 못하게 하셨은즉 하며 하나님께서 인간의 수명을 정하셨음을 고백하고, 그에게서 눈을 돌이키사 그가 품꾼같이 자기의 날을 마칠 때까지 그를 홀로 두소서 하며 하나님께서 자신을 내버려 두시기를 간구합니다. 나무는 소망이 있나니 찍힐지라도 다시 움이 나며 연한 가지가 자라나서 그 뿌리가 땅에서 늙고 줄기가 흙에서 죽을지라도 물 냄새를 맡으면 움이 나서 새 나무 같이 가지가 나느니라 하며 나무의 재생을 부러워하면서, 그러나 사람은 죽으면 사라져서 기운이 끊어지면 어디 있느뇨 하며 인간의 죽음의 확실성을 탄식합니다.',
    chapter_themes = '["여인에게서 난 사람", "꽃과 그림자 같은 생애", "깨끗한 것을 더러운 중에서", "날과 달 수를 정하심", "나무는 소망이 있으나", "사람은 죽으면 사라짐"]',
    chapter_context = '욥이 인간 존재의 유한성과 허무함을 깊이 묵상하며 죽음의 문제와 그 이후에 대한 근본적 질문을 던지는 실존적 고백입니다.',
    chapter_application = '인생의 유한함을 인정하되 절망하지 말고, 수명을 정하신 하나님을 신뢰하며, 죽음 너머의 영원한 소망을 품고 살아야 합니다.'
WHERE book_id = 'job' AND chapter = 14 AND verse = 1;

-- 욥 15장: 엘리바스의 두 번째 변론
UPDATE bible_verses SET
    chapter_title = '엘리바스의 욥에 대한 더 강한 비판과 악인의 운명',
    chapter_summary = '데만 사람 엘리바스가 대답하여 이르되 지혜로운 자가 어찌 헛된 지식으로 대답하겠으며 동풍으로 그 품에 채우겠느냐 하며 욥의 말을 헛된 것이라고 비판합니다. 어찌 무익한 말과 유용하지 아니한 언사로 변론하겠느냐 참으로 네가 하나님 경외하는 일을 그치게 하며 하나님 앞에서 묵도하는 일을 그만두게 하는구나 하며 욥이 경건을 해친다고 비난하고, 네 죄악이 네 입을 가르치나니 네가 간사한 자의 혀를 택하였구나 하며 욥의 말이 죄악에서 나온다고 주장합니다. 네 입이 너를 정죄하나니 내가 아니라 네 입술이 네게 불리하게 증언하느니라 하며 욥 자신의 말이 자신을 정죄한다고 말하고, 네가 맨 처음 난 사람이냐 산들이 있기 전에 네가 출생하였느냐 하나님의 모략을 네가 들었느냐 지혜를 홀로 가졌느냐 하며 욥의 교만을 지적합니다. 우리가 아는 것을 네가 아는 것이 무엇이냐 우리가 깨달은 것을 네가 깨달은 것이 무엇이냐 우리 중에는 머리가 센 자와 나이가 많은 자가 있어 네 아버지보다 나이 위이니라 하며 연장자의 지혜를 내세우고, 악인은 그의 모든 날에 고통을 당하며 강포자는 그에게 작정된 햇수를 지내느니라 하며 악인의 필연적 고난을 설명합니다.',
    chapter_themes = '["헛된 지식으로 대답", "하나님 경외를 그치게 함", "간사한 자의 혀", "맨 처음 난 사람이냐", "머리가 센 자와 나이 많은 자", "악인은 모든 날에 고통"]',
    chapter_context = '엘리바스가 첫 번째 변론보다 더 강하게 욥을 비판하며 전통적 지혜와 악인 응보론을 강조하는 두 번째 발언입니다.',
    chapter_application = '연장자의 지혜를 존중하되 맹신하지 말고, 다른 사람을 함부로 정죄하지 말며, 겸손한 마음으로 하나님의 뜻을 구해야 합니다.'
WHERE book_id = 'job' AND chapter = 15 AND verse = 1;

-- 욥 16장: 욥의 다섯 번째 변론 - 무정한 위로자들
UPDATE bible_verses SET
    chapter_title = '무정한 위로자들에 대한 욥의 탄식과 하나님께 대한 호소',
    chapter_summary = '욥이 대답하여 이르되 이런 말을 내가 많이 들었나니 너희는 다 번거로운 위로자로구나 하며 친구들의 반복적인 비난에 지쳤음을 표현합니다. 헛된 말이 어찌 끝이 있으랴 네가 무엇에 격동되어 이같이 대답하느냐 하며 친구들의 말이 도움이 되지 않는다고 지적하고, 나도 너희 같이 말할 수 있나니 가령 너희 마음이 내 마음 자리에 있다면 나도 말을 모아 너희를 치며 머리를 흔들 수 있느니라 하며 입장을 바꿔 생각해 보라고 합니다. 그러나 내가 입으로 위로하며 입술로 말하여 너희 근심을 풀었으리라 하며 자신이라면 진정한 위로를 베풀었을 것이라고 말합니다. 내가 말하여도 내 근심이 풀리지 아니하나니 잠잠한들 어찌 평안하랴 하며 말해도 소용없고 잠잠해도 소용없는 절망감을 토로하고, 하나님이 나를 곤고하게 하시고 나의 무리를 다 없애셨으며 나를 시들게 하셨으니 이는 나를 향하여 증거하는 것이라 하며 하나님께서 자신을 괴롭히신다고 호소합니다. 그가 진노하사 나를 찢으시며 나를 미워하시고 나를 향하여 이를 갈으시며 내 대적이 나를 향하여 그의 눈을 번뜩이는구나 하며 하나님을 원수처럼 느끼는 고통을 표현합니다.',
    chapter_themes = '["번거로운 위로자들", "헛된 말이 어찌 끝이 있으랴", "입으로 위로하며", "말하여도 근심이 풀리지 않음", "나의 무리를 다 없애심", "진노하사 나를 찢으심"]',
    chapter_context = '욥이 친구들의 무정한 위로에 실망하며 하나님께서 자신을 대적하시는 것 같은 고통스러운 심정을 토로하는 절규입니다.',
    chapter_application = '고난당하는 자에게 진정한 위로를 베풀고, 하나님께서 원수처럼 느껴질 때에도 신뢰를 잃지 말며, 솔직한 기도로 하나님께 나아가야 합니다.'
WHERE book_id = 'job' AND chapter = 16 AND verse = 1;

-- 욥 17장: 무덤을 향해 가는 욥의 절망
UPDATE bible_verses SET
    chapter_title = '무덤을 향해 가는 욥의 깊은 절망과 친구들에 대한 실망',
    chapter_summary = '내 호흡이 끊어지며 내 날이 다하며 무덤이 나를 위하여 예비되었구나 하며 죽음이 가까웠음을 느끼는 절망감을 표현합니다. 나를 조롱하는 자들이 참으로 나와 함께 있어 내 눈이 항상 그들의 거슬러 하는 것을 보는구나 하며 친구들의 조롱을 당하는 고통을 토로하고, 청하노니 보증물을 주시고 나를 위하여 보증하소서 주 외에는 누가 나와 손을 잡겠나이까 하며 하나님께서만이 자신의 보증인이 될 수 있다고 간구합니다. 주께서 그들의 마음을 깨닫지 못하게 하셨으니 그러므로 그들을 높이지 아니하시리이다 하며 친구들이 진리를 깨닫지 못한다고 말하고, 이익을 위하여 친구를 고발하는 자의 자식들의 눈이 멀리라 하며 친구들의 잘못된 동기를 지적합니다. 하나님이 나를 백성의 이야기거리가 되게 하시니 그들이 내 얼굴에 침을 뱉는구나 하며 사회적 수치를 당하는 고통을 호소하고, 내 눈은 근심으로 말미암아 어두워지고 내 온 지체는 그림자 같구나 하며 육체적, 정신적 쇠약을 토로합니다. 정직한 자는 이를 보고 놀라며 무죄한 자는 경건하지 않은 자를 향하여 분을 내느니라 하며 의인의 고난이 다른 의인들에게 충격을 준다고 말합니다.',
    chapter_themes = '["호흡이 끊어지며 무덤이 예비됨", "조롱하는 자들과 함께", "보증물을 주시고 보증하소서", "백성의 이야기거리", "눈이 근심으로 어두워짐", "정직한 자는 놀라며"]',
    chapter_context = '욥이 죽음을 앞에 두고 느끼는 절망감과 사회적 고립감, 그리고 하나님께만 의지할 수밖에 없는 상황을 절절히 토로하는 장면입니다.',
    chapter_application = '절망적 상황에서도 하나님께만 의지하고, 세상의 조롱과 수치를 견디며, 정직한 자의 고난이 주는 교훈을 깊이 새겨야 합니다.'
WHERE book_id = 'job' AND chapter = 17 AND verse = 1;

-- 욥 18장: 빌닷의 두 번째 변론
UPDATE bible_verses SET
    chapter_title = '빌닷의 악인의 운명에 대한 생생한 묘사',
    chapter_summary = '수아 사람 빌닷이 대답하여 이르되 너희가 언제까지 말 사냥을 하겠느냐 깨달으라 그 후에야 우리가 말하리라 하며 욥의 긴 변론을 비판합니다. 네가 어찌하여 우리를 짐승으로 여기며 네 눈에 우리를 어리석게 보느냐 하며 욥이 자신들을 무시한다고 항의하고, 너 분노하여 스스로 찢는 자야 너를 위하여 땅이 버림을 당하겠느냐 바위가 그 자리에서 옮겨지겠느냐 하며 욥의 분노를 비난합니다. 악인의 빛은 꺼지고 그의 불꽃은 빛나지 아니하며 그의 장막 안의 빛은 어두워지고 그의 위에 있는 등불은 꺼지리라 하며 악인의 빛이 꺼지는 것을 묘사하고, 그의 강한 걸음이 곤하여지고 그의 꾀가 그를 넘어뜨리리니 그가 그의 발로 그물에 들어가고 올무 위로 다니는구나 하며 악인이 자신의 계략에 빠진다고 설명합니다. 덫이 땅에서 그의 발뒤꿈치를 잡고 올무가 그를 잡으리니 그를 잡을 올가미가 땅에 숨겨져 있고 그를 잡을 덫이 길에 놓여 있으리라 하며 악인을 기다리는 함정들을 묘사하고, 사방의 두려움이 그를 놀라게 하며 그의 발뒤를 따르리라 하며 악인의 공포감을 그립니다.',
    chapter_themes = '["말 사냥을 언제까지", "우리를 짐승으로 여김", "악인의 빛은 꺼지고", "그물에 들어가고 올무 위로", "올가미가 땅에 숨겨짐", "사방의 두려움"]',
    chapter_context = '빌닷이 욥을 악인으로 단정하고 악인에게 닥치는 여러 재앙들을 생생하게 묘사하여 욥을 위협하는 두 번째 발언입니다.',
    chapter_application = '성급하게 남을 악인으로 판단하지 말고, 겉으로 드러나는 현상만으로 하나님의 뜻을 단정하지 말며, 진정한 지혜로 분별해야 합니다.'
WHERE book_id = 'job' AND chapter = 18 AND verse = 1;

-- 욥 19장: 욥의 여섯 번째 변론 - 나의 대속자가 살아 계심
UPDATE bible_verses SET
    chapter_title = '친구들의 무정함과 산 자의 대속자에 대한 고백',
    chapter_summary = '욥이 대답하여 이르되 너희가 어느 때까지 내 마음을 번뇌하게 하며 말로 나를 짓밟겠느냐 하며 친구들의 지속적인 공격에 지쳤음을 표현합니다. 너희가 이같이 열 번이나 나를 부끄럽게 하고도 뻔뻔스럽게 나를 학대하는구나 하며 친구들의 무정함을 지적하고, 내가 참으로 그릇되었다 할지라도 내 그릇됨이 내게만 있느니라 하며 설사 자신에게 잘못이 있다해도 그것은 자신의 문제라고 말합니다. 너희가 참으로 나를 향하여 자랑하며 내게 부끄러운 일이 있다고 증언하려면 그리하라 하며 친구들의 교만을 비판하고, 하나님이 나를 굽게 하사 그의 그물로 나를 에워싸셨느니라 하며 하나님께서 자신을 곤경에 빠뜨리셨다고 고백합니다. 내가 불의를 당한다고 부르짖으나 응답이 없고 간구할지라도 정의가 없구나 하며 하나님께서 응답하지 않으시는 답답함을 토로합니다. 그러나 내가 알기에는 나의 대속자가 살아 계시니 마침내 그가 땅 위에 서실 것이라 하며 구원자에 대한 확신을 고백하고, 내 가죽이 벗김을 당한 후에도 내가 육체 밖에서 하나님을 보리라 하며 부활에 대한 소망을 표현합니다.',
    chapter_themes = '["열 번이나 부끄럽게 함", "하나님이 굽게 하사 그물로 에워쌈", "불의를 당한다 부르짖으나", "나의 대속자가 살아 계심", "마침내 땅 위에 서실 것", "육체 밖에서 하나님을 보리라"]',
    chapter_context = '욥이 친구들의 무정한 공격과 하나님의 침묵 속에서도 구원자와 부활에 대한 확고한 신앙을 고백하는 욥기의 핵심 장면입니다.',
    chapter_application = '절망적 상황에서도 구원자 예수 그리스도를 확신하고, 부활의 소망을 품으며, 하나님의 최종적 정의 실현을 기다려야 합니다.'
WHERE book_id = 'job' AND chapter = 19 AND verse = 1;

-- 욥 20장: 소발의 두 번째 변론
UPDATE bible_verses SET
    chapter_title = '소발의 악인의 일시적 번영과 필연적 몰락',
    chapter_summary = '나아마 사람 소발이 대답하여 이르되 그러므로 내 생각이 내게 대답하나니 이는 내 마음이 초급함이니라 하며 욥의 말에 대해 급히 반박하려 합니다. 나는 나를 꾸짖는 책망을 들었으므로 나의 지식의 신이 내게 대답하느니라 하며 자신의 지혜에 확신을 표하고, 네가 알지 못하느냐 예로부터 곧 사람이 이 세상에 있어 옴으로 악인의 이기는 것이 잠시요 경건하지 못한 자의 즐거움이 잠깐이니라 하며 악인의 성공이 일시적임을 강조합니다. 그의 높은 것이 하늘에 닿고 그의 머리가 구름에 미칠지라도 자기의 똥처럼 영원히 망하나니 그를 본 자가 이르기를 그가 어디 있느냐 하리라 하며 악인의 높아진 위치도 결국 멸망한다고 설명하고, 그는 꿈같이 날아가니 다시 찾을 수 없을 것이요 밤에 보이던 환상처럼 사라지리니 하며 악인의 덧없는 운명을 묘사합니다. 그의 눈이 그를 본 자가 다시 그를 보지 못할 것이요 그의 처소도 다시 그를 보지 아니하리라 하며 악인의 완전한 소멸을 말하고, 그의 아들들이 가난한 자들에게 은혜를 구하겠고 그도 그의 재물을 자기 손으로 도로 줄 것이라 하며 악인의 후손들도 가난해질 것이라고 예언합니다.',
    chapter_themes = '["악인의 이기는 것이 잠시", "높은 것이 하늘에 닿을지라도", "똥처럼 영원히 망함", "꿈같이 날아감", "다시 찾을 수 없음", "아들들이 가난한 자들에게 은혜를 구함"]',
    chapter_context = '소발이 욥을 악인으로 간주하고 악인의 일시적 번영과 필연적 몰락을 강조하여 욥에게 회개를 촉구하는 두 번째 변론입니다.',
    chapter_application = '일시적 성공에 교만하지 말고, 진정한 성공과 행복은 하나님과의 올바른 관계에 있음을 깨닫고, 영원한 관점에서 삶을 살아야 합니다.'
WHERE book_id = 'job' AND chapter = 20 AND verse = 1;

-- 욥 21장: 욥의 일곱 번째 변론 - 현실에서의 악인의 번영
UPDATE bible_verses SET
    chapter_title = '현실에서 악인이 번영하는 모순에 대한 욥의 관찰',
    chapter_summary = '욥이 대답하여 이르되 너희는 내 말을 자세히 들으라 이것이 너희의 위로가 될 것이니라 하며 친구들에게 자신의 말을 주의 깊게 들어달라고 요청합니다. 나를 용납하여 내가 말하게 하라 내가 말한 후에 너희가 조롱할지니라 하며 자신의 발언권을 요구하고, 나의 원망이 사람을 향함이냐 내가 어찌 초조하지 아니하겠느냐 하며 자신의 불만이 하나님을 향한 것임을 밝힙니다. 너희는 나를 보고 놀라며 손으로 입을 가릴지니라 하며 자신이 말할 내용의 충격적임을 예고하고, 내가 추억하기만 하여도 두려워하며 떨림이 내 몸을 잡는구나 하며 자신이 겪은 고통의 심각성을 표현합니다. 어찌하여 악인이 생존하고 나이가 들어 세력이 강하냐 하며 친구들의 주장과 반대되는 현실을 지적하고, 그들의 후손이 그들 앞에서 굳게 서고 자손이 그들의 목전에서 성장하며 그들의 집이 평안하여 두려움이 없고 하나님의 매가 그들 위에 임하지 아니하며 하며 악인들의 안정된 삶을 묘사합니다. 그들의 황소는 새끼를 배어 낙태하지 아니하며 그들의 암소는 새끼를 낳고 죽지 아니하며 하며 악인들의 가축도 번성함을 말하고, 그들은 소고와 비파를 따라 노래하고 피리 소리에 맞춰 즐거워하며 하며 악인들의 즐거운 삶을 그립니다.',
    chapter_themes = '["내 말을 자세히 들으라", "어찌하여 악인이 생존하고", "그들의 집이 평안하여", "하나님의 매가 임하지 않음", "황소와 암소가 번성", "소고와 비파를 따라 노래"]',
    chapter_context = '욥이 친구들의 단순한 응보론에 반박하며 현실에서 악인들이 오히려 번영하는 모순된 상황을 관찰하여 제시하는 중요한 반박 논리입니다.',
    chapter_application = '현실의 복잡함을 인정하고 단순한 인과응보론을 넘어서며, 하나님의 깊은 섭리를 인정하고, 현재의 성공과 실패로 하나님의 뜻을 속단하지 말아야 합니다.'
WHERE book_id = 'job' AND chapter = 21 AND verse = 1;