-- 욥기 주석 Part 2 (22-42장)
-- 친구들의 마지막 변론과 하나님의 응답

-- 욥 22장: 엘리바스의 세 번째 변론
UPDATE bible_verses SET
    chapter_title = '엘리바스의 욥에 대한 구체적 죄목 제시',
    chapter_summary = '데만 사람 엘리바스가 대답하여 이르되 사람이 하나님께 유익하겠느냐 지혜로운 자도 스스로 유익할 따름이니라 하며 하나님이 인간으로부터 유익을 얻지 않으신다고 주장합니다. 네가 의로운 것이 전능자에게 즐거움이 되겠으며 네 행위가 온전한 것이 그에게 이익이 되겠느냐 하며 인간의 의로움이 하나님께 의미가 없다는 식으로 말하고, 하나님이 네 경외함으로 말미암아 너를 책망하시며 너를 심판하시겠느냐 하며 욥의 고난이 경건함 때문이 아니라고 주장합니다. 네 악이 크지 아니하냐 네 죄악이 끝이 없느니라 하며 욥에게 큰 죄가 있다고 단정하고, 이유 없이 형제의 물건을 볼모 잡으며 헐벗은 자의 의복을 벗기며 목마른 자에게 물을 주지 아니하며 주린 자에게 떡을 주지 아니하였구나 하며 구체적인 죄목들을 나열합니다. 권세 있는 자가 토지를 얻고 존귀한 자가 거기서 사는구나 하며 사회적 불의를 욥의 죄로 돌리고, 네가 과부를 빈손으로 돌아가게 하며 고아의 팔을 꺾었구나 하며 사회적 약자에 대한 죄를 지적합니다. 그러므로 올무가 너를 둘러싸고 갑작스런 두려움이 너를 놀라게 하며 어둠이 너로 하여금 보지 못하게 하고 창수가 너를 덮느니라 하며 이런 죄들 때문에 고난을 당한다고 주장합니다.',
    chapter_themes = '["사람이 하나님께 유익하겠느냐", "네 악이 크지 아니하냐", "이유 없이 볼모 잡음", "목마른 자에게 물을 주지 않음", "과부를 빈손으로 돌려보냄", "올무가 둘러싸고"]',
    chapter_context = '엘리바스가 욥에게 구체적인 죄목들을 제시하며 고난의 원인을 죄에서 찾으려는 마지막 시도를 하는 세 번째이자 마지막 변론입니다.',
    chapter_application = '다른 사람의 고난을 함부로 죄와 연결시켜 판단하지 말고, 사회적 약자를 돌보는 일에 힘쓰며, 성급한 정죄보다 사랑으로 위로해야 합니다.'
WHERE book_id = 'job' AND chapter = 22 AND verse = 1;

-- 욥 23장: 욥의 여덟 번째 변론 - 하나님을 찾아도 만날 수 없음
UPDATE bible_verses SET
    chapter_title = '하나님을 간절히 찾으나 만날 수 없는 욥의 탄식',
    chapter_summary = '욥이 대답하여 이르되 오늘도 내가 원망하니 내가 받는 재앙이 탄식보다 무거움이니라 하며 자신의 고통이 탄식보다 무겁다고 호소합니다. 내가 어찌하면 하나님을 발견하고 그의 계신 곳에 나아가랴 하며 간절히 하나님을 찾고 싶어하고, 내가 그 앞에서 호소하며 변론할 말을 입에 채우고 그가 내게 대답하시는 말씀을 알고자 하며 그가 나와 더불어 변론하시는 뜻을 깨닫고자 하노라 하며 하나님과 직접 대화하고 싶은 간절한 마음을 표현합니다. 그가 큰 권능으로 나와 더불어 다투실까 아니라 도리어 내게 주의하시리라 하며 하나님께서 자신을 이해해 주실 것이라는 희망을 품고, 거기서 정직한 자가 그와 변론할 수 있은즉 내가 심판하시는 이에게서 영원히 벗어나리라 하며 자신의 결백함이 드러나기를 소망합니다. 그런데 내가 앞으로 가도 그가 아니 계시고 뒤로 가도 보이지 아니하며 그가 왼쪽에서 일하시나 내가 만날 수 없고 그가 오른쪽으로 돌이키시나 뵈올 수 없구나 하며 하나님을 찾아도 만날 수 없는 답답함을 토로합니다. 그러나 내가 가는 길을 그가 아시나니 그가 나를 단련하신 후에는 내가 순금 같이 되어 나오리라 하며 하나님께서 자신을 단련하고 계심을 믿는 신앙을 고백합니다.',
    chapter_themes = '["오늘도 내가 원망하니", "어찌하면 하나님을 발견하랴", "앞뒤 좌우로 가도 만날 수 없음", "내가 가는 길을 아시나니", "단련하신 후 순금 같이", "그의 발자취를 따랐고"]',
    chapter_context = '욥이 하나님과의 직접적인 만남을 간절히 원하지만 하나님을 찾을 수 없는 고통 속에서도 하나님의 단련하심을 믿는 깊은 신앙을 보여주는 고백입니다.',
    chapter_application = '하나님이 멀리 계신 것 같을 때에도 포기하지 말고 찾으며, 고난을 하나님의 단련으로 받아들이고, 순금 같은 믿음으로 정련되기를 소망해야 합니다.'
WHERE book_id = 'job' AND chapter = 23 AND verse = 1;

-- 욥 24장: 하나님이 심판의 때를 정하지 않으시는 이유
UPDATE bible_verses SET
    chapter_title = '왜 전능자가 심판의 때를 정하지 않으시는가',
    chapter_summary = '어찌하여 전능자가 때를 정해 두지 아니하셨는고 그를 아는 자들이 그의 날을 보지 못하는고 하며 하나님께서 악인을 심판하시는 때가 보이지 않는 것에 대해 의문을 표합니다. 어떤 사람들은 경계표를 옮기며 양 떼를 빼앗아 기르며 고아의 나귀를 끌어가며 과부의 소를 볼모로 잡으며 하며 사회의 악행들을 나열하고, 가난한 자들을 길에서 몰아내나니 세상의 모든 빈민이 숨어 버리는구나 하며 가난한 자들이 핍박받는 현실을 묘사합니다. 그들이 거친 땅에서 들나귀 같이 나가서 일하며 먹을 것을 부지런히 구하니 광야가 그들과 그들의 자식들의 먹을 것을 내는구나 하며 가난한 자들의 비참한 삶을 그리고, 밭에서 남의 곡식을 베며 악인의 남겨 둔 포도를 따며 의복이 없어 벗은 몸으로 밤을 지내며 몸을 가릴 것이 없고 하며 노동자들의 고달픈 처지를 묘사합니다. 산에서 내리는 비에 젖으며 가릴 것이 없어 바위를 안고 있느니라 하며 노숙자들의 비참함을 그리고, 어떤 사람들은 고아를 어미의 품에서 빼앗으며 가난한 자의 옷을 볼모로 잡으므로 하며 사회적 약자에 대한 착취를 고발합니다.',
    chapter_themes = '["전능자가 때를 정하지 않으심", "경계표를 옮기며", "가난한 자들을 길에서 몰아냄", "들나귀 같이 나가서 일함", "벗은 몸으로 밤을 지냄", "고아를 어미 품에서 빼앗음"]',
    chapter_context = '욥이 사회의 불의와 악행들이 심판받지 않고 계속되는 현실을 관찰하며 하나님의 정의에 대해 의문을 표하는 사회비판적 고발입니다.',
    chapter_application = '사회의 불의와 약자 착취에 관심을 갖고, 하나님의 정의가 지연되는 것 같아도 포기하지 말며, 우리 자신이 정의로운 삶을 살아야 합니다.'
WHERE book_id = 'job' AND chapter = 24 AND verse = 1;

-- 욥 25장: 빌닷의 세 번째 변론
UPDATE bible_verses SET
    chapter_title = '빌닷의 하나님 앞에서 인간의 무가치함 강조',
    chapter_summary = '수아 사람 빌닷이 대답하여 이르되 주권과 위엄이 하나님께 있음이여 그가 높은 곳에서 평화를 베푸시느니라 하며 하나님의 절대적 주권을 인정합니다. 그의 군대가 어찌 계수할 수 있으랴 그의 빛을 받지 않은 자가 누구냐 하며 하나님의 군대의 무수함과 빛의 편재성을 말하고, 그런즉 사람이 어찌 하나님 앞에서 의롭다 하며 부녀의 낳은 자가 어찌 깨끗하다 하랴 하며 인간의 본질적 부정함을 강조합니다. 하나님의 눈에는 달이라도 밝지 못하고 별도 깨끗하지 못하거든 하물며 벌레인 사람, 구더기인 인생이랴 하며 인간을 벌레와 구더기에 비유하여 하나님 앞에서의 무가치함을 극단적으로 표현합니다.',
    chapter_themes = '["주권과 위엄이 하나님께", "그의 군대를 계수할 수 있으랴", "사람이 어찌 의롭다 하랴", "달도 밝지 못하고 별도 깨끗하지 못함", "벌레인 사람", "구더기인 인생"]',
    chapter_context = '빌닷의 마지막 발언으로 하나님의 절대적 거룩함과 인간의 상대적 무가치함을 극단적으로 대조하여 욥의 자기 의에 대한 주장을 꺾으려는 시도입니다.',
    chapter_application = '하나님의 거룩하심 앞에서 겸손하되 자신을 지나치게 비하하지 말고, 하나님의 형상으로 지음받은 인간의 존귀함도 인정하며, 균형 잡힌 신앙을 가져야 합니다.'
WHERE book_id = 'job' AND chapter = 25 AND verse = 1;

-- 욥 26장: 욥의 아홉 번째 변론 - 하나님의 위대한 능력
UPDATE bible_verses SET
    chapter_title = '욥이 빌닷에게 반박하며 하나님의 위대한 능력 찬양',
    chapter_summary = '욥이 대답하여 이르되 네가 힘이 없는 자를 참 잘 도왔구나 기력이 없는 팔을 참 잘 구원하였구나 하며 빌닷의 도움이 무익함을 비꼽니다. 지혜가 없는 자를 참 잘 가르쳤구나 큰 지식을 참 잘 나타내었구나 하며 빌닷의 지혜가 도움이 되지 않는다고 지적하고, 네가 누구를 향하여 말을 내뱉었느냐 누구의 영이 네게서 나왔느냐 하며 빌닷의 말의 근원을 의문시합니다. 스올에 있는 죽은 자들이 떨며 물속에 거하는 것들과 수중 동물들도 떨도다 하며 하나님의 권세가 죽은 자들에게까지 미친다고 말하고, 하나님 앞에서는 스올도 드러나며 아바돈도 가리울 것이 없느니라 하며 하나님 앞에 숨을 곳이 없다고 고백합니다. 하나님이 북쪽 하늘을 허공에 펴시며 땅을 아무것도 없는 곳에 매다시며 물을 빽빽한 구름에 싸시나 구름이 찢어지지 아니하고 하며 하나님의 창조 능력을 찬양하고, 그가 물 위에 경계를 그으사 빛과 어둠의 한계를 삼으셨으며 하늘 기둥들이 그의 견책으로 놀라며 떠는도다 하며 하나님의 자연 지배력을 묘사합니다. 그가 그의 능력으로 바다를 잔잔하게 하시며 그의 지혜로 라합을 쳐서 파하시고 하며 혼돈의 세력도 하나님이 다스리신다고 고백합니다.',
    chapter_themes = '["힘이 없는 자를 참 잘 도왔구나", "스올에 있는 죽은 자들이 떨며", "북쪽 하늘을 허공에 펴심", "물을 빽빽한 구름에 싸심", "바다를 잔잔하게 하심", "보라 이것들은 그의 행사의 단편"]',
    chapter_context = '욥이 빌닷의 무익한 위로를 거부하고 오히려 하나님의 위대한 능력을 더욱 웅장하게 찬양하며 하나님에 대한 깊은 지식을 보여주는 반박입니다.',
    chapter_application = '피상적인 위로보다 하나님의 위대하심을 인정하고, 하나님의 능력을 찬양하며, 고난 중에도 하나님의 주권을 신뢰해야 합니다.'
WHERE book_id = 'job' AND chapter = 26 AND verse = 1;

-- 욥 27장: 욥의 의로움에 대한 마지막 변론
UPDATE bible_verses SET
    chapter_title = '욥의 자신의 의로움에 대한 마지막 주장',
    chapter_summary = '욥이 또 비유를 들어 이르되 나의 의를 빼앗으신 하나님의 사심과 나의 영혼을 괴롭게 하신 전능자의 사심을 두고 맹세하노니 하며 하나님을 두고 맹세하면서도 자신의 의를 주장합니다. 나의 생명이 아직 내 속에 완전히 있고 하나님의 기운이 아직도 내 코에 있는 동안 내 입술이 불의를 말하지 아니하며 내 혀가 거짓을 뱉지 아니하리라 하며 자신의 정직함을 다짐하고, 나는 단정코 너희를 의롭다 하지 아니하겠고 내가 죽기 전에는 내 온전함을 떠나지 아니하리라 하며 친구들을 의롭다 인정하지 않겠다고 선언합니다. 내가 내 의를 굳게 잡고 놓지 아니하리니 일평생 내 마음이 나를 책망하지 아니하리라 하며 자신의 양심의 평안함을 고백하고, 나의 원수는 악인 같이 되고 나를 대적하는 자는 불의한 자 같이 되기를 원하노라 하며 원수들에 대한 저주를 선언합니다. 경건하지 아니한 자가 이익을 얻었으나 하나님이 그의 영혼을 빼앗으실 때에는 무슨 소망이 있으랴 하며 악인의 최종적 운명을 말하고, 재앙이 그에게 임할 때에 하나님이 그의 부르짖음을 들으시겠느냐 하며 악인의 기도가 응답받지 못할 것이라고 주장합니다.',
    chapter_themes = '["나의 의를 빼앗으신 하나님", "내 온전함을 떠나지 아니하리라", "일평생 내 마음이 나를 책망하지 않음", "원수는 악인 같이 되기를", "하나님이 영혼을 빼앗으실 때", "재앙이 임할 때 부르짖음을 들으시겠느냐"]',
    chapter_context = '욥이 끝까지 자신의 의로움을 주장하며 친구들의 비난을 거부하고 악인들과 자신을 구별하려는 마지막 변론입니다.',
    chapter_application = '자신의 양심의 평안함을 소중히 여기되 교만하지 말고, 하나님 앞에서 겸손하며, 완전한 의는 오직 하나님께만 있음을 인정해야 합니다.'
WHERE book_id = 'job' AND chapter = 27 AND verse = 1;

-- 욥 28장: 지혜에 대한 욥의 명상
UPDATE bible_verses SET
    chapter_title = '지혜의 근원과 하나님을 경외함이 지혜',
    chapter_summary = '은을 얻는 광맥이 있고 금을 제련하는 곳이 있으며 철은 흙에서 취하고 동은 돌에서 녹여 얻느니라 하며 인간이 귀한 금속들을 캐내는 기술을 묘사하고, 사람이 흑암의 끝까지 탐사하여 음예와 죽음의 그늘에 있는 돌을 구하며 하며 인간의 탐구 능력을 찬양합니다. 사람이 거주하는 곳에서 멀리 떨어진 곳에서 구멍을 뚫고 발이 닿지 않는 곳에 매달려서 사람들로부터 멀리 떠나 흔들리느니라 하며 광부들의 위험한 작업을 그리고, 땅은 식물을 내나 그 아래는 불로 뒤집힌 것 같고 그 돌 가운데는 남보석이 있고 사금도 있느니라 하며 땅 위아래의 대조를 묘사합니다. 그 길은 솟대도 알지 못하고 매의 눈도 보지 못하며 사나운 짐승들도 밟지 아니하였고 사자도 그리로 다니지 아니하였느니라 하며 인간만이 갈 수 있는 길을 말하고, 사람이 굳은 바위에 손을 대고 산을 뿌리까지 무너뜨리며 반석 사이로 도랑을 파서 눈으로 모든 보물을 보고 시내가 스며 나오지 못하게 하여 감추어진 것을 밝은 데로 내느니라 하며 인간의 뛰어난 기술력을 찬양합니다. 그러나 지혜는 어디서 얻으며 명철이 있는 곳은 어디인고 하며 지혜의 근원에 대한 근본적 질문을 던지고, 그 값을 사람이 알지 못하나니 산 자의 땅에서는 찾을 수 없구나 하며 지혜의 가치를 돈으로 살 수 없다고 말합니다.',
    chapter_themes = '["은을 얻는 광맥", "흑암의 끝까지 탐사", "땅은 식물을 내나 아래는 불", "지혜는 어디서 얻으며", "산 자의 땅에서 찾을 수 없음", "하나님을 경외함이 지혜"]',
    chapter_context = '욥이 인간의 뛰어난 기술과 탐구 능력을 인정하면서도 참된 지혜는 오직 하나님을 경외하는 데서 시작됨을 깨달은 깊은 철학적 명상입니다.',
    chapter_application = '인간의 지식과 기술을 존중하되 절대화하지 말고, 참된 지혜는 하나님을 경외하는 데서 시작됨을 인정하며, 겸손히 하나님의 가르치심을 구해야 합니다.'
WHERE book_id = 'job' AND chapter = 28 AND verse = 1;

-- 욥 29장: 욥의 과거 번영에 대한 회상
UPDATE bible_verses SET
    chapter_title = '욥이 회상하는 과거의 영광과 번영의 날들',
    chapter_summary = '욥이 또 비유를 들어 이르되 하나님이 나를 보호하시던 옛 달과 그의 등불이 내 머리 위에 비치던 때와 내가 그의 빛을 의지하여 흑암 중에 행하던 때를 내가 그리워하는구나 하며 하나님과 가까웠던 과거를 그리워합니다. 나의 장년 때와 같이 지내기를 원하노니 그 때는 하나님의 우정이 내 장막 위에 있었고 전능자가 아직 나와 함께 계셨으며 내 자녀들이 내 둘레에 있었던 때라 하며 가족의 화목했던 시절을 회상하고, 내가 버터로 발을 씻으며 반석이 나를 위하여 기름 시내를 흘러내던 때를 그리워하노라 하며 물질적 풍요로움을 상징적으로 표현합니다. 그 때에 내가 성문에 나가서 광장에서 내 자리를 마련하면 젊은 자들이 나를 보고 숨으며 늙은 자들이 일어나서 서며 지도자들이 말을 참고 손으로 입을 가리며 하며 사회적 존경을 받던 때를 회상하고, 귀가 들은즉 나를 복되다 하고 눈이 본즉 나를 증언하였나니 하며 사람들의 인정을 받던 모습을 그립니다. 이는 내가 부르짖는 빈민과 도와줄 자 없는 고아를 건졌음이라 망하게 된 자도 나를 위하여 복을 빌었고 과부의 마음이 나로 말미암아 기뻐 노래하였느니라 하며 자신이 사회적 약자들을 도왔던 선행을 회상합니다.',
    chapter_themes = '["하나님이 보호하시던 옛 달", "하나님의 우정이 장막 위에", "버터로 발을 씻으며", "성문에서 내 자리를 마련", "부르짖는 빈민을 건짐", "과부의 마음이 기뻐 노래"]',
    chapter_context = '욥이 과거의 번영과 존경받던 시절을 회상하며 현재의 비참한 처지와 대조시켜 자신의 고통을 더욱 부각시키는 회상담입니다.',
    chapter_application = '과거의 영광에만 매몰되지 말고, 좋았던 때를 감사로 기억하되 현재 상황도 하나님의 허락하심으로 받아들이며, 과거의 선행을 자랑거리가 아닌 감사제목으로 여겨야 합니다.'
WHERE book_id = 'job' AND chapter = 29 AND verse = 1;

-- 욥 30장: 욥의 현재 비참한 처지
UPDATE bible_verses SET
    chapter_title = '과거와 대조되는 현재의 비참하고 수치스러운 처지',
    chapter_summary = '그러나 이제는 나보다 젊은 자들이 나를 조롱하는구나 그들의 아비들은 내가 보기에 내 양 떼를 지키는 개 중에도 둘 만하지 못한 자들이니라 하며 현재 자신을 조롱하는 자들의 비천함을 묘사합니다. 그들은 곤고와 기근으로 야위매 메마른 들에서 어둡고 거친 광야의 나물을 뜯으며 떨기나무 뿌리를 먹으며 무리 중에서 쫓겨나나니 사람들이 도둑을 쫓는 것처럼 그들에게 소리를 지르느니라 하며 자신을 조롱하는 자들이 사회의 최하층민임을 강조하고, 그들이 짜는 골짜기와 토굴과 바위 구멍에서 살며 떨기나무 사이에서 나귀처럼 울며 가시나무 아래에 모여 있느니라 하며 그들의 비참한 생활상을 묘사합니다. 이들은 우매한 자의 자식들이라 비천한 자의 자식들이라 채찍으로 그 땅에서 쫓겨난 자들이니라 하며 그들의 출신을 비하하고, 그런데 이제는 내가 그들의 노래가 되며 그들의 조롱거리가 되었구나 하며 현재 자신의 처지를 한탄합니다. 그들이 나를 미워하여 멀리 하고 내 얼굴에 침 뱉기를 주저하지 아니하나니 하며 사회적 모독을 당하는 고통을 토로하고, 하나님이 내 줄을 늘어뜨려 나를 곤고하게 하시매 그들도 내 앞에서 굴레를 벗었구나 하며 하나님께서 자신의 권위를 벗기셨다고 호소합니다.',
    chapter_themes = '["나보다 젊은 자들이 조롱", "개 중에도 둘 만하지 못한", "메마른 들에서 나물을 뜯음", "내가 그들의 노래가 됨", "내 얼굴에 침 뱉기를", "하나님이 내 줄을 늘어뜨려"]',
    chapter_context = '욥이 과거의 존경받던 위치에서 현재 사회 최하층민들에게까지 조롱받는 처지로 전락한 극심한 사회적 굴욕을 토로하는 탄식입니다.',
    chapter_application = '사회적 지위의 변화를 겸손히 받아들이고, 다른 사람을 무시하거나 조롱하지 말며, 현재의 어려움도 하나님의 허락하심 안에 있음을 인정해야 합니다.'
WHERE book_id = 'job' AND chapter = 30 AND verse = 1;

-- 욥 31장: 욥의 순결 맹세와 최종 변론
UPDATE bible_verses SET
    chapter_title = '욥의 도덕적 순결에 대한 맹세와 최종 변론',
    chapter_summary = '내가 내 눈과 약속하였나니 어찌 처녀에게 주목하랴 하며 성적 순결에 대한 자신의 결심을 고백하고, 그리하면 위에 계신 하나님이 내리시는 분깃이 무엇이겠으며 높은 곳에 계신 전능자가 주시는 기업이 무엇이겠느냐 하며 하나님의 상급을 의식했음을 말합니다. 불의자에게는 환난이 있지 아니하냐 행악자에게는 재앙이 있지 아니하냐 하며 악인에 대한 응보를 확신한다고 고백하고, 그가 내 길을 살피지 아니하시느냐 내 걸음을 다 세지 아니하시느냐 하며 하나님께서 자신의 행위를 감찰하고 계심을 인정합니다. 언제 나의 발이 허탄한 데 행하였으며 내 마음이 내 눈을 따라갔으며 내 손에 더러운 것이 묻었던가 하며 자신의 정직함을 주장하고, 그리하였으면 내가 심은 것을 타인이 먹으며 내 소산이 뿌리까지 뽑히는 것이 마땅하니라 하며 자신에 대한 저주를 감수하겠다고 맹세합니다. 언제 내 마음이 여인에게 유혹되어 이웃의 문을 엿보아 기다렸던가 하며 간음죄에 대해 무죄임을 주장하고, 내가 언제 내 종이나 여종이 나와 더불어 쟁변할 때에 그들의 사정을 멸시하였던가 하며 종들에 대한 공평한 대우를 자랑합니다.',
    chapter_themes = '["내 눈과 약속하였나니", "처녀에게 주목하랴", "그가 내 길을 살피지 않느냐", "허탄한 데 행하였으며", "종들의 사정을 멸시하였던가", "전능자가 내게 대답하시기를"]',
    chapter_context = '욥이 자신의 도덕적 순결과 사회적 의무 이행에 대해 구체적으로 맹세하며 하나님 앞에서 최종 변론을 펼치는 자기변호의 절정입니다.',
    chapter_application = '도덕적 순결을 지키고, 사회적 약자에게 공평하게 대하며, 하나님 앞에서 정직한 삶을 살되 자기 의에 빠지지 말고 겸손해야 합니다.'
WHERE book_id = 'job' AND chapter = 31 AND verse = 1;

-- 욥 32장: 엘리후의 등장과 분노
UPDATE bible_verses SET
    chapter_title = '젊은 엘리후의 등장과 욥과 친구들에 대한 분노',
    chapter_summary = '욥이 스스로 의롭다 여기므로 그 세 사람이 말을 그치니 그 때에 람 족속 부스 사람 바라겔의 아들 엘리후가 노하니라 하며 새로운 인물 엘리후의 등장을 알립니다. 그가 욥에게 노함은 욥이 하나님보다 자기를 의롭다 함이요 또 세 친구에게 노함은 그들이 능히 대답하지 못하면서도 욥을 정죄함이라 하며 엘리후가 분노하는 이유를 설명하고, 엘리후가 욥에게 말하기를 기다린 것은 그들이 자기보다 나이가 많음이라 하며 연장자를 존중하여 기다렸음을 밝힙니다. 엘리후가 그들이 지혜 있는 말을 하지 못함을 보고 노가 일어나니라 하며 기대와 달리 지혜로운 해답을 듣지 못한 실망감을 표현하고, 그러므로 내가 말하노니 내게 들으라 나도 내 의견을 보이리라 하며 자신이 발언하겠다고 선언합니다. 보라 내가 너희 말을 기다렸고 너희가 무슨 말을 할까 하여 너희 지혜에 귀를 기울였더니 너희를 자세히 살핀즉 욥을 이기는 자가 없고 그의 말에 대답하는 자도 너희 중에 없구나 하며 세 친구들의 논리적 패배를 지적합니다. 너희는 우리가 지혜를 찾았다, 하나님이 그를 이기시리라, 사람은 불가능하다 말하지 말라 하며 친구들이 논쟁을 포기하려는 것을 비판하고, 그가 내게는 말을 하지 아니하였으니 나는 너희의 말처럼 대답하지 아니하리라 하며 자신은 다른 방식으로 접근하겠다고 예고합니다.',
    chapter_themes = '["욥이 스스로 의롭다 여김", "하나님보다 자기를 의롭다 함", "나이가 많음을 존중", "욥을 이기는 자가 없음", "지혜를 찾았다 말하지 말라", "다른 방식으로 대답하리라"]',
    chapter_context = '세 친구들과 욥의 논쟁이 끝난 후 젊은 엘리후가 등장하여 양측 모두에 대한 불만을 표하며 새로운 관점을 제시하려는 전환점입니다.',
    chapter_application = '연장자를 존중하되 맹목적으로 따르지 말고, 젊은이라도 진리를 말할 수 있음을 인정하며, 하나님보다 자신을 의롭다 여기는 교만을 버려야 합니다.'
WHERE book_id = 'job' AND chapter = 32 AND verse = 1;

-- 욥 33장: 엘리후의 첫 번째 변론
UPDATE bible_verses SET
    chapter_title = '엘리후의 욥에게 하는 첫 번째 변론',
    chapter_summary = '그런즉 욥이여 내 말을 들으며 내 모든 말에 귀를 기울이기 바라노라 하며 욥에게 자신의 말을 들어달라고 정중히 요청합니다. 보라 내가 입을 여니 내 혀가 입에서 말하는구나 내 말이 마음의 정직함을 나타내고 내가 아는 바를 내 입술이 진실하게 말하리라 하며 자신의 진실한 마음을 밝히고, 하나님의 영이 나를 지으셨고 전능자의 기운이 나를 살리시느니라 하며 자신도 욥과 같은 피조물임을 인정합니다. 할 수 있거든 일어서서 내게 대답하고 내 앞에서 진영을 갖추라 하며 욥에게 자유로운 반박 기회를 제공하고, 나도 하나님 앞에서는 네와 같고 나도 흙으로 지어졌나니 하며 욥과 동등한 위치에서 말한다고 밝힙니다. 보라 내가 하나님께 두려움이 네게 임하지 아니하게 하며 내 위압이 너를 누르지 아니하게 하리라 하며 욥이 부담 없이 대화할 수 있도록 배려합니다. 네가 정녕 내 듣는 데에서 말하였고 나는 네 말소리를 들었느니라 이르기를 나는 깨끗하여 죄가 없고 허물이 없어 깨끗하거늘 하며 욥의 주장을 인용하고, 하나님이 나를 치실 틈을 찾으시며 나를 그의 원수로 여기사 내 발을 착고에 채우시고 내 모든 길을 감시하신다 하였느니라 하며 욥이 하나님을 원망한 내용을 지적합니다.',
    chapter_themes = '["내 말을 들으며 귀를 기울이라", "하나님의 영이 나를 지으심", "나도 흙으로 지어졌나니", "나는 깨끗하여 죄가 없다", "하나님이 나를 원수로 여기심", "모든 길을 감시하심"]',
    chapter_context = '엘리후가 욥에게 겸손하고 정중한 태도로 접근하면서도 욥의 자기 의와 하나님에 대한 원망을 지적하며 시작하는 첫 번째 변론입니다.',
    chapter_application = '다른 사람과 대화할 때 겸손하고 정중한 태도를 보이고, 자신도 같은 피조물임을 인정하며, 하나님을 원망하기보다 그분의 뜻을 구해야 합니다.'
WHERE book_id = 'job' AND chapter = 33 AND verse = 1;

-- 욥 34장: 엘리후의 두 번째 변론
UPDATE bible_verses SET
    chapter_title = '엘리후의 하나님의 공의에 대한 변론',
    chapter_summary = '엘리후가 또 말하여 이르되 지혜 있는 자들아 내 말을 들으며 지식 있는 자들아 내게 귀를 기울이라 하며 청중들에게 주의를 요청합니다. 귀가 말을 분별함이 입이 음식의 맛을 아는 것 같나니 우리가 공의를 가려내며 우리끼리 무엇이 선한가를 알아보자 하며 함께 진리를 분별할 것을 제안하고, 욥이 말하기를 내가 의로우나 하나님이 내 의를 부인하시며 내가 무죄함에도 거짓말쟁이가 되고 화살에 상하였으나 허물이 없다 하는구나 하며 욥의 주장을 요약합니다. 욥과 같은 사람이 어디 있으랴 그가 조롱하기를 물 마시듯 하며 악한 일을 행하는 자들과 사귀며 악인과 함께 다니면서 하며 욥이 악인들과 어울린다고 비판하고, 이르기를 사람이 하나님을 기뻐한다고 해서 무슨 이익이 있으랴 하는구나 하며 욥이 하나님 섬기는 것을 무익하다고 여긴다고 해석합니다. 그러므로 너희 총명한 자들아 내게 들으라 하나님은 악을 행하지 아니하시며 전능자는 불의를 행하지 아니하시고 하며 하나님의 절대적 공의를 주장하고, 사람의 행위를 따라 갚으사 각각 그의 행동대로 받게 하시나니 하며 응보의 원칙을 확언합니다. 진실로 하나님은 악을 행하지 아니하시며 전능자는 공의를 굽히지 아니하시느니라 하며 하나님의 공의를 재차 강조하고, 누가 땅을 그에게 맡겼느냐 누가 온 세계를 그에게 위탁하였느냐 하며 하나님의 주권적 통치권을 확인합니다.',
    chapter_themes = '["지혜 있는 자들아 내 말을 들으라", "욥과 같은 사람이 어디 있으랴", "조롱하기를 물 마시듯", "하나님은 악을 행하지 않음", "각각 그의 행동대로 받게 하심", "누가 온 세계를 위탁하였느냐"]',
    chapter_context = '엘리후가 욥의 말을 분석하며 하나님의 절대적 공의와 주권을 강조하여 욥의 불평이 잘못되었다고 논박하는 두 번째 변론입니다.',
    chapter_application = '하나님의 절대적 공의를 믿고, 자신의 제한된 관점으로 하나님을 판단하지 말며, 하나님의 주권적 통치를 겸손히 인정해야 합니다.'
WHERE book_id = 'job' AND chapter = 34 AND verse = 1;

-- 욥 35장: 엘리후의 세 번째 변론
UPDATE bible_verses SET
    chapter_title = '인간의 의로움이 하나님께 무익함을 강조하는 엘리후',
    chapter_summary = '엘리후가 또 말하여 이르되 네가 이것을 합당하게 여기느냐 네 생각에 네가 하나님보다 의롭다 하느냐 하며 욥의 교만을 지적합니다. 네가 이르기를 이것이 내게 무슨 유익이 있으며 범죄하지 아니한 것이 죄 지은 것보다 내게 무슨 나음이 있느냐 하니 하며 욥이 의로움의 무익함을 말했다고 해석하고, 내가 너와 및 너와 함께 있는 네 친구들에게 대답하리라 하며 욥과 친구들 모두에게 답변하겠다고 선언합니다. 너는 하늘을 우러러보라 네 위의 높은 궁창을 바라보라 하며 하나님의 초월성을 바라보라고 권하고, 네가 범죄한들 하나님께 무슨 영향이 있겠으며 네 죄악이 많은들 하나님께 무슨 관계가 있겠으며 하며 인간의 죄가 하나님께 영향을 주지 못한다고 주장합니다. 네가 의로운들 하나님께 무엇을 드리겠으며 그가 네 손에서 무엇을 받으시겠느냐 하며 인간의 의로움도 하나님께 유익을 주지 못한다고 말하고, 네 악은 너와 같은 사람이나 해할 따름이요 네 의는 인생이나 유익하게 할 따름이니라 하며 인간의 선악이 인간 차원에서만 의미가 있다고 주장합니다. 사람들이 학대가 많으므로 부르짖으며 권세자의 팔에 눌려서 소리를 지르나 하며 고난당하는 자들의 부르짖음을 언급하고, 나를 지으신 하나님이 어디 계시냐 밤에 노래를 주시는 이가 어디 계시냐 하는 자가 없구나 하며 고난 중에 하나님을 찾지 않는다고 비판합니다.',
    chapter_themes = '["네가 하나님보다 의롭다 하느냐", "하늘을 우러러보라", "네가 범죄한들 무슨 영향이", "네 의는 인생이나 유익하게 함", "나를 지으신 하나님이 어디 계시냐", "밤에 노래를 주시는 이"]',
    chapter_context = '엘리후가 인간의 의와 악이 하나님께는 영향을 주지 못하며, 고난당하는 자들이 하나님을 제대로 찾지 않는다고 비판하는 세 번째 변론입니다.',
    chapter_application = '하나님의 초월성을 인정하되 내재성도 함께 믿고, 고난 중에 하나님을 찾으며, 자신의 의로움을 자랑하지 말고 겸손해야 합니다.'
WHERE book_id = 'job' AND chapter = 35 AND verse = 1;

-- 욥 36장: 엘리후의 네 번째 변론 시작
UPDATE bible_verses SET
    chapter_title = '엘리후의 하나님의 위대하심과 교육적 고난에 대한 설명',
    chapter_summary = '엘리후가 또 말하여 이르되 잠깐 참으라 내가 네게 보이리니 하나님을 위하여 오히려 할 말이 있음이라 하며 하나님을 변호할 말이 더 있다고 선언합니다. 내가 먼 데서 지식을 가져다가 나를 지으신 이에게 의를 돌리리라 하며 넓은 지식을 동원하여 하나님의 의를 증명하겠다고 다짐하고, 진실로 내 말이 거짓이 아니라 지식이 온전한 이가 네와 함께 있느니라 하며 자신의 지식의 온전함을 자부합니다. 보라 하나님은 능하시나 아무도 멸시하지 아니하시며 그의 지능이 무궁하시며 하며 하나님의 능력과 공평하심을 강조하고, 악인을 살려두지 아니하시며 고난받는 자에게 공의를 베푸시며 의인에게서 그의 눈을 돌이키지 아니하시고 그들을 왕과 함께 왕좌에 앉히사 영원토록 존귀하게 하시며 하며 하나님의 공의로운 통치를 설명합니다. 혹시 그들이 사슬에 매이거나 고난의 줄에 얽혔으면 하며 고난의 상황을 가정하고, 하나님이 그들의 소행과 허물을 알게 하사 그들이 교만하게 행한 것을 깨닫게 하시고 하며 고난의 교육적 목적을 설명합니다. 그들의 귀를 열어 교훈을 듣게 하시며 악에서 돌아오라 명령하시나니 하며 하나님께서 고난을 통해 교육하신다고 주장하고, 만일 그들이 듣고 섬기면 형통히 날을 보내며 즐거이 해를 지낼 것이요 하며 순종의 축복을 말합니다.',
    chapter_themes = '["하나님을 위하여 할 말이 있음", "지식이 온전한 이가 함께", "하나님은 능하시나 멸시하지 않음", "고난의 줄에 얽혔으면", "교만하게 행한 것을 깨닫게", "귀를 열어 교훈을 듣게"]',
    chapter_context = '엘리후가 하나님의 완전한 공의와 고난의 교육적 목적을 설명하며 욥의 고난을 하나님의 징계로 해석하는 네 번째 변론의 시작입니다.',
    chapter_application = '고난을 하나님의 교육적 징계로 받아들이고, 교만을 버리고 겸손히 하나님의 음성을 들으며, 순종을 통해 회복의 길을 모색해야 합니다.'
WHERE book_id = 'job' AND chapter = 36 AND verse = 1;

-- 욥 37장: 엘리후의 하나님의 위대한 능력 찬양
UPDATE bible_verses SET
    chapter_title = '하나님의 자연 지배 능력에 대한 엘리후의 찬양',
    chapter_summary = '이로 말미암아 내 마음이 떨며 그 자리에서 뛰는구나 하며 하나님의 능력 앞에서 두려워하는 마음을 고백합니다. 하나님의 음성의 울리는 소리를 들으라 그의 입에서 나오는 소리를 들으라 하며 천둥소리를 하나님의 음성으로 해석하고, 그가 번개를 온 하늘 아래에 번쩍이게 하시며 땅 끝까지 이르게 하시고 하며 번개의 광대함을 묘사합니다. 그 후에 음성이 울리나니 하나님이 그의 위엄한 음성으로 우레를 치시며 그 음성이 들릴 때에 번개를 그치지 아니하시느니라 하며 천둥번개를 하나님의 위엄으로 설명하고, 하나님이 기이하게 음성을 울리시며 우리가 알지 못하는 큰 일을 행하시느니라 하며 하나님의 신비로운 일들을 찬양합니다. 눈에게 이르시기를 땅에 내리라 하시며 큰 비와 폭우에게도 그렇게 명령하시느니라 하며 날씨를 주관하시는 하나님을 묘사하고, 그가 각 사람의 손을 봉하시나니 이는 그가 지으신 모든 사람에게 그의 일을 알게 하려 하심이라 하며 계절변화를 통해 하나님을 알게 하신다고 설명합니다. 이때에 짐승들이 숨을 곳으로 들어가서 그들의 굴에 머물며 하며 동물들도 계절변화에 반응함을 말하고, 남방 밀실에서는 회오리바람이 나오고 북방에서는 찬 기운이 나오며 하나님의 입김으로 얼음이 얼고 물의 넓이가 줄어드느니라 하며 기후변화의 원인을 하나님께 돌립니다.',
    chapter_themes = '["내 마음이 떨며 뛰는구나", "하나님의 음성의 울리는 소리", "번개를 온 하늘 아래", "기이하게 음성을 울리심", "각 사람의 손을 봉하심", "하나님의 입김으로 얼음이"]',
    chapter_context = '엘리후가 천둥번개, 폭풍우, 계절변화 등 자연현상을 통해 하나님의 위대한 능력과 신비로운 섭리를 찬양하는 웅장한 고백입니다.',
    chapter_application = '자연현상을 통해 하나님의 위대하심을 인식하고, 하나님의 신비로운 능력 앞에서 겸손해하며, 피조물을 통해 창조주를 경외해야 합니다.'
WHERE book_id = 'job' AND chapter = 37 AND verse = 1;

-- 욥 38장: 하나님의 첫 번째 말씀 - 창조의 신비
UPDATE bible_verses SET
    chapter_title = '폭풍우 중에서 욥에게 말씀하시는 하나님의 첫 번째 답변',
    chapter_summary = '그 때에 여호와께서 폭풍우 가운데서 욥에게 말씀하여 이르시되 하며 드디어 하나님께서 직접 욥에게 말씀하십니다. 무지한 말로 생각을 어둡게 하는 자가 누구냐 하며 욥의 말이 하나님의 계획을 흐리게 했다고 지적하시고, 너는 대장부처럼 허리를 묶고 내가 네게 묻는 것을 대답할지니라 하며 욥에게 당당히 대답하라고 도전하십니다. 내가 땅의 기초를 놓을 때에 네가 어디 있었느냐 네가 깨달아 알았거든 말할지니라 하며 창조 때 욥의 부재를 지적하시고, 누가 그것의 도량을 정하였는지 아느냐 누가 그 위에 줄을 띠었는지 아느냐 하며 땅의 창조 과정에 대해 질문하십니다. 그 터는 무엇 위에 세웠으며 그 모퉁잇돌은 누가 놓았느냐 하며 땅의 기초에 대해 묻고, 그 때에 새벽 별들이 함께 노래하며 하나님의 아들들이 다 기뻐 소리를 질렀느니라 하며 창조의 기쁨을 묘사하십니다. 바다가 태에서 터져 나올 때에 문으로 그것을 막은 자가 누구냐 하며 바다의 창조와 경계 설정에 대해 질문하시고, 그 때에 내가 구름으로 그 옷을 만들고 흑암으로 그 강보를 만들고 하며 바다를 감싸신 하나님의 배려를 말씀하십니다. 내가 그것을 위하여 경계를 정하여 문과 빗장을 베풀고 하며 바다의 한계를 정하신 것을 설명하시고, 이르기를 네가 여기까지 오고 더 넘어가지 못하리니 네 높은 파도가 여기서 그칠지니라 하였노라 하며 바다에 대한 명령을 인용하십니다.',
    chapter_themes = '["폭풍우 가운데서 말씀하심", "무지한 말로 생각을 어둡게", "땅의 기초를 놓을 때 어디 있었느냐", "새벽 별들이 함께 노래", "바다가 태에서 터져 나올 때", "여기까지 오고 더 넘어가지 못하리라"]',
    chapter_context = '욥기의 절정으로 하나님께서 직접 욥에게 나타나셔서 창조의 신비와 자연 경영의 지혜를 통해 인간의 한계를 깨닫게 하시는 첫 번째 말씀입니다.',
    chapter_application = '창조의 신비 앞에서 겸손해하고, 하나님의 지혜와 능력을 인정하며, 인간의 한계를 겸허히 받아들이고 하나님께 순복해야 합니다.'
WHERE book_id = 'job' AND chapter = 38 AND verse = 1;

-- 욥 39장: 하나님의 첫 번째 말씀 계속 - 동물들의 신비
UPDATE bible_verses SET
    chapter_title = '야생동물들을 통해 보여주시는 하나님의 지혜와 섭리',
    chapter_summary = '산 염소가 새끼 낳는 때를 네가 아느냐 암사슴이 새끼를 낳는 것을 네가 목격하였느냐 하며 야생동물의 출산에 대해 질문하시고, 그것들이 임신한 달 수를 네가 계수할 수 있느냐 그것들이 낳을 때를 아느냐 하며 동물들의 임신 기간을 묻습니다. 그것들이 몸을 구부려 새끼를 낳으며 그 괴로움을 벗어나며 그 새끼들이 강해져서 빈 들에서 자라고 나가서는 돌아오지 아니하느니라 하며 야생동물 새끼들의 독립성을 묘사하시고, 들나귀를 풀어 놓은 것이 누구냐 빠른 나귀의 매인 것을 누가 풀었느냐 하며 들나귀의 자유로움에 대해 질문하십니다. 내가 들을 그것의 집으로, 짠 땅을 그것이 사는 처소로 삼았느니라 하며 들나귀의 서식지를 정해주신 것을 말씀하시고, 그것이 성읍의 지꺼리는 소리를 비웃으며 나귀 치는 자의 지르는 소리를 들어보지도 아니하고 하며 들나귀의 자유분방함을 그리십니다. 들소가 어찌 네게 복종하겠으며 네 외양간에 머물겠느냐 하며 들소의 야성에 대해 질문하시고, 네가 능히 줄로 들소를 매어 이랑을 갈게 하겠느냐 그것이 골짜기에서 너를 따라 써레를 끌겠느냐 하며 들소를 길들일 수 없음을 지적하십니다. 타조는 즐거이 그 날개를 흔드나니 학의 날개와 깃털 같겠느냐 하며 타조의 특성을 묘사하시고, 그것이 알을 땅에 버려두어 모래에서 따뜻하게 하며 하며 타조의 독특한 육아법을 설명하십니다.',
    chapter_themes = '["산 염소가 새끼 낳는 때", "들나귀를 풀어 놓은 것이 누구냐", "들을 그것의 집으로 삼았느니라", "들소가 네게 복종하겠느냐", "타조는 즐거이 날개를 흔드나니", "알을 땅에 버려두어"]',
    chapter_context = '하나님께서 다양한 야생동물들의 고유한 특성과 본능을 통해 자연계를 경영하시는 하나님의 지혜와 섭리를 욥에게 보여주시는 말씀입니다.',
    chapter_application = '자연계의 질서와 각 생물의 고유성을 통해 하나님의 지혜를 배우고, 하나님의 섭리를 인정하며, 인간의 통제를 벗어난 영역이 있음을 겸손히 받아들여야 합니다.'
WHERE book_id = 'job' AND chapter = 39 AND verse = 1;

-- 욥 40장: 욥의 첫 번째 대답과 하나님의 두 번째 말씀
UPDATE bible_verses SET
    chapter_title = '욥의 겸손한 대답과 하나님의 베헤못에 대한 말씀',
    chapter_summary = '여호와께서 또 욥에게 말씀하여 이르시되 트집 잡는 자가 전능자와 다투겠느냐 하나님을 탓하는 자는 대답할지니라 하며 욥의 불평에 대해 직접적으로 지적하십니다. 욥이 여호와께 대답하여 이르되 보소서 나는 미천하오니 무엇이라 주께 대답하리이까 손으로 내 입을 가릴 뿐이로소이다 하며 욥이 겸손하게 침묵을 선택합니다. 내가 한 번 말하였사온즉 다시는 대답하지 아니하겠고 두 번 말하였사온즉 다시는 아니하겠나이다 하며 더 이상 변론하지 않겠다고 다짐하고, 여호와께서 또 폭풍우 가운데서 욥에게 말씀하여 이르시되 하며 하나님께서 두 번째로 말씀을 시작하십니다. 너는 대장부처럼 허리를 묶고 내가 네게 묻는 것을 대답할지니라 하며 다시 욥에게 도전하시고, 네가 내 심판을 폐하려느냐 스스로 의롭다 하려 하여 나를 악하다 하려느냐 하며 욥이 하나님을 판단하려 했음을 지적하십니다. 네게 하나님 같은 팔이 있느냐 하나님처럼 천둥소리를 내겠느냐 하며 하나님과 같은 능력이 있는지 묻고, 너는 위엄과 존귀로 스스로를 꾸미며 영광과 화려한 것으로 스스로를 입을지니라 하며 하나님의 위엄을 흉내 낼 수 있는지 도전하십니다. 이제 베헤못을 보라 내가 너를 지은 것 같이 그것도 지었느니라 그것이 소처럼 풀을 먹는다 하며 거대한 육상동물 베헤못을 소개하시고, 보라 그것의 허리의 힘과 그 배의 근육의 힘이 얼마나 큰가 하며 베헤못의 강력한 체력을 묘사하십니다.',
    chapter_themes = '["트집 잡는 자가 전능자와", "나는 미천하오니", "손으로 내 입을 가릴 뿐", "내 심판을 폐하려느냐", "하나님 같은 팔이 있느냐", "베헤못을 보라"]',
    chapter_context = '욥이 하나님의 위대하심 앞에서 겸손해하며 침묵을 선택하지만, 하나님께서 베헤못이라는 거대한 생물을 통해 더욱 강력한 메시지를 주시는 장면입니다.',
    chapter_application = '하나님의 권위 앞에서 겸손해하고, 하나님을 판단하려는 교만을 버리며, 창조주의 위대한 능력과 지혜를 인정하고 순복해야 합니다.'
WHERE book_id = 'job' AND chapter = 40 AND verse = 1;

-- 욥 41장: 하나님의 리워야단에 대한 말씀
UPDATE bible_verses SET
    chapter_title = '리워야단을 통해 보여주시는 하나님의 절대적 주권',
    chapter_summary = '네가 리워야단을 낚시 바늘로 낚을 수 있느냐 그 혀를 줄로 맬 수 있겠느냐 하며 바다의 거대한 괴물 리워야단에 대해 질문하시고, 네가 갈대 줄을 그 코에 꿸 수 있겠느냐 바늘로 그 턱을 뚫겠느냐 하며 리워야단을 길들일 수 없음을 강조하십니다. 그것이 네게 연속하여 간구하겠느냐 부드러운 말로 네게 이야기하겠느냐 하며 리워야단이 순순히 굴복하지 않음을 말씀하시고, 그것이 너와 언약을 맺고 네가 그것을 영원한 종으로 삼겠느냐 하며 계약 관계의 불가능성을 지적하십니다. 네가 그것을 새를 가지고 노는 것 같이 하겠으며 네 여종들을 위하여 그것을 매어 두겠느냐 하며 애완동물처럼 다룰 수 없음을 말씀하시고, 어부들이 그것으로 상품을 삼아 상인들 가운데에서 나누겠느냐 하며 상업적 이용의 불가능성을 묻습니다. 네가 작살을 그 가죽에 가득 꽂을 수 있겠느냐 고기잡이 창을 그 머리에 꽂을 수 있겠느냐 하며 무기로도 제압할 수 없음을 강조하시고, 네 손을 그것에게 좀 대어 보라 싸움을 생각하고는 다시는 그리하지 못하리라 하며 리워야단과의 대결이 불가능함을 경고하십니다. 그것을 굴복시킬 소망은 헛된 것이라 그것을 보기만 하여도 낙담하지 아니하겠느냐 하며 리워야단의 위력을 묘사하시고, 아무도 그것을 격동시킬 용맹이 없거든 누가 나를 대적하여 서겠느냐 하며 리워야단보다 훨씬 위대하신 하나님의 절대적 주권을 선언하십니다. 누가 먼저 내게 주고 나로 하여금 갚게 하였느냐 온 천하에 있는 것이 다 내 것이니라 하며 하나님의 소유권을 주장하십니다.',
    chapter_themes = '["리워야단을 낚시 바늘로", "갈대 줄을 그 코에 꿸 수 있겠느냐", "너와 언약을 맺고", "네 손을 그것에게 대어 보라", "누가 나를 대적하여 서겠느냐", "온 천하에 있는 것이 다 내 것"]',
    chapter_context = '하나님께서 인간이 전혀 통제할 수 없는 리워야단이라는 거대한 바다 괴물을 통해 하나님의 절대적 주권과 전능하심을 욥에게 깨닫게 하시는 클라이맥스입니다.',
    chapter_application = '인간의 한계를 겸손히 인정하고, 하나님의 절대적 주권에 순복하며, 모든 것이 하나님의 소유임을 인정하고 감사하는 마음으로 살아야 합니다.'
WHERE book_id = 'job' AND chapter = 41 AND verse = 1;

-- 욥 42장: 욥의 회개와 회복
UPDATE bible_verses SET
    chapter_title = '욥의 회개와 하나님의 은혜로운 회복',
    chapter_summary = '욥이 여호와께 대답하여 이르되 주께서는 못 하실 일이 없사오며 무슨 계획이든지 못 이루실 것이 없는 줄 아나이다 하며 하나님의 전능하심을 고백합니다. 무지한 말로 생각을 어둡게 하는 자가 누구냐 하셨나이다 그러므로 내가 깨닫지도 못한 일을 말하였고 스스로 알 수도 없고 헤아리기도 어려운 일을 말하였나이다 하며 자신의 무지를 인정하고, 내가 말하겠사오니 주여 들으시고 내가 주께 묻겠사오니 주여 내게 알려 주옵소서 하셨나이다 하며 하나님의 말씀을 인용합니다. 내가 주에 대하여 귀로 듣기만 하였삽더니 이제는 눈으로 주를 뵈옵나니 하며 하나님에 대한 직접적 체험을 고백하고, 그러므로 내가 스스로 거두어들이고 티끌과 재 가운데서 회개하나이다 하며 철저한 회개를 표명합니다. 여호와께서 욥에게 이 말씀을 하신 후에 데만 사람 엘리바스에게 이르시되 내가 너와 네 두 친구에게 노하나니 이는 너희가 나를 가리켜 말한 것이 내 종 욥의 말 같이 옳지 못함이라 하며 친구들의 잘못을 지적하시고, 그런즉 너희는 수송아지 일곱과 숫양 일곱을 가지고 내 종 욥에게 가서 너희를 위하여 번제를 드리라 내 종 욥이 너희를 위하여 기도할 것인즉 내가 그를 받으리니 하며 욥을 통한 중보기도를 명하십니다. 욥이 그의 친구들을 위하여 기도할 때 여호와께서 욥의 곤경을 돌이키시고 욥에게 그 전 소유보다 갑절이나 주시니 하며 욥의 회복을 기록하고, 여호와께서 욥의 말년을 그 전보다 복되게 하시니 그가 양 만 사천과 낙타 육천과 소 천 겨리와 암나귀 천을 두었고 또 아들 일곱과 딸 셋을 두었는데 하며 구체적인 복의 내용을 열거합니다.',
    chapter_themes = '["못 하실 일이 없사오며", "이제는 눈으로 주를 뵈옵나니", "티끌과 재 가운데서 회개", "내 종 욥의 말 같이 옳다", "친구들을 위하여 기도할 때", "전 소유보다 갑절이나"]',
    chapter_context = '욥기의 대단원으로 욥이 하나님을 직접 만난 후 철저히 회개하고, 하나님께서 욥을 변호하시며 갑절의 복으로 회복시켜 주시는 은혜로운 결말입니다.',
    chapter_application = '하나님의 전능하심을 인정하고 겸손히 회개하며, 원수까지도 위해 기도하는 사랑을 실천하고, 하나님의 때를 기다리며 신뢰하는 믿음을 가져야 합니다.'
WHERE book_id = 'job' AND chapter = 42 AND verse = 1;