-- 마가복음 주석 (1-16장)
-- 하나님의 아들 예수 그리스도의 복음, 즉시와 능력의 복음

-- 마가복음 1장: 세례 요한과 예수님의 세례, 사역 시작
UPDATE bible_verses SET
    chapter_title = '세례 요한의 사역과 예수님의 세례, 공생애 시작',
    chapter_summary = '하나님의 아들 예수 그리스도 복음의 시작이라 선지자 이사야의 글에 기록된 바 보라 내가 내 사자를 네 앞에 보내노니 저가 네 길을 예비하리라 광야에 외치는 자의 소리가 있어 가로되 너희는 주의 길을 예비하라 그의 첩경을 평탄케 하라 기록된 것과 같이 세례 요한이 이르러 광야에서 죄 사함을 받게 하는 회개의 세례를 전파하니 온 유대 지방과 예루살렘 사람이 다 나아가 자기 죄를 자복하고 요단강에서 그에게 세례를 받더라 요한은 약대털을 입고 허리에 가죽띠를 띠고 메뚜기와 석청을 먹더라 그가 전파하여 가로되 나보다 능력 많으신 이가 내 뒤에 오시나니 나는 굽혀 그의 신들메를 풀기도 감당치 못하겠노라 나는 너희에게 물로 세례를 주었거니와 그는 성령으로 너희에게 세례를 주시리라 하더라 하시고, 그 때에 예수께서 갈릴리 나사렛으로부터 와서 요단강에서 요한에게 세례를 받으시고 곧 물에서 올라오실새 하늘이 갈라짐과 성령이 비둘기같이 자기에게 내려오심을 보시더니 하늘로부터 소리가 나기를 너는 내 사랑하는 아들이요 내 기뻐하는 자라 하시니라 성령이 곧 예수를 광야로 몰아내신지라 광야에서 사십 일을 계시면서 사단에게 시험을 받으시며 들짐승과 함께 계시니 천사들이 수종들더라 하시고, 요한이 잡힌 후 예수께서 갈릴리에 오셔서 하나님의 복음을 전파하여 가라사대 때가 찼고 하나님의 나라가 가까웠으니 회개하고 복음을 믿으라 하시더라.',
    chapter_themes = '["하나님의 아들 예수 그리스도", "세례 요한의 전파", "회개의 세례", "성령으로 세례", "때가 찼고", "회개하고 복음을 믿으라"]',
    chapter_context = '마가복음의 서두로 예수님의 메시야 되심과 공생애 시작을 간결하고 힘있게 선언하며, 즉시성과 능력을 강조하는 마가복음의 특징을 보여줍니다.',
    chapter_application = '때가 찼음을 깨닫고 회개하며 복음을 믿고, 예수님을 하나님의 아들로 고백하며, 즉시 순종하는 삶을 살아야 합니다.'
WHERE book_id = 'mk' AND chapter = 1 AND verse = 1;

-- 마가복음 2장: 중풍병자 치유와 죄 사함의 권세
UPDATE bible_verses SET
    chapter_title = '중풍병자 치유와 죄 사함의 권세, 레위의 부르심',
    chapter_summary = '수일 후에 예수께서 다시 가버나움에 들어가시니 집에 계시다는 소문이 들린지라 많은 사람이 모여서 문 앞에라도 용신할 수 없게 되었는데 예수께서 저희에게 도를 말씀하시더니 사람들이 한 중풍병자를 네 사람에게 메워 가지고 예수께로 올새 무리를 인하여 예수께 가져갈 수 없으므로 그 계신 곳의 지붕을 뜯어 구멍을 내고 중풍병자의 누운 상을 달아 내리니 예수께서 저희의 믿음을 보시고 중풍병자에게 이르시되 소자야 네 죄 사함을 받았느니라 하시니 어떤 서기관들이 거기 앉아서 마음에 의논하되 이 사람이 어찌 이렇게 말하는가 참람하도다 오직 하나님 한 분 외에는 누가 능히 죄를 사하겠느냐 저희가 속으로 이렇게 의논하는 줄을 예수께서 곧 중심에 아시고 이르시되 어찌하여 이것을 마음에 의논하느냐 중풍병자에게 네 죄 사함을 받았느니라 하는 말과 일어나 네 상을 가지고 걸어가라 하는 말이 어느 것이 쉽겠느냐 그러나 인자가 땅에서 죄를 사하는 권세가 있는 줄을 너희로 알게 하려 하노라 하시고 중풍병자에게 말씀하시되 내가 네게 이르노니 일어나 네 상을 가지고 집으로 가라 하시니 그가 일어나 곧 상을 가지고 모든 사람 앞에서 나가거늘 저희가 다 놀라 하나님께 영광을 돌리며 가로되 우리가 이런 일을 도무지 보지 못하였다 하더라 하시고, 예수께서 다시 바닷가에 나가시매 무리가 다 나아왔거늘 예수께서 저희를 가르치시니라 또 지나가시다가 알패오의 아들 레위가 세관에 앉아 있는 것을 보시고 저에게 이르시되 나를 좇으라 하시니 일어나 좇으니라.',
    chapter_themes = '["중풍병자 치유", "지붕을 뜯어", "네 죄 사함을 받았느니라", "죄를 사하는 권세", "레위의 부르심", "나를 좇으라"]',
    chapter_context = '예수님의 죄 사함의 권세가 기적을 통해 확증되고, 죄인을 부르시는 주님의 사역이 레위의 부르심을 통해 나타납니다.',
    chapter_application = '믿음으로 예수님께 나아가고, 죄 사함의 은혜를 받으며, 즉시 순종하여 주님을 따르고, 다른 죄인들에게도 복음을 전해야 합니다.'
WHERE book_id = 'mk' AND chapter = 2 AND verse = 1;

-- 마가복음 3장: 열두 제자 선택과 바알세불 논쟁
UPDATE bible_verses SET
    chapter_title = '열두 제자 선택과 바알세불 논쟁',
    chapter_summary = '예수께서 다시 회당에 들어가시니 한편 손 마른 사람이 거기 있는지라 사람들이 예수를 송사하려 하여 안식일에 그 사람을 고치시는가 엿보거늘 예수께서 손 마른 사람에게 이르시되 한가운데 일어서라 하시고 저희에게 이르시되 안식일에 선을 행하는 것과 악을 행하는 것, 생명을 구하는 것과 죽이는 것, 어느 것이 옳으냐 하시니 저희가 잠잠하거늘 저희 마음의 완악함을 인하여 근심하사 노하심으로 저희를 둘러 보시고 그 사람에게 이르시되 네 손을 내밀라 하시니 내밀매 그 손이 회복되었더라 바리새인들이 나가서 곧 헤롯당과 함께 어떻게 하여 예수를 죽일꼬 의논하니라 하시고, 예수께서 제자들과 함께 바다로 물러가시니 갈릴리에서 큰 무리가 좇으며 유대와 예루살렘과 이두매와 요단 강 건너편과 또 두로와 시돈 근처에서 많은 무리가 그의 하신 큰 일을 듣고 나아오는지라 예수께서 무리의 에워쌈을 면키 위하여 작은 배를 등대하도록 제자들에게 명하시고 이는 많은 사람을 고치셨으므로 병에 고생하는 자들이 예수를 만지고자 하여 핍근히 하였음이더라 더러운 귀신들도 어느 때든지 예수를 보면 그 앞에 엎드려 부르짖어 가로되 당신은 하나님의 아들이니이다 하니 예수께서 자기를 나타내지 말라고 많이 경계하시니라 하시고, 또 산에 오르사 자기의 원하는 자들을 부르시니 나아온지라 이에 열둘을 세우셨으니 이는 자기와 함께 있게 하시고 또 보내사 전도도 하며 귀신을 내어쫓는 권세도 있게 하려 하심이러라.',
    chapter_themes = '["손 마른 사람", "안식일에 선을 행하는 것", "열두 제자를 세우심", "바알세불 논쟁", "성령을 거스리는 죄", "누가 내 모친이며"]',
    chapter_context = '안식일 논쟁과 열두 제자 선택을 통해 예수님의 사역이 체계화되고, 바알세불 논쟁으로 영적 전쟁의 실상이 드러납니다.',
    chapter_application = '안식일에도 선을 행하고, 예수님과 함께 있는 것을 우선순위로 삼으며, 성령을 거스리지 말고, 하나님의 뜻을 행하는 진정한 가족이 되어야 합니다.'
WHERE book_id = 'mk' AND chapter = 3 AND verse = 1;

-- 마가복음 4장: 씨 뿌리는 자의 비유와 여러 비유들
UPDATE bible_verses SET
    chapter_title = '씨 뿌리는 자의 비유와 하나님 나라의 비유들',
    chapter_summary = '예수께서 다시 바닷가에서 가르치시니 큰 무리가 모여들어 예수께서 배에 올라 바다에 앉으시고 온 무리는 바다 곁 육지에 있더라 이에 예수께서 여러 가지를 비유로 가르치시니 그 가르치시는 중에 저희에게 이르시되 들으라 씨를 뿌리는 자가 뿌리러 나가서 뿌릴새 더러는 길 가에 떨어지매 새들이 와서 먹어 버렸고 더러는 흙이 얇은 돌밭에 떨어지매 흙이 깊지 아니하므로 곧 싹이 나오나 해가 돋은 후에 타져서 뿌리가 없으므로 말랐고 더러는 가시떨기에 떨어지매 가시가 자라 기운을 막으므로 결실치 못하였고 더러는 좋은 땅에 떨어지매 자라 무성하여 결실하였으니 삼십 배와 육십 배와 백 배가 되었느니라 하시고 또 이르시되 들을 귀 있는 자는 들으라 하시니라 하시고, 예수께서 홀로 계실 때에 함께한 사람들이 열두 제자와 더불어 그 비유들을 묻자오니 이르시되 하나님 나라의 비밀을 너희에게는 주었으나 외인에게는 모든 것을 비유로 하나니 이는 저희로 보기는 보아도 알지 못하며 듣기는 들어도 깨닫지 못하게 하여 돌이켜 죄 사함을 얻지 못하게 하려 함이니라 하시고 또 가라사대 너희가 이 비유를 알지 못할진대 어떻게 모든 비유를 알겠느뇨 뿌리는 자는 말씀을 뿌리는 것이라 말씀이 길 가에 뿌리워졌다는 것은 이들이니 곧 말씀을 들었을 때에 사단이 즉시 와서 저희에게 뿌리워진 말씀을 빼앗는 것이요 또 이와 같이 돌밭에 뿌리워졌다는 것은 이들이니 곧 말씀을 들을 때에 즉시 기쁨으로 받으나 그 속에 뿌리가 없어 잠시 견디다가 말씀을 인하여 환난이나 핍박이 일어나는 때에는 곧 넘어지는 자요 또 어떤이는 가시떨기에 뿌리워지는 자니 이들은 말씀을 듣되 세상의 염려와 재리의 유혹과 기타 욕심이 들어와 말씀을 막아 결실치 못하게 되는 자요 좋은 땅에 뿌리워졌다는 것은 곧 말씀을 듣고 받아 삼십 배와 육십 배와 백 배의 결실을 하는 자니라.',
    chapter_themes = '["씨 뿌리는 자 비유", "하나님 나라의 비밀", "좋은 땅", "삼십 배 육십 배 백 배", "겨자씨 비유", "바람과 바다를 잔잔케 함"]',
    chapter_context = '비유를 통해 하나님 나라의 성격과 말씀에 대한 다양한 반응, 그리고 점진적 성장의 원리를 가르치시는 중요한 교훈입니다.',
    chapter_application = '좋은 땅이 되어 말씀을 받고 결실하며, 하나님 나라의 비밀을 깨닫고, 작은 시작을 귀히 여기며, 주님의 능력을 신뢰해야 합니다.'
WHERE book_id = 'mk' AND chapter = 4 AND verse = 1;

-- 마가복음 5장: 거라사 광인과 야이로의 딸, 혈루증 여인
UPDATE bible_verses SET
    chapter_title = '거라사 광인 치유와 야이로의 딸, 혈루증 여인',
    chapter_summary = '예수께서 바다 건너편 거라사인의 지방에 이르러 배에서 나오시매 곧 더러운 귀신 들린 사람이 무덤 사이에서 나와 예수를 만나니라 그 사람은 무덤 사이에 거처하는데 이제는 아무나 쇠사슬로라도 맬 수 없게 되었으니 이는 여러 번 고랑과 쇠사슬에 매였어도 쇠사슬을 끊고 고랑을 깨뜨렸음이러라 그리하여 아무도 저를 제어할 힘이 없는지라 밤낮 무덤 사이에서와 산에서 늘 소리지르며 돌로 제 몸을 해하고 있었더라 그가 멀리서 예수를 보고 달려와 절하며 큰 소리로 부르짖어 가로되 지극히 높으신 하나님의 아들 예수여 나와 당신과 무슨 상관이 있나이까 원컨대 하나님 앞에 맹세하고 나를 괴롭게 마옵소서 하니 이는 예수께서 이미 그에게 이르시기를 더러운 귀신아 그 사람에게서 나오라 하셨음이라 이에 물으시되 네 이름이 무엇이냐 가로되 내 이름은 군대니 우리가 많음이니이다 하고 자기를 그 지방에서 내어보내지 마시기를 간구하더니 마침 그곳 산 곁에 돼지의 큰 떼가 먹고 있는지라 이에 간구하여 가로되 우리를 돼지에게로 보내어 들어가게 하소서 하니 허락하신대 더러운 귀신들이 나와서 돼지에게로 들어가니 거의 이천 마리 되는 떼가 바다를 향하여 비탈로 내리달아 바다에서 몰사하거늘 하시고, 회당장 중 하나인 야이로라 하는 이가 와서 예수를 보고 발 아래 엎드리어 간곧히 구하여 가로되 내 어린 딸이 죽게 되었사오니 오셔서 그 위에 손을 얹으사 그로 구원을 얻어 살게 하소서 하거늘 이에 그와 함께 가실새 큰 무리가 따라가며 에워싸 밀더라 열두 해를 혈루증으로 앓는 한 여자가 있어 많은 의원에게 많은 괴로움을 받았고 있던 것도 다 허비하였으되 아무 효험이 없고 도리어 더 중하여졌던 차에 예수의 소문을 듣고 무리 가운데 끼어 뒤로 와서 그의 옷에 손을 대니 이는 내가 그의 옷에만 손을 대어도 구원을 얻으리라 생각함일러라 이에 그의 혈루 근원이 곧 마르매 병이 나은 줄을 몸에 깨달으니라.',
    chapter_themes = '["거라사 광인", "내 이름은 군대", "돼지 떼에 들어감", "야이로의 딸", "혈루증 여인", "네 믿음이 너를 구원하였느니라"]',
    chapter_context = '예수님의 능력이 가장 절망적인 상황들에서도 완전한 구원을 이루시며, 믿음의 중요성을 보여주는 세 가지 기적이 연속으로 일어납니다.',
    chapter_application = '어떤 절망적 상황에서도 예수님께 나아가고, 믿음으로 주님께 나아가며, 두려워하지 말고 믿기만 하고, 구원받은 은혜를 증거해야 합니다.'
WHERE book_id = 'mk' AND chapter = 5 AND verse = 1;

-- 마가복음 6장: 고향에서 배척당함과 오병이어 기적
UPDATE bible_verses SET
    chapter_title = '고향에서의 배척과 오병이어 기적, 물 위로 걸으심',
    chapter_summary = '예수께서 거기를 떠나사 고향으로 가시니 제자들도 좇으니라 안식일이 되어 회당에서 가르치시니 많은 사람이 듣고 놀라 가로되 이 사람이 어디서 이런 것을 얻었느뇨 이 사람의 받은 지혜와 그 손으로 이루어지는 이런 권능이 어찌됨이뇨 이 사람이 마리아의 아들 목수가 아니냐 야고보와 요셉과 유다와 시몬의 형제가 아니냐 그 누이들이 우리와 함께 여기 있지 아니하냐 하고 예수를 배척한지라 예수께서 저희에게 이르시되 선지자가 자기 고향과 자기 친족과 자기 집 외에서는 존경을 받지 않음이 없느니라 하시며 거기서는 아무 권능도 행하실 수 없어 다만 소수의 병인에게 안수하여 고치실 뿐이었고 저희의 믿지 않음을 이상히 여기셨더라 이에 모든 촌에 두루 다니시며 가르치시니라 하시고, 열두 제자를 부르사 둘씩 둘씩 보내시며 더러운 귀신을 제어하는 권세를 주시고 명하시되 여행을 위하여 지팡이 외에는 양식이나 주머니나 전대의 돈이나 아무 것도 가지지 말며 신은 신되 두 벌 옷은 입지 말라 하시고 또 가라사대 어느 곳에서든지 뉘 집에 들어가거든 그곳을 떠나기까지 거기 유하라 어느 곳에서든지 너희를 영접지 아니하고 너희 말을 듣지도 아니하거든 거기서 나갈 때에 발 아래 먼지를 떨어버려 저희에게 증거를 삼으라 하시니 제자들이 나가서 회개하라 전파하고 많은 귀신을 쫓아내며 많은 병인에게 기름을 발라 고치더라.',
    chapter_themes = '["고향에서 배척", "선지자가 존경받지 못함", "열두 제자 파송", "오병이어 기적", "물 위로 걸으심", "게네사렛에서의 치유"]',
    chapter_context = '불신앙의 한계와 믿음의 능력, 그리고 제자 파송을 통한 사역 확장과 예수님의 신적 능력이 계속 나타나는 중요한 전환점입니다.',
    chapter_application = '익숙함 때문에 예수님을 무시하지 말고, 단순한 믿음과 순종으로 사명을 감당하며, 주님의 공급하심을 신뢰하고, 물질보다 영적 능력을 의지해야 합니다.'
WHERE book_id = 'mk' AND chapter = 6 AND verse = 1;

-- 마가복음 7장: 전통과 정결에 대한 가르침
UPDATE bible_verses SET
    chapter_title = '전통과 정결에 대한 가르침, 수로보니게 여인',
    chapter_summary = '바리새인들과 또 서기관 중 몇이 예루살렘에서 와서 예수께 모였다가 그의 제자 중 몇 사람의 부정한 손 곧 씻지 아니한 손으로 떡 먹는 것을 보고 (바리새인들과 모든 유대인들이 장로들의 유전을 지키어 손을 부지런히 씻지 않으면 먹지 아니하며 또 시장에서 돌아와서는 물을 뿌리지 않으면 먹지 아니하고 그 외에도 여러 가지를 지키어 오는 것이 있으니 잔과 주발과 놋그릇을 씻음이러라) 이에 바리새인들과 서기관들이 예수께 묻되 어찌하여 당신의 제자들은 장로들의 유전을 준행치 아니하고 부정한 손으로 떡을 먹나이까 가라사대 이사야가 너희 외식하는 자에 대하여 잘 예언하였도다 기록하였으되 이 백성이 입술로는 나를 존경하되 마음은 내게서 멀도다 사람의 계명으로 교훈을 삼아 가르치니 나를 헛되이 경배하는도다 하였느니라 너희가 하나님의 계명은 버리고 사람의 유전을 지키느니라 하시고, 또 가라사대 너희가 너희 유전을 지키려고 하나님의 계명을 잘 저버리는도다 모세는 네 부모를 공경하라 하고 또 아비나 어미를 훼방하는 자는 반드시 죽으리라 하였거늘 너희는 가로되 사람이 아비에게나 어미에게 말하기를 내가 드릴 것이 고르반 곧 하나님께 드림이 되었다고 하기만 하면 그 후에는 아비나 어미에게 아무것이라도 하여 드리기를 허하지 아니하여 너희의 전한 유전으로 하나님의 말씀을 폐하며 또 이같은 일을 많이 행하느니라 하시고 무리를 다시 불러 이르시되 너희는 다 내 말을 듣고 깨달으라 무엇이든지 밖에서 사람에게로 들어가는 것은 능히 사람을 더럽게 하지 못하되 사람 안에서 나오는 것이 사람을 더럽게 하는 것이니라 하시고.',
    chapter_themes = '["장로들의 유전", "사람의 계명으로 교훈", "고르반", "밖에서 들어가는 것", "마음에서 나오는 것", "수로보니게 여인의 믿음"]',
    chapter_context = '외식적 종교와 참된 신앙의 차이를 명확히 구분하시고, 이방인에게도 복음이 확장됨을 보여주시는 중요한 교훈입니다.',
    chapter_application = '형식적 종교보다 마음의 정결을 추구하고, 사람의 전통보다 하나님의 말씀을 우선시하며, 겸손한 믿음으로 은혜를 구해야 합니다.'
WHERE book_id = 'mk' AND chapter = 7 AND verse = 1;

-- 마가복음 8장: 사천 명을 먹이심과 베드로의 신앙고백
UPDATE bible_verses SET
    chapter_title = '사천 명을 먹이심과 베드로의 신앙고백, 첫 번째 수난예고',
    chapter_summary = '그 즈음에 또 큰 무리가 있어 먹을 것이 없는지라 예수께서 제자들을 불러 이르시되 내가 무리를 불쌍히 여기노라 저희가 나와 함께 있은 지 이미 사흘이매 먹을 것이 없도다 만일 내가 저희를 굶겨 집으로 보내면 길에서 기진하리라 그 중에는 멀리서 온 사람도 있느니라 제자들이 대답하되 이 광야에서 어디서 떡을 얻어 이 사람들로 배부르게 할 수 있으리이까 예수께서 물으시되 너희에게 떡 몇 개나 있느냐 가로되 일곱 개로소이다 하거늘 예수께서 무리를 명하여 땅에 앉게 하시고 떡 일곱 개를 가지사 축사하시고 떼어 제자들에게 주어 나누게 하시니 제자들이 무리에게 나누어 주더라 또 작은 생선 몇 마리가 있는지라 이에 축사하시고 명하사 이것도 나누게 하시니 배불리 먹고 남은 조각 일곱 광주리를 거두었으며 사람은 약 사천 명이었더라 하시고, 예수와 제자들이 빌립보 가이사랴 여러 마을로 나가실새 노중에서 제자들에게 물어 가라사대 사람들이 나를 누구라 하느냐 여짜와 가로되 세례 요한이라 하고 더러는 엘리야, 더러는 선지자 중의 하나라 하나이다 또 물으시되 너희는 나를 누구라 하느냐 베드로가 대답하여 가로되 주는 그리스도시니이다 하매 이에 자기의 일을 아무에게라도 말하지 말라 경계하시고 인자가 많은 고난을 받고 장로들과 대제사장들과 서기관들에게 버린바 되어 죽임을 당하고 사흘 만에 살아나야 할 것을 비로소 저희에게 가르치시되 드러내놓고 이 말씀을 하시니 베드로가 예수를 붙들고 간하매 예수께서 돌이키사 제자들을 보시며 베드로를 꾸짖어 가라사대 사단아 내 뒤로 물러가라 네가 하나님의 일을 생각지 아니하고 도리어 사람의 일을 생각하는도다 하시고.',
    chapter_themes = '["사천 명을 먹이심", "바리새인들의 표적 요구", "주는 그리스도시니이다", "첫 번째 수난예고", "사단아 내 뒤로 물러가라", "자기 십자가를 지고"]',
    chapter_context = '예수님의 메시야 정체성이 확증되는 동시에 고난받는 메시야의 길이 명확해지며, 참된 제자도의 의미가 제시되는 중요한 전환점입니다.',
    chapter_application = '예수님을 그리스도로 고백하고, 고난의 필요성을 이해하며, 자기 십자가를 지고 주님을 따르며, 하나님의 일을 우선순위로 생각해야 합니다.'
WHERE book_id = 'mk' AND chapter = 8 AND verse = 1;

-- 마가복음 9장: 변화산과 귀신 들린 아이 치유
UPDATE bible_verses SET
    chapter_title = '변화산의 영광과 믿음 없는 세대, 두 번째 수난예고',
    chapter_summary = '또 저희에게 이르시되 내가 진실로 너희에게 이르노니 여기 섰는 사람 중에 죽기 전에 하나님의 나라가 권능으로 임하는 것을 볼 자들도 있느니라 하시니라 엿새 후에 예수께서 베드로와 야고보와 요한을 데리시고 따로 높은 산에 올라가셨더니 저희 앞에서 변형되사 그 옷이 광채가 나며 세상에서 빨래하는 자가 그렇게 희게 할 수 없을 만큼 심히 희어졌더라 이에 엘리야가 모세와 함께 저희에게 나타나 예수로 더불어 말씀하거늘 베드로가 예수께 고하되 랍비여 우리가 여기 있는 것이 좋사오니 우리가 초막 셋을 짓되 하나는 주를 위하여, 하나는 모세를 위하여, 하나는 엘리야를 위하여 하사이다 하니 이는 저희가 심히 무서워하므로 그가 무슨 말을 할는지 알지 못함이더라 마침 구름이 와서 저희를 덮으며 구름 속에서 소리가 나되 이는 내 사랑하는 아들이니 너희는 저의 말을 들으라 하는지라 문득 둘러보니 아무도 보이지 아니하고 오직 예수와 자기들뿐이었더라 하시고, 저희가 산에서 내려올 때에 예수께서 경계하시되 인자가 죽은 자 가운데서 살아날 때까지는 본 것을 아무에게라도 이르지 말라 하시니 저희가 이 말씀을 마음에 두며 서로 문의하되 죽은 자 가운데서 살아나는 것이 무엇일까 하고 이에 예수께 묻자와 가로되 어찌하여 서기관들이 엘리야가 먼저 와야 하리라 하나이까 가라사대 엘리야가 과연 먼저 와서 모든 것을 회복하거니와 어찌 인자에 대하여 많은 고난을 받고 멸시를 당하리라 기록되었느냐 그러나 내가 너희에게 이르노니 엘리야가 이미 왔으되 기록된 바와 같이 사람들이 임의로 대우하였느니라 하시니라.',
    chapter_themes = '["변화산 영광", "이는 내 사랑하는 아들", "믿지 못하는 세대", "믿는 자에게는 능치 못할 일이 없느니라", "기도 외에 다른 것으로는", "두 번째 수난예고"]',
    chapter_context = '예수님의 신성이 영광으로 드러나는 동시에 제자들의 믿음 부족과 고난의 길이 더욱 명확해지는 대조적인 상황들이 나타납니다.',
    chapter_application = '예수님의 신성을 믿고 그의 말씀을 들으며, 믿음 없음을 회개하고, 기도로 영적 능력을 구하며, 섬기는 자가 되어야 합니다.'
WHERE book_id = 'mk' AND chapter = 9 AND verse = 1;

-- 마가복음 10장: 부자 청년과 예루살렘으로 올라가심
UPDATE bible_verses SET
    chapter_title = '결혼과 이혼, 부자 청년, 세 번째 수난예고',
    chapter_summary = '예수께서 거기서 떠나 유대 지경과 요단강 건너편으로 가시니 무리가 다시 모여들거늘 예수께서 다시 전례대로 가르치시더니 바리새인들이 나아와 그를 시험하여 묻되 사람이 아내를 내어버리는 것이 옳으니이까 대답하여 가라사대 모세가 어떻게 너희에게 명하였느냐 가로되 모세는 이혼증서를 써 주어 내어버리기를 허락하였나이다 예수께서 저희에게 이르시되 너희 마음의 완악함을 인하여 이 명령을 기록하였거니와 창조시로부터 저희를 남자와 여자로 만드셨으니 이러므로 사람이 그 부모를 떠나서 그 둘이 한 몸이 될지니라 이러한즉 이제 둘이 아니요 한 몸이니 그러므로 하나님이 짝지어 주신 것을 사람이 나누지 못할지니라 하시더라 하시고, 사람들이 예수의 만지심을 바라고 어린 아이들을 데리고 오매 제자들이 꾸짖거늘 예수께서 보시고 분히 여겨 이르시되 어린 아이들의 내게 오는 것을 용납하고 금하지 말라 하나님의 나라가 이런 자의 것이니라 내가 진실로 너희에게 이르노니 누구든지 하나님의 나라를 어린 아이와 같이 받들지 않는 자는 결단코 들어가지 못하리라 하시고 그 어린 아이들을 안고 저희 위에 안수하시고 축복하시니라 하시고, 예수께서 길에 나가실새 한 사람이 달려와서 꿇어 앉아 묻자오되 선한 선생님이여 내가 무엇을 하여야 영생을 얻으리이까 예수께서 이르시되 네가 어찌하여 나를 선하다 일컫느냐 하나님 한 분 외에는 선한 이가 없느니라 네가 계명을 아나니 살인하지 말라, 간음하지 말라, 도적질하지 말라, 거짓 증거하지 말라, 속여 취하지 말라, 네 부모를 공경하라 하였느니라 여짜오되 선생님이여 이것은 내가 어려서부터 다 지켰나이다 예수께서 그를 보시고 사랑하사 가라사대 네게 오히려 한 가지 부족한 것이 있으니 가서 네게 있는 것을 다 팔아 가난한 자들을 주라 그리하면 하늘에서 보화가 네게 있으리라 그리고 와서 나를 좇으라 하시니 그 사람은 재물이 많은고로 이 말씀을 인하여 슬픈 기색을 띠고 근심하며 가니라.',
    chapter_themes = '["하나님이 짝지어 주신 것", "어린 아이와 같이", "부자 청년", "한 가지 부족한 것", "낙타가 바늘귀로", "섬기는 자가 되라"]',
    chapter_context = '결혼의 신성함, 하나님 나라의 자격, 영생의 조건, 그리고 참된 리더십에 대한 예수님의 가르침이 연속으로 나타나는 교훈입니다.',
    chapter_application = '결혼을 신성하게 여기고, 어린아이 같은 순수함을 가지며, 재물보다 하나님을 우선순위로 삼고, 섬기는 리더십을 실천해야 합니다.'
WHERE book_id = 'mk' AND chapter = 10 AND verse = 1;

-- 마가복음 11장: 예루살렘 입성과 성전 정화
UPDATE bible_verses SET
    chapter_title = '나귀 타신 입성과 무화과나무 저주, 성전 정화',
    chapter_summary = '저희가 예루살렘에 가까이 와서 감람산 벳바게와 베다니에 이르렀을 때에 예수께서 제자 중 둘을 보내시며 이르시되 너희 맞은편 마을로 가라 그리로 들어가면 곧 아직 아무 사람도 타보지 않은 나귀 새끼의 매인 것을 보리니 풀어 끌고 오너라 만일 누가 너희에게 어찌하여 이렇게 하느냐 묻거든 주가 쓰시겠다 하라 그리하면 즉시 이리로 보내리라 하시니 제자들이 가서 나귀 새끼를 문 앞 거리에 매인 것을 보고 그것을 푸니 거기 섰는 사람 중 어떤이들이 가로되 나귀 새끼를 풀어 무엇하려느냐 하매 제자들이 예수의 이르신 대로 말하니 이에 허락하는지라 나귀 새끼를 예수께로 끌고 와서 자기들의 겉옷을 그 위에 얹어 놓으매 예수께서 타시니 많은 사람은 자기의 겉옷을, 또 다른 이들은 들에서 벤 나뭇가지를 길에 펴며 앞에서 가고 뒤에서 따르는 자들이 소리지르되 호산나 찬송하리로다 주의 이름으로 오시는 이여 찬송하리로다 오는 우리 조상 다윗의 나라여 가장 높은 곳에서 호산나 하더라 하시고, 예수께서 예루살렘에 이르러 성전에 들어가사 모든 것을 둘러 보시고 때가 이미 저물매 열두 제자를 데리시고 베다니에 나가시니라 이튿날 저희가 베다니에서 나왔을 때에 예수께서 시장하신지라 멀리서 잎사귀 있는 한 무화과나무를 보시고 혹 그 나무에 무엇이 있을까 하여 가셨더니 가서 보신즉 잎사귀 외에 아무것도 없더라 이는 무화과의 때가 아님이라 예수께서 나무에게 일러 가라사대 이제부터 영원토록 사람이 네게서 열매를 따먹지 못하리라 하시니 제자들이 이를 듣더라.',
    chapter_themes = '["나귀 새끼를 타심", "호산나 주의 이름으로", "무화과나무 저주", "성전 정화", "기도하는 집", "믿고 구하는 모든 것"]',
    chapter_context = '예수님이 왕으로 예루살렘에 입성하시고 성전을 정화하시며, 종교적 형식주의를 심판하시는 메시야 사역의 절정입니다.',
    chapter_application = '예수님을 왕으로 모시고 호산나를 외치며, 형식적 종교를 버리고 참된 예배를 드리며, 믿음으로 기도하고 용서하는 마음을 가져야 합니다.'
WHERE book_id = 'mk' AND chapter = 11 AND verse = 1;

-- 마가복음 12장: 악한 농부들과 가장 큰 계명
UPDATE bible_verses SET
    chapter_title = '악한 농부들의 비유와 가장 큰 계명',
    chapter_summary = '예수께서 비유로 저희에게 말씀하시되 한 사람이 포도원을 만들고 산울로 두르고 즙 짜는 구유를 파고 망대를 지었더니 농부들에게 세로 주고 타국에 갔더니 때가 이르매 농부들에게 포도원 소출 중에서 얻을 것을 받으려고 한 종을 보내니 저희가 종을 잡아 심히 때리고 거저 보내었거늘 다시 다른 종을 보내니 그의 머리에 상처를 내고 능욕하였거늘 또 다른 종을 보내니 저희가 그를 죽이고 또 그외 많은 종들도 혹은 때리고 혹은 죽인지라 이제 한 사람이 남았으니 곧 그의 사랑하는 아들이라 최후로 이를 보내며 가로되 내 아들은 공경하리라 하였더니 저 농부들이 서로 말하되 이는 상속자니 자 죽이자 그러면 그 유업이 우리 것이 되리라 하고 이에 잡아 죽여 포도원 밖에 내어던졌느니라 포도원 주인이 어떻게 하겠느뇨 와서 그 농부들을 진멸하고 포도원을 다른 사람들에게 주리라 너희가 성경에 건축자들의 버린 돌이 모퉁이의 머릿돌이 되었나니 이것은 주로 말미암아 된 것이요 우리 눈에 기이하도다 함을 읽어보지도 못하였느냐 하시니라 하시고, 서기관 중 하나가 저희의 변론하는 것을 듣고 예수께서 대답 잘하신 줄을 알고 나아와 묻되 모든 계명 중에 첫째가 무엇이니이까 예수께서 대답하시되 첫째는 이것이니 이스라엘아 들으라 주 곧 우리 하나님은 유일한 주시라 네 마음을 다하고 목숨을 다하고 뜻을 다하고 힘을 다하여 주 너의 하나님을 사랑하라 하신 것이요 둘째는 이것이니 네 이웃을 네 몸과 같이 사랑하라 하신 것이라 이에서 더 큰 계명이 없느니라 서기관이 가로되 선생님이여 옳소이다 하나님은 한 분이시요 그 외에 다른 이가 없다 하신 말씀이 참이니이다 또 마음을 다하고 지혜를 다하고 힘을 다하여 하나님을 사랑하는 것과 또 이웃을 제 몸과 같이 사랑하는 것이 전체로 드리는 모든 번제물과 기타 제물보다 나으니이다 예수께서 그의 지혜 있게 대답함을 보시고 이르시되 네가 하나님의 나라에서 멀지 않도다 하시니 그 후에 감히 묻는 자가 없더라.',
    chapter_themes = '["악한 농부들 비유", "사랑하는 아들", "건축자들이 버린 돌", "가장 큰 계명", "하나님 사랑과 이웃 사랑", "하나님 나라에서 멀지 않도다"]',
    chapter_context = '이스라엘의 불순종에 대한 심판과 하나님 나라의 진정한 가치가 무엇인지를 명확히 제시하시는 예수님의 가르침입니다.',
    chapter_application = '하나님의 보내신 아들을 영접하고, 건축자들이 버린 돌이신 예수님을 모퉁이 돌로 모시며, 하나님과 이웃을 진심으로 사랑해야 합니다.'
WHERE book_id = 'mk' AND chapter = 12 AND verse = 1;

-- 마가복음 13장: 종말에 대한 예언
UPDATE bible_verses SET
    chapter_title = '성전 파괴와 종말의 징조들',
    chapter_summary = '예수께서 성전에서 나가실 때에 제자 중 하나가 가로되 선생님이여 보소서 이 돌들이 어떠하며 이 건물들이 어떠하니이까 예수께서 이르시되 네가 이 큰 건물들을 보느냐 돌 하나도 돌 위에 남지 않고 다 무너뜨리우리라 하시니라 예수께서 감람산에서 성전을 마주 대하여 앉으셨을 때에 베드로와 야고보와 요한과 안드레가 종용히 묻자오되 우리에게 이르소서 어느 때에 이런 일이 있겠사오며 또 이 모든 일이 이루려 할 때에 무슨 징조가 있사오리이까 예수께서 이르시되 아무나 너희를 미혹하지 못하게 하라 많은 사람이 내 이름으로 와서 가로되 내가 그로라 하여 많은 사람을 미혹케 하리라 난리와 난리 소문을 들을 때에 두려워 말라 이런 일이 있어야 하되 끝은 아직 아니니라 민족이 민족을, 나라가 나라를 대적하여 일어나겠고 처처에 지진이 있으며 기근이 있으리니 이런 일들이 재난의 시작이니라 하시고, 너희는 스스로 조심하라 사람들이 너희를 공회에 넘겨주겠고 너희를 회당에서 매질하겠으며 나를 인하여 너희가 관장들과 임금들 앞에 서리니 이는 저희에게 증거가 되려 함이라 또 복음이 먼저 만국에 전파되어야 하리라 사람들이 너희를 끌어다가 넘겨줄 때에 무엇을 말할까 미리 염려치 말고 무엇이든지 그 시에 너희에게 주시는 그 말을 하라 말하는 이는 너희가 아니요 성령이시니라.',
    chapter_themes = '["성전 파괴 예언", "아무나 미혹하지 못하게", "재난의 시작", "복음이 먼저 만국에", "성령이 말하게 하심", "깨어 있으라"]',
    chapter_context = '예루살렘 멸망과 종말에 대한 예언을 통해 제자들이 역사의 흐름 속에서 어떻게 살아야 할지를 가르치시는 중요한 교훈입니다.',
    chapter_application = '거짓 선지자들을 분별하고, 환난 중에도 복음을 증거하며, 성령의 도우심을 의지하고, 항상 깨어 있어 주님의 재림을 준비해야 합니다.'
WHERE book_id = 'mk' AND chapter = 13 AND verse = 1;

-- 마가복음 14장: 최후의 만찬과 겟세마네, 체포
UPDATE bible_verses SET
    chapter_title = '베다니 향유와 최후의 만찬, 겟세마네 기도와 체포',
    chapter_summary = '이틀이 지나면 유월절과 무교절이라 대제사장들과 서기관들이 예수를 궤계로 잡아 죽일 방책을 구하되 가로되 민요가 날까 하노니 명절에는 하지 말자 하더라 예수께서 베다니 문둥이 시몬의 집에서 식사하실 때에 한 여자가 매우 값진 향유 곧 순전한 나드 한 옥합을 가지고 와서 그 옥합을 깨뜨리고 예수의 머리에 부으니 어떤 사람들이 분내어 서로 말하되 어찌하여 이 향유를 허비하느뇨 이 향유를 삼백 데나리온 이상에 팔아 가난한 자들에게 줄 수 있었겠도다 하며 그 여자를 책망하는지라 예수께서 가라사대 가만 두어라 너희가 어찌하여 저를 괴롭게 하느냐 저가 내게 좋은 일을 하였느니라 가난한 자들은 항상 너희와 함께 있으니 아무 때라도 원하는 대로 도울 수 있거니와 나는 너희와 항상 함께 있지 아니하리라 저가 힘을 다하여 내 몸에 향유를 부어 내 장사를 미리 준비하였느니라 내가 진실로 너희에게 이르노니 온 천하에 어디서든지 복음이 전파되는 곳에는 이 여자의 행한 일도 말하여 저를 기념하리라 하시니라 하시고, 열둘 중 하나인 가룟 유다가 예수를 넘겨 주려고 대제사장들에게 가니 저희가 듣고 기뻐하여 돈을 주기로 약속하므로 유다가 예수를 어떻게 넘겨줄 기회를 찾더라 무교절의 첫날 곧 유월절 양 잡는 날에 제자들이 예수께 여짜오되 우리가 어디로 가서 선생님으로 유월절을 잡수시게 준비하기를 원하시나이까.',
    chapter_themes = '["베다니 향유", "내 장사를 미리 준비", "가룟 유다의 배반", "최후의 만찬", "겟세마네 기도", "칼을 든 무리"]',
    chapter_context = '예수님의 죽음을 앞두고 일어나는 사건들로 헌신과 배반, 사랑과 고난이 대조적으로 나타나며 수난이 본격적으로 시작됩니다.',
    chapter_application = '베다니 여인처럼 최선을 다해 주님께 헌신하고, 배반하지 말고 끝까지 충성하며, 성찬의 의미를 깊이 새기고, 깨어 기도해야 합니다.'
WHERE book_id = 'mk' AND chapter = 14 AND verse = 1;

-- 마가복음 15장: 빌라도 재판과 십자가 처형
UPDATE bible_verses SET
    chapter_title = '빌라도 재판과 십자가 처형',
    chapter_summary = '새벽에 대제사장들이 즉시 장로들과 서기관들 및 온 공회로 더불어 의논하고 예수를 결박하여 끌고 가서 빌라도에게 넘겨주니 빌라도가 묻되 네가 유대인의 왕이냐 예수께서 대답하여 가라사대 네 말이 옳도다 하시매 대제사장들이 여러 가지로 고소하는지라 빌라도가 또 물어 가로되 아무 대답도 없느냐 저희가 얼마나 많은 것으로 너를 고소하는가 보라 하되 예수께서 다시 아무 말씀으로도 대답지 아니하시니 빌라도가 기이히 여기더라 명절을 당하면 백성의 요구하는 대로 죄수 하나를 놓아 주는 전례가 있더니 민란을 꾸미고 그 민란중에 살인하고 포박된 자 중에 바라바라 하는 자가 있는지라 무리가 나아가서 전례대로 하여 주기를 요구한대 빌라도가 대답하여 가로되 너희는 내가 유대인의 왕을 너희에게 놓아 주기를 원하느냐 하니 이는 저가 대제사장들이 시기로 예수를 넘겨준 줄 앎이러라 그러나 대제사장들이 무리를 충동하여 도리어 바라바를 놓아 달라 하게 하니 빌라도가 또 대답하여 가로되 그러면 너희의 말하는 유대인의 왕을 내가 어떻게 하랴 저희가 다시 소리지르되 십자가에 못 박게 하소서 빌라도가 가로되 어찜이뇨 무슨 악한 일을 하였느냐 하니 더욱 소리지르되 십자가에 못 박게 하소서 하는지라 하시고, 제삼시에 예수를 십자가에 못 박고 그 위에 있는 죄패에 유대인의 왕이라 썼고 강도 둘을 예수와 함께 십자가에 못 박으니 하나는 그의 우편에, 하나는 좌편에 있더라 지나가는 자들은 자기 머리를 흔들며 예수를 모욕하여 가로되 아하 성전을 헐고 사흘에 짓는다는 자여 십자가에서 내려와 자기를 구원하라 하고 그와 같이 대제사장들도 서기관들과 함께 희롱하며 서로 말하되 저가 남은 구원하였으되 자기는 구원할 수 없도다 이스라엘의 왕 그리스도가 지금 십자가에서 내려와 우리로 보고 믿게 할지어다 하며 함께 십자가에 못 박힌 자들도 예수를 욕하더라.',
    chapter_themes = '["빌라도 재판", "바라바를 놓아 달라", "십자가에 못 박게 하소서", "제삼시에 못 박고", "나의 하나님 어찌하여", "아리마대 요셉"]',
    chapter_context = '무죄하신 예수님이 죄인들의 죄를 대신 지시고 십자가에서 죽으심으로 구속 사역을 완성하시는 결정적 사건입니다.',
    chapter_application = '예수님의 대속적 죽음을 믿고 감사하며, 죄의 심각성을 깨닫고, 구원의 은혜에 합당한 삶을 살며, 부활의 소망을 가져야 합니다.'
WHERE book_id = 'mk' AND chapter = 15 AND verse = 1;

-- 마가복음 16장: 부활과 승천, 대위임령
UPDATE bible_verses SET
    chapter_title = '부활하신 주님과 승천, 복음 전파 명령',
    chapter_summary = '안식일이 지나매 막달라 마리아와 야고보의 어머니 마리아와 또 살로메가 가서 예수께 바르기 위하여 향품을 사다 두었다가 안식 후 첫날 매우 일찍이 해 돋을 때에 그 무덤으로 가며 서로 말하되 누가 우리를 위하여 무덤 문에서 돌을 굴려 주리요 하더니 눈을 들어본즉 벌써 돌이 굴려져 있는데 그 돌이 심히 크더라 무덤에 들어가서 흰 옷을 입은 한 청년이 우편에 앉은 것을 보고 놀라매 청년이 이르되 놀라지 말라 너희가 십자가에 못 박히신 나사렛 예수를 찾는구나 그가 살아나셨고 여기 계시지 아니하니라 보라 그를 두었던 곳이니라 가서 그의 제자들과 베드로에게 이르기를 예수께서 너희보다 먼저 갈릴리로 가시나니 전에 너희에게 말씀하신 대로 너희가 거기서 뵈오리라 하라 하니 여자들이 나와서 무덤에서 도망하니 이는 떨림과 놀라움이 저희를 사로잡았음이라 아무에게 아무 말도 하지 못하더라 두려워함이러라 하시고, 예수께서 안식 후 첫날 이른 아침에 살아나신 후 전에 일곱 귀신을 쫓아내어 주신 막달라 마리아에게 먼저 보이시니 마리아가 가서 예수와 함께 하던 사람들의 슬퍼하며 울고 있는 중에 이 일을 고하매 저희는 예수의 살으셨다는 것과 마리아에게 보이셨다는 것을 듣고도 믿지 아니하니라 하시고, 그 후에 저희가 식사할 때에 열한 제자에게 나타나사 저희의 믿음 없는 것과 마음이 완악한 것을 꾸짖으시니 이는 자기의 살아난 것을 본 자들의 말을 믿지 아니함이러라 또 가라사대 너희는 온 천하에 다니며 만민에게 복음을 전파하라 믿고 세례를 받는 자는 구원을 얻을 것이요 믿지 않는 자는 정죄를 받으리라 믿는 자들에게는 이런 표적이 따르리니 곧 저희가 내 이름으로 귀신을 쫓아내며 새 방언을 말하며 뱀을 집으며 무엇이든지 독을 마실지라도 해를 받지 아니하며 병든 사람에게 손을 얹은즉 나으리라 하시더라 주 예수께서 말씀을 마치신 후에 하늘로 올리우사 하나님 우편에 앉으시니라 제자들이 나가 두루 전파할새 주께서 함께 역사하사 그 따르는 표적으로 말씀을 확실히 증거하시니라.',
    chapter_themes = '["빈 무덤", "그가 살아나셨고", "먼저 갈릴리로", "온 천하에 다니며", "만민에게 복음을", "하나님 우편에 앉으시니라"]',
    chapter_context = '예수님의 부활이 확증되고 제자들에게 세계 복음화의 대명령이 주어지며, 승천으로 마가복음이 대단원의 막을 내립니다.',
    chapter_application = '부활의 증인으로 살며, 온 세상에 복음을 전하고, 믿고 세례받아 구원을 얻으며, 주님의 동행하심을 확신하고 대위임령을 실천해야 합니다.'
WHERE book_id = 'mk' AND chapter = 16 AND verse = 1;