-- 마태복음 주석 Part 2 (15-28장)
-- 예수님의 후반기 사역, 예루살렘 입성, 십자가와 부활

-- 마태복음 15장: 전통과 진정한 정결, 가나안 여자의 믿음
UPDATE bible_verses SET
    chapter_title = '전통과 정결에 대한 가르침, 가나안 여자의 믿음',
    chapter_summary = '그 때에 바리새인과 서기관들이 예루살렘으로부터 예수께 나아와 가로되 당신의 제자들이 어찌하여 장로들의 유전을 범하나이까 떡 먹을 때에 손을 씻지 아니하나이다 하니 대답하여 가라사대 너희는 어찌하여 너희 유전으로 하나님의 계명을 범하느냐 하시며 입에 들어가는 것이 사람을 더럽게 하는 것이 아니라 입에서 나오는 그것이 사람을 더럽게 하는 것이니라 하시고, 두로와 시돈 지방으로 떠나가시니 가나안 족속의 한 여자가 그 지경에서 나와서 소리질러 가로되 주 다윗의 자손이여 나를 불쌍히 여기소서 내 딸이 귀신들려 심히 고생하나이다 하되 예수는 한 말씀도 대답지 아니하시니 제자들이 와서 청하여 가로되 그 여자가 우리 뒤에서 소리지르오니 보내소서 하나 예수께서 대답하여 가라사대 나는 이스라엘 집의 잃어버린 양 외에는 다른 데로 보내심을 받지 아니하였노라 하시니 여자가 와서 예수께 절하며 가로되 주여 저를 도우소서 하니 대답하여 가라사대 자녀의 떡을 취하여 개들에게 던짐이 마땅치 아니하니라 하시되 여자가 가로되 주여 옳소이다마는 개들도 제 주인의 상에서 떨어지는 부스러기를 먹나이다 하니 이에 예수께서 대답하여 가라사대 여자야 네 믿음이 크도다 네 소원대로 되라 하시니 그 시로부터 그의 딸이 나으니라.',
    chapter_themes = '["장로들의 유전", "입에서 나오는 것", "가나안 여자", "다윗의 자손", "네 믿음이 크도다", "칠병이어 기적"]',
    chapter_context = '외식적 전통을 비판하시고 참된 정결의 의미를 가르치시며, 이방인에게도 확장되는 하나님의 은혜를 보여주시는 말씀입니다.',
    chapter_application = '형식적 종교보다 마음의 정결을 추구하고, 가나안 여자처럼 끈질긴 믿음을 가지며, 하나님의 은혜가 모든 족속에게 미침을 믿어야 합니다.'
WHERE book_id = 'mt' AND chapter = 15 AND verse = 1;

-- 마태복음 16장: 베드로의 신앙고백과 첫 번째 수난예고
UPDATE bible_verses SET
    chapter_title = '베드로의 신앙고백과 교회 건설, 수난 예고',
    chapter_summary = '바리새인과 사두개인들이 예수를 시험하여 하늘로서 오는 표적 보이기를 청하거늘 대답하여 가라사대 너희가 저녁에 하늘이 붉으면 날이 좋겠다 하고 새벽에 하늘이 붉고 흐리면 오늘은 날이 궂겠다 하나니 너희가 천기는 분별할 줄 알면서 시대의 표적은 분별할 수 없느냐 악하고 음란한 세대가 표적을 구하나 요나의 표적 밖에는 보여줄 표적이 없느니라 하시고 떠나가시니라 하시고, 가이사랴 빌립보 지방에 이르러 제자들에게 물어 가라사대 사람들이 인자를 누구라 하느냐 하시니 가로되 더러는 세례 요한, 더러는 엘리야, 어떤 이는 예레미야나 선지자 중의 하나라 하나이다 하거늘 가라사대 너희는 나를 누구라 하느냐 하시니 시몬 베드로가 대답하여 가로되 주는 그리스도시요 살아계신 하나님의 아들이시니이다 하니 예수께서 대답하여 가라사대 바요나 시몬아 네가 복이 있도다 이를 네게 알게 한 이는 혈육이 아니요 하늘에 계신 내 아버지시니라 또 내가 네게 이르노니 너는 베드로라 내가 이 반석 위에 내 교회를 세우리니 음부의 권세가 이기지 못하리라 하시고 이때로부터 예수 그리스도께서 자기가 예루살렘에 올라가 장로들과 대제사장들과 서기관들에게 많은 고난을 받고 죽임을 당하고 제삼일에 살아나야 할 것을 제자들에게 비로소 나타내시니.',
    chapter_themes = '["시대의 표적", "가이사랴 빌립보", "주는 그리스도시요", "이 반석 위에 교회", "음부의 권세", "첫 번째 수난예고"]',
    chapter_context = '예수님의 메시야 되심이 베드로의 고백을 통해 확증되고, 교회의 기초가 놓이며, 수난에 대한 예고가 시작되는 전환점입니다.',
    chapter_application = '예수님을 그리스도요 하나님의 아들로 고백하고, 교회의 사명을 감당하며, 수난의 필요성을 이해하고 십자가를 지는 제자도를 실천해야 합니다.'
WHERE book_id = 'mt' AND chapter = 16 AND verse = 1;

-- 마태복음 17장: 변화산 사건과 간질병 아이 치유
UPDATE bible_verses SET
    chapter_title = '변화산의 영광과 간질병 아이 치유',
    chapter_summary = '엿새 후에 예수께서 베드로와 야고보와 그 형제 요한을 데리시고 따로 높은 산에 올라가사 저희 앞에서 변형되사 그 얼굴이 해같이 빛나며 옷이 빛과 같이 희어졌고 때에 모세와 엘리야가 예수로 더불어 말씀하는 것이 저희에게 보이거늘 베드로가 예수께 여짜와 가로되 주여 우리가 여기 있는 것이 좋사오니 주께서 만일 원하시면 내가 여기서 초막 셋을 짓되 하나는 주를 위하여, 하나는 모세를 위하여, 하나는 엘리야를 위하여 하리이다 하는 말 할 때에 홀연히 빛난 구름이 저희를 덮으며 구름 속에서 소리가 나서 가로되 이는 내 사랑하는 아들이요 내 기뻐하는 자니 너희는 저의 말을 들으라 하시는지라 제자들이 듣고 엎드리어 심히 두려워하거늘 예수께서 나아와 저희에게 손을 대시며 가라사대 일어나라 두려워 말라 하시니라 하시고, 산에서 내려와 간질병으로 고생하는 아이를 고치시며 제자들이 능히 고치지 못한 이유는 믿음이 적은 연고라 하시고 겨자씨 한 알만한 믿음이 있으면 이 산을 명하여 여기서 저기로 옮기라 하여도 옮길 것이요 너희가 못할 것이 없으리라 하십니다.',
    chapter_themes = '["변화산 사건", "모세와 엘리야", "이는 내 사랑하는 아들", "간질병 아이", "믿음이 적은 연고", "겨자씨만한 믿음"]',
    chapter_context = '예수님의 신성이 영광스럽게 드러나고 하나님의 음성이 증거하시며, 믿음의 능력에 대한 가르침이 주어지는 중요한 계시 사건입니다.',
    chapter_application = '예수님의 신성을 믿고 그의 말씀에 순종하며, 작은 믿음이라도 하나님께 불가능이 없음을 확신하고, 기도와 금식으로 영적 능력을 추구해야 합니다.'
WHERE book_id = 'mt' AND chapter = 17 AND verse = 1;

-- 마태복음 18장: 천국에서 큰 자와 용서에 대한 교훈
UPDATE bible_verses SET
    chapter_title = '천국에서 큰 자와 무한 용서의 교훈',
    chapter_summary = '그 때에 제자들이 예수께 나아와 가로되 천국에서는 누가 크니이까 하거늘 예수께서 한 어린 아이를 불러 저희 가운데 세우시고 가라사대 진실로 너희에게 이르노니 너희가 돌이켜 어린 아이들과 같이 되지 아니하면 결단코 천국에 들어가지 못하리라 그러므로 누구든지 이 어린 아이와 같이 자기를 낮추는 그이가 천국에서 큰 자니라 하시고, 만일 네 형제가 죄를 범하거든 가서 너와 그 사람과만 상대하여 권고하라 만일 들으면 네가 네 형제를 얻은 것이요 하시며 교회에 말하라 하시고, 그 때에 베드로가 나아와 가로되 주여 형제가 내게 죄를 범하면 몇 번이나 용서하여 주리이까 일곱 번까지 하리이까 하거늘 예수께서 가로되 네게 이르노니 일곱 번뿐 아니라 일흔 번씩 일곱 번이라도 할지니라 하시고 만 달란트 빚진 종의 비유를 들어 용서받은 자가 다른 사람을 용서해야 함을 가르치시며 너희가 각각 중심으로 형제를 용서하지 아니하면 나의 천부께서도 너희에게 이와 같이 하시리라 하십니다.',
    chapter_themes = '["천국에서 큰 자", "어린 아이와 같이", "교회 권징", "일흔 번씩 일곱 번", "만 달란트 종 비유", "중심으로 용서"]',
    chapter_context = '하나님 나라의 가치관과 공동체 내에서의 관계 회복, 그리고 무한 용서에 대한 예수님의 핵심 가르침입니다.',
    chapter_application = '어린아이 같은 겸손한 마음을 가지고, 죄 지은 형제를 사랑으로 권면하며, 무한히 용서하는 마음으로 살아야 합니다.'
WHERE book_id = 'mt' AND chapter = 18 AND verse = 1;

-- 마태복음 19장: 이혼, 어린아이, 부자 청년에 대한 가르침
UPDATE bible_verses SET
    chapter_title = '이혼과 결혼, 어린아이 축복, 부자 청년',
    chapter_summary = '예수께서 이 말씀을 마치시고 갈릴리를 떠나 요단강 건너편 유대 지경에 이르시니 큰 무리가 좇거늘 거기서 저희의 병을 고치시니라 하시고, 바리새인들이 예수를 시험하여 가로되 사람이 아무 연고를 물론하고 그 아내를 내어버리는 것이 옳으니이까 하니 대답하여 가라사대 사람을 지으신 이가 본래 저희를 남자와 여자로 만드사 말씀하시기를 이러므로 사람이 그 부모를 떠나서 아내에게 합하여 그 둘이 한 몸이 될지니라 하신 것을 읽지 못하였느냐 그런즉 이제 둘이 아니요 한 몸이니 그러므로 하나님이 짝지어 주신 것을 사람이 나누지 못할지니라 하시니 음행한 연고 외에는 아내를 내어버리는 자는 간음하게 함이요 또한 내어버린 여자에게 장가드는 자도 간음함이니라 하시고, 사람들이 안수하고 기도하시기를 바라고 어린 아이들을 데리고 오거늘 제자들이 꾸짖거늘 예수께서 가라사대 어린 아이들을 용납하고 내게 오는 것을 금하지 말라 천국이 이런 자의 것이니라 하시고, 한 사람이 주께 와서 가로되 선생님이여 내가 무슨 선한 일을 하여야 영생을 얻으리이까 하니 가라사대 네가 생명에 들어가려면 계명들을 지키라 하시니 어느 계명이니이까 하거늘 예수께서 가라사대 살인하지 말라, 간음하지 말라, 도적질하지 말라, 거짓 증거하지 말라, 네 부모를 공경하라, 네 이웃을 네 몸과 같이 사랑하라 하신 것이니라 하시니 그 청년이 가로되 이 모든 것을 내가 지키었사온데 무엇이 부족하니이까 하거늘 예수께서 가로되 네가 온전하고자 할진대 가서 네 소유를 팔아 가난한 자들을 주라 그리하면 하늘에서 보화가 네게 있으리라 그리고 와서 나를 좇으라 하시니 그 청년이 재물이 많으므로 이 말씀을 듣고 근심하며 가니라.',
    chapter_themes = '["하나님이 짝지어 주신 것", "음행한 연고 외에는", "어린 아이들을 용납하라", "선한 일을 하여 영생", "소유를 팔아 가난한 자들에게", "낙타가 바늘귀로"]',
    chapter_context = '결혼과 이혼, 하나님 나라의 자격, 영생의 조건에 대한 예수님의 명확한 가르침으로 세상 가치관과 대조되는 하나님 나라의 원리를 제시합니다.',
    chapter_application = '결혼의 신성함을 지키고, 어린아이 같은 순수함을 유지하며, 재물보다 하나님을 우선순위로 삼고, 영생을 위해 모든 것을 버릴 각오를 해야 합니다.'
WHERE book_id = 'mt' AND chapter = 19 AND verse = 1;

-- 마태복음 20장: 포도원 품꾼의 비유와 두 번째 수난예고
UPDATE bible_verses SET
    chapter_title = '포도원 품꾼의 비유와 섬김의 리더십',
    chapter_summary = '천국은 마치 품꾼을 얻어 포도원에 들여보내려고 이른 아침에 나간 집 주인과 같으니 저가 하루 한 데나리온씩 하기로 품꾼들과 약속하여 포도원에 들여보내고 또 제삼시에 나가 보니 장터에 놀고 서 있는 사람들이 또 있는지라 그들에게 이르되 너희도 포도원에 들어가라 내가 너희에게 상당하게 주리라 하니 그들이 가고 제육시와 제구시에도 또한 그렇게 하고 제십일시에도 나가 보니 서 있는 사람들이 또 있는지라 가로되 너희는 어찌하여 종일토록 놀고 여기 서 있느냐 가로되 아무도 우리를 품꾼으로 쓰지 아니함이니이다 가로되 너희도 포도원에 들어가라 하니라 저물매 포도원 주인이 청지기에게 이르되 품꾼들을 불러 나중 온 자로부터 시작하여 먼저 온 자까지 삯을 주라 하니 제십일시에 온 자들이 와서 한 데나리온씩을 받거늘 먼저 온 자들이 와서 더 받을 줄 알았더니 그들도 한 데나리온씩 받은지라 받은 후 집 주인을 원망하여 가로되 나중 온 이 사람들은 한 시간만 일하였거늘 저희를 종일 수고하며 더위를 견딘 우리와 같게 하였나이다 하니 주인이 그 중의 한 사람에게 대답하여 가로되 친구여 내가 네게 잘못한 것이 없노라 네가 나와 한 데나리온의 약속을 하지 아니하였느냐 네 것이나 가지고 가라 나중 온 이 사람에게 너와 같이 주는 것이 내 뜻이니라 내 것을 가지고 내 뜻대로 할 것이 아니냐 내가 선하므로 네가 악하게 보느냐 하니라 이와 같이 나중 된 자로서 먼저 되고 먼저 된 자로서 나중 되리라 하시고, 예수께서 예루살렘으로 올라가려 하실 때에 열두 제자를 따로 데리시고 길에서 이르시되 보라 우리가 예루살렘으로 올라가노니 인자가 대제사장들과 서기관들에게 넘기우리니 저희가 죽이기로 결안하고 이방인들에게 넘겨주어 희롱하며 채찍질하며 십자가에 못 박게 하리니 제삼일에 살아나리라 하시니라.',
    chapter_themes = '["포도원 품꾼", "나중 된 자가 먼저", "내 뜻대로 할 것", "두 번째 수난예고", "섬기는 자가 큰 자", "많은 사람의 대속물"]',
    chapter_context = '하나님의 은혜의 주권성과 공평성, 그리고 참된 리더십의 본질을 가르치시며 예루살렘에서의 수난을 구체적으로 예고하신 말씀입니다.',
    chapter_application = '하나님의 은혜와 주권을 인정하고, 먼저 된 것을 자랑하지 말며, 섬기는 리더십을 실천하고, 많은 사람을 위한 대속의 은혜에 감사해야 합니다.'
WHERE book_id = 'mt' AND chapter = 20 AND verse = 1;

-- 마태복음 21장: 예루살렘 입성과 성전 정화
UPDATE bible_verses SET
    chapter_title = '나귀 타신 입성과 성전 정화, 권위 논쟁',
    chapter_summary = '저희가 예루살렘에 가까이 와서 감람산 벳바게에 이르렀을 때에 예수께서 두 제자를 보내시며 가라사대 너희 맞은편 마을로 가라 곧 매인 나귀와 나귀 새끼가 함께 있는 것을 보리니 풀어 내게로 끌고 오너라 하시니 이는 선지자로 하신 말씀을 이루려 하심이라 일렀으되 시온 딸에게 이르기를 네 왕이 네게 임하나니 겸손하여 나귀를 타되 멍에 메는 짐승의 새끼를 탔도다 하라 하였느니라 하시고, 무리의 대부분은 그 옷을 길에 펴며 다른 이는 나무 가지를 베어 길에 펴고 전후에 가는 무리가 소리질러 가로되 호산나 다윗의 자손이여 찬송하리로다 주의 이름으로 오시는 이여 가장 높은 곳에서 호산나 하더라 하시고, 예수께서 하나님의 성전에 들어가사 성전 안에서 매매하는 모든 자를 내어쫓으시며 돈 바꾸는 자들의 상과 비둘기 파는 자들의 의자를 둘러 엎으시고 저희에게 이르시되 기록된 바 내 집은 기도하는 집이라 일컬음을 받으리라 하였거늘 너희는 강도의 굴혈로 만드는도다 하시니라 하시고, 대제사장들과 백성의 장로들이 성전에서 가르치실 때에 나아와서 가로되 네가 무슨 권세로 이런 일을 하느냐 또 누가 이 권세를 네게 주었느냐 하니 예수께서 대답하시되 나도 한 말을 너희에게 물으리니 너희가 대답하면 나도 무슨 권세로 이런 일을 하는지 이르리라 요한의 세례가 어디로서냐 하늘로서냐 사람에게로서냐 하시니 저희가 서로 의논하여 가로되 만일 하늘로서라 하면 그러면 너희가 어찌하여 저를 믿지 아니하였느냐 할 것이요 만일 사람에게로서라 하면 모든 백성이 요한을 선지자로 여기니 백성이 무섭다 하여 예수께 대답하여 가로되 우리가 알지 못하노라 하니 예수께서 가라사대 나도 무슨 권세로 이런 일을 하는지 너희에게 이르지 아니하리라 하시니라.',
    chapter_themes = '["나귀 타신 입성", "호산나 다윗의 자손", "성전 정화", "기도하는 집", "권위에 대한 질문", "요한의 세례"]',
    chapter_context = '예수님이 메시야 왕으로서 예루살렘에 입성하시고 성전을 정화하시며 종교 지도자들과의 갈등이 본격화되는 전환점입니다.',
    chapter_application = '예수님을 왕으로 모시고 찬송하며, 거룩한 예배를 회복하고, 종교적 형식주의를 버리며, 예수님의 권위를 인정하고 순종해야 합니다.'
WHERE book_id = 'mt' AND chapter = 21 AND verse = 1;

-- 마태복음 22장: 혼인잔치 비유와 여러 논쟁들
UPDATE bible_verses SET
    chapter_title = '혼인잔치 비유와 가이사의 것, 부활 논쟁',
    chapter_summary = '예수께서 다시 비유로 대답하여 가라사대 천국은 마치 자기 아들을 위하여 혼인 잔치를 베푼 어떤 임금과 같으니 그 종들을 보내어 그 청한 자들을 혼인 잔치에 오라 하였더니 오기를 싫어하거늘 다시 다른 종들을 보내며 가로되 청한 자들에게 이르기를 내가 오찬을 준비하되 나의 소와 살진 짐승을 잡고 모든 것을 갖추었으니 혼인 잔치에 오소서 하라 하였더니 저희가 돌아보지도 않고 하나는 자기 밭으로, 하나는 자기 상업차로 가고 그 남은 자들은 종들을 잡아 욕보이고 죽이니 임금이 노하여 군대를 보내어 그 살인한 자들을 진멸하고 그 동네를 불사르고 이에 종들에게 이르되 혼인 잔치는 준비되었으나 청한 자들은 합당치 아니하니 네거리 길에 가서 사람을 만나는 대로 혼인 잔치에 청하여 오라 하니 종들이 길에 나가 악한 자나 선한 자나 만나는 대로 모두 데려오니 혼인 잔치에 손님들이 가득한지라 임금이 손님들을 보러 들어올새 거기서 예복을 입지 않은 한 사람을 보고 가로되 친구여 어찌하여 예복을 입지 않고 여기 들어왔느냐 하니 저가 유구무언이거늘 임금이 사환들에게 말하되 그 손발을 결박하여 바깥 어두운데 내어 던지라 거기서 슬피 울며 이를 갊이 있으리라 하니라 청함을 받은 자는 많되 택함을 입은 자는 적으니라 하시니라 하시고, 바리새인들이 가서 어떻게 하면 예수로 말의 올무에 걸리게 할까 상의하고 자기 제자들을 헤롯당과 함께 예수께 보내어 가로되 선생님이여 우리가 아노니 당신은 참되시고 참으로써 하나님의 도를 가르치시며 아무라도 꺼리는 일이 없으시니 이는 사람을 외모로 보지 아니하심이니이다 그러면 어떻게 생각하시나이까 가이사에게 세를 바치는 것이 옳으니이까 옳지 아니하니이까 하거늘 예수께서 저희의 악함을 아시고 가라사대 외식하는 자들아 어찌하여 나를 시험하느냐 세 바칠 돈을 내게 보이라 하시니 데나리온 하나를 가져왔거늘 예수께서 말씀하시되 이 형상과 이 글이 뉘 것이냐 하시니 가로되 가이사의 것이니이다 이에 가라사대 그런즉 가이사의 것은 가이사에게, 하나님의 것은 하나님께 바치라 하시니 저희가 이 말씀을 듣고 기이히 여겨 예수를 떠나가니라.',
    chapter_themes = '["혼인잔치 비유", "청함을 받은 자는 많되", "예복을 입지 않은 자", "가이사의 것과 하나님의 것", "부활시에는 장가도 가지 않고", "가장 큰 계명"]',
    chapter_context = '하나님 나라 초청의 보편성과 선택성, 세속 권력과 종교적 의무의 구분, 부활과 계명의 본질에 대한 예수님의 지혜로운 답변들입니다.',
    chapter_application = '하나님의 초청에 합당한 준비를 하고, 세속적 의무와 신앙적 의무를 균형있게 이행하며, 하나님과 이웃 사랑의 계명을 실천해야 합니다.'
WHERE book_id = 'mt' AND chapter = 22 AND verse = 1;

-- 마태복음 23장: 외식하는 서기관과 바리새인들에 대한 화
UPDATE bible_verses SET
    chapter_title = '외식하는 서기관과 바리새인들에 대한 일곱 화',
    chapter_summary = '그 때에 예수께서 무리와 제자들에게 말씀하여 가라사대 서기관들과 바리새인들이 모세의 자리에 앉았으니 그러므로 무엇이든지 저희의 말하는 바는 행하고 지키되 저희의 하는 행위는 본받지 말라 저희는 말만 하고 행치 아니하며 또 무거운 짐을 묶어 사람의 어깨에 지우되 자기는 이것을 한 손가락으로도 움직이려 하지 아니하며 저희 모든 행위를 사람에게 보이고자 하여 하나니 경문을 넓게 하며 옷술을 크게 하고 잔치의 상석과 회당의 상좌와 시장에서 문안 받는 것과 사람에게 랍비라 칭함을 받는 것을 좋아하느니라 그러나 너희는 랍비라 칭함을 받지 말라 너희 선생은 하나이요 너희는 다 형제니라 하시고, 화 있을진저 외식하는 서기관들과 바리새인들이여 너희가 천국 문을 사람들 앞에서 닫고 너희도 들어가지 않고 들어가려 하는 자도 들어가지 못하게 하는도다 하시며 일곱 가지 화를 선언하시고, 예루살렘아 예루살렘아 선지자들을 죽이고 네게 파송된 자들을 돌로 치는 자여 암탉이 그 새끼를 날개 아래 모음같이 내가 네 자녀를 모으려 한 일이 몇 번이냐 그러나 너희가 원치 아니하였도다 보라 너희 집이 황폐하여 버린 바 되리라 내가 너희에게 이르노니 이제부터 너희는 찬송하리로다 주의 이름으로 오시는 이여 할 때까지 나를 보지 못하리라 하시니라.',
    chapter_themes = '["모세의 자리", "무거운 짐을 묶어", "일곱 가지 화", "외식하는 자들", "예루살렘아 예루살렘아", "주의 이름으로 오시는 이"]',
    chapter_context = '종교적 외식과 위선에 대한 예수님의 강력한 경고와 심판 선언으로, 참된 영성과 거짓 종교의 차이를 명확히 보여주는 말씀입니다.',
    chapter_application = '형식적 종교를 버리고 진실한 마음으로 하나님을 섬기며, 다른 사람에게 무거운 짐을 지우지 말고, 겸손한 마음으로 서로 섬겨야 합니다.'
WHERE book_id = 'mt' AND chapter = 23 AND verse = 1;

-- 마태복음 24장: 종말에 대한 예언
UPDATE bible_verses SET
    chapter_title = '성전 파괴와 종말의 징조들',
    chapter_summary = '예수께서 성전에서 나와서 가실 때에 제자들이 성전 건물들을 가리켜 보이려고 나아오거늘 대답하여 가라사대 너희가 이 모든 것을 보지 못하느냐 내가 진실로 너희에게 이르노니 돌 하나도 돌 위에 남지 않고 다 무너뜨리우리라 하시니라 하시고, 예수께서 감람산 위에 앉으셨을 때에 제자들이 종용히 와서 가로되 우리에게 이르소서 어느 때에 이런 일이 있겠사오며 또 주의 임하심과 세상 끝에는 무슨 징조가 있사오리이까 하니 예수께서 대답하여 가라사대 너희가 사람의 미혹을 받지 않도록 주의하라 많은 사람이 내 이름으로 와서 가로되 나는 그리스도라 하여 많은 사람을 미혹케 하리라 난리와 난리 소문을 듣겠으나 너희는 삼가 두려워 말라 이런 일이 있어야 하되 끝은 아직 아니니라 민족이 민족을, 나라가 나라를 대적하여 일어나겠고 처처에 기근과 지진이 있으리니 이 모든 것이 재난의 시작이니라 하시고, 그 때에 사람들이 너희를 환난에 넘겨주겠으며 너희를 죽이리니 너희가 내 이름을 인하여 모든 민족에게 미움을 받으리라 그 때에 많은 사람이 시험에 빠져 서로 잡아주고 서로 미워하겠으며 거짓 선지자가 많이 일어나 많은 사람을 미혹케 하겠으며 불법이 성하므로 많은 사람의 사랑이 식어지리라 그러나 끝까지 견디는 자는 구원을 얻으리라 이 천국 복음이 모든 민족에게 증거되기 위하여 온 세상에 전파되리니 그제야 끝이 오리라 하시니라.',
    chapter_themes = '["성전 파괴 예언", "사람의 미혹을 받지 말라", "재난의 시작", "끝까지 견디는 자", "천국 복음이 온 세상에", "인자의 임함"]',
    chapter_context = '예루살렘 멸망과 종말에 대한 예수님의 예언으로, 제자들이 역사의 흐름 속에서 어떻게 준비하고 살아야 할지를 가르치신 말씀입니다.',
    chapter_application = '거짓 그리스도들을 분별하고, 환난 중에도 믿음을 지키며, 끝까지 견디고, 온 세상에 복음을 전파하며, 주님의 재림을 준비해야 합니다.'
WHERE book_id = 'mt' AND chapter = 24 AND verse = 1;

-- 마태복음 25장: 열 처녀, 달란트, 양과 염소의 비유
UPDATE bible_verses SET
    chapter_title = '열 처녀와 달란트 비유, 최후 심판',
    chapter_summary = '그 때에 천국은 마치 등을 들고 신랑을 맞으러 나간 열 처녀와 같다 하리니 그 중에 다섯은 미련하고 다섯은 슬기 있는지라 미련한 자들은 등을 가지되 기름을 가지지 아니하고 슬기 있는 자들은 그릇에 기름을 담아 등과 함께 가져갔더니 신랑이 더디 오므로 다 졸며 잘새 밤중에 소리가 나되 보라 신랑이로다 맞으러 나오라 하매 이에 그 처녀들이 다 일어나 등을 준비할새 미련한 자들이 슬기 있는 자들에게 이르되 우리 등이 꺼져가니 너희 기름을 좀 나누어 달라 하거늘 슬기 있는 자들이 대답하여 가로되 우리와 너희의 쓰기에 다 부족할까 하노니 차라리 파는 자들에게 가서 너희 쓸 것을 사라 하니 저희가 사러 간 동안에 신랑이 오므로 예비하였던 자들은 함께 혼인 잔치에 들어가고 문이 닫힌지라 그 후에 남은 처녀들이 와서 가로되 주여 주여 우리에게 열어 주소서 하되 대답하여 가로되 진실로 너희에게 이르노니 내가 너희를 알지 못하노라 하였느니라 그런즉 깨어 있으라 너희는 그 날과 그 시를 알지 못하느니라 하시고, 또 어떤 사람이 타국에 갈새 그 종들을 불러 자기 소유를 맡김과 같으니 각각 그 재능대로 하나에게는 금 다섯 달란트를, 하나에게는 두 달란트를, 하나에게는 한 달란트를 주고 떠났더니 다섯 달란트 받은 자는 바로 가서 그것으로 장사하여 또 다섯 달란트를 남기고 두 달란트 받은 자도 그같이 하여 또 두 달란트를 남겼으되 한 달란트 받은 자는 가서 땅을 파고 그 주인의 돈을 감추어 두었더니 오랜 후에 그 종들의 주인이 돌아와 저희와 회계할새 다섯 달란트 받았던 자는 다섯 달란트를 더 가지고 와서 가로되 주여 내게 다섯 달란트를 주셨는데 보소서 또 다섯 달란트를 남겼나이다 하니 그 주인이 이르되 잘 하였도다 착하고 충성된 종아 네가 적은 일에 충성하였으매 내가 많은 것으로 네게 맡기리니 네 주인의 즐거움에 참예할지어다 하고 한 달란트 받았던 자도 와서 가로되 주여 당신은 심지 않은 데서 거두고 헤치지 않은 데서 모으는 줄을 내가 알았으므로 두려워하여 나가서 당신의 달란트를 땅에 감추어 두었었나이다 보소서 당신의 것을 받으시옵소서 하니 그 주인이 대답하여 가로되 악하고 게으른 종아 나는 심지 않은 데서 거두고 헤치지 않은 데서 모으는 줄로 네가 알았느냐 그러면 네가 마땅히 내 돈을 취리하는 자들에게나 두었다가 나로 돌아와서 내 본전과 변리를 받게 할 것이어늘 그에게서 그 한 달란트를 빼앗아 열 달란트 가진 자에게 주라 무릇 있는 자는 받아 풍족하게 되고 없는 자는 그 있는 것까지 빼앗기리라 이 무익한 종을 바깥 어두운 데로 내어 쫓으라 거기서 슬피 울며 이를 갊이 있으리라 하니라.',
    chapter_themes = '["열 처녀 비유", "기름을 준비한 슬기로운 자", "달란트 비유", "착하고 충성된 종", "양과 염소 심판", "지극히 작은 자 하나"]',
    chapter_context = '주님의 재림을 위한 준비와 충성된 청지기 역할, 그리고 최후 심판에서의 기준을 제시하는 종말론적 교훈입니다.',
    chapter_application = '항상 깨어있어 주님을 맞을 준비를 하고, 받은 달란트를 충성되게 사용하며, 지극히 작은 자들을 섬김으로 주님을 섬겨야 합니다.'
WHERE book_id = 'mt' AND chapter = 25 AND verse = 1;

-- 마태복음 26장: 최후의 만찬과 체포
UPDATE bible_verses SET
    chapter_title = '베다니 향유, 최후의 만찬, 겟세마네 기도와 체포',
    chapter_summary = '예수께서 이 말씀을 다 마치시고 제자들에게 이르시되 너희의 아는 바와 같이 이틀을 지나면 유월절이라 인자가 십자가에 못 박히기 위하여 팔리리라 하시니라 하시고, 예수께서 베다니 문둥이 시몬의 집에 계실 때에 한 여자가 매우 값진 향유 한 옥합을 가지고 나아와서 식사하시는 예수의 머리에 부으니 제자들이 보고 분하여 가로되 무슨 의도로 이것을 허비하느뇨 이것을 많은 값에 팔아 가난한 자들에게 줄 수 있었겠도다 하거늘 예수께서 아시고 저희에게 이르시되 너희가 어찌하여 이 여자를 괴롭게 하느냐 저가 내게 좋은 일을 하였느니라 가난한 자들은 항상 너희와 함께 있거니와 나는 항상 함께 있지 아니하리라 저가 내 몸에 이 향유를 부은 것은 내 장례를 위하여 함이니라 내가 진실로 너희에게 이르노니 온 천하에 어디서든지 이 복음이 전파되는 곳에서는 이 여자의 행한 일도 말하여 저를 기념하리라 하시니라 하시고, 그 때에 열둘 중에 하나인 가룟 유다라 하는 자가 대제사장들에게 가서 말하되 내가 예수를 너희에게 넘겨 주리니 얼마나 주려느냐 하니 그들이 은 삼십을 달아 주거늘 저가 그때부터 예수를 넘겨 줄 기회를 찾더라 하시고, 무교절 첫날에 제자들이 예수께 나아와서 가로되 유월절 잡수실 것을 우리가 어디서 예비하기를 원하시나이까 하니 가라사대 성안 아무에게 가서 이르되 선생님 말씀이 내 때가 가까웠으니 내 제자들과 함께 유월절을 네 집에서 지키겠다 하시더라 하라 하신대로 제자들이 예비하니라 하시고 저녁에 열두 제자와 함께 앉으사 먹을 때에 가라사대 내가 진실로 너희에게 이르노니 너희 중에 한 사람이 나를 팔리라 하시니 저희가 심히 근심하여 각각 여짜오되 주여 내니이까 하거늘 대답하여 가라사대 나와 함께 그릇에 손을 넣는 그이가 나를 팔리라 인자는 자기에게 대하여 기록된 대로 가거니와 인자를 파는 그 사람에게는 화가 있으리로다 그 사람은 차라리 나지 아니하였더면 제게 좋을뻔하였느니라 하시니라.',
    chapter_themes = '["베다니 향유 사건", "가난한 자들은 항상 함께", "은 삼십", "최후의 만찬", "나를 팔리라", "겟세마네 기도"]',
    chapter_context = '예수님의 죽음을 앞두고 벌어지는 사건들로, 헌신과 배신이 대조되며 수난이 본격적으로 시작되는 중요한 전환점입니다.',
    chapter_application = '베다니 여인처럼 아낌없는 헌신을 하고, 배신하지 말고 충성을 다하며, 성찬의 의미를 깊이 새기고, 시험에 빠지지 않도록 깨어 기도해야 합니다.'
WHERE book_id = 'mt' AND chapter = 26 AND verse = 1;

-- 마태복음 27장: 재판과 십자가 처형
UPDATE bible_verses SET
    chapter_title = '빌라도 재판과 십자가 처형, 장사됨',
    chapter_summary = '새벽에 모든 대제사장과 백성의 장로들이 예수를 죽이려고 함께 의논하고 결박하여 끌고 가서 총독 빌라도에게 넘겨 주니라 하시고, 그 때에 예수를 판 유다가 그의 정죄됨을 보고 스스로 뉘우쳐 그 은 삼십을 대제사장들과 장로들에게 도로 갖다 주며 가로되 내가 무죄한 피를 팔고 죄를 범하였도다 하니 저희가 가로되 그것이 우리에게 무슨 상관이 있느냐 네가 당하라 하거늘 유다가 은을 성소에 던져 넣고 떠나가서 스스로 목매어 죽은지라 하시고, 총독이 가로되 그러면 그리스도라 하는 예수를 내가 어떻게 하랴 하니 저희가 다 가로되 십자가에 못 박혀야 하겠나이다 하거늘 빌라도가 가로되 어찜이뇨 무슨 악한 일을 하였느냐 하니 저희가 더욱 소리질러 가로되 십자가에 못 박혀야 하겠나이다 하는지라 빌라도가 아무 효험도 없이 도리어 민란이 날 것을 보고 물을 가져다가 무리 앞에서 손을 씻으며 가로되 이 사람의 피에 대하여 나는 무죄하니 너희가 당하라 하니 백성이 다 대답하여 가로되 그 피를 우리와 우리 자손에게 돌릴지어다 하거늘 이에 바라바는 저희에게 놓아 주고 예수는 채찍질하고 십자가에 못 박히게 넘겨 주니라 하시고, 골고다라 하는 곳 해골의 곳에 이르러 쓸개 탄 포도주를 예수께 주어 마시게 하려 하였으나 예수께서 맛보시고 마시려 하지 아니하시더라 저희가 예수를 십자가에 못 박은 후에 그 옷을 제비 뽑아 나누고 거기 앉아서 예수를 지키더니 그 머리 위에 이는 유대인의 왕 예수라 쓴 죄패를 붙였더라 하시고, 제육시로부터 온 땅에 어두움이 임하여 제구시까지 계속하더니 제구시 즈음에 예수께서 크게 소리질러 가라사대 엘리 엘리 라마 사박다니 하시니 이는 곧 나의 하나님, 나의 하나님, 어찌하여 나를 버리셨나이까 하는 뜻이라 하시고 예수께서 다시 크게 소리지르시고 영혼이 떠나가시니라.',
    chapter_themes = '["유다의 자살", "빌라도의 심문", "십자가에 못 박혀야", "골고다", "엘리 엘리 라마 사박다니", "아리마대 요셉"]',
    chapter_context = '무죄하신 예수님이 죄인들의 죄를 대신 지시고 십자가에서 죽으심으로 구속사역을 완성하시는 결정적 사건입니다.',
    chapter_application = '예수님의 대속적 죽음을 믿고 감사하며, 죄의 심각성을 깨닫고, 구원의 은혜에 합당한 삶을 살며, 부활의 소망을 가져야 합니다.'
WHERE book_id = 'mt' AND chapter = 27 AND verse = 1;

-- 마태복음 28장: 부활과 대위임령
UPDATE bible_verses SET
    chapter_title = '부활하신 주님과 대위임령',
    chapter_summary = '안식일이 다하여가고 안식 후 첫날이 되려는 미명에 막달라 마리아와 다른 마리아가 무덤을 보러 왔더니 큰 지진이 나며 주의 사자가 하늘로부터 내려와 돌을 굴려 내고 그 위에 앉았는데 그 형상이 번개같고 그 옷이 눈같이 희거늘 수직하던 자들이 저를 무서워하여 떨며 죽은 자같이 되었더라 천사가 여자들에게 일러 가로되 너희는 무서워 말라 십자가에 못 박히신 예수를 너희가 찾는 줄을 내가 아노라 그가 여기 계시지 않고 그의 말씀하시던 대로 살아나셨느니라 와서 그의 누우셨던 곳을 보고 빨리 가서 그의 제자들에게 이르되 그가 죽은 자 가운데서 살아나셨고 너희보다 먼저 갈릴리로 가시나니 거기서 너희가 뵈오리라 하라 보라 내가 너희에게 일렀느니라 하거늘 그 여자들이 무서움과 큰 기쁨으로 빨리 무덤을 떠나 제자들에게 알게 하려고 달음질할새 예수께서 저희를 만나 가라사대 평안하뇨 하시거늘 여자들이 나아가 그 발을 붙잡고 경배하니 이에 예수께서 가라사대 무서워 말라 가서 내 형제들에게 갈릴리로 가라 하라 거기서 나를 보리라 하시니라 하시고, 열한 제자가 갈릴리에 가서 예수의 명하시던 산에 이르러 예수를 뵈옵고 경배하나 오히려 의심하는 자도 있더라 예수께서 나아와 일러 가라사대 하늘과 땅의 모든 권세를 내게 주셨으니 그러므로 너희는 가서 모든 족속으로 제자를 삼아 아버지와 아들과 성령의 이름으로 세례를 주고 내가 너희에게 분부한 모든 것을 가르쳐 지키게 하라 볼지어다 내가 세상 끝날까지 너희와 항상 함께 있으리라 하시니라.',
    chapter_themes = '["빈 무덤", "그가 살아나셨느니라", "갈릴리에서 만남", "모든 족속으로 제자를 삼아", "삼위일체의 이름으로 세례", "세상 끝날까지 함께"]',
    chapter_context = '예수님의 부활이 확증되고 제자들에게 세계 복음화의 사명이 주어지며, 임마누엘의 약속으로 마태복음이 대단원의 막을 내립니다.',
    chapter_application = '부활의 증인으로 살며, 모든 족속에게 복음을 전하고, 삼위일체 하나님을 믿으며, 주님의 동행하심을 확신하고 대위임령을 실천해야 합니다.'
WHERE book_id = 'mt' AND chapter = 28 AND verse = 1;