-- 에스겔서(Ezekiel) 1-24장 주석
-- 총 48장의 대예언서 1부 (소명과 예루살렘 심판)

-- 에스겔 1장: 하나님의 영광의 환상
UPDATE bible_verses SET
    chapter_title = '그발강 가에서 본 하나님의 영광과 네 생물의 환상',
    chapter_summary = '제삼십년 사월 오일에 에스겔이 그발강 가 사로잡힌 자 중에 있을 때에 하늘이 열리며 하나님의 이상을 보았다고 기록합니다. 북방에서 폭풍과 큰 구름이 오는데 그 속에서 불이 번쩍번쩍하며 빛이 그 사면에 비치고 그 불 가운데 단 쇠 같은 것이 나타났으며, 그 속에서 네 생물의 형상이 나타나는데 그들의 모양이 사람과 같고 각각 네 얼굴과 네 날개가 있어 그 날개 아래는 사람의 손 같고 네 생물의 얼굴은 사람과 사자와 소와 독수리의 얼굴이었다고 묘사합니다. 또한 생물들 곁에 땅 위에 바퀴들이 있는데 그 모양과 구조가 황옥 같고 네 바퀴의 모양이 한결같으며 그 구조는 바퀴 안에 바퀴가 있는 것 같고 행할 때에는 사방으로 향한 대로 돌이키지 않고 행하며 그 둘레는 높고 무서우며 네 바퀴 둘레에 다 눈이 가득하였다고 기록합니다.',
    chapter_themes = '["하늘이 열림", "하나님의 이상", "북방의 폭풍", "네 생물의 환상", "네 얼굴과 네 날개", "바퀴 안에 바퀴"]',
    chapter_context = '바벨론 포로 5년째 되는 해에 에스겔이 받은 하나님의 소명 환상으로, 하나님의 영광과 임재를 상징하는 복잡하고 신비로운 환상입니다.',
    chapter_application = '하나님의 임재와 영광을 경외하며, 하나님의 전능하심과 무소부재하심을 인정하고, 환상을 통해 주시는 하나님의 계시에 순종해야 합니다.'
WHERE book_id = 'ez' AND chapter = 1 AND verse = 1;

-- 에스겔 2장: 에스겔의 소명
UPDATE bible_verses SET
    chapter_title = '패역한 족속에게 보냄을 받은 에스겔의 사명',
    chapter_summary = '하나님께서 에스겔을 인자야 하며 부르시고 네 발로 일어서라 내가 네게 말하리라 하시며, 말씀하실 때에 성령이 에스겔에게 임하여 그를 일으켜 세우시고 말씀하시는 소리를 듣게 하십니다. 인자야 내가 너를 이스라엘 자손 곧 나를 배반하는 패역한 민족에게 보내노라 그들과 그 열조가 내게 범죄하여 오늘날까지 이르렀나니 이 자손은 얼굴이 뻔뻔하고 마음이 강퍅한 자니라 내가 너를 그들에게 보내노니 너는 그들에게 이르기를 주 여호와의 말씀이 이러하다 하라 하시며, 그들이 듣든지 아니 듣든지 그들 가운데 선지자가 있은 줄은 알리라 하십니다. 인자야 너는 그들을 두려워 말며 그들의 말을 두려워 말라 비록 가시와 찔레가 너와 함께 있으며 전갈 가운데 거할지라도 그들의 말을 두려워 말며 그들의 얼굴을 무서워 말라 그들은 패역한 족속임이니라 하시고, 또 손을 내게 펴시는데 그 손에 두루마리 책이 있더라 그것을 내 앞에 펴시니 그 안팎에 글이 있는데 애가와 애곡과 재앙의 말이 기록되었더라고 기록합니다.',
    chapter_themes = '["인자야 하는 부르심", "패역한 민족에게 보냄", "얼굴이 뻔뻔하고 마음이 강퍅", "선지자가 있은 줄 알리라", "가시와 찔레와 전갈", "애가와 애곡과 재앙"]',
    chapter_context = '에스겔이 바벨론 포로 중에서 이스라엘의 패역함을 책망하고 심판을 선포할 선지자로 부름받은 소명의 장면입니다.',
    chapter_application = '하나님의 부르심에 순종해야 하며, 어려운 사명이라도 두려워하지 말고, 하나님의 말씀을 타협 없이 전하며, 사람들의 반응에 상관없이 충성해야 합니다.'
WHERE book_id = 'ez' AND chapter = 2 AND verse = 1;

-- 에스겔 3장: 두루마리를 먹고 파수꾼이 됨
UPDATE bible_verses SET
    chapter_title = '두루마리를 먹고 이스라엘 족속의 파수꾼이 된 에스겔',
    chapter_summary = '하나님께서 에스겔에게 인자야 너는 얻는 것을 먹으라 이 두루마리를 먹고 가서 이스라엘 족속에게 고하라 하시므로 에스겔이 입을 벌리니 그가 그 두루마리를 먹이시며 내게 이르시되 인자야 내가 네게 주는 이 두루마리로 네 배에 채우라 하시기에 내가 먹으니 그것이 내 입에서 달기가 꿀 같더라고 기록합니다. 하나님께서 인자야 내가 너를 이스라엘 족속에게 보내노니 방언이 다르거나 말이 어려운 민족에게가 아니라 이스라엘 족속에게 보내는 것이라 하시며, 인자야 내가 너로 이스라엘 족속의 파수꾼을 삼았으니 너는 내 입의 말을 듣고 나를 대신하여 그들을 깨우치라 가령 내가 악인에게 말하기를 너는 꼭 죽으리라 할 때에 네가 깨우치지 아니하거나 말로 악인에게 말하여서 그 악한 길을 떠나 생명을 구원케 하지 아니하면 그 악인은 그 죄악 중에서 죽으려니와 내가 그 피를 네 손에서 찾을 것이고 네가 악인을 깨우치되 그가 그 악한 마음과 악한 행위에서 돌이키지 아니하면 그는 그 죄악 중에서 죽으려니와 너는 네 생명을 보전하리라 하십니다.',
    chapter_themes = '["두루마리를 먹음", "입에서 달기가 꿀 같음", "이스라엘 족속의 파수꾼", "나를 대신하여 깨우치라", "악인을 깨우치지 않으면", "그 피를 네 손에서 찾음"]',
    chapter_context = '에스겔이 하나님의 말씀을 완전히 소화하고 이스라엘의 영적 파수꾼으로서의 막중한 책임을 받아들이는 장면입니다.',
    chapter_application = '하나님의 말씀을 깊이 묵상하고 소화해야 하며, 영적 파수꾼으로서 책임감을 가져야 하고, 죄인들을 깨우치는 사명을 감당하며, 영혼들에 대한 책임을 인식해야 합니다.'
WHERE book_id = 'ez' AND chapter = 3 AND verse = 1;

-- 에스겔 4장: 예루살렘 포위 상징 행동
UPDATE bible_verses SET
    chapter_title = '벽돌로 예루살렘을 그리고 에워싸는 상징적 행동',
    chapter_summary = '하나님께서 에스겔에게 너는 벽돌 하나를 가져다가 네 앞에 놓고 그 위에 성 하나를 그리되 예루살렘을 그리라 하시고 그것을 에워싸되 운제를 세우고 토성을 쌓고 진을 치고 공성퇴를 사면에 배치하라 하시며, 또 철판을 가져다가 너와 성 사이에 철성을 삼고 네 얼굴을 성으로 향하라 성이 에워쌈을 당하리니 이것이 이스라엘 족속에게 징조가 되리라 하십니다. 또한 너는 왼편으로 누워 이스라엘 족속의 죄악을 담당하되 네가 눕는 날수대로 그 죄악을 담당할찌니라 내가 그들의 범죄한 해수대로 네게 날수를 정하였나니 곧 삼백구십일이니라 그 수가 차거든 너는 우편으로 누워 유다 족속의 죄악을 담당하되 사십일로 하되 일일이 일년이니라 하시며, 네가 에워싸인 예루살렘을 향하여 팔을 벗어 메고 예언하라 하시고, 내가 줄로 너를 동이리니 네가 에워싸는 날이 맞도록 이편에서 저편으로 몸을 돌이키지 못하리라 하십니다.',
    chapter_themes = '["벽돌에 예루살렘을 그림", "철판으로 철성을 삼음", "왼편으로 누워 이스라엘 죄악 담당", "우편으로 누워 유다 죄악 담당", "일일이 일년", "줄로 동임"]',
    chapter_context = '예루살렘의 포위와 멸망을 상징적 행동으로 예언하고, 남북 이스라엘의 죄악에 대한 형벌 기간을 보여주는 상징적 예언입니다.',
    chapter_application = '하나님의 명령에 절대 순종해야 하며, 죄악에 대한 하나님의 공의로운 심판을 인정하고, 상징적 행동을 통해서도 하나님의 뜻을 전할 수 있음을 깨달아야 합니다.'
WHERE book_id = 'ez' AND chapter = 4 AND verse = 1;

-- 에스겔 5장: 머리털과 수염을 깎는 상징
UPDATE bible_verses SET
    chapter_title = '머리털과 수염을 깎고 태우고 흩는 심판의 상징',
    chapter_summary = '인자야 너는 날카로운 칼을 취하여 삭도로 삼아 네 머리털과 수염을 깎아서 저울로 털을 달아 나누라 그 성읍을 에워싸는 날이 차거든 너는 털 삼분의 일은 성읍 안에서 불로 사르고 삼분의 일은 성읍 사면에서 칼로 치고 삼분의 일은 바람에 흩으라 내가 그 뒤를 따라 칼을 빼리라 하시며, 또 너는 그 가운데서 조금을 취하여 네 옷자락에 싸고 또 그 가운데서 얼마를 취하여 불에 던져 사르라 거기서 불이 이스라엘 온 족속에게로 나오리라 하십니다. 주 여호와께서 가라사대 이것이 곧 예루살렘이라 내가 그를 이방인 가운데 두어 열국으로 둘러 있게 하였거늘 그가 내 규례를 거스려서 이방인보다 악을 행하며 내 율례도 그리함이 그 둘러 있는 열국보다 심하니 이는 그들이 내 규례를 버리고 내 율례를 행치 아니하였음이니라 그러므로 나 주 여호와가 말하노라 너희 요란함이 너희를 둘러 있는 이방인보다 심하여 내 율례를 행치 아니하며 내 규례를 지키지 아니하고 너희를 둘러 있는 이방인의 규례대로도 행치 아니하였느니라고 책망하십니다.',
    chapter_themes = '["머리털과 수염을 깎음", "삼분의 일씩 나누어 심판", "불로 사르고 칼로 치고 바람에 흩음", "이방인보다 악을 행함", "열국보다 요란함이 심함", "이방인의 규례대로도 행치 않음"]',
    chapter_context = '예루살렘 백성들이 당할 세 가지 재앙(기근, 전쟁, 유배)을 머리털을 삼등분하는 상징적 행동으로 보여준 심판 예언입니다.',
    chapter_application = '하나님의 백성이 이방인보다 더 악하게 행하면 더 큰 책임을 져야 함을 깨닫고, 하나님의 규례와 율례를 지켜야 하며, 세상보다 더 거룩한 삶을 살아야 합니다.'
WHERE book_id = 'ez' AND chapter = 5 AND verse = 1;

-- 에스겔 6장: 산들을 향하여 예언하라
UPDATE bible_verses SET
    chapter_title = '이스라엘 산들을 향한 우상숭배 심판 예언',
    chapter_summary = '여호와의 말씀이 내게 임하여 가라사대 인자야 너는 이스라엘 산들을 향하여 그들에게 예언하여 가로되 이스라엘 산들아 주 여호와의 말씀을 들을찌어다 주 여호와께서 산과 작은 산과 시내와 골짜기를 대하여 말씀하시되 내가 칼로 너희에게 임하게 하여 너희 산당을 멸하리니 너희 제단들이 황폐하고 태양상들이 깨어질 것이며 너희 중에 살륙 당한 자로 너희 우상 앞에 엎드러지게 할 것이라 이스라엘 자손의 시체를 그 우상 앞에 두며 너희 해골을 너희 제단 사면에 흩으리라 하시며, 그러나 너희가 열국에 흩어질 때에 내가 너희 중에서 칼을 피하여 이방 민족 중에 남아 있는 자가 있게 할 것이라 너희 중 피하는 자가 사로잡혀 이방인 중에 있어서 나를 기억하되 그들이 음란한 마음으로 나를 떠나고 음란한 눈으로 우상을 섬겨서 나로 근심케 한 것을 기억하고 그 악행과 모든 가증한 일로 인하여 스스로 한탄하리라고 약속하십니다.',
    chapter_themes = '["이스라엘 산들을 향하여", "산당을 멸함", "제단이 황폐하고 태양상이 깨어짐", "우상 앞에 엎드러짐", "남은 자가 나를 기억함", "스스로 한탄함"]',
    chapter_context = '이스라엘 전 지역의 산당에서 행해지는 우상숭배에 대한 하나님의 심판과 그 후에 올 회개를 예언한 내용입니다.',
    chapter_application = '우상숭배를 완전히 버려야 하며, 하나님의 심판 중에서도 남은 자들이 있을 것을 믿고, 자신의 죄를 깨닫고 진정한 회개를 해야 합니다.'
WHERE book_id = 'ez' AND chapter = 6 AND verse = 1;

-- 에스겔 7장: 이스라엘 땅의 끝이 왔도다
UPDATE bible_verses SET
    chapter_title = '이스라엘 땅의 끝이 왔도다 - 임박한 멸망의 날',
    chapter_summary = '또 여호와의 말씀이 내게 임하여 가라사대 너 인자야 주 여호와께서 이스라엘 땅에 대하여 말씀하시되 끝이 났도다 이 땅 사방의 끝이 났도다 이제는 네게 끝이 이르렀나니 내가 내 진노를 네게 발하여 네 행위를 국문하고 네 모든 가증한 일을 네게 보응하리라 내가 너를 아껴 보지 아니하며 긍휼히 여기지도 아니하고 네 행위대로 네게 보응하여 너의 가증한 일이 너희 중에 나타나게 하리니 너희가 나를 여호와인줄 알리라 하시며, 보라 재앙이로다 비상한 재앙이로다 보라 임하도다 끝이 왔도다 끝이 왔도다 너를 향하여 일어났나니 보라 임하도다 이 땅 거민아 정한 때가 왔도다 날이 가까왔으니 요란한 날이요 산에서 즐거이 부르는 날이 아니로다 하시며, 그들이 밖으로 나가려 하여도 칼이 있고 안에 거하여도 기근과 온역이 있어서 밭에 있는 자는 칼에 죽을 것이요 성읍에 있는 자는 기근과 온역에 망할 것이며 그 중에 피하는 자가 있어도 산 위에 있으리니 다 각기 자기 죄악으로 인하여 골짜기 비둘기 같이 슬피 울 것이라고 선언하십니다.',
    chapter_themes = '["끝이 났도다", "사방의 끝", "비상한 재앙", "정한 때가 왔도다", "요란한 날", "골짜기 비둘기 같이 슬피 울음"]',
    chapter_context = '예루살렘과 이스라엘 전체에 임박한 최종적 멸망을 선언하는 절박하고 긴급한 심판 예언입니다.',
    chapter_application = '하나님의 심판의 때가 반드시 온다는 것을 깨닫고, 미루지 말고 즉시 회개해야 하며, 하나님의 공의로운 심판을 두려워하고 경외해야 합니다.'
WHERE book_id = 'ez' AND chapter = 7 AND verse = 1;

-- 에스겔 8장: 성전의 가증한 일들
UPDATE bible_verses SET
    chapter_title = '예루살렘 성전에서 행해지는 네 가지 가증한 일',
    chapter_summary = '제육년 유월 오일에 에스겔이 집에 앉았는데 유다 장로들이 앞에 앉았을 때에 주 여호와의 손이 그에게 임했고, 에스겔이 환상 중에 예루살렘으로 옮겨져서 성전에서 행해지는 가증한 일들을 보게 됩니다. 첫째로 북문 어귀에 있는 투기의 우상을 보고, 둘째로 장로 칠십인이 각기 향로를 손에 들고 온갖 곤충과 가증한 짐승과 이스라엘 족속의 모든 우상을 그린 벽 앞에서 분향하는 것을 보며, 셋째로 여인들이 북문에서 담무스를 위하여 애곡하는 것을 보고, 넷째로 스물다섯 명 가량의 사람이 여호와의 전 문과 제단 사이에서 등을 여호와의 전으로 향하고 낯을 동으로 향하여 동방 태양에 경배하는 것을 봅니다. 하나님께서 인자야 네가 보았느냐 유다 족속이 여기서 행하는 가증한 일을 적다 하겠느냐 그들이 강포로 이 땅에 채우고 또 다시 내 노를 격동하고 심지어 나무가지를 그 코에 두었느니라 그러므로 나도 분노로 갚으리라 내가 아껴 보지 아니하며 긍휼히 여기지도 아니하리니 그들이 큰 소리로 내 귀에 부르짖을지라도 내가 듣지 아니하리라 하십니다.',
    chapter_themes = '["투기의 우상", "장로들의 은밀한 우상숭배", "담무스를 위한 애곡", "태양에 경배", "나무가지를 코에 둠", "분노로 갚으리라"]',
    chapter_context = '예루살렘 성전 내에서 이루어지고 있는 네 종류의 우상숭배를 통해 이스라엘의 영적 타락의 극치를 보여주는 환상입니다.',
    chapter_application = '하나님의 성전을 더럽히지 말아야 하며, 은밀한 죄도 하나님께서 다 보신다는 것을 깨닫고, 진정한 예배자가 되어야 하며, 이방 종교와 혼합하지 말아야 합니다.'
WHERE book_id = 'ez' AND chapter = 8 AND verse = 1;

-- 에스겔 9장: 이마에 표를 받은 자들
UPDATE bible_verses SET
    chapter_title = '탄식하는 자들의 이마에 표하고 성을 진멸하라',
    chapter_summary = '하나님께서 큰 소리로 부르시되 이 성을 관할하는 자들이 각기 살륙하는 기계를 손에 들고 나아오라 하시매 여섯 사람이 북편 상문 길로 좇아오는데 각 사람의 손에 살륙하는 기계가 있고 그 중의 한 사람은 가는 베옷을 입고 허리에 서기관의 먹 그릇을 찼는데 그들이 들어와서 놋 제단 곁에 서더라고 기록합니다. 그때에 이스라엘 하나님의 영광이 그룹에서 올라와서 전 문지방에 이르더니 여호와께서 가는 베옷을 입고 허리에 서기관의 먹 그릇을 찬 사람을 불러 가라사대 너는 이 성 중 예루살렘 중에 순행하여 그 가운데서 행하는 모든 가증한 일로 인하여 탄식하며 우는 자의 이마에 표하라 하시고 나머지 사람들에게는 그를 따라 성중으로 다니며 죽이되 늙은 자와 젊은 자와 처녀와 어린 아이와 부녀를 다 죽이되 이마에 표 있는 자에게는 가까이 말라 내 성소에서 시작할찌니라 하시매 그들이 성전 앞에 있는 늙은 자들로부터 시작하더라고 기록합니다.',
    chapter_themes = '["여섯 살륙자와 한 서기관", "가는 베옷을 입은 자", "탄식하며 우는 자의 이마에 표", "이마에 표 있는 자는 살리라", "내 성소에서 시작", "늙은 자들로부터 시작"]',
    chapter_context = '성전의 가증한 일들로 인해 시행되는 예루살렘 심판에서 의인들을 구별하여 보호하시는 하나님의 공의와 긍휼을 보여주는 환상입니다.',
    chapter_application = '죄악에 대해 탄식하고 우는 마음을 가져야 하며, 하나님께서 의인과 악인을 구별하여 심판하신다는 것을 믿고, 하나님의 보호의 표를 받는 성도가 되어야 합니다.'
WHERE book_id = 'ez' AND chapter = 9 AND verse = 1;

-- 에스겔 10장: 그룹들과 하나님의 영광이 떠남
UPDATE bible_verses SET
    chapter_title = '그룹들 위의 하나님의 영광이 성전을 떠나심',
    chapter_summary = '내가 보니 그룹들의 머리 위 궁창에 남보석 같은 것이 나타나는데 보좌 형상 같더라 하나님이 가는 베옷 입은 사람에게 말씀하여 가라사대 너는 그룹 밑바퀴 사이로 들어가서 그룹들 사이에서 숯불을 두 손에 가득히 움켜 가지고 성읍 위에 흩으라 하시매 그가 내 목전에 들어가더라고 기록합니다. 그때에 그룹들이 성전 우편에 서 있고 구름이 안뜰에 가득하며 여호와의 영광이 그룹에서 올라와서 성전 문지방에 이르니 구름이 성전에 가득하며 여호와의 영광의 광휘가 뜰에 가득하더라고 하며, 그룹들의 날개 소리는 전능하신 하나님의 말씀하시는 음성 같아서 바깥뜰까지 들리더라고 기록합니다. 또한 여호와의 영광이 성전 문지방을 떠나서 그룹들 위에 머물고 그룹들이 날개를 들고 내 목전에서 땅에서 올라가는데 그들이 나갈 때에 바퀴도 그 곁에서 함께 하고 여호와의 전 동문 어귀에 머물더니 이스라엘 하나님의 영광이 그 위에 덮였더라고 기록합니다.',
    chapter_themes = '["그룹들 위의 보좌", "숯불을 성읍 위에 흩음", "구름이 성전에 가득", "여호와의 영광이 떠남", "날개 소리가 전능하신 하나님의 음성 같음", "동문 어귀에 머물음"]',
    chapter_context = '하나님의 영광이 점진적으로 성전을 떠나시는 과정을 보여주는 환상으로, 이스라엘에 대한 하나님의 심판과 버리심을 상징합니다.',
    chapter_application = '하나님의 임재와 영광을 소중히 여겨야 하며, 죄로 인해 하나님의 영광이 떠나시지 않도록 주의해야 하고, 하나님의 거룩하심과 영광을 경외해야 합니다.'
WHERE book_id = 'ez' AND chapter = 10 AND verse = 1;

-- 에스겔 11장: 거짓 평안과 새 마음의 약속
UPDATE bible_verses SET
    chapter_title = '거짓 평안을 말하는 지도자들과 새 마음의 약속',
    chapter_summary = '성령이 에스겔을 들어 여호와의 전 동문으로 가져가시니 그 문에 이십오 명이 있는데 그 중에서 앗술의 아들 야아사냐와 브나야의 아들 블라댜를 보았는데 그들은 백성의 방백이더라 하나님이 내게 이르시되 인자야 이는 이 성중에서 악을 꾀하며 잘못된 꾀를 베푸는 자들이라 그들의 말이 집 건축할 때가 가깝지 아니하니 이 성읍은 가마가 되고 우리는 고기가 된다 하는도다 그러므로 인자야 너는 그들을 쳐서 예언하라 예언하라 하시기로 여호와의 신이 내게 임하여 가라사대 너는 말하기를 여호와의 말씀에 이스라엘 족속아 너희가 이같이 말하였도다 너희 마음에 일어나는 것을 내가 다 아노라고 하시며, 내가 그들을 사로잡혀 간 열방 중에서 모으며 그들에게 허하였던 이스라엘 땅을 그들에게 주리라 하고 그들이 그리로 가서 그 가운데 모든 가증한 것과 모든 가증한 일을 제하여 버릴찌라 내가 그들에게 일치한 마음을 주고 그 속에 새 신을 주며 그 몸에서 굳은 마음을 제하고 부드러운 마음을 주어서 내 율례를 좇으며 내 규례를 지켜 행하게 하리니 그들은 내 백성이 되고 나는 그들의 하나님이 되리라고 약속하십니다.',
    chapter_themes = '["잘못된 꾀를 베푸는 자들", "가마가 되고 고기가 됨", "마음에 일어나는 것을 다 아심", "열방 중에서 모음", "일치한 마음과 새 신", "굳은 마음을 제하고 부드러운 마음"]',
    chapter_context = '거짓된 안전감을 조장하는 지도자들에 대한 책망과 함께 미래의 영적 회복과 새 언약에 대한 약속을 제시한 내용입니다.',
    chapter_application = '거짓된 평안에 속지 말고, 하나님께서 마음의 생각까지 아신다는 것을 인정하며, 새 마음과 새 영을 받아 하나님의 백성으로 살아야 합니다.'
WHERE book_id = 'ez' AND chapter = 11 AND verse = 1;

-- 에스겔 12장: 사로잡힐 자의 행장을 꾸리라
UPDATE bible_verses SET
    chapter_title = '포로의 행장을 꾸리고 땅을 파고 나가는 상징적 행동',
    chapter_summary = '여호와의 말씀이 내게 임하여 가라사대 인자야 너는 패역한 족속 중에 거하도다 그들은 볼 눈이 있어도 보지 아니하고 들을 귀가 있어도 듣지 아니하나니 그들은 패역한 족속임이니라 그러므로 인자야 너는 행장을 꾸리되 포로의 행장 같이 하고 그들의 목전에서 낮에 이사하되 네 곳에서 다른 곳으로 이사하라 비록 그들이 패역한 족속이라도 혹시 보리라 너는 낮에 그들의 목전에서 네 행장을 포로의 행장 같이 내어놓고 저물 때에 그들의 목전에서 포로 같이 나가되 그들의 목전에서 담을 파고 그리로 가지고 나가라 하시며, 내가 왕에게 대한 징조니라 예루살렘에 있는 왕과 온 이스라엘 족속에게 대한 징조라 하고 너는 말하기를 나는 너희 징조라 내가 행한 대로 그들이 당하여 사로잡혀 옮겨갈 것이라 하라 그 중의 왕은 어둠 중에 어깨에 행장을 메고 나가리라 그들이 담을 파고 그것으로 내어갈 것이며 그가 그 얼굴을 가리고 눈으로 땅을 보지 못하리라고 예언하십니다.',
    chapter_themes = '["패역한 족속", "포로의 행장을 꾸림", "담을 파고 나감", "시드기야 왕에 대한 징조", "어둠 중에 어깨에 행장을 멤", "얼굴을 가리고 땅을 보지 못함"]',
    chapter_context = '시드기야 왕의 도망과 포로됨을 미리 보여주는 상징적 행동을 통해 예루살렘의 멸망과 포로를 예언한 내용입니다.',
    chapter_application = '하나님의 경고를 무시하지 말고, 상징적 행동을 통해서도 하나님의 뜻을 깨달아야 하며, 교만한 왕도 하나님의 심판을 피할 수 없음을 인정해야 합니다.'
WHERE book_id = 'ez' AND chapter = 12 AND verse = 1;

-- 에스겔 13장: 거짓 선지자들에 대한 심판
UPDATE bible_verses SET
    chapter_title = '자기 마음에서 나온 것을 예언하는 거짓 선지자들',
    chapter_summary = '여호와의 말씀이 내게 임하여 가라사대 인자야 이스라엘의 예언하는 선지자들을 쳐서 예언하되 자기 마음에서 나온 것을 예언하는 자에게 말하기를 너희는 여호와의 말씀을 들으라 하고 주 여호와의 말씀에 본 것이 없이 자기 심령을 따라 예언하는 우매한 선지자에게 화 있을진저 이스라엘아 너의 선지자들은 황무지에 있는 여우 같으니라 하시며, 너희가 성벽을 바르지 아니하며 이스라엘 족속을 위하여 성벽을 쌓지 아니하고 여호와의 날에 전장에 서지 못하게 하였느니라 그들이 허탄한 것과 거짓 복술을 말하며 여호와께서 보내지 아니하셨어도 여호와의 말씀이라 하며 사람으로 자기 말이 응하기를 바라게 하거니와 너희가 말하기는 여호와의 말씀이라 하여도 내가 말한 것이 아닌즉 어찌 허탄한 묵시를 보며 거짓 복술을 말한 것이 아니냐 하시며, 또한 자기 마음대로 예언하는 부녀들도 쳐서 예언하여 말하기를 손목마다 수건을 꿰어 매고 키 큰 자나 작은 자의 머리를 위하여 수건을 만드는 부녀들에게 화 있을진저 너희가 내 백성의 영혼을 사냥하고자 하여 너희 자신의 영혼을 살리려 하는가 하시고 선고하십니다.',
    chapter_themes = '["자기 마음에서 나온 예언", "황무지의 여우 같은 선지자", "성벽을 바르지 않음", "허탄한 묵시와 거짓 복술", "손목마다 수건을 꿰어 맴", "영혼을 사냥함"]',
    chapter_context = '거짓된 평안을 전하며 백성들을 속이는 거짓 선지자들과 무당들에 대한 하나님의 강력한 심판 선언입니다.',
    chapter_application = '하나님의 말씀이 아닌 것을 하나님의 말씀이라 하지 말아야 하며, 거짓 교사들을 분별해야 하고, 영적 사기꾼들을 조심하며, 참된 하나님의 말씀만을 전해야 합니다.'
WHERE book_id = 'ez' AND chapter = 13 AND verse = 1;

-- 에스겔 14장: 우상을 마음에 품은 장로들
UPDATE bible_verses SET
    chapter_title = '마음에 우상을 품고 묻는 자들과 노아 다니엘 욥의 의',
    chapter_summary = '이스라엘 장로들 중 몇 사람이 내게 와서 내 앞에 앉으니 여호와의 말씀이 내게 임하여 가라사대 인자야 이 사람들이 자기 우상을 마음에 들이며 죄악의 거치는 것을 자기 앞에 두었으니 그들이 내게 묻기를 내가 용납하겠느냐 하시고, 그러므로 너는 그들에게 말하여 이르라 주 여호와의 말씀에 이스라엘 족속 중에 무릇 자기 우상을 마음에 들이며 죄악의 거치는 것을 자기 앞에 두고 선지자에게 나아오는 자에게는 나 여호와가 그 우상의 많은 대로 응답하리니 이는 이스라엘 족속이 다 자기 우상으로 인하여 나를 배반하였으므로 내가 그들의 마음에서 그들을 잡으려 함이니라 하시며, 또 그 땅이 내게 범죄하여 크게 패역하면 내가 손을 그 위에 펴서 그 의뢰하는 양식을 끊어 기근을 내려 사람과 짐승을 그 땅에서 끊는다 할지라도 비록 노아 다니엘 욥 이 세 사람이 거기 있을지라도 그들은 자기의 의로 자기의 생명만 건지리라 나 주 여호와의 말이니라 하시고, 내가 만일 악한 짐승으로 그 땅에 통행하여 그 땅을 적막하게 하여 황무케 하므로 짐승을 인하여 사람이 능히 통행치 못하게 한다 할지라도 비록 이 세 사람이 거기 있을지라도 나의 삶을 두고 맹세하노니 그들은 자녀도 건지지 못하고 자기만 건지겠고 그 땅은 황무하리라고 선언하십니다.',
    chapter_themes = '["우상을 마음에 들임", "죄악의 거치는 것", "우상의 많은 대로 응답", "노아 다니엘 욥", "자기의 의로 자기 생명만 건짐", "자녀도 건지지 못함"]',
    chapter_context = '겉으로는 하나님께 나아오지만 마음에 우상을 품은 위선적인 지도자들과 개인의 의로는 다른 사람을 구원할 수 없다는 개인 책임의 원리를 다룬 내용입니다.',
    chapter_application = '마음의 우상을 제거하고 진심으로 하나님께 나아가야 하며, 다른 사람의 의에 의존하지 말고 자신의 믿음으로 살아야 하고, 위선적인 신앙을 버려야 합니다.'
WHERE book_id = 'ez' AND chapter = 14 AND verse = 1;

-- 에스겔 15장: 포도나무 비유
UPDATE bible_verses SET
    chapter_title = '쓸모없는 포도나무 가지로 비유된 예루살렘',
    chapter_summary = '여호와의 말씀이 내게 임하여 가라사대 인자야 포도나무가 모든 나무보다 나은 것이 무엇이랴 삼림 중 여러 나무 가운데 있는 그 포도나무 가지가 나은 것이 무엇이랴 그 나무를 가지고 무엇을 제조할 수 있겠느냐 그것으로 무슨 그릇걸이 나무못이라도 만들 수 있겠느냐 불에 던질 화목이 될 뿐이라 불이 그 양끝을 사르고 그 가운데도 태웠으면 제조에 무슨 소용이 있겠느냐 그것이 온전할 때에도 아무 제조에 합당치 아니하거든 하물며 불에 살라지고 탄 후에 어찌 제조에 합당하겠느냐 하시며, 그러므로 주 여호와 내가 말하노라 내가 삼림 중 포도나무를 불에 던질 화목 되게 한 것 같이 예루살렘 거민도 그같이 할지라 내가 그들을 대적할지라 그들이 불에서 나와도 불이 그들을 사르리니 내가 그들을 대적할 때에 그들이 나를 여호와인줄 알리라 내가 그 땅을 황무케 하리니 이는 그들이 범법함이니라 나 주 여호와의 말이니라 하시니라고 선언하십니다.',
    chapter_themes = '["포도나무 비유", "불에 던질 화목", "제조에 합당치 않음", "양끝을 사르고 가운데도 태움", "불에서 나와도 불이 사름", "그들이 범법함"]',
    chapter_context = '열매를 맺지 못하는 포도나무 가지처럼 하나님께 무용한 존재가 된 예루살렘의 운명을 비유로 설명한 심판 예언입니다.',
    chapter_application = '하나님께서 원하시는 영적 열매를 맺어야 하며, 무용한 존재가 되지 않도록 주의해야 하고, 하나님의 백성으로서 가치 있는 삶을 살아야 합니다.'
WHERE book_id = 'ez' AND chapter = 15 AND verse = 1;

-- 에스겔 16장: 음란한 아내 예루살렘
UPDATE bible_verses SET
    chapter_title = '버림받은 아이에서 음란한 아내가 된 예루살렘의 역사',
    chapter_summary = '여호와의 말씀이 내게 임하여 가라사대 인자야 예루살렘으로 그 가증한 일을 알게 하여 이르라 주 여호와께서 예루살렘에 대하여 말씀하시되 네 근본과 난 땅은 가나안이요 네 아비는 아모리 사람이요 네 어미는 헷 사람이라 하시며, 너의 난 때를 말하건대 네가 날 때에 네 배꼽줄을 자르지 아니하였고 너를 물로 씻어 정결케 하지 아니하였고 소금으로 문지르지 아니하였고 강보로 싸지도 아니하였나니 너를 돌아보아 이 중에 한 가지라도 네게 행하여 너를 긍휼히 여길 자가 없었으므로 네가 나던 날에 네 몸이 꺼린바 되어 들에 버리웠었느니라 하시고, 내가 네 곁으로 지나갈 때에 네가 피투성이가 되어 발짓하는 것을 보고 네게 이르기를 너는 피투성이라도 살라 다시 이르기를 너는 피투성이라도 살라 하여 들의 풀같이 번성하게 하였더니 네가 자라고 커서 극히 아름다우며 유방이 뚜렷하고 네 머리털이 자랐으나 네가 오히려 벌거벗어 적신이더라고 기록하며, 그러나 네가 네 아름다움을 믿고 네 명성을 인하여 행음하되 무릇 지나가는 자면 더불어 음란을 행하므로 네 몸이 그들의 것이 되도다 하시며 음행의 죄를 상세히 책망하십니다.',
    chapter_themes = '["근본과 난 땅은 가나안", "들에 버려진 아이", "피투성이라도 살라", "극히 아름다운 처녀", "네 아름다움을 믿고 행음", "지나가는 자와 음란"]',
    chapter_context = '하나님께서 예루살렘을 버려진 아이에서 아름다운 신부로 키우셨으나 그들이 배은망덕하게 음행을 행한 것을 긴 비유로 설명한 내용입니다.',
    chapter_application = '하나님의 은혜를 기억하고 감사해야 하며, 하나님께 받은 축복을 자랑하지 말고, 영적 음행인 우상숭배를 경계하며, 하나님께만 신실해야 합니다.'
WHERE book_id = 'ez' AND chapter = 16 AND verse = 1;

-- 에스겔 17장: 두 독수리와 포도나무 비유
UPDATE bible_verses SET
    chapter_title = '두 큰 독수리와 백향목 꼭대기의 비유',
    chapter_summary = '여호와의 말씀이 내게 임하여 가라사대 인자야 너는 이스라엘 족속에게 수수께끼와 비유를 베풀어 이르라 주 여호와의 말씀에 채색이 구비하고 날개가 크고 깃이 길고 털이 숱한 큰 독수리가 레바논에 이르러 백향목 꼭대기를 취하되 그 연한 가지 끝을 꺾어다가 장사하는 땅에 가져다가 상고의 성읍에 두고 또 그 땅의 종자를 취하여 옥토에 심되 수양버들 가지처럼 큰 물가에 심더니 그것이 자라며 퍼져서 높지 아니한 포도나무 곧 그 가지는 독수리를 향하고 뿌리는 독수리 아래 있는 포도나무가 되매 능히 가지를 내고 잎사귀를 피우더라 하시며, 또 날개가 크고 털이 많은 다른 큰 독수리가 있었는데 그 포도나무가 심긴 두둑에서 뿌리가 그를 향하고 가지가 그를 향하여 그로 자기에게 물을 주기를 바랐도다 그것이 좋은 토지 큰 물가에 심기운바 됨은 가지를 내고 열매를 맺어서 아름다운 포도나무가 되게 하려 하였음이니라 하시고, 너는 이르기를 주 여호와의 말씀에 그것이 형통하겠느냐 그 뿌리를 빼며 실과를 따므로 그것으로 시들게 하지 아니하겠느냐 그 연한 잎사귀가 다 시들리니 큰 힘이나 많은 백성이 아니라도 그 뿌리를 빼리라고 해석하십니다.',
    chapter_themes = '["큰 독수리와 백향목", "연한 가지 끝을 꺾음", "상고의 성읍", "높지 아니한 포도나무", "다른 큰 독수리", "뿌리를 빼며 실과를 따름"]',
    chapter_context = '바벨론과 이집트를 두 독수리로, 여호야긴과 시드기야를 포도나무로 비유하여 유다의 정치적 배신과 그 결과를 예언한 우화입니다.',
    chapter_application = '하나님보다 세상 권력을 의지하지 말아야 하며, 정치적 기회주의를 경계하고, 하나님과의 언약을 신실하게 지키며, 이중적인 마음을 버려야 합니다.'
WHERE book_id = 'ez' AND chapter = 17 AND verse = 1;

-- 에스겔 18장: 개인 책임의 원리
UPDATE bible_verses SET
    chapter_title = '아비가 신 포도를 먹었으므로 아들의 이가 시다 - 개인 책임',
    chapter_summary = '여호와의 말씀이 내게 임하여 가라사대 너희가 이스라엘 땅에 대한 속담에 이르기를 아비가 신 포도를 먹었으므로 그 아들의 이가 시다 함은 어찜이뇨 나 주 여호와가 말하노라 내가 나의 삶을 두고 맹세하노니 너희가 이스라엘 가운데서 다시는 이 속담을 쓰지 못하게 되리라 모든 영혼이 다 내게 속한지라 아비의 영혼이 내게 속함 같이 아들의 영혼도 내게 속하였나니 범죄하는 그 영혼이 죽으리라 하시며, 사람이 만일 의로워서 법과 의를 행하며 산 위에서 제물을 먹지 아니하며 이스라엘 족속의 우상에게 눈을 들지 아니하며 이웃의 아내를 더럽히지 아니하며 월경 중의 여인을 가까이 하지 아니하며 사람을 학대하지 아니하며 빚진 자의 전당물을 도로 주며 강탈하지 아니하며 주린 자에게 식물을 주며 벗은 자에게 의복을 입히며 변리를 위하여 꾸이지 아니하며 이식을 받지 아니하며 스스로 손을 금하여 죄악을 짓지 아니하며 사람 사람 사이에 진실히 판단하며 내 율례를 행하며 내 규례를 지켜 진실히 행하면 그는 의인이니 정녕 살리라 나 주 여호와의 말이니라 하시고, 그가 만일 아들을 낳았는데 그 아들이 강포하여 피를 흘리는 자라 이런 의무는 하나도 행치 아니하고 도리어 산 위에서 제물을 먹으며 이웃의 아내를 더럽히며 가난하고 궁핍한 자를 학대하며 강탈하며 빚진 자의 전당물을 도로 주지 아니하며 우상에게 눈을 들며 가증한 일을 행하며 변리를 위하여 꾸이며 이식을 받는다면 그가 살겠느냐 살지 못하리라 이 모든 가증한 일을 행하였은즉 정녕 죽을찌라 자기의 피가 자기에게 돌아가리라고 선언하십니다.',
    chapter_themes = '["아비가 신 포도를 먹었으므로", "모든 영혼이 다 내게 속함", "범죄하는 그 영혼이 죽음", "의인의 조건들", "강포하여 피를 흘리는 자", "자기의 피가 자기에게 돌아감"]',
    chapter_context = '조상의 죄 때문에 후손이 벌받는다는 잘못된 운명론을 반박하고 개인의 책임과 회개의 가능성을 강조한 중요한 교리적 내용입니다.',
    chapter_application = '개인의 책임을 인정하고, 조상이나 환경을 탓하지 말며, 회개하면 용서받을 수 있다는 소망을 가지고, 의로운 삶을 살기 위해 노력해야 합니다.'
WHERE book_id = 'ez' AND chapter = 18 AND verse = 1;