-- 마태복음 주석 Part 1 (1-14장)
-- 예수님의 탄생, 사역 시작, 산상수훈, 비유들과 초기 기적들

-- 마태복음 1장: 예수 그리스도의 족보와 탄생
UPDATE bible_verses SET
    chapter_title = '예수 그리스도의 족보와 동정녀 탄생',
    chapter_summary = '아브라함과 다윗의 후손 예수 그리스도의 세계를 기록하여 아브라함부터 다윗까지 14대, 다윗부터 바벨론 이주까지 14대, 바벨론 이주부터 그리스도까지 14대로 나누어 제시합니다. 마리아가 요셉과 정혼하고 동거하기 전에 성령으로 잉태한 사실이 나타나자 의로운 요셉이 가만히 끊고자 하나, 주의 사자가 꿈에 나타나 다윗의 후손 요셉아 네 아내 마리아 데려오기를 무서워하지 말라 저에게 잉태된 자는 성령으로 된 것이라 하며, 아들을 낳으리니 이름을 예수라 하라 이는 그가 자기 백성을 저희 죄에서 구원할 자이심이라 하고, 임마누엘 곧 하나님이 우리와 함께 계시다는 예언의 성취임을 밝힙니다.',
    chapter_themes = '["예수님의 족보", "아브라함의 후손", "다윗의 후손", "동정녀 탄생", "요셉의 의로움", "임마누엘 예언"]',
    chapter_context = '구약의 약속이 신약에서 성취되는 것을 보여주며, 예수님이 약속된 메시야임을 족보와 탄생 기사를 통해 증명하는 마태복음의 서론입니다.',
    chapter_application = '하나님의 약속은 반드시 성취되며, 예수님이 구원자 되심을 믿고, 하나님이 우리와 함께 하신다는 임마누엘의 은혜를 누려야 합니다.'
WHERE book_id = 'mt' AND chapter = 1 AND verse = 1;

-- 마태복음 2장: 동방박사들과 이집트 피난, 헤롯의 학살
UPDATE bible_verses SET
    chapter_title = '동방박사들의 경배와 이집트 피난',
    chapter_summary = '헤롯 왕 때에 예수께서 유대 베들레헴에서 나시매 동방으로부터 박사들이 예루살렘에 와서 유대인의 왕으로 나신 이가 어디 계시냐 우리가 동방에서 그의 별을 보고 그에게 경배하러 왔노라 하니 헤롯 왕과 온 예루살렘이 소동합니다. 대제사장들과 백성의 서기관들에게 그리스도가 어디서 나겠느냐 물으니 미가 선지자의 글을 인용하여 베들레헴이라 답하고, 박사들이 별의 인도로 아기를 찾아 경배하며 황금과 유향과 몰약을 예물로 드린 후 꿈에 지시하심을 받아 헤롯에게로 돌아가지 않고 다른 길로 본국에 돌아갑니다. 주의 사자가 요셉에게 꿈에 나타나 헤롯이 아기를 찾아 죽이려 하니 이집트로 피하라 하여 호세아 선지자의 말씀이 응하고, 헤롯이 박사들에게 속은 줄 알고 크게 노하여 베들레헴과 그 모든 지경 안에 있는 사내아이를 두 살부터 그 아래로 다 죽여 예레미야의 애곡 예언이 성취됩니다.',
    chapter_themes = '["동방박사들", "별의 인도", "베들레헴 예언", "황금 유향 몰약", "이집트 피난", "헤롯의 학살"]',
    chapter_context = '예수님이 이방인들에게도 경배받을 왕이시며, 구약 예언들이 정확히 성취되고 있음을 보여주는 초기 사건들입니다.',
    chapter_application = '별을 따라 예수님을 찾아온 박사들처럼 진실한 마음으로 주님을 경배하고, 어려운 상황에서도 하나님의 보호하심을 신뢰해야 합니다.'
WHERE book_id = 'mt' AND chapter = 2 AND verse = 1;

-- 마태복음 3장: 세례 요한의 사역과 예수님의 세례
UPDATE bible_verses SET
    chapter_title = '세례 요한의 회개 선포와 예수님의 세례',
    chapter_summary = '그 때에 세례 요한이 이르러 유대 광야에서 전파하여 가로되 회개하라 천국이 가까왔느니라 하니 저는 선지자 이사야로 말씀하신 바 광야에 외치는 자의 소리가 있어 가로되 너희는 주의 길을 예비하라 그의 첩경을 평탄케 하라 하신 그이라 합니다. 요한은 약대털 옷을 입고 허리에 가죽띠를 띠고 음식은 메뚜기와 석청이었고, 바리새인과 사두개인들이 많이 와서 세례를 받으려 하나 독사의 자식들아 누가 너희에게 임할 진노를 피하라 하더냐 하며 회개에 합당한 열매를 맺으라 하고, 나는 물로 세례를 주거니와 내 뒤에 오시는 이는 나보다 능력이 많으시니 나는 그의 신을 들기도 감당치 못하겠노라 그는 성령과 불로 세례를 주실 것이라 합니다. 이때에 예수께서 갈릴리로부터 요단강에 이르러 요한에게 세례를 받으려 하시니 요한이 만류하나 예수님이 이와 같이 하여 모든 의를 이루는 것이 합당하다 하시고 세례를 받으실 때 하늘이 열리고 하나님의 성령이 비둘기같이 내려와 그의 위에 임하시고 하늘로서 소리가 있어 말씀하시되 이는 내 사랑하는 아들이요 내 기뻐하는 자라 하십니다.',
    chapter_themes = '["세례 요한의 전파", "회개하라 천국이 가까이", "독사의 자식들", "회개에 합당한 열매", "예수님의 세례", "성령 강림과 하늘의 음성"]',
    chapter_context = '예수님의 공생애 시작을 위한 준비 단계로 세례 요한이 길을 예비하고, 예수님이 세례를 통해 공식적으로 메시야 사역을 시작하심을 보여줍니다.',
    chapter_application = '진정한 회개의 열매를 맺으며 살고, 예수님처럼 모든 의를 이루는 삶을 추구하며, 성령의 인도하심을 받아야 합니다.'
WHERE book_id = 'mt' AND chapter = 3 AND verse = 1;

-- 마태복음 4장: 광야에서의 시험과 사역 시작
UPDATE bible_verses SET
    chapter_title = '광야 시험과 갈릴리에서의 사역 시작',
    chapter_summary = '그 때에 예수께서 성령에게 이끌리어 마귀에게 시험을 받으러 광야로 가사 사십 일을 밤낮으로 금식하신 후에 주리신지라 시험하는 자가 가서 가로되 네가 만일 하나님의 아들이어든 명하여 이 돌들이 떡덩이가 되게 하라 하나 예수께서 대답하여 가라사대 기록되었으되 사람이 떡으로만 살 것이 아니요 하나님의 입으로 나오는 모든 말씀으로 살 것이라 하셨느니라 하시고, 성전 꼭대기에서 뛰어내리라는 시험과 천하 만국을 보여주며 절하면 주겠다는 시험도 모두 말씀으로 물리치십니다. 요한이 잡힌 것을 들으시고 갈릴리로 물러가사 나사렛을 떠나 스불론과 납달리 지경 해변에 있는 가버나움에 가서 사시니 선지자 이사야의 말이 이루어졌고, 이때부터 비로소 전파하여 가라사대 회개하라 천국이 가까왔느니라 하시며, 갈릴리 해변에 다니시다가 어업에 종사하는 시몬 베드로와 안드레, 야고보와 요한을 부르시어 나를 따라 오라 내가 너희로 사람을 낚는 어부가 되게 하리라 하시니 곧 그물을 버려 두고 예수를 좇습니다.',
    chapter_themes = '["광야에서 40일 금식", "마귀의 세 번 시험", "말씀으로 물리침", "갈릴리로 이주", "회개하라 천국이 가까이", "첫 제자들 부르심"]',
    chapter_context = '예수님이 공생애를 시작하기 전 마귀의 시험을 이기시고, 갈릴리에서 본격적인 사역과 제자 부르심을 시작하시는 중요한 전환점입니다.',
    chapter_application = '시험이 올 때 하나님의 말씀으로 대항하고, 주님의 부르심에 즉시 순종하며, 사람을 낚는 어부가 되어 복음을 전해야 합니다.'
WHERE book_id = 'mt' AND chapter = 4 AND verse = 1;

-- 마태복음 5장: 산상수훈 - 팔복과 제자의 삶
UPDATE bible_verses SET
    chapter_title = '산상수훈 - 팔복과 제자의 신분과 사명',
    chapter_summary = '무리를 보시고 산에 올라가 앉으시니 제자들이 나아온지라 입을 열어 가르쳐 가라사대 심령이 가난한 자는 복이 있나니 천국이 저희 것임이요 애통하는 자는 복이 있나니 저희가 위로를 받을 것임이요 온유한 자는 복이 있나니 저희가 땅을 기업으로 받을 것임이요 의에 주리고 목마른 자는 복이 있나니 저희가 배부를 것임이요 긍휼히 여기는 자는 복이 있나니 저희가 긍휼히 여김을 받을 것임이요 마음이 청결한 자는 복이 있나니 저희가 하나님을 볼 것임이요 화평케 하는 자는 복이 있나니 저희가 하나님의 아들이라 일컬음을 받을 것임이요 의를 위하여 핍박을 받는 자는 복이 있나니 천국이 저희 것임이라 하시고, 제자들을 세상의 소금과 빛이라 하시며 너희 빛을 사람 앞에 비추어 저희로 너희 착한 행실을 보고 하늘에 계신 너희 아버지께 영광을 돌리게 하라 하시고, 율법이나 선지자를 폐하러 온 것이 아니라 완전케 하려 함이라 하시며 너희 의가 서기관과 바리새인보다 더 낫지 못하면 결단코 천국에 들어가지 못하리라 하십니다.',
    chapter_themes = '["팔복", "심령이 가난한 자", "의에 주리고 목마른 자", "세상의 소금과 빛", "율법 완성", "서기관과 바리새인보다 나은 의"]',
    chapter_context = '예수님의 핵심 교훈인 산상수훈의 시작으로, 하나님 나라 백성의 성품과 사명을 제시하는 기독교 윤리의 기초입니다.',
    chapter_application = '팔복의 성품을 추구하며, 세상의 소금과 빛 역할을 하고, 율법의 정신을 완성하는 더 높은 차원의 의를 실천해야 합니다.'
WHERE book_id = 'mt' AND chapter = 5 AND verse = 1;

-- 마태복음 6장: 산상수훈 - 참된 경건과 주기도문
UPDATE bible_verses SET
    chapter_title = '참된 경건생활과 주기도문, 재물관',
    chapter_summary = '사람에게 보이려고 그들 앞에서 너희 의를 행치 말라 하시며 구제할 때에 외식하는 자들같이 사람에게서 영광을 받으려고 회당과 거리에서 나팔을 불지 말고 은밀한 중에 하라 하시고, 기도할 때에도 외식하는 자들같이 회당과 큰 거리 어귀에 서서 기도하기를 좋아하지 말고 골방에 들어가 문을 닫고 은밀한 중에 계신 네 아버지께 기도하라 하시며, 그러므로 너희는 이렇게 기도하라 하시고 하늘에 계신 우리 아버지여 이름이 거룩히 여김을 받으시오며 나라이 임하옵시며 뜻이 하늘에서 이룬 것같이 땅에서도 이루어지이다 오늘날 우리에게 일용할 양식을 주옵시고 우리가 우리에게 죄 지은 자를 사하여 준 것같이 우리 죄를 사하여 주옵시고 우리를 시험에 들게 하지 마옵시고 다만 악에서 구하옵소서 나라와 권세와 영광이 아버지께 영원히 있사옵나이다 아멘 하는 주기도문을 가르쳐 주시고, 금식할 때에도 외식하지 말며, 너희를 위하여 보물을 땅에 쌓아 두지 말고 하늘에 쌓아 두라 네 보물 있는 그곳에는 네 마음도 있느니라 하시며 하나님과 재물을 겸하여 섬기지 못한다 하십니다.',
    chapter_themes = '["구제", "기도", "금식", "주기도문", "하늘에 계신 아버지", "하늘의 보물", "하나님과 재물"]',
    chapter_context = '외식적 종교행위를 경계하고 하나님과의 진실한 관계를 추구하는 참된 경건의 본질을 가르치시는 말씀입니다.',
    chapter_application = '은밀한 중에 하나님과 교제하고, 주기도문의 정신으로 기도하며, 하늘의 가치를 추구하고 하나님을 최우선으로 섬겨야 합니다.'
WHERE book_id = 'mt' AND chapter = 6 AND verse = 1;

-- 마태복음 7장: 산상수훈 - 판단하지 말라, 황금률
UPDATE bible_verses SET
    chapter_title = '판단 금지와 황금률, 좁은 문과 열매로 아는 법',
    chapter_summary = '비판을 받지 아니하려거든 비판하지 말라 너희의 비판하는 그 비판으로 너희가 비판을 받을 것이요 어찌하여 형제의 눈 속에 있는 티는 보고 네 눈 속에 있는 들보는 깨닫지 못하느냐 하시며 먼저 네 눈에서 들보를 빼어라 그 후에야 밝히 보고 형제의 눈에서 티를 빼리라 하시고, 구하라 그러면 너희에게 주실 것이요 찾으라 그러면 찾을 것이요 문을 두드리라 그러면 너희에게 열릴 것이니 구하는 이마다 받을 것이요 찾는 이가 찾을 것이요 두드리는 이에게 열릴 것이니라 하시며, 그러므로 무엇이든지 남에게 대접을 받고자 하는 대로 너희도 남을 대접하라 이것이 율법이요 선지자니라 하시고, 좁은 문으로 들어가라 멸망으로 인도하는 문은 크고 그 길이 넓어 그리로 들어가는 자가 많고 생명으로 인도하는 문은 좁고 길이 협착하여 찾는 이가 적음이니라 하시며 거짓 선지자들을 삼가라 양의 옷을 입고 와도 속에는 노략질하는 이리라 그의 열매로 그들을 알지니 가시나무에서 포도를 또는 엉겅퀴에서 무화과를 따겠느냐 하십니다.',
    chapter_themes = '["비판하지 말라", "들보와 티", "구하라 찾으라 두드리라", "황금률", "좁은 문", "거짓 선지자", "열매로 아는 법"]',
    chapter_context = '산상수훈의 결론 부분으로 인간관계의 원리와 신앙생활의 실제적 지침을 제시하시는 교훈입니다.',
    chapter_application = '다른 사람을 판단하기 전에 자신을 돌아보고, 황금률대로 남을 대접하며, 좁은 길을 선택하고, 열매로 참된 것을 분별해야 합니다.'
WHERE book_id = 'mt' AND chapter = 7 AND verse = 1;

-- 마태복음 8장: 치유의 기적들과 제자도의 대가
UPDATE bible_verses SET
    chapter_title = '나병환자와 백부장의 믿음, 여러 치유들',
    chapter_summary = '예수께서 산에서 내려오시니 허다한 무리가 좇았는데 한 나병환자가 나아와 절하며 주여 원하시면 저를 깨끗케 하실 수 있나이다 하니 예수께서 손을 내밀어 저에게 대시며 내가 원하노니 깨끗함을 받으라 하시니 즉시 그 나병이 깨끗하여진지라 하시고, 가버나움에 들어가시니 한 백부장이 나아와 간구하여 가로되 주여 내 하인이 중풍병으로 집에 누워 몹시 괴로워하나이다 하니 예수께서 가서 고쳐 주리라 하시거늘 백부장이 대답하여 가로되 주여 내 집에 들어오심을 나는 감당치 못하겠사오니 다만 말씀으로만 하옵소서 그러면 내 하인이 낫겠삽나이다 하니 예수께서 들으시고 기이히 여겨 가라사대 내가 진실로 너희에게 이르노니 이스라엘 중 아무에게서도 이만한 믿음을 만나보지 못하였노라 하시고 백부장에게 이르시되 가라 네 믿은 대로 될지어다 하시니 그 시로 하인이 나으니라 하시며, 베드로의 장모의 열병을 고치시고 저물매 귀신 들린 자를 많이 데려오거늘 말씀으로 귀신들을 쫓아 내시고 병든 자를 다 고치시니 이는 선지자 이사야로 하신 말씀에 우리 연약한 것을 친히 담당하시고 병을 짊어지셨도다 함을 이루려 하심이더라 하십니다.',
    chapter_themes = '["나병환자 치유", "백부장의 믿음", "말씀으로만 하옵소서", "베드로 장모", "연약한 것을 담당", "제자도의 대가"]',
    chapter_context = '산상수훈 후에 예수님의 치유 능력이 실제로 나타나며, 믿음의 중요성과 제자도의 진정한 의미를 보여주는 사건들입니다.',
    chapter_application = '백부장같은 믿음을 가지고 하나님의 말씀을 신뢰하며, 예수님께서 우리의 연약함을 아시고 도우심을 감사하고, 제자도의 대가를 각오해야 합니다.'
WHERE book_id = 'mt' AND chapter = 8 AND verse = 1;

-- 마태복음 9장: 중풍병자와 세리 마태의 부르심, 여러 기적들
UPDATE bible_verses SET
    chapter_title = '중풍병자 치유와 마태의 부르심, 믿음의 치유들',
    chapter_summary = '배에 올라 건너편으로 가사 본 동네에 이르시니 침상에 누운 중풍병자를 사람들이 데리고 오거늘 예수께서 저희의 믿음을 보시고 중풍병자에게 이르시되 소자야 안심하라 네 죄 사함을 받았느니라 하시니 어떤 서기관들이 속으로 이르되 이 사람이 참람하도다 하거늘 예수께서 그 생각을 아시고 가라사대 너희가 어찌하여 마음에 악한 생각을 하느냐 네 죄 사함을 받았느니라 하는 말과 일어나 걸어가라 하는 말이 어느 것이 쉽겠느냐 그런즉 인자가 땅에서 죄를 사하는 권세가 있는 줄을 너희로 알게 하려 하노라 하시고 중풍병자에게 말씀하시되 일어나 네 침상을 가지고 집으로 가라 하시니 그가 일어나 집으로 돌아가거늘 무리가 보고 두려워하며 이런 권세를 사람에게 주신 하나님께 영광을 돌리니라 하시고, 세관에 앉은 마태라 하는 사람을 보시고 나를 좇으라 하시니 일어나 좇으니라 하시며, 야이로의 딸을 살리시고 혈루증 앓는 여자를 고치시며 소경 둘과 벙어리를 고치시되 너희 믿음대로 되라 하십니다.',
    chapter_themes = '["중풍병자 치유", "죄 사함의 권세", "마태의 부르심", "야이로의 딸", "혈루증 여자", "너희 믿음대로 되라"]',
    chapter_context = '예수님의 치유 능력이 죄 사함의 권세와 연결되어 나타나며, 세리 마태를 부르심으로 죄인을 구원하시는 사역의 본질을 보여줍니다.',
    chapter_application = '죄 사함과 치유의 은혜를 받고, 예수님의 부르심에 즉시 순종하며, 믿음대로 되는 하나님의 역사를 경험해야 합니다.'
WHERE book_id = 'mt' AND chapter = 9 AND verse = 1;

-- 마태복음 10장: 열두 제자 파송과 사명
UPDATE bible_verses SET
    chapter_title = '열두 제자 파송과 복음 전도의 사명',
    chapter_summary = '열두 제자를 부르사 더러운 귀신을 쫓아내며 모든 병과 모든 약한 것을 고치는 권능을 주시니라 하시고 열두 사도의 이름은 베드로라 하는 시몬을 비롯하여 안드레와 세베대의 아들 야고보와 요한, 빌립과 바돌로매, 도마와 세리 마태, 알패오의 아들 야고보와 다대오, 가나안인 시몬과 가룟 유다라 하시며, 이 열둘을 내어 보내시며 명하여 가라사대 이방인의 길로도 가지 말고 사마리아인의 고을에도 들어가지 말고 차라리 이스라엘 집의 잃어버린 양에게로 가서 가면서 전파하여 말하되 천국이 가까왔다 하고 병든 자를 고치며 죽은 자를 살리며 나병환자를 깨끗하게 하며 귀신을 쫓아내되 너희가 거저 받았으니 거저 주라 하시고, 뱀같이 지혜롭고 비둘기같이 순결하라 하시며 사람들을 삼가라 저희가 너희를 공회에 넘겨 주고 저희 회당에서 채찍질하리라 하시고 아비보다 나를 더 사랑하는 자는 내게 합당치 아니하고 자기 십자가를 지고 나를 좇지 않는 자도 내게 합당치 아니하니라 하십니다.',
    chapter_themes = '["열두 제자 명단", "권능 주심", "이스라엘 집의 잃어버린 양", "거저 받았으니 거저 주라", "뱀같이 지혜롭고 비둘기같이 순결", "자기 십자가"]',
    chapter_context = '예수님이 제자들에게 복음 전파의 사명을 주시며, 사역의 원리와 고난에 대한 각오를 가르치시는 파송 교육입니다.',
    chapter_application = '받은 은혜를 거저 나누어주고, 지혜롭고 순결하게 복음을 전하며, 자기 십자가를 지고 주님을 따르는 제자도를 실천해야 합니다.'
WHERE book_id = 'mt' AND chapter = 10 AND verse = 1;

-- 마태복음 11장: 세례 요한과 예수님, 안식을 주시는 주님
UPDATE bible_verses SET
    chapter_title = '세례 요한의 질문과 예수님이 주시는 안식',
    chapter_summary = '예수께서 열두 제자에게 명하시기를 마치시고 저희 여러 동네에서 가르치시며 전도하시려고 거기를 떠나가시니라 하시고, 요한이 옥에서 그리스도의 하신 일을 듣고 제자들을 보내어 예수께 여쭈어 가로되 오실 그이가 당신이오니이까 우리가 다른 이를 기다리오리이까 하니 예수께서 대답하여 가라사대 너희가 가서 듣고 보는 것을 요한에게 고하되 소경이 보며 앉은뱅이가 걸으며 나병환자가 깨끗함을 받으며 귀머거리가 들으며 죽은 자가 살아나며 가난한 자에게 복음이 전파된다 하고 나를 인하여 실족지 않는 자는 복이 있도다 하라 하시고, 고라신과 벳새다와 가버나움을 책망하시며 화 있을진저 하시고, 그 때에 예수께서 대답하여 가라사대 천지의 주재이신 아버지여 이것을 지혜롭고 슬기 있는 자들에게는 숨기시고 어린 아이들에게는 나타내심을 감사하나이다 하시고 수고하고 무거운 짐 진 자들아 다 내게로 오라 내가 너희를 쉬게 하리라 나는 마음이 온유하고 겸손하니 나의 멍에를 메고 내게 배우라 그러면 너희 마음이 쉼을 얻으리니 이는 나의 멍에는 쉽고 나의 짐은 가벼움이라 하시니라.',
    chapter_themes = '["세례 요한의 의심", "메시야 증거들", "고라신 벳새다 가버나움", "어린 아이들에게 나타내심", "수고하고 무거운 짐 진 자", "쉽고 가벼운 멍에"]',
    chapter_context = '메시야이신 예수님의 정체성이 확증되고, 거부하는 도시들을 책망하시며, 참된 안식을 주시는 주님의 초청이 나타납니다.',
    chapter_application = '예수님이 약속하신 메시야임을 확신하고, 교만을 버리고 어린 아이 같은 마음을 가지며, 예수님께 나아와 참된 안식을 얻어야 합니다.'
WHERE book_id = 'mt' AND chapter = 11 AND verse = 1;

-- 마태복음 12장: 안식일 논쟁과 표적을 구하는 세대
UPDATE bible_verses SET
    chapter_title = '안식일 논쟁과 표적을 구하는 악한 세대',
    chapter_summary = '그 때에 예수께서 안식일에 밀밭 사이로 가실새 제자들이 시장하여 이삭을 잘라 먹으니 바리새인들이 보고 예수께 고하되 보시오 당신의 제자들이 안식일에 하지 못할 일을 하나이다 하거늘 예수께서 가라사대 다윗이 자기와 그 함께한 자들이 시장할 때에 한 일을 읽지 못하였느냐 하나님의 전에 들어가서 제사장 외에는 먹지 못하는 진설병을 먹고 함께한 자들에게도 주지 아니하였느냐 하시고 또 안식일에 제사장들이 성전 안에서 안식을 범하여도 죄가 없음을 너희가 율법에서 읽지 못하였느냐 내가 너희에게 이르노니 성전보다 더 큰 이가 여기 있느니라 나는 자비를 원하고 제사를 원치 아니하노라 하신 뜻을 너희가 알았더면 무죄한 자를 죄로 정치 아니하였으리라 인자는 안식일의 주인이니라 하시고, 손 마른 사람을 안식일에 고치시며, 바리새인들이 예수를 베엘세불의 힘으로 귀신을 쫓는다 비방하자 스스로 분쟁하는 나라마다 황폐하여질 것이요 성령을 거스려 말하는 자는 이 세상과 오는 세상에도 사하심을 얻지 못하리라 하시고, 표적 구하는 악하고 음란한 세대에게 요나의 표적 밖에는 보일 표적이 없다 하십니다.',
    chapter_themes = '["안식일의 주인", "자비를 원하고 제사를 원치 않음", "손 마른 사람 치유", "베엘세불 논쟁", "성령을 거스리는 죄", "요나의 표적"]',
    chapter_context = '안식일의 참된 의미와 예수님의 권위, 성령의 역사에 대한 바리새인들의 무지와 고의적 반대가 드러나는 중요한 갈등상황입니다.',
    chapter_application = '형식적 종교보다 자비를 실천하고, 성령의 역사를 인정하며, 표적보다는 말씀을 믿고, 예수님을 안식일의 주인으로 인정해야 합니다.'
WHERE book_id = 'mt' AND chapter = 12 AND verse = 1;

-- 마태복음 13장: 하나님 나라의 비유들
UPDATE bible_verses SET
    chapter_title = '하나님 나라의 비유들 - 씨 뿌리는 자부터 그물까지',
    chapter_summary = '그 날에 예수께서 집에서 나가사 바닷가에 앉으시매 큰 무리가 그에게로 모여들거늘 배에 올라가 앉으시고 온 무리는 해변에 서 있더니 비유로 여러 가지를 말씀하여 가라사대 씨를 뿌리는 자가 뿌리러 나가서 뿌릴새 더러는 길 가에 떨어지매 새들이 와서 먹어버렸고 더러는 흙이 얕은 돌밭에 떨어지매 흙이 깊지 아니하므로 곧 싹이 나오나 해가 돋은 후에 타져서 뿌리가 없으므로 말랐고 더러는 가시떨기 위에 떨어지매 가시가 자라 기운을 막았고 더러는 좋은 땅에 떨어지매 혹 백배 혹 육십배 혹 삼십배의 결실을 하였느니라 하시고, 가라지 비유와 겨자씨 비유, 누룩 비유, 보화와 진주 비유, 그물 비유를 들어 하나님 나라의 성격과 가치, 성장과 심판을 설명하시며, 비유로 말씀하시는 이유는 하늘나라의 비밀을 깨닫게 하시려 함이라 하시고 새것과 옛것을 그 곳간에서 내어오는 집주인 같다 하십니다.',
    chapter_themes = '["씨 뿌리는 자 비유", "가라지 비유", "겨자씨와 누룩", "보화와 진주", "그물 비유", "새것과 옛것"]',
    chapter_context = '하나님 나라의 다양한 측면을 일곱 개의 비유로 설명하시며, 복음에 대한 사람들의 반응과 나라의 성장, 최종 심판을 가르치신 말씀입니다.',
    chapter_application = '말씀을 받는 좋은 땅이 되고, 하나님 나라의 가치를 최우선으로 여기며, 선악이 함께 자라는 현실을 이해하고, 최종 심판을 준비해야 합니다.'
WHERE book_id = 'mt' AND chapter = 13 AND verse = 1;

-- 마태복음 14장: 세례 요한의 죽음과 오병이어 기적
UPDATE bible_verses SET
    chapter_title = '세례 요한의 순교와 오병이어 기적, 물 위로 걸으심',
    chapter_summary = '그 때에 분봉왕 헤롯이 예수의 소문을 듣고 그 신하들에게 이르되 이는 세례 요한이라 저가 죽은 자 가운데서 살아났으니 그러므로 이런 능력이 그 속에서 역사하느니라 하더라 하시고, 헤롯이 그 동생 빌립의 아내 헤로디아의 일로 요한을 잡아 결박하여 옥에 가두었으나 백성들이 요한을 선지자로 여기므로 죽이기를 무서워하다가 헤롯의 생일에 헤로디아의 딸이 연석 가운데서 춤을 추어 헤롯을 기쁘게 하니 맹세로 그에게 무엇이든지 달라는 대로 주겠다고 약속하매 그가 제 어미의 시킴을 받아 가로되 세례 요한의 머리를 소반에 담아 여기서 내게 주소서 하니 왕이 근심하나 맹세한 것과 그 함께 앉은 자들을 인하여 주라 명하여 사람을 보내어 요한을 옥에서 목베어 그 머리를 소반에 담아다가 그 여아에게 주니 그가 제 어미에게 가져다 주니라 하시고, 예수께서 들으시고 배를 타고 따로 빈 들로 떠나가시니 무리가 듣고 여러 고을로부터 걸어서 좇아온지라 저녁이 되매 제자들이 나아와 가로되 이곳은 빈 들이요 때도 저물었으니 무리를 보내어 마을에 들어가 먹을 것을 사먹게 하소서 하나 예수께서 가라사대 갈 것 없다 너희가 먹을 것을 주라 하시니 떡 다섯 개와 물고기 두 마리로 오천 명을 먹이시고 열두 바구니에 차고 넘치게 거두시며, 밤에 바다에서 풍랑을 만난 제자들에게 바다 위로 걸어서 오시고 베드로도 물 위로 걸어가게 하십니다.',
    chapter_themes = '["세례 요한의 순교", "헤롯과 헤로디아", "오병이어 기적", "오천 명을 먹이심", "물 위로 걸으심", "베드로의 믿음과 의심"]',
    chapter_context = '의인의 순교와 하나님의 기적적 공급, 그리고 자연을 다스리시는 예수님의 신적 권능이 대조적으로 나타나는 사건들입니다.',
    chapter_application = '의를 위해 순교한 세례 요한을 기억하고, 하나님의 기적적 공급을 믿으며, 풍랑 중에도 예수님을 신뢰하고, 의심보다는 믿음으로 나아가야 합니다.'
WHERE book_id = 'mt' AND chapter = 14 AND verse = 1;