-- 요한계시록 주석 (Revelation Commentary)
-- 작성자: Claude AI 성경 주석 시스템
-- 작성일: 2025년 9월 30일

-- =================================================================
-- 요한계시록 (Revelation) - 22장
-- 주제: 예수 그리스도의 계시, 일곱 교회, 대환난, 새 하늘과 새 땅
-- =================================================================

-- 요한계시록 1장: 예수 그리스도의 계시
UPDATE bible_verses SET
    chapter_title = '예수 그리스도의 계시라 하나님이 그에게 주사',
    chapter_summary = '예수 그리스도의 계시라 하나님이 그에게 주사 반드시 속히 될 일을 그 종들에게 보이시려고 그 천사를 그 종 요한에게 보내어 지시하신 것이라고 시작하며, 이 예언의 말씀을 읽는 자와 듣는 자들과 그 가운데 기록한 것을 지키는 자들이 복이 있나니 때가 가까움이라고 복을 선포합니다. 나는 알파와 오메가라 이제도 있고 전에도 있었고 장차 올 자요 전능한 자라 하시더라고 하나님의 영원성을 선언하며, 인자 같은 이가 발에 끌리는 옷을 입고 가슴에 금띠를 띠고 있는 환상을 보고 죽은 자 같이 엎드렸다가 두려워 말라 나는 처음이요 나중이니 곧 산 자라 내가 전에 죽었었노라 볼지어다 이제 세세토록 살아 있어 사망과 음부의 열쇠를 가졌노니라는 위로를 받습니다.',
    chapter_themes = '["예수 그리스도의 계시", "반드시 속히 될 일", "알파와 오메가", "인자 같은 이", "사망과 음부의 열쇠"]',
    chapter_context = '요한계시록의 서론으로, 이 책의 성격과 목적을 밝히고 영광 중에 나타나신 그리스도의 장엄한 모습을 묘사하며, 일곱 교회에 편지를 쓰라는 명령을 받는 장엄한 환상의 시작입니다.',
    chapter_application = '하나님의 계시인 성경을 읽고 듣고 지키는 자가 복이 있음을 믿고, 알파와 오메가이신 하나님의 영원하심을 경배하며, 영광 중에 계신 그리스도를 두려워하되 그분의 위로를 받고, 사망과 음부의 열쇠를 가지신 그리스도를 의지하며, 교회에 주신 말씀을 잘 전해야 합니다.'
WHERE book_id = 're' AND chapter = 1 AND verse = 1;

-- 요한계시록 2장: 에베소, 서머나, 버가모, 두아디라 교회
UPDATE bible_verses SET
    chapter_title = '일곱 교회에 보내는 편지 (1-4)',
    chapter_summary = '에베소 교회에게는 처음 사랑을 버린 것을 책망하시며 회개하라 그리하지 아니하면 내가 네게 임하여 네 촛대를 그 자리에서 옮기리라고 경고하시고, 서머나 교회에게는 장차 받을 고난을 두려워 말고 죽도록 충성하라 그리하면 내가 생명의 면류관을 네게 주리라고 격려하십니다. 버가모 교회에게는 발람의 교훈과 니골라당의 교훈을 지키는 자들이 있는 것을 책망하시며 회개하라 그리하지 아니하면 내가 네게 속히 임하여 내 입의 검으로 그들과 싸우리라고 경고하시고, 두아디라 교회에게는 나중 행위가 처음 것보다 많은 것을 칭찬하시나 자칭 선지자라 하는 여자 이세벨을 용납함을 책망하십니다.',
    chapter_themes = '["처음 사랑을 버림", "죽도록 충성하라", "발람의 교훈", "이세벨을 용납함", "이기는 그에게는"]',
    chapter_context = '일곱 교회 중 처음 네 교회에 보내는 편지로, 각 교회의 상황에 맞는 칭찬과 책망, 회개의 촉구와 이기는 자에게 주시는 약속을 통해 모든 시대 교회에 주시는 교훈을 담고 있습니다.',
    chapter_application = '처음 사랑을 회복하고 처음 행위를 다시 하며, 고난 중에도 죽도록 충성하고, 거짓 교훈과 타협하지 말며, 교회 안의 이단을 용납하지 말고, 끝까지 이겨서 주님이 주시는 상급을 받으며, 귀 있는 자는 성령이 교회들에게 하시는 말씀을 들어야 합니다.'
WHERE book_id = 're' AND chapter = 2 AND verse = 1;

-- 요한계시록 3장: 사데, 빌라델비아, 라오디게아 교회
UPDATE bible_verses SET
    chapter_title = '일곱 교회에 보내는 편지 (5-7)',
    chapter_summary = '사데 교회에게는 네가 살았다 하는 이름은 가졌으나 죽은 자로다 너는 일깨워 그 남은 바 죽게 된 것을 굳게 하라고 책망하시며, 빌라델비아 교회에게는 적은 능력을 가지고도 내 말을 지키며 내 이름을 배반치 아니하였기에 내가 네 앞에 열린 문을 두었으되 능히 닫을 사람이 없으리라고 약속하십니다. 라오디게아 교회에게는 네가 차지도 아니하고 더웁지도 아니하니 차라리 차든지 더웁든지 하라 네가 미지근하니 내 입에서 너를 토하여 내치리라고 책망하시며, 볼지어다 내가 문 밖에 서서 두드리노니 누구든지 내 음성을 듣고 문을 열면 내가 그에게로 들어가 그로 더불어 먹고 그는 나로 더불어 먹으리라고 초청하십니다.',
    chapter_themes = '["살았다 하나 죽은 자", "열린 문을 두었으되", "미지근하니 토하여 내치리라", "문 밖에 서서 두드리노니", "나와 함께 보좌에 앉게 하리라"]',
    chapter_context = '일곱 교회의 마지막 세 교회에 보내는 편지로, 형식적 신앙의 위험성과 작은 교회의 충성에 대한 격려, 그리고 미지근한 신앙에 대한 강력한 경고와 회복의 초청을 담고 있습니다.',
    chapter_application = '형식적이고 죽은 신앙에서 깨어나 살아있는 신앙을 회복하고, 적은 능력이라도 충성하여 주님이 열어주신 문을 통과하며, 미지근한 신앙을 버리고 뜨겁거나 차가운 확실한 신앙을 가지고, 주님의 음성을 듣고 마음 문을 열어 그분과 교제하며, 끝까지 이겨서 주님과 함께 보좌에 앉는 영광을 얻어야 합니다.'
WHERE book_id = 're' AND chapter = 3 AND verse = 1;

-- 요한계시록 4장: 하늘 보좌의 환상
UPDATE bible_verses SET
    chapter_title = '하늘에 열린 문과 보좌에 앉으신 이',
    chapter_summary = '이 일 후에 내가 보니 하늘에 열린 문이 있는데 내가 들은 바 처음 소리는 나팔 소리 같은데 가로되 이리로 올라오라 이 후에 마땅히 될 일을 내가 네게 보이리라 하시더라고 시작하여, 즉시 내가 성령에 감동하였더니 보라 하늘에 보좌를 베풀었고 그 보좌 위에 앉으신 이가 있는데 앉으신 이의 모양이 벽옥과 홍보석 같고 또 무지개가 그 보좌에 둘렸는데 그 모양이 녹보석 같더라고 묘사합니다. 그 보좌로부터 번개와 음성과 뇌성이 나고 보좌 앞에 일곱 등불 켠 것이 있으니 이는 하나님의 일곱 영이요 보좌 앞에 수정 같은 유리 바다가 있더라고 계속하며, 네 생물이 각각 여섯 날개가 있고 그 안과 주위에 눈이 가득하여 밤낮 쉬지 않고 거룩하다 거룩하다 거룩하다 주 하나님 곧 전능하신 이여 전에도 계셨고 이제도 계시고 장차 오실 자라 하는 찬양을 기록합니다.',
    chapter_themes = '["하늘에 열린 문", "보좌에 앉으신 이", "이십사 장로", "네 생물", "거룩하다 거룩하다 거룩하다"]',
    chapter_context = '교회 시대가 끝나고 마지막 때의 환상이 시작되는 전환점으로, 하늘 보좌에서 벌어지는 장엄한 예배의 모습을 통해 하나님의 절대 주권과 거룩하심을 보여주는 환상입니다.',
    chapter_application = '하늘의 관점에서 세상 역사를 바라보고, 보좌에 앉으신 하나님의 절대 주권을 인정하며, 거룩하신 하나님께 끊임없는 찬양과 경배를 드리고, 이십사 장로처럼 겸손히 면류관을 드리며, 네 생물처럼 하나님의 거룩하심을 선포해야 합니다.'
WHERE book_id = 're' AND chapter = 4 AND verse = 1;

-- 요한계시록 5장: 어린 양이 두루마리를 취하시다
UPDATE bible_verses SET
    chapter_title = '어린 양이 일곱 인으로 봉한 책을 취하시니',
    chapter_summary = '내가 보니 보좌에 앉으신 이의 오른손에 책이 있으니 안팎으로 썼고 일곱 인으로 봉하였더라 또 보니 힘센 천사가 큰 음성으로 외치기를 누가 책을 펴며 그 인을 떼기에 합당하뇨 하니 하늘 위에나 땅 위에나 땅 아래에 능히 책을 펴거나 보거나 할 이가 없더라고 기록하며, 장로 중에 하나가 내게 말하되 울지 말라 보라 유다 지파의 사자 다윗의 뿌리가 이기었으니 이 책과 그 일곱 인을 떼시리라 하더라고 위로합니다. 내가 또 보니 보좌와 네 생물과 장로들 사이에 어린 양이 섰는데 일찍 죽임을 당한 것 같더라고 묘사하며, 그들이 새 노래를 부르니 가로되 책을 가지시고 그 인을 떼기에 합당하시도다 일찍 죽임을 당하사 각 족속과 방언과 백성과 나라 가운데서 사람들을 피로 사서 하나님께 드리시고라는 찬양을 기록합니다.',
    chapter_themes = '["일곱 인으로 봉한 책", "책을 펴기에 합당한 자", "유다 지파의 사자", "일찍 죽임을 당한 어린 양", "피로 사서 하나님께 드리심"]',
    chapter_context = '인류 역사의 마지막을 기록한 봉인된 책을 오직 그리스도만이 열 수 있음을 보여주며, 십자가에서 죽으시고 부활하신 어린 양이신 그리스도의 구속 사역을 찬양하는 천상의 예배 장면입니다.',
    chapter_application = '오직 그리스도만이 역사의 주관자이심을 인정하고, 죽임당하신 어린 양의 희생에 감사하며, 모든 족속에서 피로 사신 구속의 은혜를 찬양하고, 천상의 무수한 천사들과 함께 어린 양께 찬양과 영광을 돌리며, 그리스도의 승리와 권능을 선포해야 합니다.'
WHERE book_id = 're' AND chapter = 5 AND verse = 1;

-- 요한계시록 6장: 일곱 인 중 여섯 인
UPDATE bible_verses SET
    chapter_title = '어린 양이 일곱 인 중에 하나를 떼시니',
    chapter_summary = '내가 보니 어린 양이 일곱 인 중에 하나를 떼시는데 그때에 내가 들으니 네 생물 중에 하나가 우뢰 소리 같이 말하되 와서 보라 하니라고 시작하여 첫째 인에서 흰 말 탄 자가 이기고 또 이기려고 나가고, 둘째 인에서 붉은 말 탄 자가 땅에서 화평을 제하여 서로 죽이게 하며, 셋째 인에서 검은 말 탄 자가 손에 저울을 가져 기근을 가져오고, 넷째 인에서 청황색 말 탄 자 이름은 사망이요 음부가 따르며 검과 흉년과 사망과 땅의 짐승으로 땅 사분의 일을 죽이는 권세를 받았더라고 기록합니다. 다섯째 인을 떼실 때에는 하나님의 말씀과 저희의 가진 증거를 인하여 죽임을 당한 영혼들이 큰 소리로 불러 가로되 거룩하고 참되신 대주재여 땅에 거하는 자들을 심판하여 우리 피를 신원하여 주지 아니하시기를 어느 때까지 하시려나이까 하니라고 순교자들의 호소를 기록합니다.',
    chapter_themes = '["네 말의 재앙", "이기고 또 이기려고", "화평을 제함", "죽임당한 영혼들의 호소", "해가 검어지고 달이 피같이"]',
    chapter_context = '대환난의 시작을 알리는 일곱 인 재앙 중 여섯 인까지의 내용으로, 말세에 일어날 전쟁, 기근, 죽음과 함께 순교자들의 기도와 우주적 재앙을 통해 하나님의 진노가 나타남을 보여줍니다.',
    chapter_application = '말세의 징조들을 분별하고 깨어있으며, 환난 중에도 하나님의 주권을 인정하고, 순교자들의 신앙을 본받아 끝까지 충성하며, 하나님의 공의로운 심판을 믿고, 어린 양의 진노의 날을 두려워하며 회개하고, 누가 능히 서리요 하는 질문 앞에 겸손해야 합니다.'
WHERE book_id = 're' AND chapter = 6 AND verse = 1;

-- 요한계시록 7장: 십사만 사천과 흰 옷 입은 큰 무리
UPDATE bible_verses SET
    chapter_title = '하나님의 종들의 이마에 인치기까지',
    chapter_summary = '이 일 후에 내가 네 천사가 땅 네 모퉁이에 선 것을 보니 땅의 사방의 바람을 붙잡아 바람으로 하여금 땅에나 바다에나 각종 나무에 불지 못하게 하더라고 시작하여, 다른 천사가 살아 계신 하나님의 인을 가지고 해 돋는 데로부터 올라와서 땅과 바다를 해롭게 할 권세를 얻은 네 천사를 향하여 큰 소리로 외쳐 가로되 우리가 우리 하나님의 종들의 이마에 인치기까지 땅이나 바다나 나무나 해하지 말라 하더라고 기록합니다. 내가 인 맞은 자의 수를 들으니 이스라엘 자손의 각 지파 중에서 인 맞은 자들이 십사만 사천이니라고 하며, 이 일 후에 내가 보니 각 나라와 족속과 백성과 방언에서 아무라도 능히 셀 수 없는 큰 무리가 흰 옷을 입고 손에 종려 가지를 들고 보좌 앞과 어린 양 앞에 서서 큰 소리로 외쳐 가로되 구원하심이 보좌에 앉으신 우리 하나님과 어린 양에게 있도다 하니라고 찬양을 기록합니다.',
    chapter_themes = '["하나님의 종들의 이마에 인", "십사만 사천", "큰 무리가 흰 옷을 입고", "큰 환난에서 나온 자들", "어린 양이 목자가 되어"]',
    chapter_context = '하나님의 진노의 심판이 내리기 전에 하나님의 백성들이 보호받는 모습과 대환난을 통과한 성도들의 최종적인 구원과 영광에 대한 환상으로 하나님의 백성에 대한 보호와 구원을 보여줍니다.',
    chapter_application = '하나님께서 자기 백성들을 보호하시고 인치심을 믿고, 환난 중에도 하나님의 종으로서의 정체성을 확신하며, 각 나라와 족속에서 구원받을 큰 무리가 있음을 믿고 전도에 힘쓰고, 큰 환난을 이겨내는 믿음을 준비하며, 어린 양을 목자로 모시고 하나님이 모든 눈물을 씻어주실 영광을 소망해야 합니다.'
WHERE book_id = 're' AND chapter = 7 AND verse = 1;

-- 요한계시록 8장: 일곱째 인과 일곱 나팔 심판의 시작
UPDATE bible_verses SET
    chapter_title = '일곱째 인을 떼실 때에 하늘이 반시 동안쯤 고요하더니',
    chapter_summary = '일곱째 인을 떼실 때에 하늘이 반시 동안쯤 고요하더니 내가 보니 하나님 앞에 시위한 일곱 천사가 있어 일곱 나팔을 받았더라고 시작하여, 또 다른 천사가 와서 제단 곁에 서서 금 향로를 가졌는데 저에게 많은 향이 주어졌으니 이는 모든 성도의 기도들과 합하여 보좌 앞 금단에 드리고자 함이라고 성도들의 기도를 기록합니다. 첫째 나팔에 피 섞인 우박과 불이 땅에 쏟아져 땅 삼분의 일과 나무 삼분의 일과 각종 푸른 풀이 타버리고, 둘째 나팔에 불 붙는 큰 산 같은 것이 바다에 던지워져 바다 삼분의 일이 피가 되고, 셋째 나팔에 횃불 같이 타는 큰 별이 강들과 물 근원들에 떨어져 물들이 쓰게 되고, 넷째 나팔에 해와 달과 별들 삼분의 일이 침을 받아 삼분의 일이 어두워지니라고 기록합니다.',
    chapter_themes = '["하늘이 반시 동안 고요", "성도의 기도들과 합하여", "피 섞인 우박과 불", "바다 삼분의 일이 피", "화, 화, 화가 있으리로다"]',
    chapter_context = '일곱째 인이 열리면서 새로운 단계의 심판인 일곱 나팔 재앙이 시작되며, 성도들의 기도가 하나님의 심판을 촉발시키는 모습과 함께 자연계에 대한 하나님의 심판이 시작됨을 보여줍니다.',
    chapter_application = '하나님 앞에서의 고요함의 의미를 깨닫고, 성도들의 기도가 하나님의 역사에 중요한 역할을 함을 믿어 더욱 기도에 힘쓰며, 하나님의 심판이 점진적으로 임함을 인식하고, 자연계를 통한 하나님의 경고에 귀 기울이며, 더 큰 화가 올 것을 경고하는 말씀에 회개하고 준비해야 합니다.'
WHERE book_id = 're' AND chapter = 8 AND verse = 1;

-- 요한계시록 9장: 다섯째와 여섯째 나팔
UPDATE bible_verses SET
    chapter_title = '다섯째 천사가 나팔을 불매',
    chapter_summary = '다섯째 천사가 나팔을 불매 내가 보니 하늘에서 땅에 떨어진 별 하나가 있는데 저가 무저갱의 열쇠를 받았더라 저가 무저갱을 여니 그 구멍에서 큰 풀무의 연기 같은 연기가 올라오매 해와 공기가 그 연기로 인하여 어두워지며 또 그 연기 가운데로부터 황충이 땅 위에 나오매라고 기록하며, 그들에게 이르되 땅의 풀이나 각종 푸른 것이나 각종 나무는 해하지 말고 오직 이마에 하나님의 인 맞지 아니한 사람들만 해하라 하시더라고 명령을 전합니다. 여섯째 천사가 나팔을 불매 내가 들으니 하나님 앞 금단 네 뿔에서 한 음성이 나서 나팔 가진 여섯째 천사에게 말하되 큰 강 유브라데에 결박한 네 천사를 놓아 주라 하매 네 천사가 놓였으니 이들은 그 년 월 일 시에 이르러 사람 삼분의 일을 죽이기로 예비된 자들이더라고 더 큰 재앙을 예고합니다.',
    chapter_themes = '["무저갱에서 올라온 황충", "이마에 하나님의 인", "사람 삼분의 일을 죽임", "말과 기병의 수가 이만만", "아직도 회개치 아니하더라"]',
    chapter_context = '다섯째와 여섯째 나팔의 더욱 심각한 재앙으로, 영적 세계의 악한 세력들이 하나님의 허락 하에 인간을 괴롭히고 죽이는 모습과 함께 그럼에도 회개하지 않는 인간의 완악함을 보여줍니다.',
    chapter_application = '하나님의 인을 받은 자들은 보호받음을 확신하고, 영적 전쟁의 실체를 인식하여 깨어 기도하며, 하나님의 심판 중에도 제한이 있음을 알고 하나님의 자비를 감사하고, 큰 재앙에도 회개하지 않는 인간의 완악함을 경계하며, 지금이 회개할 때임을 깨닫고 즉시 돌이켜야 합니다.'
WHERE book_id = 're' AND chapter = 9 AND verse = 1;

-- 요한계시록 10장: 힘센 천사와 작은 책
UPDATE bible_verses SET
    chapter_title = '힘센 다른 천사가 구름을 입고 하늘에서 내려오는데',
    chapter_summary = '내가 또 보니 힘센 다른 천사가 구름을 입고 하늘에서 내려오는데 그 머리 위에 무지개가 있고 그 얼굴은 해 같고 그 발은 불기둥 같으며 그 손에는 펴 놓인 작은 책을 들고 그 오른발은 바다를 밟고 왼발은 땅을 밟고 사자의 부르짖는 것 같이 큰 음성으로 외치니 외칠 때에 일곱 우뢰가 그 소리를 발하더라고 장엄한 모습을 묘사합니다. 일곱 우뢰가 발할 때에 내가 기록하려고 하다가 곧 들으니 하늘에서 소리 나서 가로되 일곱 우뢰가 발한 것을 인봉하고 기록하지 말라 하더라고 기록 금지 명령을 받으며, 내가 하늘에서 들은 음성이 또 내게 말하여 가로되 가서 바다와 땅을 밟고 선 천사의 손에 펴 놓인 책을 가지라 하기로 내가 천사에게 나아가 작은 책을 달라 한즉 천사가 가로되 가져 먹어버리라 네 배에는 쓰겠으나 네 입에는 꿀 같이 달리라 하거늘라는 명령을 받습니다.',
    chapter_themes = '["힘센 다른 천사", "펴 놓인 작은 책", "일곱 우뢰를 인봉하라", "입에는 꿀같이 달고 배에는 쓰다", "다시 예언하여야 하리라"]',
    chapter_context = '일곱 나팔 심판의 중간에 나타나는 막간으로, 요한이 새로운 계시를 받아 먹고 다시 예언할 사명을 받는 장면으로 하나님의 말씀의 달콤함과 동시에 심판 메시지의 쓴맛을 보여줍니다.',
    chapter_application = '하나님의 말씀을 받을 때 겸손한 자세로 받아들이고, 하나님의 계시 중에는 감추어진 것도 있음을 인정하며, 하나님의 말씀이 입에는 달지만 전할 때는 쓴 경험도 있음을 각오하고, 받은 계시를 모든 민족에게 전하는 사명을 감당하며, 지속적으로 예언의 사명을 감당해야 합니다.'
WHERE book_id = 're' AND chapter = 10 AND verse = 1;

-- 요한계시록 11장: 두 증인과 일곱째 나팔
UPDATE bible_verses SET
    chapter_title = '내 두 증인에게 권세를 주리니',
    chapter_summary = '또 내게 지팡이 같은 갈대를 주며 말하기를 일어나서 하나님의 성전과 제단과 그 안에서 경배하는 자들을 척량하되 성전 밖 마당은 척량하지 말고 그냥 두라 이것을 이방인에게 주었은즉 저희가 거룩한 성을 마흔두 달 동안 짓밟으리라고 시작하여, 내가 나의 두 증인에게 권세를 주리니 저희가 굵은 베옷을 입고 일천이백육십 일을 예언하리라 이는 이 땅의 주 앞에 섰는 두 감람나무와 두 촛대니라고 두 증인을 소개합니다. 저희가 그 증거를 마칠 때에 무저갱으로부터 올라오는 짐승이 저희로 더불어 전쟁을 일으켜 저희를 이기고 저희를 죽일 터인데 저희 시체가 큰 성 길에 있으리니 그 성은 영적으로 하면 소돔과 애굽이라 하니 곧 저희 주께서 십자가에 못 박히신 곳이니라고 순교를 예언하며, 일곱째 천사가 나팔을 불매 하늘에 큰 음성들이 나서 가로되 세상 나라가 우리 주와 그 그리스도의 나라가 되어 그가 세세토록 왕노릇 하시리로다 하니라는 최종 승리를 선포합니다.',
    chapter_themes = '["하나님의 성전을 척량", "나의 두 증인", "일천이백육십 일을 예언", "무저갱의 짐승이 죽임", "세상 나라가 우리 주의 나라가 됨"]',
    chapter_context = '대환난 중에 하나님께서 보내시는 두 증인의 사역과 순교, 그리고 그들의 부활과 승천을 통해 하나님의 최종 승리가 확정되고 일곱째 나팔로 하나님의 나라가 임함을 선포하는 중요한 전환점입니다.',
    chapter_application = '하나님의 성전된 교회를 보호하시는 하나님을 신뢰하고, 마지막 때 증인의 사명을 감당할 준비를 하며, 복음 증거로 인한 핍박과 순교도 각오하고, 하나님의 능력으로 원수들을 이기는 믿음을 가지며, 최종적으로 하나님의 나라가 이 땅에 임할 것을 확신하고 소망해야 합니다.'
WHERE book_id = 're' AND chapter = 11 AND verse = 1;

-- 요한계시록 12장: 여자와 용
UPDATE bible_verses SET
    chapter_title = '하늘에 큰 이적이 보이니 해를 입은 한 여자가',
    chapter_summary = '하늘에 큰 이적이 보이니 해를 입은 한 여자가 있는데 그 발 아래는 달이 있고 그 머리에는 열두 별의 면류관을 썼더라 이 여자가 아이를 배어 해산하게 되매 아파서 애써 부르짖더라고 시작하여, 하늘에 또 다른 이적이 보이니 보라 큰 붉은 용이 있어 머리가 일곱이요 뿔이 열이라 그 여러 머리에 일곱 면류관이 있더라고 용을 묘사합니다. 여자가 아들을 낳으니 이는 장차 철장으로 만국을 다스릴 남자라 그 아이를 하나님 앞과 그 보좌 앞으로 올려가더라고 그리스도의 승천을 기록하며, 하늘에서 전쟁이 있으니 미가엘과 그의 사자들이 용으로 더불어 싸울새 용과 그의 사자들도 싸우나 이기지 못하여 다시 하늘에서 저희의 있을 곳을 얻지 못한지라 큰 용이 내어쫓기니 옛 뱀 곧 마귀라고도 하고 사단이라고도 하는 온 천하를 꾀는 자라 저가 땅으로 내어쫓기니 그의 사자들도 저와 함께 내어쫓기니라고 사단의 축출을 기록합니다.',
    chapter_themes = '["해를 입은 한 여자", "큰 붉은 용", "철장으로 만국을 다스릴 남자", "하늘에서 전쟁", "용이 땅으로 내어쫓김"]',
    chapter_context = '그리스도의 탄생에서 승천까지의 구속사와 사단의 하늘에서의 축출, 그리고 교회(여자)에 대한 사단의 핍박을 상징적으로 묘사한 환상으로 영적 전쟁의 본질과 그리스도의 승리를 보여줍니다.',
    chapter_application = '그리스도의 승리와 사단의 패배를 확신하고, 영적 전쟁의 실체를 인식하여 깨어 기도하며, 교회에 대한 사단의 공격을 각오하되 하나님의 보호하심을 신뢰하고, 광야에서 양육받는 교회의 모습처럼 환난 중에도 하나님의 돌보심을 믿으며, 어린 양의 피와 증거하는 말씀으로 사단을 이겨야 합니다.'
WHERE book_id = 're' AND chapter = 12 AND verse = 1;

-- 요한계시록 13장: 바다에서 올라온 짐승과 땅에서 올라온 짐승
UPDATE bible_verses SET
    chapter_title = '내가 바다 모래 위에 섰더니',
    chapter_summary = '내가 바다 모래 위에 섰더니 바다에서 한 짐승이 나오는데 뿔이 열이요 머리가 일곱이라 그 뿔에는 열 면류관이 있고 그 머리들에는 참람한 이름들이 있더라고 첫 번째 짐승을 묘사하며, 용이 자기의 능력과 보좌와 큰 권세를 그에게 주었더라 그의 머리 하나가 상하여 죽게 된 것 같았는데 그 죽게 되었던 상처가 나으매 온 땅이 이상히 여겨 짐승을 따르고라고 기록합니다. 또 내가 보니 다른 짐승이 땅에서 올라오니 새끼 양 같이 두 뿔이 있고 용처럼 말하더라 저가 먼저 나온 짐승의 모든 권세를 그 앞에서 행하고 땅과 땅에 거하는 자들로 처음 짐승에게 경배하게 하니라고 두 번째 짐승을 소개하며, 저가 모든 자 곧 작은 자나 큰 자나 부자나 빈 자나 자유한 자나 종들로 그 오른손에나 이마에 표를 받게 하고 누구든지 이 표를 가진 자 외에는 매매를 못하게 하니 이 표는 그 짐승의 이름이나 그 이름의 수라 지혜가 여기 있으니 총명한 자는 그 짐승의 수를 세어 보라 그 수는 사람의 수니 육백육십육이니라고 경고합니다.',
    chapter_themes = '["바다에서 올라온 짐승", "죽게 된 상처가 나음", "땅에서 올라온 짐승", "오른손이나 이마에 표", "그 수는 육백육십육"]',
    chapter_context = '마지막 때에 나타날 적그리스도(바다의 짐승)와 거짓 선지자(땅의 짐승)에 대한 예언으로, 사단이 세운 정치적, 종교적 체제를 통해 전 세계를 미혹하고 통제하는 모습을 보여주는 중요한 예언입니다.',
    chapter_application = '마지막 때 적그리스도의 출현을 분별하고 미혹되지 않으며, 거짓 기적과 표적에 속지 말고, 경제적 압박과 핍박이 있어도 짐승의 표를 받지 않고, 하나님께만 경배하며, 666의 의미를 깨달아 마지막 때를 분별하고, 어린 양을 따르는 자가 되어 끝까지 신앙을 지켜야 합니다.'
WHERE book_id = 're' AND chapter = 13 AND verse = 1;

-- 요한계시록 14장: 십사만 사천과 영원한 복음
UPDATE bible_verses SET
    chapter_title = '또 내가 보니 보라 어린 양이 시온 산에 섰고',
    chapter_summary = '또 내가 보니 보라 어린 양이 시온 산에 섰고 그와 함께 십사만 사천이 섰는데 그 이마에 어린 양의 이름과 그 아버지의 이름을 쓴 것이 있더라고 시작하여, 이들은 여자로 더불어 더럽히지 아니하고 정절이 있는 자라 이들은 어린 양이 어디로 인도하든지 따라가는 자며 사람 가운데서 구속을 받아 처음 익은 열매로 하나님과 어린 양에게 속한 자들이니 그 입에 거짓말이 없고 흠이 없는 자들이더라고 찬양합니다. 또 보니 다른 천사가 공중에 날아가는데 땅에 거하는 자들 곧 여러 나라와 족속과 방언과 백성에게 전할 영원한 복음을 가졌더니 그가 큰 음성으로 가로되 하나님을 두려워하며 그에게 영광을 돌리라 그의 심판하실 시간이 이르렀으니 하늘과 땅과 바다와 물들의 근원을 만드신 이를 경배하라 하더라고 복음 선포를 기록하며, 또 다른 천사 곧 둘째가 그 뒤를 따라 말하되 무너졌도다 무너졌도다 큰 성 바벨론이여 모든 나라를 그 음행의 진노의 포도주로 먹인 자로다 하더라고 바벨론의 멸망을 선포합니다.',
    chapter_themes = '["어린 양이 시온 산에", "십사만 사천의 찬양", "영원한 복음", "바벨론이 무너졌도다", "인내하는 성도들이 여기 있나니"]',
    chapter_context = '대환난 중에도 어린 양을 따르는 순결한 성도들의 승리와 마지막 복음 전파, 바벨론의 멸망 선언을 통해 하나님의 최종 승리를 보여주는 환상입니다.',
    chapter_application = '어린 양 되신 그리스도를 끝까지 따르며, 세상의 더러움에 물들지 않는 순결한 삶을 살고, 마지막까지 복음을 전하며, 바벨론으로 상징되는 세상 체제의 멸망을 확신하고, 하나님의 계명을 지키며 예수 믿음을 지키는 성도의 인내를 보이며, 복 있는 죽음을 준비해야 합니다.'
WHERE book_id = 're' AND chapter = 14 AND verse = 1;

-- 요한계시록 15장: 일곱 대접의 준비
UPDATE bible_verses SET
    chapter_title = '하늘에 또 다른 크고 이상한 이적을 보매',
    chapter_summary = '하늘에 또 다른 크고 이상한 이적을 보매 일곱 천사가 일곱 재앙을 가졌는데 곧 마지막 재앙이라 하나님의 진노가 이것으로 마치리라고 시작하여, 또 보니 불이 섞인 유리 바다 같은 것이 있고 짐승과 그의 우상과 그의 이름의 수를 이기고 벗어난 자들이 유리 바다 가에서 하나님의 거문고를 가지고 하나님의 종 모세의 노래 어린 양의 노래를 불러 가로되 주 하나님 곧 전능하신 이시여 하시는 일이 크고 기이하시도다 만국의 왕이시여 주의 길이 의롭고 참되시도다라고 찬양을 기록합니다. 일곱 천사가 일곱 재앙을 가지고 성전에서 나오는데 맑고 빛난 세마포 옷을 입고 가슴에 금띠를 띠고 네 생물 중에 하나가 세세에 계신 하나님의 진노를 가득히 담은 금 대접 일곱을 그 일곱 천사에게 주니 하나님의 영광과 능력을 인하여 성전에 연기가 차게 되매 일곱 천사의 일곱 재앙이 마치기까지는 성전에 능히 들어갈 자가 없더라고 준비 과정을 묘사합니다.',
    chapter_themes = '["일곱 천사가 일곱 재앙", "불이 섞인 유리 바다", "모세의 노래 어린 양의 노래", "하나님의 진노를 담은 금 대접", "성전에 연기가 차게 됨"]',
    chapter_context = '마지막 심판인 일곱 대접 재앙의 준비 단계로, 승리한 성도들의 찬양과 함께 하나님의 최종적이고 완전한 진노의 심판이 시작되기 전의 장엄한 예배 장면입니다.',
    chapter_application = '하나님의 의로우신 심판을 찬양하고, 짐승을 이기고 벗어난 승리자들의 믿음을 본받으며, 모세와 어린 양의 노래를 불러 하나님을 찬양하고, 하나님의 거룩하심과 의로우심을 인정하며, 마지막 진노의 때가 다가옴을 깨닫고 회개하고 준비해야 합니다.'
WHERE book_id = 're' AND chapter = 15 AND verse = 1;

-- 요한계시록 16장: 일곱 대접의 재앙
UPDATE bible_verses SET
    chapter_title = '또 내가 들으니 성전에서 큰 음성이 나서',
    chapter_summary = '또 내가 들으니 성전에서 큰 음성이 나서 일곱 천사에게 말하되 너희는 가서 하나님의 진노의 일곱 대접을 땅에 쏟으라 하더라고 시작하여, 첫째가 가서 그 대접을 땅에 쏟으매 짐승의 표를 받은 사람들과 그 우상에게 경배하는 자들에게 악하고 독한 헌데가 나더라고 첫째 재앙을 기록합니다. 둘째는 바다에 쏟으매 바다가 죽은 자의 피 같이 되니 바다 가운데 모든 생물이 죽더라, 셋째는 강과 물 근원에 쏟으매 피가 되니라, 넷째는 해에 쏟으매 해가 권세를 받아 불로 사람들을 태우니 사람들이 크게 태움에 태워져서 이 재앙들을 행하는 권세를 가지신 하나님의 이름을 훼방하며 또 회개하여 영광을 주께 돌리지 아니하더라고 계속 기록하며, 여섯째가 그 대접을 큰 강 유브라데에 쏟으매 강물이 말라서 동방에서 오는 왕들의 길이 예비되더라 또 내가 보매 개구리 같은 세 더러운 영이 용의 입과 짐승의 입과 거짓 선지자의 입에서 나오니 저희는 귀신의 영이라 이적을 행하여 온 천하 임금들에게 가서 하나님 곧 전능하신 이의 큰 날에 전쟁을 위하여 그들을 모으더라 보라 내가 도적 같이 오리니 누구든지 깨어 자기 옷을 지켜 벌거벗고 다니지 아니하며 자기의 부끄러움을 보이지 아니하는 자가 복이 있도다 세 영이 히브리 음으로 아마겟돈이라 하는 곳으로 왕들을 모으더라고 아마겟돈 전쟁을 예언합니다.',
    chapter_themes = '["하나님의 진노의 일곱 대접", "짐승의 표 받은 자들에게 헌데", "바다와 강이 피가 됨", "아마겟돈 전쟁", "되었다 하는 큰 음성"]',
    chapter_context = '하나님의 최종적이고 완전한 진노의 심판인 일곱 대접 재앙을 통해 죄악된 세상에 대한 하나님의 심판이 완성되고 아마겟돈 대전쟁이 준비되는 과정을 보여줍니다.',
    chapter_application = '하나님의 공의로운 심판을 인정하고, 짐승의 표를 받지 않도록 끝까지 충성하며, 재앙 중에도 회개하지 않는 완악함을 경계하고, 주님이 도적같이 오실 것을 준비하여 깨어있으며, 영적 옷을 잘 지켜 부끄러움을 당하지 않고, 마지막 대전쟁에서 하나님의 편에 서야 합니다.'
WHERE book_id = 're' AND chapter = 16 AND verse = 1;

-- 요한계시록 17장: 큰 음녀 바벨론
UPDATE bible_verses SET
    chapter_title = '일곱 대접을 가진 일곱 천사 중 하나가 와서',
    chapter_summary = '일곱 대접을 가진 일곱 천사 중 하나가 와서 내게 말하여 가로되 이리 와서 많은 물 위에 앉은 큰 음녀의 받을 심판을 네게 보이리라 땅의 임금들도 그로 더불어 음행하였고 땅에 거하는 자들도 그 음행의 포도주에 취하였다 하고라고 시작하여, 곧 성령으로 나를 데리고 광야로 가니라 내가 보니 여자가 붉은 빛 짐승을 탔는데 그 짐승의 몸에 참람된 이름들이 가득하고 일곱 머리와 열 뿔이 있더라 그 여자는 자주 빛과 붉은 빛 옷을 입고 금과 보석과 진주로 꾸미고 손에 금잔을 가졌는데 가증한 물건과 그의 음행의 더러운 것들이 가득하더라고 묘사합니다. 그 이마에 이름이 기록되었으니 비밀이라, 큰 바벨론이라, 땅의 음녀들과 가증한 것들의 어미라 하였더라 또 내가 보매 이 여자가 성도들의 피와 예수의 증인들의 피에 취한지라고 정체를 밝히며, 네가 본 짐승은 전에 있었다가 시방 없으나 장차 무저갱으로부터 올라와 멸망으로 들어갈 자니 땅에 거하는 자들로서 창세 이후로 생명책에 기록되지 못한 자들이 이전에 있었다가 시방 없으나 장차 나올 짐승을 보고 기이히 여기리라고 설명합니다.',
    chapter_themes = '["많은 물 위에 앉은 큰 음녀", "붉은 빛 짐승을 탔는데", "큰 바벨론이라", "성도들의 피에 취함", "어린 양과 싸우려니와 어린 양은 이기시리라"]',
    chapter_context = '마지막 때의 종교적, 정치적 바벨론 체제를 큰 음녀로 상징하여 그 정체와 멸망에 대해 예언하며, 세상의 모든 악한 세력이 그리스도를 대적하지만 결국 어린 양이 승리하실 것을 보여줍니다.',
    chapter_application = '세상의 음녀 바벨론 체제를 분별하고 그것과 타협하지 않으며, 성도들을 핍박하는 세력의 정체를 깨닫고, 세상의 화려함과 부에 미혹되지 않고, 어린 양의 최종 승리를 확신하며, 부름받고 택하심받고 충성된 자가 되어 그리스도와 함께 승리해야 합니다.'
WHERE book_id = 're' AND chapter = 17 AND verse = 1;

-- 요한계시록 18장: 바벨론의 멸망
UPDATE bible_verses SET
    chapter_title = '이 일 후에 다른 천사가 하늘에서 내려오는 것을 보니',
    chapter_summary = '이 일 후에 다른 천사가 하늘에서 내려오는 것을 보니 큰 권세를 가졌는데 그의 영광으로 땅이 환하여지더라 힘센 음성으로 외쳐 가로되 무너졌도다 무너졌도다 큰 성 바벨론이여 귀신의 처소와 각종 더러운 영의 모이는 곳과 각종 더럽고 가증한 새의 모이는 곳이 되었도다라고 선포하며, 또 내가 들으니 하늘로서 다른 음성이 나서 가로되 내 백성아 거기서 나와 그의 죄에 참예하지 말고 그의 받을 재앙들을 받지 말라 그의 죄는 하늘에 사무쳤으며 하나님은 그의 불의한 일을 기억하신지라고 경고합니다. 땅의 임금들이 그로 인하여 울고 애통하는 것은 그 불타는 연기를 보고 그 고난을 무서워하여 멀리 서서 가로되 화 있도다 화 있도다 큰 성, 견고한 성 바벨론이여 일시간에 네 심판이 이르렀도다 하고 땅의 상고들도 그를 인하여 울고 애통하는 것은 다시 그 상품을 사는 자가 없음이라고 애가를 기록하며, 힘센 천사가 큰 맷돌 같은 돌을 들어 바다에 던져 가로되 큰 성 바벨론이 이같이 몹시 던지우리니 결코 다시 보이지 아니하리로다라고 완전한 멸망을 선언합니다.',
    chapter_themes = '["무너졌도다 큰 성 바벨론", "내 백성아 거기서 나와", "일시간에 네 심판이 이르렀도다", "결코 다시 보이지 아니하리로다", "선지자들과 성도들의 피가 그 성에서 발견됨"]',
    chapter_context = '큰 음녀 바벨론의 완전한 멸망을 선포하며, 그 경제적, 정치적, 종교적 체제의 몰락에 대한 상세한 묘사와 함께 하나님의 백성들에게는 그곳에서 나올 것을 명령하는 중요한 예언입니다.',
    chapter_application = '세상의 바벨론 체제에서 나와 분리된 삶을 살고, 그 죄에 참예하지 않도록 주의하며, 세상의 부와 번영이 순식간에 무너질 수 있음을 깨닫고, 하나님의 공의로운 심판을 찬양하며, 성도들의 피를 흘린 세력에 대한 하나님의 보복을 확신하고, 영원한 하나님 나라만을 소망해야 합니다.'
WHERE book_id = 're' AND chapter = 18 AND verse = 1;

-- 요한계시록 19장: 어린 양의 혼인 잔치와 그리스도의 재림
UPDATE bible_verses SET
    chapter_title = '이 일 후에 내가 들으니 하늘에 허다한 무리의 큰 음성 같은 것이 있어',
    chapter_summary = '이 일 후에 내가 들으니 하늘에 허다한 무리의 큰 음성 같은 것이 있어 가로되 할렐루야 구원과 영광과 능력이 우리 하나님께 있도다 그의 심판은 참되고 의로운지라 음행으로 땅을 더럽게 한 큰 음녀를 심판하사 자기 종들의 피를 그의 손에 갚으셨도다 하고라고 찬양으로 시작하여, 그 연기가 세세토록 올라가더라 하며 또 이십사 장로와 네 생물이 엎드려 보좌에 앉으신 하나님께 경배하여 가로되 아멘 할렐루야 하니라고 계속합니다. 또 내가 들으니 허다한 무리의 음성과도 같고 많은 물소리와도 같고 큰 뇌성과도 같은 소리로 가로되 할렐루야 주 우리 하나님 곧 전능하신 이가 통치하시도다 우리가 즐거워하고 크게 기뻐하여 그에게 영광을 돌리자 어린 양의 혼인 기약이 이르렀고 그 아내가 예비하였으니 그에게 허락하사 빛나고 깨끗한 세마포를 입게 하셨은즉 이 세마포는 성도들의 옳은 행실이로다라고 어린 양의 혼인을 선포하며, 또 내가 보니 하늘이 열린 것과 보라 백마와 탄 자가 있으니 그 이름은 충신과 진실이라 그가 공의로 심판하며 싸우더라 그 눈이 불꽃 같고 그 머리에 많은 면류관이 있고 또 이름 쓴 것이 하나가 있으니 자기 밖에 아는 자가 없고 또 그가 피 뿌린 옷을 입었는데 그 이름은 하나님의 말씀이라 칭하더라고 재림하시는 그리스도를 묘사합니다.',
    chapter_themes = '["할렐루야 구원과 영광", "어린 양의 혼인 기약", "빛나고 깨끗한 세마포", "백마 탄 자 충신과 진실", "만왕의 왕 만주의 주"]',
    chapter_context = '바벨론의 멸망에 대한 천상의 찬양과 어린 양과 신부(교회)의 혼인 잔치, 그리고 백마를 타고 재림하시는 그리스도의 영광스러운 모습을 통해 하나님의 최종 승리를 보여주는 장엄한 환상입니다.',
    chapter_application = '하나님의 공의로운 심판을 할렐루야로 찬양하고, 어린 양의 혼인 잔치에 참여할 신부로 준비하며, 빛나고 깨끗한 세마포 같은 의로운 행실을 쌓고, 충신과 진실이신 그리스도의 재림을 기다리며, 만왕의 왕이신 그리스도께 최고의 경배와 순종을 드리고, 하나님의 말씀에 순종하는 삶을 살아야 합니다.'
WHERE book_id = 're' AND chapter = 19 AND verse = 1;

-- 요한계시록 20장: 천년 왕국과 최후 심판
UPDATE bible_verses SET
    chapter_title = '또 내가 보니 천사가 하늘로서 내려오는데',
    chapter_summary = '또 내가 보니 천사가 하늘로서 내려오는데 무저갱 열쇠와 큰 쇠사슬을 그 손에 가졌더라 용을 잡으니 곧 옛 뱀이요 마귀요 사단이라 잡아 일천년 동안 결박하여 무저갱에 던져 잠그고 그 위에 인봉하여 천년이 차도록 다시는 만국을 미혹하지 못하게 하였다가 그 후에는 반드시 잠간 놓이리라고 시작하여, 또 내가 보니 보좌들이 있고 거기 앉은 자들이 있어 심판하는 권세를 받았더라 또 내가 보니 예수의 증거와 하나님의 말씀을 인하여 목 베임을 받은 자의 영혼들과 또 짐승과 그의 우상에게 경배하지도 아니하고 이마와 손에 그의 표를 받지도 아니한 자들이 살아서 그리스도로 더불어 천년 동안 왕노릇 하니 이는 첫째 부활이라고 천년 왕국을 설명합니다. 천년이 차매 사단이 그 옥에서 놓여 나와서 땅의 사방 백성 곧 곡과 마곡을 미혹하여 모아 싸움을 위하여 모으리니 그 수가 바다 모래 같으리라고 마지막 반역을 예언하며, 또 내가 크고 흰 보좌와 그 위에 앉으신 이를 보니 땅과 하늘이 그 앞에서 피하여 간 데 없더라 또 내가 보니 죽은 자들이 무론대소하고 그 보좌 앞에 섰는데 책들이 펴 있고 또 다른 책이 펴졌으니 곧 생명책이라 죽은 자들이 자기 행위를 따라 책들에 기록된 대로 심판을 받더라고 최후 심판을 기록합니다.',
    chapter_themes = '["사단을 일천년 동안 결박", "그리스도로 더불어 천년 동안 왕노릇", "첫째 부활", "크고 흰 보좌", "생명책에 기록되지 못한 자"]',
    chapter_context = '그리스도의 천년 왕국과 그 기간 중 사단의 결박, 그리고 천년 후 사단의 마지막 반역과 최후 심판을 통해 하나님의 완전한 승리와 영원한 질서의 확립을 보여주는 중요한 장입니다.',
    chapter_application = '그리스도와 함께 왕노릇 할 첫째 부활에 참여하기 위해 충성하고, 천년 왕국의 소망을 가지고 현재의 고난을 견디며, 사단의 마지막 미혹에도 흔들리지 않는 믿음을 준비하고, 크고 흰 보좌의 최후 심판을 두려워하며, 생명책에 이름이 기록되도록 예수 그리스도를 믿고 의롭게 살아야 합니다.'
WHERE book_id = 're' AND chapter = 20 AND verse = 1;

-- 요한계시록 21장: 새 하늘과 새 땅
UPDATE bible_verses SET
    chapter_title = '또 내가 새 하늘과 새 땅을 보니',
    chapter_summary = '또 내가 새 하늘과 새 땅을 보니 처음 하늘과 처음 땅이 없어졌고 바다도 다시 있지 않더라 또 내가 보매 거룩한 성 새 예루살렘이 하나님께로부터 하늘에서 내려오니 그 예비한 것이 신부가 남편을 위하여 단장한 것 같더라고 시작하여, 내가 들으니 보좌에서 큰 음성이 나서 가로되 보라 하나님의 장막이 사람들과 함께 있으매 하나님이 저희와 함께 거하시리니 저희는 하나님의 백성이 되고 하나님은 친히 저희와 함께 계셔서 저희 하나님이 되시고 모든 눈물을 그 눈에서 씻기시매 다시 사망이 없고 애통하는 것이나 곡하는 것이나 아픈 것이 다시 있지 아니하리니 처음 것들이 다 지나갔음이러라고 새 창조를 묘사합니다. 보좌에 앉으신 이가 가라사대 보라 내가 만물을 새롭게 하노라 하시고 또 가라사대 이 말은 신실하고 참된지라 기록하라 하시고 또 내게 말씀하시되 이루었도다 나는 알파와 오메가요 처음과 나중이라 내가 생명수 샘물로 목마른 자에게 값없이 주리니라고 약속하시며, 성곽은 벽옥으로 쌓였고 그 성은 정금인데 맑은 유리 같더라 성의 성곽의 기초석은 각색 보석으로 꾸몄는데라고 새 예루살렘의 영광을 상세히 묘사합니다.',
    chapter_themes = '["새 하늘과 새 땅", "새 예루살렘", "하나님의 장막이 사람들과 함께", "모든 눈물을 씻기시매", "내가 만물을 새롭게 하노라"]',
    chapter_context = '하나님의 구속 계획의 최종 완성인 새 하늘과 새 땅, 그리고 새 예루살렘의 영광스러운 모습을 통해 성도들의 영원한 본향과 완전한 구원의 실현을 보여주는 계시록의 절정입니다.',
    chapter_application = '새 하늘과 새 땅의 영원한 소망을 가지고 현재의 고난을 견디며, 하나님과 영원히 함께 거할 것을 기대하고, 모든 고통과 눈물이 없을 영광의 날을 소망하며, 만물을 새롭게 하시는 하나님의 능력을 믿고, 생명수를 값없이 주시는 은혜에 감사하며, 새 예루살렘의 영광에 참여할 자격을 갖추어야 합니다.'
WHERE book_id = 're' AND chapter = 21 AND verse = 1;

-- 요한계시록 22장: 생명수 강과 예수님의 재림 약속
UPDATE bible_verses SET
    chapter_title = '또 저가 수정같이 맑은 생명수의 강을 내게 보이니',
    chapter_summary = '또 저가 수정같이 맑은 생명수의 강을 내게 보이니 하나님과 및 어린 양의 보좌로부터 나서 그 성 길 가운데로 흐르더라 강 좌우에 생명나무가 있어 열두 가지 실과를 맺되 달마다 그 실과를 맺고 그 나무 잎사귀들은 만국을 소성하기 위하여 있더라고 시작하여, 다시 저주가 없으며 하나님과 그 어린 양의 보좌가 그 가운데 있으리니 그의 종들이 그를 섬기며 그의 얼굴을 볼 터이요 그의 이름도 저희 이마에 있으리라 다시 밤이 없겠고 등불과 햇빛이 쓸데없으니 이는 주 하나님이 저희에게 비취심이라 저희가 세세토록 왕노릇 하리로다라고 영원한 복을 묘사합니다. 또 그가 내게 말하기를 이 말은 신실하고 참된지라 주 곧 선지자들의 영의 하나님이 그의 종들에게 결코 속히 될 일을 보이시려고 그의 천사를 보내셨도다 보라 내가 속히 오리니 이 책의 예언의 말씀을 지키는 자가 복이 있다 하시더라고 재림을 약속하시며, 이 책의 예언의 말씀에 누구든지 더하면 하나님이 이 책에 기록된 재앙들을 그에게 더하실 터이요 만일 누구든지 이 책의 예언의 말씀에서 제하여 버리면 하나님이 이 책에 기록된 생명나무와 및 거룩한 성에 참예함을 제하여 버리시리라고 경고하며, 이것들을 증거하신 이가 가라사대 내가 진실로 속히 오리라 하시거늘 아멘 주 예수여 오시옵소서라고 간절한 기도로 마칩니다.',
    chapter_themes = '["생명수의 강", "생명나무의 열두 가지 실과", "그의 얼굴을 볼 터이요", "보라 내가 속히 오리니", "주 예수여 오시옵소서"]',
    chapter_context = '요한계시록과 전체 성경의 마지막 장으로, 새 예루살렘의 완전한 모습과 그리스도의 재림 약속, 그리고 이 예언서에 대한 엄중한 경고로 하나님의 구속 계획의 완전한 성취를 마무리합니다.',
    chapter_application = '생명수와 생명나무의 영원한 복을 소망하고, 하나님의 얼굴을 뵐 그날을 기대하며, 예언의 말씀을 지켜 복을 받고, 하나님의 말씀을 더하거나 빼지 말고 순수하게 보전하며, 주 예수님의 속히 오심을 간절히 사모하고 기다리며, 마라나타의 기도로 주님의 재림을 촉구해야 합니다.'
WHERE book_id = 're' AND chapter = 22 AND verse = 1;

SELECT 'Revelation commentary completed - 22 chapters' as status;