-- 바울 서신 주석 Part 1 - 고린도전서 (1-16장)
-- 교회의 문제들과 그리스도인의 삶

-- 고린도전서 1장: 교회의 분열과 십자가의 지혜
UPDATE bible_verses SET
    chapter_title = '교회 분열 문제와 십자가의 지혜',
    chapter_summary = '하나님의 뜻으로 말미암아 그리스도 예수의 사도로 부르심을 받은 바울과 형제 소스데네는 고린도에 있는 하나님의 교회 곧 그리스도 예수 안에서 거룩하여지고 성도라 부르심을 받은 자들과 또 각처에서 우리의 주 곧 저희와 우리의 주 되신 예수 그리스도의 이름을 부르는 모든 자들에게 하나님 우리 아버지와 주 예수 그리스도로부터 은혜와 평강이 있기를 원하노라 하며, 그리스도 예수 안에서 너희에게 주신 하나님의 은혜를 인하여 내가 너희를 위하여 항상 하나님께 감사하노니 이는 너희가 그 안에서 모든 일 곧 모든 언변과 모든 지식에 풍족하므로 그리스도의 증거가 너희 중에 견고케 되어 너희가 모든 은사에 부족함이 없이 우리 주 예수 그리스도의 나타나심을 기다림이라 주께서 너희를 우리 주 예수 그리스도의 날에 책망할 것이 없는 자로 끝까지 견고케 하시리라 너희를 불러 그의 아들 예수 그리스도 우리 주로 더불어 교제케 하시는 하나님은 미쁘시니라 하시고, 형제들아 내가 우리 주 예수 그리스도의 이름으로 너희를 권하노니 모두가 같은 말을 하고 너희 가운데 분쟁이 없이 같은 마음과 같은 뜻으로 온전히 합하라 그는 글로에의 집 편으로 너희에게 대한 말이 내게 들려왔으니 곧 너희 가운데 분쟁이 있다는 것이라 이는 너희가 각각 나는 바울에게, 나는 아볼로에게, 나는 게바에게, 나는 그리스도에게 속한 자라 하는 것이니 그리스도께서 어찌 나뉘었느냐 바울이 너희를 위하여 십자가에 못 박혔으며 바울의 이름으로 너희가 세례를 받았느냐.',
    chapter_themes = '["교회의 분열", "나는 바울에게 나는 아볼로에게", "그리스도께서 어찌 나뉘었느냐", "십자가의 도", "하나님의 지혜와 세상의 지혜", "약한 것들을 택하사"]',
    chapter_context = '고린도 교회의 분열 문제를 다루며, 십자가의 복음이 세상의 지혜와 어떻게 다른지를 설명하고 교회 연합의 중요성을 강조합니다.',
    chapter_application = '교회의 하나됨을 추구하고, 인간 지도자가 아닌 그리스도를 중심으로 연합하며, 세상의 지혜보다 십자가의 지혜를 택해야 합니다.'
WHERE book_id = '1co' AND chapter = 1 AND verse = 1;

-- 고린도전서 2장: 성령의 지혜와 영적 분별
UPDATE bible_verses SET
    chapter_title = '성령의 지혜와 영적 분별력',
    chapter_summary = '형제들아 내가 너희에게 나아가 하나님의 증거를 전할 때에 말과 지혜의 아름다운 것으로 아니하였나니 내가 너희 중에서 예수 그리스도와 그의 십자가에 못 박히신 것 외에는 아무것도 알지 아니하기로 작정하였음이라 내가 너희 가운데 거할 때에 약하며 두려워하며 심히 떨었노라 내 말과 내 전도함이 지혜의 권하는 말로 하지 아니하고 다만 성령의 나타남과 능력으로 하여 너희 믿음이 사람의 지혜에 있지 아니하고 다만 하나님의 능력에 있게 하려 하였노라 하시고, 그러나 우리가 온전한 자들 중에서는 지혜를 말하노니 이 지혜는 이 세상의 지혜가 아니요 또 이 세상의 없어질 관원들의 지혜도 아니요 오직 은밀한 가운데 있는 하나님의 지혜를 말하는 것으로서 곧 감취었던 것인데 하나님이 우리의 영광을 위하사 만세 전에 미리 정하신 것이라 이 지혜는 이 세상의 관원들이 알지 못하였나니 만일 알았더면 영광의 주를 십자가에 못 박지 아니하였으리라 기록된 바 하나님이 자기를 사랑하는 자들을 위하여 예비하신 모든 것은 눈으로 보지 못하고 귀로 듣지 못하고 사람의 마음으로 생각지도 못하였다 함과 같으니라 오직 하나님이 성령으로 이것을 우리에게 보이셨으니 성령은 모든 것 곧 하나님의 깊은 것이라도 통달하시느니라.',
    chapter_themes = '["십자가에 못 박히신 것 외에는", "성령의 나타남과 능력", "하나님의 지혜", "영광의 주", "성령이 보이셨으니", "하나님의 깊은 것"]',
    chapter_context = '바울의 사역 방법과 성령의 역할, 그리고 영적 지혜와 세상 지혜의 차이를 설명하며 성령의 계시 역할을 강조합니다.',
    chapter_application = '십자가 중심의 복음을 전하고, 성령의 능력을 의지하며, 하나님의 깊은 것들을 성령을 통해 분별하고, 영적 지혜를 추구해야 합니다.'
WHERE book_id = '1co' AND chapter = 2 AND verse = 1;

-- 고린도전서 3장: 영적 성숙과 하나님의 동역자
UPDATE bible_verses SET
    chapter_title = '영적 성숙과 하나님의 동역자들',
    chapter_summary = '형제들아 내가 신령한 자들을 대함과 같이 너희에게 말할 수 없어서 육신에 속한 자 곧 그리스도 안에서 어린 아이들을 대함과 같이 하노라 내가 너희를 젖으로 먹이고 밥으로 아니하였노니 이는 너희가 감당치 못하였음이거니와 지금도 못하리라 너희가 아직도 육신에 속한 자로다 너희 가운데 시기와 분쟁이 있으니 어찌 육신에 속하여 사람을 따라 행함이 아니리요 어떤 이는 말하되 나는 바울에게라 하고 다른 이는 나는 아볼로에게라 하니 너희가 사람이 아니리요 하시고, 그런즉 아볼로는 무엇이며 바울은 무엇이뇨 저희는 주께서 각각 주신 대로 너희로 하여금 믿게 한 사역자들이니라 나는 심었고 아볼로는 물을 주었으되 오직 하나님은 자라나게 하셨나니 그런즉 심는 이나 물 주는 이는 아무것도 아니로되 오직 자라나게 하시는 하나님뿐이니라 심는 이와 물 주는 이가 일반이나 각각 자기의 일하는 대로 자기의 상을 받으리라 우리는 하나님의 동역자들이요 너희는 하나님의 밭이요 하나님의 집이니라 내게 주신 하나님의 은혜를 따라 내가 지혜로운 건축자와 같이 터를 닦아 두매 다른 이가 그 위에 세우나 그러나 각각 어떻게 그 위에 세울까를 조심할지니라 이 닦아 둔 것 외에 능히 다른 터를 닦아 둘 자가 없으니 이 터는 곧 예수 그리스도라.',
    chapter_themes = '["어린 아이들을 대함", "젖으로 먹이고", "육신에 속한 자", "하나님의 동역자", "나는 심었고 아볼로는 물을", "터는 곧 예수 그리스도"]',
    chapter_context = '고린도 교회의 영적 미숙함을 지적하고, 사역자들의 역할과 하나님의 역사, 그리고 교회 건설의 기초에 대해 가르칩니다.',
    chapter_application = '영적으로 성숙해져야 하고, 하나님의 동역자로서 겸손히 섬기며, 예수 그리스도라는 터 위에 바르게 건축하고, 사역자들 간의 연합을 이루어야 합니다.'
WHERE book_id = '1co' AND chapter = 3 AND verse = 1;

-- 고린도전서 4장: 사도의 사명과 고난
UPDATE bible_verses SET
    chapter_title = '사도의 사명과 그리스도를 위한 고난',
    chapter_summary = '사람이 마땅히 우리를 그리스도의 일군이요 하나님의 비밀을 맡은 자로 여길지어다 그리고 맡은 자들에게 구할 것은 충성이니라 너희에게나 다른 사람의 심판을 받는 것이 내게는 매우 작은 일이라 나도 나를 판단치 아니하노니 내가 자책할 아무것도 깨닫지 못하나 그러나 이를 인하여 의롭다 함을 얻지 못하노라 다만 나를 판단하실 이는 주시니라 그러므로 때가 이르기 전 곧 주께서 오시기까지 아무것도 판단치 말라 그가 어둠에 감추인 것들을 드러내고 마음의 뜻을 나타내시리니 그 때에 각 사람이 하나님께 칭찬을 받으리라 하시고, 형제들아 내가 너희를 위하여 이 일에 나와 아볼로를 가지고 본을 보였으니 이는 너희로 하여금 기록한 말씀 밖에 넘어가지 말라 함을 우리에게서 배워 서로 대적하여 교만하지 말게 하려 함이라 누가 너를 구별하였느뇨 네게 있는 것 중에 받지 아니한 것이 무엇이뇨 네가 받았은즉 어찌하여 받지 아니한 것같이 자랑하느뇨 너희가 이미 배부르며 이미 풍성하며 우리 없이도 왕 노릇 하였도다 우리도 너희와 함께 왕 노릇하기 위하여 참으로 너희의 왕 노릇하기를 원하노라 내가 생각건대 하나님이 사도인 우리를 죽이기로 작정된 자같이 맨 나중에 두셨으매 우리가 세계 곧 천사와 사람에게 구경거리가 되었노라.',
    chapter_themes = '["그리스도의 일군", "하나님의 비밀을 맡은 자", "구할 것은 충성", "받지 아니한 것이 무엇이뇨", "구경거리가 되었노라", "그리스도 안에서 아버지"]',
    chapter_context = '사도직의 본질과 사명, 그리고 고린도 교회 성도들의 교만을 경계하며 진정한 사도의 삶이 어떤 것인지를 제시합니다.',
    chapter_application = '하나님의 비밀을 맡은 청지기로서 충성을 다하고, 받은 은혜를 겸손히 인정하며, 그리스도를 위한 고난을 각오하고, 영적 아버지의 마음으로 섬겨야 합니다.'
WHERE book_id = '1co' AND chapter = 4 AND verse = 1;

-- 고린도전서 5장: 교회 권징과 정결
UPDATE bible_verses SET
    chapter_title = '교회 권징과 도덕적 정결',
    chapter_summary = '너희 중에 심지어 음행이 있다 함을 들으니 그런 음행은 이방인 중에서라도 없는 것이라 누가 그 아비의 아내를 취하였다 하는도다 그리하고도 너희가 오히려 교만하여져서 어찌하여 통한히 여기지 아니하고 이런 일 행한 자를 너희 중에서 내어 쫓지 아니하였느냐 내가 실로 몸으로는 떠나 있으나 영으로는 함께 있어서 거기 있는 것같이 이런 일 행한 자를 이미 판단하였노라 주 예수의 이름으로 너희가 내 영과 함께 모여서 우리 주 예수의 능력으로 이런 자를 사단에게 내어 주었으니 이는 육신은 멸하고 영은 주 예수의 날에 구원 얻게 하려 함이라 너희의 자랑하는 것이 옳지 아니하도다 적은 누룩이 온 덩어리에 퍼지는 것을 알지 못하느냐 너희는 누룩 없는 자인데 새 덩어리가 되기 위하여 묵은 누룩을 내어버리라 우리의 유월절 양 곧 그리스도께서 희생이 되셨느니라 이러므로 우리가 명절을 지키되 묵은 누룩도 말고 괴악하고 악독한 누룩도 말고 오직 순전함과 진실함의 무교병으로 하자 하시고, 내가 너희에게 쓴 편지에 음행하는 자들을 사귀지 말라 하였거니와 이 말은 이 세상의 음행하는 자들이나 탐하는 자들과 토색하는 자들이나 우상 숭배하는 자들을 도무지 사귀지 말라 하는 것이 아니니 만일 그리하려면 세상 밖으로 나가야 할 것이라 이제 내가 너희에게 쓴 것은 만일 어떤 형제라 일컫는 자가 음행하거나 탐람하거나 우상 숭배를 하거나 후욕하거나 술 취하거나 토색하거든 사귀지도 말고 그런 자와는 함께 먹지도 말라 함이라.',
    chapter_themes = '["교회 안의 음행", "적은 누룩이 온 덩어리에", "우리의 유월절 양", "순전함과 진실함", "형제라 일컫는 자", "함께 먹지도 말라"]',
    chapter_context = '고린도 교회 내의 심각한 도덕적 문제를 다루며, 교회의 정결성을 위한 권징의 필요성과 원리를 가르칩니다.',
    chapter_application = '교회의 도덕적 정결을 유지하고, 죄에 대해 단호히 대처하며, 그리스도의 희생을 기억하여 순전하고 진실한 삶을 살아야 합니다.'
WHERE book_id = '1co' AND chapter = 5 AND verse = 1;

-- 고린도전서 6장: 송사와 몸의 성전
UPDATE bible_verses SET
    chapter_title = '성도 간의 송사와 몸이 성령의 전임',
    chapter_summary = '너희 중에 누가 다른 이로 더불어 일이 있는데 구태여 불의한 자들 앞에서 송사하고 성도 앞에서 하지 아니하느냐 성도가 세상을 판단할 것을 너희가 알지 못하느냐 세상도 너희에게 판단을 받겠거든 지극히 작은 일 판단하기를 감당치 못하겠느냐 우리가 천사들을 판단할 것을 너희가 알지 못하느냐 그러하거든 하물며 세상 일이랴 그런즉 너희가 세상 사건이 있을 때에 교회에서 경히 여김을 받는 자들을 세우느냐 내가 너희를 부끄럽게 하려 하여 이 말을 하노니 너희 가운데 그 형제간의 일을 판단할 만한 지혜 있는 자가 이같이 하나도 없느냐 형제가 형제로 더불어 송사할 뿐더러 믿지 아니하는 자들 앞에서 하느냐 너희가 피차 송사함으로 너희 가운데 이미 완전한 허물이 있나니 차라리 불의를 당하는 것이 낫지 아니하며 차라리 속는 것이 낫지 아니하냐 하시고, 불의한 자가 하나님의 나라를 유업으로 받지 못할 줄을 알지 못하느냐 미혹을 받지 말라 음행하는 자나 우상 숭배하는 자나 간음하는 자나 탐색하는 자나 남색하는 자나 도적이나 탐람하는 자나 술 취하는 자나 후욕하는 자나 토색하는 자들은 하나님의 나라를 유업으로 받지 못하리라 너희 중에 이와 같은 자들이 있더니 주 예수 그리스도의 이름과 우리 하나님의 성령 안에서 씻음과 거룩함과 의롭다 하심을 얻었느니라.',
    chapter_themes = '["성도가 세상을 판단", "차라리 불의를 당하는 것이", "씻음과 거룩함과 의롭다 하심", "몸은 성령의 전", "값으로 산 것", "하나님께 영광을 돌리라"]',
    chapter_context = '성도 간의 분쟁 해결 방법과 성적 순결의 중요성, 그리고 몸이 성령의 전이라는 진리를 가르칩니다.',
    chapter_application = '성도 간의 분쟁을 교회 내에서 해결하고, 차라리 손해를 감수하며, 몸을 성령의 전으로 거룩하게 지키고, 하나님께 영광을 돌려야 합니다.'
WHERE book_id = '1co' AND chapter = 6 AND verse = 1;

-- 고린도전서 7장: 결혼과 독신에 대한 교훈
UPDATE bible_verses SET
    chapter_title = '결혼과 독신, 신분에 대한 교훈',
    chapter_summary = '너희의 쓴 말에 대하여는 남자가 여자를 가까이 아니함이 좋으나 음행의 연고로 남자마다 자기 아내를 두고 여자마다 자기 남편을 두라 남편은 그 아내에 대한 의무를 다하고 아내도 그 남편에게 그렇게 할지라 아내가 자기 몸을 주장하지 못하고 오직 그 남편이 하며 남편도 그와 같이 자기 몸을 주장하지 못하고 오직 그 아내가 하나니 서로 분방하지 말라 다만 기도할 틈을 얻기 위하여 합의상 얼마 동안은 하되 다시 합하라 이는 너희의 절제 못함을 인하여 사단으로 너희를 시험하지 못하게 하려 함이라 그러나 이 말은 허락이요 명령은 아니라 내가 모든 사람이 나와 같기를 원하노라 그러나 각각 하나님께 받은 은사가 있으니 하나는 이러하고 하나는 저러하니라 하시고, 그러므로 처녀들에 대하여는 내가 주께 받은 계명이 없으되 주의 자비하심을 받아서 충성된 자가 되어 의견을 고하노니 내 생각에는 이것이 좋으니 곧 임박한 환난을 인하여 사람이 그냥 지내는 것이 좋으니라 네가 아내에게 매였느냐 놓이기를 구하지 말며 아내에게서 놓였느냐 아내를 구하지 말라 그러나 장가 가도 죄 짓는 것이 아니요 처녀가 시집 가도 죄 짓는 것이 아니로되 이런 이들은 육신에 고난이 있으리니 나는 너희를 아끼노라.',
    chapter_themes = '["남자마다 자기 아내를", "서로 분방하지 말라", "각각 하나님께 받은 은사", "임박한 환난", "부른 그 부르심 그대로", "시간이 단축하여"]',
    chapter_context = '결혼과 독신의 문제, 그리고 사회적 신분에 대한 바울의 실용적이고 균형 잡힌 교훈을 제시합니다.',
    chapter_application = '결혼하든 독신이든 하나님께 받은 은사대로 살고, 부름받은 상태에서 하나님을 섬기며, 임박한 주님의 재림을 준비하며 살아야 합니다.'
WHERE book_id = '1co' AND chapter = 7 AND verse = 1;

-- 고린도전서 8장: 우상 제물에 대한 지식과 사랑
UPDATE bible_verses SET
    chapter_title = '우상 제물과 지식보다 중요한 사랑',
    chapter_summary = '우상의 제물에 대하여는 우리가 다 지식이 있는 줄을 아나 지식은 교만하게 하며 사랑은 덕을 세우나니 만일 누구든지 무엇을 아는 줄로 생각하면 아직도 마땅히 알 것을 알지 못하는 것이요 또 누구든지 하나님을 사랑하면 이 사람은 하나님의 아시는 바 되었느니라 그러므로 우상의 제물 먹는 일에 대하여는 우리가 우상은 세상에 아무것도 아니며 또한 하나님은 한 분밖에 없는 줄 아노라 비록 하늘에나 땅에나 신이라 칭하는 자가 있어 많은 신과 많은 주가 있으나 그러나 우리에게는 한 하나님 곧 아버지가 계시니 만물이 그에게서 났고 우리도 그를 위하며 또한 한 주 예수 그리스도께서 계시니 만물이 그로 말미암았고 우리도 그로 말미암았느니라 하시고, 그러나 이 지식은 사람마다 가지지 못하여 어떤 이들은 지금까지 우상에 대한 습관이 있어 우상의 제물로 알고 먹는고로 저희 양심이 약하여져서 더러워지느니라 식물은 우리를 하나님 앞에 세우지 못하나니 우리가 먹지 아니하여도 부족함이 없고 먹어도 풍족함이 없으리라 그런즉 너희 이 자유함이 약한 자들에게 거치는 것이 되지 않도록 조심하라 지식 있는 네가 우상의 사당에 앉아 먹는 것을 누구든지 보면 그 약한 자의 양심이 담력을 얻어 우상의 제물을 먹게 되지 않겠느냐 그러면 네 지식으로 그 약한 자가 멸망하나니 그는 그리스도께서 위하여 죽으신 형제라.',
    chapter_themes = '["지식은 교만하게 하며", "사랑은 덕을 세우나니", "우상은 아무것도 아니며", "한 하나님 한 주", "약한 자의 양심", "그리스도께서 위하여 죽으신 형제"]',
    chapter_context = '우상 제물 문제를 통해 지식과 사랑의 관계, 그리고 약한 형제를 배려하는 그리스도인의 자유 사용법을 가르칩니다.',
    chapter_application = '지식보다 사랑을 우선시하고, 자유를 약한 형제를 배려하는 데 사용하며, 그리스도께서 위해 죽으신 형제를 사랑으로 돌봐야 합니다.'
WHERE book_id = '1co' AND chapter = 8 AND verse = 1;

-- 고린도전서 9장: 사도의 권리와 절제
UPDATE bible_verses SET
    chapter_title = '사도의 권리 포기와 복음을 위한 절제',
    chapter_summary = '내가 자유자가 아니냐 사도가 아니냐 예수 우리 주를 보지 못하였느냐 너희는 주 안에서 나의 행한 일이 아니냐 다른 사람들에게는 내가 사도가 아닐지라도 너희에게는 사도니 나의 사도 됨을 인치는 것이 너희라 나를 힐난하는 자들에게 변명할 것이 이것이니 우리가 먹고 마실 권이 없느냐 우리가 다른 사도들과 주의 형제들과 게바와 같이 자매 된 아내를 데리고 다닐 권이 없느냐 어찌 나와 바나바만 일하지 아니할 권이 없겠느냐 누가 자비로 군병을 하겠느냐 누가 포도원을 심고 그 실과를 먹지 않겠느냐 누가 양떼를 기르고 그 양떼의 젖을 먹지 않겠느냐 내가 이것을 사람의 예대로 말하는 것뿐이냐 율법도 이것을 말하지 아니하느냐 하시고, 내가 복음을 전할지라도 자랑할 것이 없음은 내가 부득불 할 일임이라 만일 복음을 전하지 아니하면 내게 화가 있을 것임이로라 내가 내 임의로 이것을 행하면 상을 얻으려니와 임의로 아니한다 할지라도 나는 직분을 맡았노라 그런즉 내 상이 무엇이뇨 내가 복음을 전할 때에 값없이 전하여 복음에 대한 나의 권을 다 쓰지 아니하는 것이로라 내가 모든 사람에게 자유하였으나 스스로 모든 사람의 종이 되어 더 많은 사람을 얻고자 함이라.',
    chapter_themes = '["사도의 권리", "값없이 복음을", "모든 사람의 종이 되어", "유대인에게는 유대인처럼", "상을 얻기 위하여", "몸을 쳐 복종케 함"]',
    chapter_context = '바울이 사도로서의 권리를 포기하고 복음을 위해 모든 사람의 종이 된 사역 철학과 자기 절제의 중요성을 가르칩니다.',
    chapter_application = '권리를 주장하기보다 섬기는 자세를 가지고, 복음을 위해 모든 사람에게 모든 것이 되며, 자기 절제로 신앙의 경주를 완주해야 합니다.'
WHERE book_id = '1co' AND chapter = 9 AND verse = 1;

-- 고린도전서 10장: 이스라엘의 실패와 우상숭배 경계
UPDATE bible_verses SET
    chapter_title = '이스라엘의 실패 교훈과 우상숭배 경계',
    chapter_summary = '형제들아 너희가 알지 못하기를 원치 아니하노니 우리 조상들이 다 구름 아래 있고 바다 가운데로 지나며 모세에게 속하여 다 구름과 바다에서 세례를 받고 다 같은 신령한 식물을 먹으며 다 같은 신령한 음료를 마셨으니 이는 저희를 따르는 신령한 반석으로부터 마셨으매 그 반석은 곧 그리스도시라 그러나 저희의 다수를 하나님이 기뻐하지 아니하심으로 저희가 광야에서 멸망하였느니라 이런 일이 우리의 거울이 되어 우리로 하여금 저희가 악을 즐겨한 것같이 즐겨하는 자가 되지 않게 하려 함이니 저희 중에 어떤 이들과 같이 우상 숭배하는 자가 되지 말라 기록된 바 백성이 앉아서 먹고 마시며 일어나서 뛰논다 함과 같으니라 하시고, 저희 중에 어떤 이들이 간음하다가 하루에 이만 삼천 명이 죽었나니 우리는 저희와 같이 간음하지 말자 저희 중에 어떤 이들이 주를 시험하다가 뱀에게 멸망하였나니 우리는 저희와 같이 시험하지 말자 저희 중에 어떤 이들이 원망하다가 멸망시키는 자에게 멸망하였나니 너희는 저희와 같이 원망하지 말라 저희에게 당한 이런 일이 거울이 되고 또한 말세를 만난 우리의 경계로 기록하였느니라 그런즉 선 줄로 생각하는 자는 넘어질까 조심하라 사람이 감당할 시험 밖에는 너희에게 당한 것이 없나니 오직 하나님은 미쁘사 너희가 감당치 못할 시험 당함을 허락지 아니하시고 시험 당할 즈음에 또한 피할 길을 내사 너희로 능히 감당하게 하시느니라.',
    chapter_themes = '["신령한 반석은 그리스도", "저희의 다수를 기뻐하지 아니하심", "우리의 거울이 되어", "선 줄로 생각하는 자는", "감당치 못할 시험 허락지 아니하시고", "하나님의 영광을 위하여"]',
    chapter_context = '출애굽 이스라엘의 실패를 거울삼아 우상숭배와 각종 죄를 경계하고, 하나님의 신실하심과 시험 중의 도우심을 가르칩니다.',
    chapter_application = '이스라엘의 실패를 거울삼아 교만하지 말고, 하나님의 신실하심을 믿으며, 시험 중에 피할 길을 찾고, 모든 일을 하나님의 영광을 위해 해야 합니다.'
WHERE book_id = '1co' AND chapter = 10 AND verse = 1;

-- 고린도전서 11장: 머리 가리개와 주의 만찬
UPDATE bible_verses SET
    chapter_title = '머리 가리개와 주의 만찬의 질서',
    chapter_summary = '너희가 나를 기억하고 또 내가 너희에게 전하여 준 대로 유전을 지키므로 너희를 칭찬하노라 그러나 나는 너희가 알기를 원하노니 각 남자의 머리는 그리스도요 여자의 머리는 남자요 그리스도의 머리는 하나님이시라 무릇 남자로서 머리에 무엇을 쓰고 기도나 예언을 하는 자는 그 머리를 욕되게 하는 것이요 무릇 여자로서 머리에 쓴 것을 벗고 기도나 예언을 하는 자는 그 머리를 욕되게 하는 것이니 이는 머리 민 것과 다름이 없음이니라 만일 여자가 머리에 쓰지 않거든 깎을 것이요 만일 깎거나 미는 것이 여자에게 부끄러움이 되거든 쓸지니라 남자는 하나님의 형상과 영광이니 그 머리에 쓰지 않는 것이 마땅하거니와 여자는 남자의 영광이니라 하시고, 그런즉 내 형제들아 먹기 위하여 모일 때에 서로 기다리라 만일 누구든지 시장하거든 집에서 먹을지니 이는 너희의 모임이 판단 받는 모임이 되지 않게 하려 함이라 그 남은 것은 내가 언제든지 갈 때에 귀정하리라 하시고, 내가 주께 받은 것을 또한 너희에게 전하였노니 곧 주 예수께서 잡히시던 밤에 떡을 가지사 축사하시고 떼어 가라사대 이것은 너희를 위하는 내 몸이니 이것을 행하여 나를 기념하라 하시고 식후에 또한 이와 같이 잔을 가지시고 가라사대 이 잔은 내 피로 세운 새 언약이니 이것을 행하여 마실 때마다 나를 기념하라 하셨으니 너희가 이 떡을 먹으며 이 잔을 마실 때마다 주의 죽으심을 오실 때까지 전하는 것이니라.',
    chapter_themes = '["각 남자의 머리는 그리스도", "하나님의 형상과 영광", "주의 만찬", "이것을 행하여 나를 기념하라", "주의 죽으심을 전하는 것", "자기를 살피고"]',
    chapter_context = '예배 질서와 성별의 원리, 그리고 주의 만찬의 참된 의미와 올바른 태도에 대한 교훈을 제시합니다.',
    chapter_application = '하나님이 정하신 질서를 존중하고, 주의 만찬을 거룩하게 지키며, 자기를 살펴 합당하게 참여하고, 주님의 죽으심을 기념해야 합니다.'
WHERE book_id = '1co' AND chapter = 11 AND verse = 1;

-- 고린도전서 12장: 영적 은사의 다양성과 한 몸
UPDATE bible_verses SET
    chapter_title = '영적 은사의 다양성과 그리스도의 한 몸',
    chapter_summary = '형제들아 신령한 것에 대하여 나는 너희가 알지 못하기를 원치 아니하노니 너희도 알거니와 너희가 이방인으로 있을 때에 말 못하는 우상에게로 끄는 그대로 끌려 갔느니라 그러므로 내가 너희에게 알게 하노니 하나님의 영으로 말하는 자는 누구든지 예수를 저주할 자라 하지 않고 또 성령으로 아니하고는 누구든지 예수를 주시라 할 수 없느니라 은사는 여러 가지나 성령은 같고 직임은 여러 가지나 주는 같으며 또 역사는 여러 가지나 모든 것을 모든 사람 가운데서 역사하시는 하나님은 같으니 각 사람에게 성령의 나타남을 주심은 유익하게 하려 하심이라 하시고, 어떤 이에게는 성령으로 말미암아 지혜의 말씀을, 어떤 이에게는 같은 성령을 따라 지식의 말씀을, 다른 이에게는 같은 성령으로 믿음을, 어떤 이에게는 한 성령으로 병 고치는 은사를, 어떤 이에게는 능력 행함을, 어떤 이에게는 예언함을, 어떤 이에게는 영들 분별함을, 다른 이에게는 각종 방언 말함을, 어떤 이에게는 방언들 통역함을 주시나니 이 모든 일은 같은 한 성령이 행하사 그 뜻대로 각 사람에게 나눠 주시느니라 몸은 하나인데 많은 지체가 있고 몸의 지체가 많으나 한 몸임과 같이 그리스도도 그러하니라.',
    chapter_themes = '["예수를 주시라", "은사는 여러 가지나 성령은 같고", "유익하게 하려 하심", "각종 은사들", "그 뜻대로 나눠 주시느니라", "몸은 하나 지체는 많으나"]',
    chapter_context = '영적 은사의 본질과 다양성, 그리고 교회가 그리스도의 한 몸이라는 진리를 통해 연합과 상호 의존의 중요성을 가르칩니다.',
    chapter_application = '성령의 주권을 인정하고, 받은 은사를 공동체의 유익을 위해 사용하며, 다른 지체들을 존중하고 협력하여 그리스도의 몸을 세워야 합니다.'
WHERE book_id = '1co' AND chapter = 12 AND verse = 1;

-- 고린도전서 13장: 사랑의 찬가
UPDATE bible_verses SET
    chapter_title = '사랑의 찬가 - 가장 좋은 길',
    chapter_summary = '내가 사람의 방언과 천사의 말을 할지라도 사랑이 없으면 소리 나는 구리와 울리는 꽹과리가 되고 내가 예언하는 능이 있어 모든 비밀과 모든 지식을 알고 또 산을 옮길 만한 모든 믿음이 있을지라도 사랑이 없으면 내가 아무것도 아니요 내가 내게 있는 모든 것으로 구제하고 또 내 몸을 불사르게 내어줄지라도 사랑이 없으면 내게 아무 유익이 없느니라 사랑은 오래 참고 사랑은 온유하며 투기하지 아니하며 사랑은 자랑하지 아니하며 교만하지 아니하며 무례히 행치 아니하며 자기의 유익을 구치 아니하며 성내지 아니하며 악한 것을 생각지 아니하며 불의를 기뻐하지 아니하며 진리와 함께 기뻐하고 모든 것을 참으며 모든 것을 믿으며 모든 것을 바라며 모든 것을 견디느니라 사랑은 언제까지든지 떨어지지 아니하나 예언도 폐하고 방언도 그치고 지식도 폐하리라 하시고, 우리가 부분적으로 알고 부분적으로 예언하니 온전한 것이 올 때에는 부분적으로 하던 것이 폐하리라 내가 어렸을 때에는 말하는 것이 어린 아이와 같고 깨닫는 것이 어린 아이와 같고 생각하는 것이 어린 아이와 같다가 장성한 사람이 되어서는 어린 아이의 일을 버렸노라 우리가 이제는 거울로 보는 것같이 희미하나 그 때에는 얼굴과 얼굴을 대하여 볼 것이요 이제는 내가 부분적으로 아나 그 때에는 주께서 나를 아신 것같이 내가 온전히 알리라 그런즉 믿음, 소망, 사랑 이 세 가지는 항상 있을 것인데 그 중에 제일은 사랑이라.',
    chapter_themes = '["사랑이 없으면", "사랑은 오래 참고", "사랑은 언제까지든지", "부분적으로 알고", "거울로 보는 것같이", "그 중에 제일은 사랑"]',
    chapter_context = '영적 은사들 중에서 가장 중요한 것이 사랑임을 보여주며, 사랑의 성품과 영원성을 아름답게 묘사한 불멸의 사랑 찬가입니다.',
    chapter_application = '모든 은사와 행위에 사랑이 동기가 되어야 하고, 사랑의 성품을 닮아가며, 사랑을 최고의 가치로 추구하고, 영원한 관점에서 살아야 합니다.'
WHERE book_id = '1co' AND chapter = 13 AND verse = 1;

-- 고린도전서 14장: 예언과 방언의 질서
UPDATE bible_verses SET
    chapter_title = '예언과 방언, 예배의 질서',
    chapter_summary = '사랑을 따라 구하라 신령한 것을 사모하되 특별히 예언을 하려고 하라 방언을 말하는 자는 사람에게 하지 아니하고 하나님께 하나니 이는 알아듣는 자가 없고 그 영으로 비밀을 말함이니라 그러나 예언하는 자는 사람에게 말하여 덕을 세우며 권면하며 안위하는 것이요 방언을 말하는 자는 자기의 덕을 세우고 예언하는 자는 교회의 덕을 세우나니 나는 너희가 다 방언 말하기를 원하나 특별히 예언하기를 원하노라 만일 방언을 말하는 자가 통역하여 교회의 덕을 세우지 아니하면 예언하는 자만 못하니라 하시고, 그런즉 형제들아 내가 너희에게 나아가서 방언으로 말하고 계시나 지식이나 예언이나 가르치는 것이나 말하지 아니하면 너희에게 무엇이 유익하리요 저희가 소리를 분별치 못하면 말하는 자에게나 듣는 자에게나 다 야만이 되리라 이와 같이 너희도 신령한 것을 사모하는 자인즉 교회의 덕 세우기를 위하여 그것이 풍성하기를 구하라 그러므로 방언을 말하는 자는 통역하기를 기도할지니 내가 만일 방언으로 기도하면 나의 영이 기도하거니와 나의 마음은 열매를 맺히지 못하리라 그러면 어떻게 할꼬 내가 영으로 기도하고 또 마음으로 기도하며 내가 영으로 찬미하고 또 마음으로 찬미하리라.',
    chapter_themes = '["특별히 예언을 하려고", "교회의 덕을 세우나니", "계시나 지식이나 예언이나", "교회의 덕 세우기를 위하여", "모든 것을 적당하게 하고", "하나님은 어지러움의 하나님이 아니요"]',
    chapter_context = '방언과 예언의 목적과 올바른 사용법, 그리고 예배에서의 질서와 덕 세움의 원리를 가르칩니다.',
    chapter_application = '교회의 덕을 세우는 은사를 우선시하고, 예배에서 질서를 지키며, 모든 것을 사랑 안에서 적당하게 하고, 하나님의 영광을 드러내야 합니다.'
WHERE book_id = '1co' AND chapter = 14 AND verse = 1;

-- 고린도전서 15장: 부활에 대한 교리
UPDATE bible_verses SET
    chapter_title = '그리스도의 부활과 성도의 부활 소망',
    chapter_summary = '형제들아 내가 너희에게 전한 복음을 너희로 알게 하노니 이 복음을 너희가 받았고 또 그 가운데 선 것이라 너희가 만일 나의 전한 말을 굳게 잡고 있으면 이 복음으로 말미암아 구원을 얻으리라 그렇지 않으면 헛되이 믿었느니라 내가 받은 것을 먼저 너희에게 전하였노니 이는 성경대로 그리스도께서 우리 죄를 위하여 죽으시고 장사 지낸바 되었다가 성경대로 사흘 만에 다시 살아나사 게바에게 보이시고 후에 열두 제자에게와 그 후에 오백여 형제에게 일시에 보이셨나니 그 중에 지금까지 태반이 살아 있고 어떤 이는 잠들었으며 그 후에 야고보에게 보이셨으며 그 후에 모든 사도에게와 맨 나중에 만삭되지 못하여 난 자 같은 내게도 보이셨느니라 하시고, 만일 죽은 자의 부활이 없으면 그리스도도 다시 살지 못하셨으리라 그리스도께서 만일 다시 살지 못하셨으면 우리의 전파하는 것도 헛것이요 또 너희 믿음도 헛것이며 또 우리가 하나님의 거짓 증인으로 발견되리니 이는 하나님이 그리스도를 다시 살리셨다고 우리가 하나님께 대하여 증거하였음이라 만일 죽은 자가 다시 사는 것이 없으면 하나님이 그리스도를 다시 살리지 아니하셨으리라 만일 죽은 자가 다시 사는 것이 없으면 너희의 믿음도 헛되고 너희가 여전히 죄 가운데 있을 것이요 또한 그리스도 안에서 잠자는 자도 망하였으리라 만일 그리스도 안에서 우리의 바라는 것이 다만 이생뿐이면 모든 사람 가운데 우리가 더욱 불쌍한 자리라 그러나 이제 그리스도께서 죽은 자 가운데서 다시 살아 잠자는 자들의 첫 열매가 되셨도다.',
    chapter_themes = '["성경대로 죽으시고 살아나사", "오백여 형제에게 일시에", "부활이 없으면 믿음도 헛것", "잠자는 자들의 첫 열매", "육의 몸과 신령한 몸", "사망아 너의 승리가 어디 있느냐"]',
    chapter_context = '그리스도의 부활의 역사성과 성도의 부활 소망, 그리고 부활의 몸에 대한 교리를 체계적으로 설명하는 부활 장입니다.',
    chapter_application = '그리스도의 부활을 확실히 믿고, 부활 소망으로 현재의 고난을 이기며, 신령한 몸의 영광을 소망하고, 사망을 이기신 주님을 찬양해야 합니다.'
WHERE book_id = '1co' AND chapter = 15 AND verse = 1;

-- 고린도전서 16장: 연보와 마지막 권면
UPDATE bible_verses SET
    chapter_title = '성도를 위한 연보와 마지막 권면들',
    chapter_summary = '성도를 위하는 연보에 대하여는 내가 갈라디아 교회들에게 명한 것같이 너희도 그렇게 하라 매주일 첫날에 너희 각 사람이 이를 얻은 대로 저축하여 두어서 내가 갈 때에 연보를 하지 않게 하라 내가 이를 때에 너희의 인정한 사람에게 편지를 주어 너희의 은혜를 예루살렘으로 가지고 가게 하리니 만일 나도 가는 것이 합당하면 저희가 나와 함께 가리라 내가 마게도냐를 지날 터이니 마게도냐를 지난 후에 너희에게 가서 혹 너희와 함께 머물며 과동하기도 하리니 이는 너희로 하여금 나의 갈 곳을 보내어 주게 하려 함이라 이제는 지나는 길에 너희 보기를 원치 아니하노니 만일 주께서 허락하시면 너희와 함께 얼마 동안 머물기를 바라노라 하시고, 그러나 내가 오순절까지 에베소에 유하려 하노니 이는 크고 공효한 문이 내게 열렸고 대적하는 자가 많음이니라 디모데가 이르거든 너희는 조심하여 저로 하여금 너희 가운데서 두려움이 없게 하라 이는 저도 나와 같이 주의 일을 힘쓰는 자임이라 그러므로 누구든지 저를 멸시하지 말고 평안히 보내어 내게로 오게 하라 나는 저가 형제들과 함께 오기를 기다리노라 형제 아볼로에 대하여는 저더러 형제들과 함께 너희에게 가라고 내가 많이 권하였으되 지금 갈 뜻이 일절 없으나 기회가 있으면 가리라 깨어 믿음에 굳게 서서 남자답게 강하여라 너희 모든 일을 사랑으로 행하라.',
